/** @type {import("prettier").Config} */
export default {
  semi: false,
  singleQuote: true,
  trailingComma: 'es5',
  tabWidth: 2,
  printWidth: 100,
  arrowParens: 'always',
}

/* eslint-disable */
var jumpToCode = (function init() {
    // Classes of code we would like to highlight in the file view
    var missingCoverageClasses = ['.cbranch-no', '.cstat-no', '.fstat-no'];

    // Elements to highlight in the file listing view
    var fileListingElements = ['td.pct.low'];

    // We don't want to select elements that are direct descendants of another match
    var notSelector = ':not(' + missingCoverageClasses.join('):not(') + ') > '; // becomes `:not(a):not(b) > `

    // Selector that finds elements on the page to which we can jump
    var selector =
        fileListingElements.join(', ') +
        ', ' +
        notSelector +
        missingCoverageClasses.join(', ' + notSelector); // becomes `:not(a):not(b) > a, :not(a):not(b) > b`

    // The NodeList of matching elements
    var missingCoverageElements = document.querySelectorAll(selector);

    var currentIndex;

    function toggleClass(index) {
        missingCoverageElements
            .item(currentIndex)
            .classList.remove('highlighted');
        missingCoverageElements.item(index).classList.add('highlighted');
    }

    function makeCurrent(index) {
        toggleClass(index);
        currentIndex = index;
        missingCoverageElements.item(index).scrollIntoView({
            behavior: 'smooth',
            block: 'center',
            inline: 'center'
        });
    }

    function goToPrevious() {
        var nextIndex = 0;
        if (typeof currentIndex !== 'number' || currentIndex === 0) {
            nextIndex = missingCoverageElements.length - 1;
        } else if (missingCoverageElements.length > 1) {
            nextIndex = currentIndex - 1;
        }

        makeCurrent(nextIndex);
    }

    function goToNext() {
        var nextIndex = 0;

        if (
            typeof currentIndex === 'number' &&
            currentIndex < missingCoverageElements.length - 1
        ) {
            nextIndex = currentIndex + 1;
        }

        makeCurrent(nextIndex);
    }

    return function jump(event) {
        if (
            document.getElementById('fileSearch') === document.activeElement &&
            document.activeElement != null
        ) {
            // if we're currently focused on the search input, we don't want to navigate
            return;
        }

        switch (event.which) {
            case 78: // n
            case 74: // j
                goToNext();
                break;
            case 66: // b
            case 75: // k
            case 80: // p
                goToPrevious();
                break;
        }
    };
})();
window.addEventListener('keydown', jumpToCode);

/**
 * Comprehensive CLS (Cumulative Layout Shift) Profiler
 *
 * This script profiles layout shifts on a page and provides detailed analysis
 * of what elements are causing shifts and when they occur.
 */

import puppeteer from 'puppeteer';
import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

// ES module equivalent of __dirname
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

class CLSProfiler {
  constructor(url, options = {}) {
    this.url = url;
    this.options = {
      headless: false,
      slowMo: 0,
      viewport: { width: 1920, height: 1080 },
      waitTime: 10000, // Time to wait for page to fully load
      screenshots: true,
      ...options
    };
    this.results = {
      url,
      timestamp: new Date().toISOString(),
      layoutShifts: [],
      totalCLS: 0,
      maxCLS: 0,
      scenarios: {}
    };
  }

  /**
   * Initialize browser and page with performance monitoring
   */
  async initialize() {
    this.browser = await puppeteer.launch({
      headless: this.options.headless,
      slowMo: this.options.slowMo,
      args: ['--enable-precise-memory-info']
    });

    this.page = await this.browser.newPage();
    await this.page.setViewport(this.options.viewport);

    // Enable performance metrics
    await this.page.evaluateOnNewDocument(() => {
      window.layoutShifts = [];
      window.performanceMetrics = {
        navigationStart: 0,
        fontLoadStart: 0,
        fontLoadEnd: 0,
        imageLoadStart: 0,
        imageLoadEnd: 0
      };
    });
  }

  /**
   * Inject CLS monitoring script into the page
   */
  async injectCLSMonitor() {
    await this.page.evaluateOnNewDocument(() => {
      // Track layout shifts with detailed information
      const observer = new PerformanceObserver((list) => {
        for (const entry of list.getEntries()) {
          if (entry.entryType === 'layout-shift' && !entry.hadRecentInput) {
            const shift = {
              value: entry.value,
              startTime: entry.startTime,
              duration: entry.duration,
              hadRecentInput: entry.hadRecentInput,
              lastInputTime: entry.lastInputTime,
              sources: entry.sources ? Array.from(entry.sources).map(source => ({
                node: source.node ? {
                  tagName: source.node.tagName,
                  id: source.node.id,
                  className: source.node.className,
                  outerHTML: source.node.outerHTML ? source.node.outerHTML.substring(0, 200) : '',
                  boundingRect: source.node.getBoundingClientRect ? {
                    top: source.node.getBoundingClientRect().top,
                    left: source.node.getBoundingClientRect().left,
                    width: source.node.getBoundingClientRect().width,
                    height: source.node.getBoundingClientRect().height
                  } : null
                } : null,
                previousRect: source.previousRect ? {
                  x: source.previousRect.x,
                  y: source.previousRect.y,
                  width: source.previousRect.width,
                  height: source.previousRect.height
                } : null,
                currentRect: source.currentRect ? {
                  x: source.currentRect.x,
                  y: source.currentRect.y,
                  width: source.currentRect.width,
                  height: source.currentRect.height
                } : null
              })) : []
            };

            window.layoutShifts.push(shift);
            console.log('Layout Shift Detected:', shift);
          }
        }
      });

      observer.observe({ type: 'layout-shift', buffered: true });

      // Monitor font loading
      if (document.fonts) {
        window.performanceMetrics.fontLoadStart = performance.now();
        document.fonts.ready.then(() => {
          window.performanceMetrics.fontLoadEnd = performance.now();
          console.log('Fonts loaded at:', window.performanceMetrics.fontLoadEnd);
        });
      }

      // Monitor image loading
      window.addEventListener('load', () => {
        const images = document.querySelectorAll('img');
        let loadedImages = 0;
        const totalImages = images.length;

        window.performanceMetrics.imageLoadStart = performance.now();

        images.forEach(img => {
          if (img.complete) {
            loadedImages++;
          } else {
            img.addEventListener('load', () => {
              loadedImages++;
              if (loadedImages === totalImages) {
                window.performanceMetrics.imageLoadEnd = performance.now();
                console.log('All images loaded at:', window.performanceMetrics.imageLoadEnd);
              }
            });
          }
        });

        if (loadedImages === totalImages) {
          window.performanceMetrics.imageLoadEnd = performance.now();
        }
      });

      // Track navigation start
      window.performanceMetrics.navigationStart = performance.now();
    });
  }

  /**
   * Get Web Vitals metrics
   */
  async getWebVitals() {
    return await this.page.evaluate(() => {
      return new Promise((resolve) => {
        const metrics = {
          CLS: 0,
          LCP: 0,
          FID: 0,
          FCP: 0,
          TTFB: 0
        };

        // Get CLS
        let clsValue = 0;
        const clsObserver = new PerformanceObserver((list) => {
          for (const entry of list.getEntries()) {
            if (!entry.hadRecentInput) {
              clsValue += entry.value;
            }
          }
        });
        clsObserver.observe({ type: 'layout-shift', buffered: true });
        metrics.CLS = clsValue;

        // Get LCP
        const lcpObserver = new PerformanceObserver((list) => {
          const entries = list.getEntries();
          const lastEntry = entries[entries.length - 1];
          metrics.LCP = lastEntry.renderTime || lastEntry.loadTime;
        });
        lcpObserver.observe({ type: 'largest-contentful-paint', buffered: true });

        // Get FCP
        const fcpObserver = new PerformanceObserver((list) => {
          for (const entry of list.getEntries()) {
            if (entry.name === 'first-contentful-paint') {
              metrics.FCP = entry.startTime;
            }
          }
        });
        fcpObserver.observe({ type: 'paint', buffered: true });

        // Get TTFB
        const navigationTiming = performance.getEntriesByType('navigation')[0];
        if (navigationTiming) {
          metrics.TTFB = navigationTiming.responseStart - navigationTiming.requestStart;
        }

        setTimeout(() => resolve(metrics), 100);
      });
    });
  }

  /**
   * Capture layout shifts during page load
   */
  async profileInitialLoad() {
    console.log('\n=== Profiling Initial Page Load ===\n');

    await this.injectCLSMonitor();

    const startTime = Date.now();
    await this.page.goto(this.url, { waitUntil: 'networkidle0', timeout: 30000 });

    // Wait for additional time to catch late shifts
    await new Promise(resolve => setTimeout(resolve, this.options.waitTime));

    const loadTime = Date.now() - startTime;

    // Get layout shifts
    const layoutShifts = await this.page.evaluate(() => window.layoutShifts);
    const performanceMetrics = await this.page.evaluate(() => window.performanceMetrics);
    const webVitals = await this.getWebVitals();

    // Calculate total CLS
    const totalCLS = layoutShifts.reduce((sum, shift) => sum + shift.value, 0);

    this.results.scenarios.initialLoad = {
      loadTime,
      layoutShifts,
      totalCLS,
      performanceMetrics,
      webVitals,
      shiftCount: layoutShifts.length
    };

    if (this.options.screenshots && layoutShifts.length > 0) {
      await this.page.screenshot({
        path: path.join(__dirname, 'cls-profile-initial-load.png'),
        fullPage: true
      });
    }

    console.log(`Initial Load CLS: ${totalCLS.toFixed(4)}`);
    console.log(`Layout Shifts Detected: ${layoutShifts.length}`);
    console.log(`Load Time: ${loadTime}ms`);
  }

  /**
   * Profile during font loading
   */
  async profileFontLoading() {
    console.log('\n=== Profiling Font Loading ===\n');

    // Clear cache and disable font cache
    await this.page.setCacheEnabled(false);

    await this.page.goto(this.url, { waitUntil: 'domcontentloaded' });
    await new Promise(resolve => setTimeout(resolve, 5000));

    const layoutShifts = await this.page.evaluate(() => window.layoutShifts);
    const fontMetrics = await this.page.evaluate(() => window.performanceMetrics);

    // Filter shifts that occurred during font loading
    const fontLoadShifts = layoutShifts.filter(shift => {
      return shift.startTime >= fontMetrics.fontLoadStart &&
             shift.startTime <= fontMetrics.fontLoadEnd;
    });

    this.results.scenarios.fontLoading = {
      layoutShifts: fontLoadShifts,
      totalCLS: fontLoadShifts.reduce((sum, shift) => sum + shift.value, 0),
      fontLoadTime: fontMetrics.fontLoadEnd - fontMetrics.fontLoadStart,
      shiftCount: fontLoadShifts.length
    };

    console.log(`Font Loading CLS: ${this.results.scenarios.fontLoading.totalCLS.toFixed(4)}`);
    console.log(`Font Load Time: ${this.results.scenarios.fontLoading.fontLoadTime.toFixed(2)}ms`);
  }

  /**
   * Profile during image loading with slow 3G network
   */
  async profileSlowImageLoading() {
    console.log('\n=== Profiling Image Loading (Slow 3G) ===\n');

    const client = await this.page.target().createCDPSession();

    // Emulate slow 3G
    await client.send('Network.emulateNetworkConditions', {
      offline: false,
      downloadThroughput: 500 * 1024 / 8, // 500kb/s
      uploadThroughput: 500 * 1024 / 8,
      latency: 400
    });

    await this.injectCLSMonitor();
    await this.page.goto(this.url, { waitUntil: 'domcontentloaded' });
    await new Promise(resolve => setTimeout(resolve, 15000)); // Wait longer for slow network

    const layoutShifts = await this.page.evaluate(() => window.layoutShifts);
    const imageMetrics = await this.page.evaluate(() => window.performanceMetrics);

    this.results.scenarios.slowImageLoading = {
      layoutShifts,
      totalCLS: layoutShifts.reduce((sum, shift) => sum + shift.value, 0),
      imageLoadTime: imageMetrics.imageLoadEnd - imageMetrics.imageLoadStart,
      shiftCount: layoutShifts.length
    };

    // Reset network conditions
    await client.send('Network.emulateNetworkConditions', {
      offline: false,
      downloadThroughput: -1,
      uploadThroughput: -1,
      latency: 0
    });

    console.log(`Slow Image Loading CLS: ${this.results.scenarios.slowImageLoading.totalCLS.toFixed(4)}`);
  }

  /**
   * Profile during scroll and interaction
   */
  async profileScrollAndInteraction() {
    console.log('\n=== Profiling Scroll and Interaction ===\n');

    await this.page.goto(this.url, { waitUntil: 'networkidle0' });
    await new Promise(resolve => setTimeout(resolve, 2000));

    // Clear previous shifts
    await this.page.evaluate(() => { window.layoutShifts = []; });

    // Scroll down the page
    await this.page.evaluate(() => {
      window.scrollTo({ top: document.body.scrollHeight / 2, behavior: 'smooth' });
    });
    await new Promise(resolve => setTimeout(resolve, 2000));

    await this.page.evaluate(() => {
      window.scrollTo({ top: document.body.scrollHeight, behavior: 'smooth' });
    });
    await new Promise(resolve => setTimeout(resolve, 2000));

    const layoutShifts = await this.page.evaluate(() => window.layoutShifts);

    this.results.scenarios.scrollInteraction = {
      layoutShifts,
      totalCLS: layoutShifts.reduce((sum, shift) => sum + shift.value, 0),
      shiftCount: layoutShifts.length
    };

    console.log(`Scroll Interaction CLS: ${this.results.scenarios.scrollInteraction.totalCLS.toFixed(4)}`);
  }

  /**
   * Check for images without dimensions
   */
  async checkImagesWithoutDimensions() {
    console.log('\n=== Checking Images Without Dimensions ===\n');

    const imagesWithoutDimensions = await this.page.evaluate(() => {
      const images = document.querySelectorAll('img');
      const problematic = [];

      images.forEach((img, index) => {
        const hasWidth = img.hasAttribute('width') || img.style.width;
        const hasHeight = img.hasAttribute('height') || img.style.height;
        const hasAspectRatio = img.style.aspectRatio ||
                               window.getComputedStyle(img).aspectRatio !== 'auto';

        if (!hasWidth || !hasHeight) {
          if (!hasAspectRatio) {
            problematic.push({
              index,
              src: img.src,
              alt: img.alt,
              className: img.className,
              id: img.id,
              hasWidth,
              hasHeight,
              hasAspectRatio,
              computedWidth: window.getComputedStyle(img).width,
              computedHeight: window.getComputedStyle(img).height,
              naturalWidth: img.naturalWidth,
              naturalHeight: img.naturalHeight
            });
          }
        }
      });

      return problematic;
    });

    this.results.imagesWithoutDimensions = imagesWithoutDimensions;
    console.log(`Images without proper dimensions: ${imagesWithoutDimensions.length}`);
  }

  /**
   * Analyze layout shift sources
   */
  analyzeShiftSources() {
    console.log('\n=== Analyzing Layout Shift Sources ===\n');

    const allShifts = [];
    Object.values(this.results.scenarios).forEach(scenario => {
      if (scenario.layoutShifts) {
        allShifts.push(...scenario.layoutShifts);
      }
    });

    // Group by element
    const elementShifts = {};
    allShifts.forEach(shift => {
      shift.sources.forEach(source => {
        if (source.node) {
          const key = `${source.node.tagName}.${source.node.className || source.node.id || 'unknown'}`;
          if (!elementShifts[key]) {
            elementShifts[key] = {
              element: source.node,
              shifts: [],
              totalScore: 0,
              count: 0
            };
          }
          elementShifts[key].shifts.push(shift);
          elementShifts[key].totalScore += shift.value;
          elementShifts[key].count++;
        }
      });
    });

    // Sort by impact
    const sortedElements = Object.entries(elementShifts)
      .sort(([, a], [, b]) => b.totalScore - a.totalScore);

    this.results.topShiftingElements = sortedElements.slice(0, 10).map(([key, data]) => ({
      element: key,
      totalScore: data.totalScore,
      shiftCount: data.count,
      averageShift: data.totalScore / data.count,
      elementDetails: data.element
    }));

    console.log('Top 5 shifting elements:');
    this.results.topShiftingElements.slice(0, 5).forEach((item, i) => {
      console.log(`${i + 1}. ${item.element} - CLS: ${item.totalScore.toFixed(4)} (${item.shiftCount} shifts)`);
    });
  }

  /**
   * Generate comprehensive report
   */
  generateReport() {
    console.log('\n=== CLS Profile Report ===\n');

    // Calculate overall metrics
    let totalCLS = 0;
    let totalShifts = 0;

    Object.entries(this.results.scenarios).forEach(([name, scenario]) => {
      totalCLS += scenario.totalCLS || 0;
      totalShifts += scenario.shiftCount || 0;
    });

    this.results.summary = {
      totalCLS,
      totalShifts,
      grade: totalCLS <= 0.1 ? 'Good' : totalCLS <= 0.25 ? 'Needs Improvement' : 'Poor',
      timestamp: this.results.timestamp
    };

    console.log(`Total CLS Score: ${totalCLS.toFixed(4)}`);
    console.log(`Grade: ${this.results.summary.grade}`);
    console.log(`Total Layout Shifts: ${totalShifts}`);
    console.log('\nScenario Breakdown:');

    Object.entries(this.results.scenarios).forEach(([name, scenario]) => {
      console.log(`  ${name}: ${scenario.totalCLS.toFixed(4)} (${scenario.shiftCount} shifts)`);
    });

    return this.results;
  }

  /**
   * Save report to file
   */
  async saveReport(filename = 'cls-profile-report.json') {
    const reportPath = path.join(__dirname, filename);
    fs.writeFileSync(reportPath, JSON.stringify(this.results, null, 2));
    console.log(`\nReport saved to: ${reportPath}`);

    // Also save a human-readable markdown report
    const mdReport = this.generateMarkdownReport();
    const mdPath = path.join(__dirname, filename.replace('.json', '.md'));
    fs.writeFileSync(mdPath, mdReport);
    console.log(`Markdown report saved to: ${mdPath}`);
  }

  /**
   * Generate markdown report
   */
  generateMarkdownReport() {
    const { summary, scenarios, topShiftingElements, imagesWithoutDimensions } = this.results;

    let md = `# CLS Profile Report\n\n`;
    md += `**URL:** ${this.url}\n`;
    md += `**Timestamp:** ${summary.timestamp}\n`;
    md += `**Total CLS Score:** ${summary.totalCLS.toFixed(4)}\n`;
    md += `**Grade:** ${summary.grade}\n`;
    md += `**Total Layout Shifts:** ${summary.totalShifts}\n\n`;

    md += `## Summary\n\n`;
    md += `| Metric | Value | Status |\n`;
    md += `|--------|-------|--------|\n`;
    md += `| CLS Score | ${summary.totalCLS.toFixed(4)} | ${summary.grade} |\n`;
    md += `| Threshold (Good) | ‚â§ 0.1 | ${summary.totalCLS <= 0.1 ? '‚úÖ' : '‚ùå'} |\n`;
    md += `| Threshold (Needs Improvement) | ‚â§ 0.25 | ${summary.totalCLS <= 0.25 ? '‚úÖ' : '‚ùå'} |\n\n`;

    md += `## Scenario Analysis\n\n`;
    Object.entries(scenarios).forEach(([name, scenario]) => {
      md += `### ${name}\n\n`;
      md += `- **CLS Score:** ${scenario.totalCLS.toFixed(4)}\n`;
      md += `- **Shift Count:** ${scenario.shiftCount}\n`;
      if (scenario.loadTime) md += `- **Load Time:** ${scenario.loadTime}ms\n`;
      if (scenario.fontLoadTime) md += `- **Font Load Time:** ${scenario.fontLoadTime.toFixed(2)}ms\n`;
      if (scenario.imageLoadTime) md += `- **Image Load Time:** ${scenario.imageLoadTime.toFixed(2)}ms\n`;
      md += `\n`;

      if (scenario.webVitals) {
        md += `**Web Vitals:**\n`;
        md += `- LCP: ${scenario.webVitals.LCP.toFixed(2)}ms\n`;
        md += `- FCP: ${scenario.webVitals.FCP.toFixed(2)}ms\n`;
        md += `- TTFB: ${scenario.webVitals.TTFB.toFixed(2)}ms\n\n`;
      }
    });

    if (topShiftingElements && topShiftingElements.length > 0) {
      md += `## Top Shifting Elements\n\n`;
      md += `| Rank | Element | CLS Score | Shift Count | Avg Shift |\n`;
      md += `|------|---------|-----------|-------------|----------|\n`;
      topShiftingElements.forEach((item, i) => {
        md += `| ${i + 1} | ${item.element} | ${item.totalScore.toFixed(4)} | ${item.shiftCount} | ${item.averageShift.toFixed(4)} |\n`;
      });
      md += `\n`;
    }

    if (imagesWithoutDimensions && imagesWithoutDimensions.length > 0) {
      md += `## Images Without Proper Dimensions\n\n`;
      md += `Found ${imagesWithoutDimensions.length} images without proper width/height or aspect-ratio:\n\n`;
      imagesWithoutDimensions.forEach((img, i) => {
        md += `${i + 1}. **${img.src}**\n`;
        md += `   - Class: ${img.className || 'none'}\n`;
        md += `   - ID: ${img.id || 'none'}\n`;
        md += `   - Natural Size: ${img.naturalWidth}x${img.naturalHeight}\n`;
        md += `   - Has Width: ${img.hasWidth ? '‚úÖ' : '‚ùå'}\n`;
        md += `   - Has Height: ${img.hasHeight ? '‚úÖ' : '‚ùå'}\n`;
        md += `   - Has Aspect Ratio: ${img.hasAspectRatio ? '‚úÖ' : '‚ùå'}\n\n`;
      });
    }

    md += `## Recommendations\n\n`;
    if (summary.totalCLS <= 0.1) {
      md += `‚úÖ **Excellent!** Your CLS score is within the "Good" threshold.\n\n`;
      md += `Continue to monitor for regressions and ensure:\n`;
      md += `- All images have explicit dimensions or aspect ratios\n`;
      md += `- Fonts are loaded with font-display: swap or optional\n`;
      md += `- Dynamic content has reserved space\n`;
    } else if (summary.totalCLS <= 0.25) {
      md += `‚ö†Ô∏è **Needs Improvement** - Your CLS score needs optimization.\n\n`;
      md += `Priority actions:\n`;
      md += `1. Review top shifting elements above\n`;
      md += `2. Add dimensions to images without them\n`;
      md += `3. Optimize font loading strategy\n`;
      md += `4. Reserve space for dynamic content\n`;
    } else {
      md += `‚ùå **Poor** - Your CLS score requires immediate attention.\n\n`;
      md += `Critical actions:\n`;
      md += `1. Fix all images without dimensions\n`;
      md += `2. Implement font loading optimization\n`;
      md += `3. Eliminate layout shifts from dynamic content\n`;
      md += `4. Review and fix top shifting elements\n`;
    }

    return md;
  }

  /**
   * Run complete profiling suite
   */
  async run() {
    try {
      await this.initialize();

      await this.profileInitialLoad();
      await this.checkImagesWithoutDimensions();
      await this.profileFontLoading();
      await this.profileSlowImageLoading();
      await this.profileScrollAndInteraction();

      this.analyzeShiftSources();
      this.generateReport();
      await this.saveReport();

    } catch (error) {
      console.error('Error during profiling:', error);
      throw error;
    } finally {
      if (this.browser) {
        await this.browser.close();
      }
    }
  }
}

// Main execution
const url = process.argv[2] || 'http://localhost:3000/feed';
const profiler = new CLSProfiler(url, {
  headless: true,
  screenshots: true,
  waitTime: 8000
});

profiler.run().catch(console.error);

/**
 * User Deletion Script
 *
 * This script safely deletes a user and all their associated data from Firebase.
 * It handles all collections, subcollections, and references.
 *
 * REQUIRED FIREBASE IAM ROLES:
 * - roles/firebase.admin (or equivalent with these permissions)
 * - roles/firebasedatabase.admin (for Realtime Database, if applicable)
 * - roles/datastore.owner (for Firestore access)
 *
 * REQUIRED SERVICE ACCOUNT PERMISSIONS:
 * - firebase.auth.users.delete
 * - datastore.databases.update
 * - datastore.databases.get
 * - datastore.entities.delete
 * - datastore.entities.get
 * - datastore.entities.update
 *
 * VERIFICATION:
 * To verify your service account has required permissions, run:
 *   gcloud projects get-iam-policy PROJECT_ID --flatten="bindings[].members" --format="table(bindings.role)" --filter="bindings.members:serviceAccount:YOUR_SA@PROJECT_ID.iam.gserviceaccount.com"
 *
 * Usage:
 *   npx ts-node scripts/ops/deleteUser.ts <userId> [--dry-run]
 *
 * Examples:
 *   npx ts-node scripts/ops/deleteUser.ts abc123xyz
 *   npx ts-node scripts/ops/deleteUser.ts abc123xyz --dry-run
 */

import { initializeApp, cert, ServiceAccount } from 'firebase-admin/app';
import { getFirestore, FieldValue, QueryDocumentSnapshot, DocumentData } from 'firebase-admin/firestore';
import { getAuth } from 'firebase-admin/auth';
import * as path from 'path';
import { fileURLToPath } from 'url';
import { readFileSync } from 'fs';

// ES module equivalent of __dirname
const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Initialize Firebase Admin
const serviceAccount = JSON.parse(
  readFileSync(path.join(__dirname, '../serviceAccountKey.json'), 'utf-8')
) as ServiceAccount;

initializeApp({
  credential: cert(serviceAccount)
});

const db = getFirestore();
const auth = getAuth();

interface DeletionStats {
  user: boolean;
  auth: boolean;
  projects: number;
  tasks: number;
  sessions: number;
  comments: number;
  supports: number;
  follows: number;
  streaks: number;
  challengeParticipants: number;
  groupMembers: number;
  notifications: number;
  activeSession: number;
}

interface DeletionOptions {
  dryRun: boolean;
}

async function deleteUser(userId: string, options: DeletionOptions): Promise<void> {
  const dryRunPrefix = options.dryRun ? '[DRY RUN] ' : '';
  console.log(`${dryRunPrefix}üöÄ Starting deletion process for user: ${userId}\n`);

  const stats: DeletionStats = {
    user: false,
    auth: false,
    projects: 0,
    tasks: 0,
    sessions: 0,
    comments: 0,
    supports: 0,
    follows: 0,
    streaks: 0,
    challengeParticipants: 0,
    groupMembers: 0,
    notifications: 0,
    activeSession: 0,
  };

  try {
    // Get user document first to verify existence and get username
    const userDoc = await db.collection('users').doc(userId).get();
    if (!userDoc.exists) {
      throw new Error(`User with ID ${userId} does not exist`);
    }

    const userData = userDoc.data();
    const username = userData?.username || 'unknown';
    console.log(`${dryRunPrefix}üìã User found: ${username} (${userId})`);
    console.log(`   Followers: ${userData?.followerCount || 0}`);
    console.log(`   Following: ${userData?.followingCount || 0}\n`);

    // Show dry run mode notice
    if (options.dryRun) {
      console.log('üîç DRY RUN MODE - No changes will be made\n');
    }

    // Confirm deletion
    console.log(`${dryRunPrefix}‚ö†Ô∏è  This will permanently delete:`);
    console.log('   - User profile');
    console.log('   - All projects and tasks');
    console.log('   - All sessions');
    console.log('   - All comments');
    console.log('   - All supports (likes)');
    console.log('   - All follow relationships');
    console.log('   - Streak data');
    console.log('   - Challenge participations');
    console.log('   - Group memberships');
    console.log('   - Notifications');
    console.log('   - Firebase Authentication account\n');

    // In a real scenario, you'd want a confirmation prompt here
    // For this script, we'll proceed automatically

    // 1. Delete active session subcollection
    console.log(`${dryRunPrefix}üîÑ Deleting active session...`);
    const activeSessionSnapshot = await db
      .collection('users')
      .doc(userId)
      .collection('activeSession')
      .get();

    stats.activeSession = activeSessionSnapshot.docs.length;
    if (!options.dryRun) {
      const activeSessionBatch = db.batch();
      activeSessionSnapshot.docs.forEach((doc: QueryDocumentSnapshot<DocumentData>) => {
        activeSessionBatch.delete(doc.ref);
      });
      await activeSessionBatch.commit();
    }
    console.log(`   ‚úÖ ${options.dryRun ? 'Would delete' : 'Deleted'} ${stats.activeSession} active session records\n`);

    // 2. Delete all projects and their tasks
    console.log(`${dryRunPrefix}üîÑ Deleting projects and tasks...`);
    const projectsSnapshot = await db
      .collection('projects')
      .doc(userId)
      .collection('userProjects')
      .get();

    for (const projectDoc of projectsSnapshot.docs) {
      // Count tasks for this project
      const tasksSnapshot = await projectDoc.ref.collection('tasks').get();
      stats.tasks += tasksSnapshot.docs.length;

      if (!options.dryRun) {
        const taskBatch = db.batch();
        tasksSnapshot.docs.forEach((taskDoc: QueryDocumentSnapshot<DocumentData>) => {
          taskBatch.delete(taskDoc.ref);
        });
        await taskBatch.commit();

        // Delete project
        await projectDoc.ref.delete();
      }
      stats.projects++;
    }

    if (!options.dryRun) {
      // Delete the projects document
      await db.collection('projects').doc(userId).delete();
    }
    console.log(`   ‚úÖ ${options.dryRun ? 'Would delete' : 'Deleted'} ${stats.projects} projects and ${stats.tasks} tasks\n`);

    // 3. Delete all sessions created by this user
    console.log(`${dryRunPrefix}üîÑ Deleting sessions...`);
    const sessionsSnapshot = await db
      .collection('sessions')
      .where('userId', '==', userId)
      .get();

    stats.sessions = sessionsSnapshot.docs.length;
    if (!options.dryRun) {
      const sessionBatch = db.batch();
      sessionsSnapshot.docs.forEach((doc: QueryDocumentSnapshot<DocumentData>) => {
        sessionBatch.delete(doc.ref);
      });
      await sessionBatch.commit();
    }
    console.log(`   ‚úÖ ${options.dryRun ? 'Would delete' : 'Deleted'} ${stats.sessions} sessions\n`);

    // 4. Delete all comments by this user
    console.log(`${dryRunPrefix}üîÑ Deleting comments...`);
    const commentsSnapshot = await db
      .collection('comments')
      .where('userId', '==', userId)
      .get();

    stats.comments = commentsSnapshot.docs.length;
    if (!options.dryRun) {
      const commentBatch = db.batch();
      commentsSnapshot.docs.forEach((doc: QueryDocumentSnapshot<DocumentData>) => {
        commentBatch.delete(doc.ref);
      });
      await commentBatch.commit();
    }
    console.log(`   ‚úÖ ${options.dryRun ? 'Would delete' : 'Deleted'} ${stats.comments} comments\n`);

    // 5. Delete all supports (likes) by this user
    console.log(`${dryRunPrefix}üîÑ Deleting supports...`);
    const supportsSnapshot = await db
      .collection('supports')
      .where('userId', '==', userId)
      .get();

    stats.supports = supportsSnapshot.docs.length;
    if (!options.dryRun) {
      const supportBatch = db.batch();
      supportsSnapshot.docs.forEach((doc: QueryDocumentSnapshot<DocumentData>) => {
        supportBatch.delete(doc.ref);
      });
      await supportBatch.commit();
    }
    console.log(`   ‚úÖ ${options.dryRun ? 'Would delete' : 'Deleted'} ${stats.supports} supports\n`);

    // 6. Delete follow relationships and update counts
    console.log(`${dryRunPrefix}üîÑ Deleting follow relationships...`);

    // Delete where user is the follower
    const followingSnapshot = await db
      .collection('follows')
      .where('followerId', '==', userId)
      .get();

    if (!options.dryRun && followingSnapshot.docs.length > 0) {
      const followBatch1 = db.batch();
      for (const doc of followingSnapshot.docs) {
        const followData = doc.data();
        // Decrement the following user's follower count
        const followingUserRef = db.collection('users').doc(followData.followingId);
        followBatch1.update(followingUserRef, {
          followerCount: FieldValue.increment(-1)
        });
        followBatch1.delete(doc.ref);
      }
      await followBatch1.commit();
    }
    stats.follows += followingSnapshot.docs.length;

    // Delete where user is being followed
    const followersSnapshot = await db
      .collection('follows')
      .where('followingId', '==', userId)
      .get();

    if (!options.dryRun && followersSnapshot.docs.length > 0) {
      const followBatch2 = db.batch();
      for (const doc of followersSnapshot.docs) {
        const followData = doc.data();
        // Decrement the follower's following count
        const followerUserRef = db.collection('users').doc(followData.followerId);
        followBatch2.update(followerUserRef, {
          followingCount: FieldValue.increment(-1)
        });
        followBatch2.delete(doc.ref);
      }
      await followBatch2.commit();
    }
    stats.follows += followersSnapshot.docs.length;
    console.log(`   ‚úÖ ${options.dryRun ? 'Would delete' : 'Deleted'} ${stats.follows} follow relationships\n`);

    // 7. Delete streak data
    console.log(`${dryRunPrefix}üîÑ Deleting streak data...`);
    const streakRef = db.collection('streaks').doc(userId);
    const streakDoc = await streakRef.get();
    if (streakDoc.exists) {
      if (!options.dryRun) {
        await streakRef.delete();
      }
      stats.streaks = 1;
    }
    console.log(`   ‚úÖ ${options.dryRun ? 'Would delete' : 'Deleted'} ${stats.streaks} streak records\n`);

    // 8. Delete challenge participations
    console.log(`${dryRunPrefix}üîÑ Deleting challenge participations...`);
    const challengeParticipantsSnapshot = await db
      .collection('challengeParticipants')
      .where('userId', '==', userId)
      .get();

    stats.challengeParticipants = challengeParticipantsSnapshot.docs.length;
    if (!options.dryRun && challengeParticipantsSnapshot.docs.length > 0) {
      const challengeBatch = db.batch();
      challengeParticipantsSnapshot.docs.forEach((doc: QueryDocumentSnapshot<DocumentData>) => {
        challengeBatch.delete(doc.ref);
      });
      await challengeBatch.commit();
    }
    console.log(`   ‚úÖ ${options.dryRun ? 'Would delete' : 'Deleted'} ${stats.challengeParticipants} challenge participations\n`);

    // 9. Remove from group members
    console.log(`${dryRunPrefix}üîÑ Removing from groups...`);
    const groupsSnapshot = await db
      .collection('groups')
      .where('members', 'array-contains', userId)
      .get();

    stats.groupMembers = groupsSnapshot.docs.length;
    if (!options.dryRun && groupsSnapshot.docs.length > 0) {
      const groupBatch = db.batch();
      groupsSnapshot.docs.forEach((doc: QueryDocumentSnapshot<DocumentData>) => {
        groupBatch.update(doc.ref, {
          members: FieldValue.arrayRemove(userId),
          memberCount: FieldValue.increment(-1)
        });
      });
      await groupBatch.commit();
    }
    console.log(`   ‚úÖ ${options.dryRun ? 'Would remove' : 'Removed'} from ${stats.groupMembers} groups\n`);

    // 10. Delete notifications for this user
    console.log(`${dryRunPrefix}üîÑ Deleting notifications...`);
    const notificationsSnapshot = await db
      .collection('notifications')
      .where('userId', '==', userId)
      .get();

    stats.notifications = notificationsSnapshot.docs.length;
    if (!options.dryRun && notificationsSnapshot.docs.length > 0) {
      const notificationBatch = db.batch();
      notificationsSnapshot.docs.forEach((doc: QueryDocumentSnapshot<DocumentData>) => {
        notificationBatch.delete(doc.ref);
      });
      await notificationBatch.commit();
    }
    console.log(`   ‚úÖ ${options.dryRun ? 'Would delete' : 'Deleted'} ${stats.notifications} notifications\n`);

    // 11. Delete user document
    if (!options.dryRun) {
      console.log(`${dryRunPrefix}üîÑ Deleting user document...`);
      await db.collection('users').doc(userId).delete();
      stats.user = true;
      console.log('   ‚úÖ User document deleted\n');
    } else {
      console.log(`${dryRunPrefix}üîÑ Would delete user document...`);
      console.log('   ‚úÖ Would delete\n');
      stats.user = true;
    }

    // 12. Delete Firebase Authentication account
    if (!options.dryRun) {
      console.log(`${dryRunPrefix}üîÑ Deleting Firebase Auth account...`);
      try {
        await auth.deleteUser(userId);
        stats.auth = true;
        console.log('   ‚úÖ Firebase Auth account deleted\n');
      } catch (error: any) {
        if (error.code === 'auth/user-not-found') {
          console.log('   ‚ö†Ô∏è  Auth account not found (may have been deleted already)\n');
          stats.auth = true;
        } else {
          throw error;
        }
      }
    } else {
      console.log(`${dryRunPrefix}üîÑ Would delete Firebase Auth account...`);
      console.log('   ‚úÖ Would delete\n');
      stats.auth = true;
    }

    // Print summary
    const completionStatus = options.dryRun ? 'DRY RUN COMPLETE' : 'DELETION COMPLETE';
    console.log(`‚úÖ ${completionStatus}\n`);
    console.log('üìä Summary:');
    console.log(`   User Document: ${stats.user ? '‚úÖ' : '‚ùå'}`);
    console.log(`   Auth Account: ${stats.auth ? '‚úÖ' : '‚ùå'}`);
    console.log(`   Projects: ${stats.projects}`);
    console.log(`   Tasks: ${stats.tasks}`);
    console.log(`   Sessions: ${stats.sessions}`);
    console.log(`   Comments: ${stats.comments}`);
    console.log(`   Supports: ${stats.supports}`);
    console.log(`   Follows: ${stats.follows}`);
    console.log(`   Streaks: ${stats.streaks}`);
    console.log(`   Challenge Participations: ${stats.challengeParticipants}`);
    console.log(`   Group Memberships: ${stats.groupMembers}`);
    console.log(`   Notifications: ${stats.notifications}`);
    console.log(`   Active Session Records: ${stats.activeSession}\n`);

    if (options.dryRun) {
      console.log('To execute this deletion, run without the --dry-run flag.\n');
    }

  } catch (error) {
    console.error('‚ùå Error during deletion:', error);
    throw error;
  }
}

// Main execution
const userId = process.argv[2];
const dryRunFlag = process.argv.includes('--dry-run');

if (!userId) {
  console.error('‚ùå Error: User ID is required');
  console.log('\nUsage:');
  console.log('  npx ts-node scripts/ops/deleteUser.ts <userId> [--dry-run]');
  console.log('\nExamples:');
  console.log('  npx ts-node scripts/ops/deleteUser.ts abc123xyz');
  console.log('  npx ts-node scripts/ops/deleteUser.ts abc123xyz --dry-run\n');
  process.exit(1);
}

const options: DeletionOptions = {
  dryRun: dryRunFlag
};

deleteUser(userId, options)
  .then(() => {
    console.log('‚úÖ Script completed successfully');
    process.exit(0);
  })
  .catch((error) => {
    console.error('‚ùå Script failed:', error);
    process.exit(1);
  });

import React from 'react';
import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import { ImageGallery } from '@/components/ImageGallery';

// Mock Next.js Image component
jest.mock('next/image', () => ({
  __esModule: true,
  default: (props: any) => {
    // Extract Next.js specific props to avoid passing them to img tag
    const { fill, priority, quality, sizes, loading, ...imgProps } = props;
    return <img {...imgProps} />;
  },
}));

describe('ImageGallery', () => {
  const mockImages = [
    'https://example.com/image1.jpg',
    'https://example.com/image2.jpg',
    'https://example.com/image3.jpg',
  ];

  beforeEach(() => {
    // Mock URL.createObjectURL for blob URLs
    global.URL.createObjectURL = jest.fn(() => 'mock-blob-url');
  });

  it('should render nothing when images array is empty', () => {
    const { container } = render(<ImageGallery images={[]} />);
    expect(container.firstChild).toBeNull();
  });

  it('should render nothing when images is undefined', () => {
    const { container } = render(<ImageGallery images={undefined as any} />);
    expect(container.firstChild).toBeNull();
  });

  it('should render a single image without navigation', () => {
    const firstImage = mockImages[0];
    if (!firstImage) return;
    render(<ImageGallery images={[firstImage]} />);

    const image = screen.getByAltText('Image 1');
    expect(image).toBeInTheDocument();
    expect(image).toHaveAttribute('src', firstImage);

    // Should not show navigation arrows for single image
    expect(screen.queryByLabelText('Previous image')).not.toBeInTheDocument();
    expect(screen.queryByLabelText('Next image')).not.toBeInTheDocument();
  });

  it('should render multiple images with first image visible', () => {
    render(<ImageGallery images={mockImages} />);

    const image = screen.getByAltText('Image 1');
    expect(image).toHaveAttribute('src', mockImages[0]);
  });

  it('should show dot indicators for multiple images', () => {
    render(<ImageGallery images={mockImages} />);

    const dots = screen.getAllByRole('button', { name: /Go to image/i });
    expect(dots).toHaveLength(3);
  });

  it('should not show dot indicators for single image', () => {
    const firstImage = mockImages[0];
    if (!firstImage) return;
    render(<ImageGallery images={[firstImage]} />);

    const dots = screen.queryAllByRole('button', { name: /Go to image/i });
    expect(dots).toHaveLength(0);
  });

  it('should navigate to next image when next button is clicked', () => {
    render(<ImageGallery images={mockImages} />);

    const nextButton = screen.getByLabelText('Next image');
    fireEvent.click(nextButton);

    const image = screen.getByAltText('Image 2');
    expect(image).toHaveAttribute('src', mockImages[1]);
  });

  it('should navigate to previous image when previous button is clicked', () => {
    render(<ImageGallery images={mockImages} />);

    // Go to second image first
    const nextButton = screen.getByLabelText('Next image');
    fireEvent.click(nextButton);

    // Then go back
    const prevButton = screen.getByLabelText('Previous image');
    fireEvent.click(prevButton);

    const image = screen.getByAltText('Image 1');
    expect(image).toHaveAttribute('src', mockImages[0]);
  });

  it('should not show previous button on first image', () => {
    render(<ImageGallery images={mockImages} />);

    expect(screen.queryByLabelText('Previous image')).not.toBeInTheDocument();
    expect(screen.getByLabelText('Next image')).toBeInTheDocument();
  });

  it('should not show next button on last image', () => {
    render(<ImageGallery images={mockImages} />);

    // Navigate to last image
    const nextButton = screen.getByLabelText('Next image');
    fireEvent.click(nextButton); // to image 2
    fireEvent.click(nextButton); // to image 3

    expect(screen.queryByLabelText('Next image')).not.toBeInTheDocument();
    expect(screen.getByLabelText('Previous image')).toBeInTheDocument();
  });

  it('should navigate using dot indicators', () => {
    render(<ImageGallery images={mockImages} />);

    const dots = screen.getAllByRole('button', { name: /Go to image/i });
    const thirdDot = dots[2];
    if (!thirdDot) return;

    // Click on third dot
    fireEvent.click(thirdDot);

    const image = screen.getByAltText('Image 3');
    expect(image).toHaveAttribute('src', mockImages[2]);
  });

  it('should highlight the active dot indicator', () => {
    const { container } = render(<ImageGallery images={mockImages} />);

    const dots = screen.getAllByRole('button', { name: /Go to image/i });
    const firstDot = dots[0];
    const secondDot = dots[1];
    if (!firstDot || !secondDot) return;

    // First dot should be active (has bg-[#007AFF] class)
    expect(firstDot).toHaveClass('bg-[#007AFF]');
    expect(secondDot).toHaveClass('bg-gray-300');

    // Click second dot
    fireEvent.click(secondDot);

    // Second dot should now be active
    expect(secondDot).toHaveClass('bg-[#007AFF]');
    expect(firstDot).toHaveClass('bg-gray-300');
  });

  it('should handle swipe gestures on touch devices', () => {
    render(<ImageGallery images={mockImages} />);

    const container = screen.getByAltText('Image 1').closest('div');

    // Simulate swipe left (next image)
    fireEvent.touchStart(container!, { targetTouches: [{ clientX: 200 }] });
    fireEvent.touchMove(container!, { targetTouches: [{ clientX: 100 }] });
    fireEvent.touchEnd(container!);

    const image = screen.getByAltText('Image 2');
    expect(image).toHaveAttribute('src', mockImages[1]);
  });

  it('should handle swipe right gesture', () => {
    render(<ImageGallery images={mockImages} />);

    const container = screen.getByAltText('Image 1').closest('div');

    // Go to second image first
    const nextButton = screen.getByLabelText('Next image');
    fireEvent.click(nextButton);

    // Simulate swipe right (previous image)
    fireEvent.touchStart(container!, { targetTouches: [{ clientX: 100 }] });
    fireEvent.touchMove(container!, { targetTouches: [{ clientX: 200 }] });
    fireEvent.touchEnd(container!);

    const image = screen.getByAltText('Image 1');
    expect(image).toHaveAttribute('src', mockImages[0]);
  });

  it('should not swipe if distance is too small', () => {
    render(<ImageGallery images={mockImages} />);

    const container = screen.getByAltText('Image 1').closest('div');

    // Simulate small swipe (less than minSwipeDistance of 50)
    fireEvent.touchStart(container!, { targetTouches: [{ clientX: 100 }] });
    fireEvent.touchMove(container!, { targetTouches: [{ clientX: 90 }] });
    fireEvent.touchEnd(container!);

    // Should still be on first image
    const image = screen.getByAltText('Image 1');
    expect(image).toHaveAttribute('src', mockImages[0]);
  });

  it('should not swipe past the first image', () => {
    render(<ImageGallery images={mockImages} />);

    const container = screen.getByAltText('Image 1').closest('div');

    // Try to swipe right on first image
    fireEvent.touchStart(container!, { targetTouches: [{ clientX: 100 }] });
    fireEvent.touchMove(container!, { targetTouches: [{ clientX: 200 }] });
    fireEvent.touchEnd(container!);

    // Should still be on first image
    const image = screen.getByAltText('Image 1');
    expect(image).toHaveAttribute('src', mockImages[0]);
  });

  it('should not swipe past the last image', () => {
    render(<ImageGallery images={mockImages} />);

    const container = screen.getByAltText('Image 1').closest('div');

    // Navigate to last image
    const dots = screen.getAllByRole('button', { name: /Go to image/i });
    const thirdDot = dots[2];
    if (!thirdDot) return;
    fireEvent.click(thirdDot);

    // Try to swipe left on last image
    fireEvent.touchStart(container!, { targetTouches: [{ clientX: 200 }] });
    fireEvent.touchMove(container!, { targetTouches: [{ clientX: 100 }] });
    fireEvent.touchEnd(container!);

    // Should still be on last image
    const image = screen.getByAltText('Image 3');
    expect(image).toHaveAttribute('src', mockImages[2]);
  });

  it('should apply custom className', () => {
    const { container } = render(
      <ImageGallery images={mockImages} className="custom-class" />
    );

    expect(container.firstChild).toHaveClass('custom-class');
  });

  it('should have proper aspect ratio', () => {
    const { container } = render(<ImageGallery images={mockImages} />);

    const imageContainer = container.querySelector('.aspect-\\[16\\/10\\]');
    expect(imageContainer).toBeInTheDocument();
  });

  it('should pass priority prop to first image for Next.js optimization', () => {
    render(<ImageGallery images={mockImages} />);

    const image = screen.getByAltText('Image 1');
    // In the real Next.js Image component, priority is handled internally
    // Our mock renders it as a regular img tag, so we just verify image exists
    expect(image).toBeInTheDocument();
    expect(image).toHaveAttribute('src', mockImages[0]);
  });
});

