      ...baseSession,
      description: 'A'.repeat(300), // Long description
      images: ['https://example.com/image.jpg'],
    };

    render(
      <SessionCard
        session={sessionWithImages}
        onSupport={mockOnSupport}
        onRemoveSupport={mockOnRemoveSupport}
        onShare={mockOnShare}
      />
