  it('should limit to maximum 3 images', async () => {
    // Mock window.alert
    const alertMock = jest.spyOn(window, 'alert').mockImplementation(() => {});

    render(<SessionTimerEnhanced projectId="proj1" />);

    const finishButton = screen.getByRole('button', { name: /finish/i });
