function EditActivityContent({ activityId }: { activityId: string }) {
  const router = useRouter();
  const { user } = useAuth();
  const { data: projects = [] } = useActivities(user?.id);
  const updateProject = useUpdateActivity();
  const [isLoading, setIsLoading] = useState(true);
  const [formData, setFormData] = useState<UpdateActivityData>({
    name: '',
    description: '',
    icon: 'briefcase',
    color: 'orange',
    weeklyTarget: undefined,
    totalTarget: undefined,
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [errors, setErrors] = useState<
    Partial<Record<keyof UpdateActivityData, string>>
  >({});
  const [successMessage, setSuccessMessage] = useState('');

  // Load activity data
  useEffect(() => {
    const activity = projects?.find(p => p.id === activityId);
    if (activity) {
      setFormData({
        name: activity.name,
        description: activity.description || '',
        icon: activity.icon,
        color: activity.color,
        weeklyTarget: activity.weeklyTarget,
        totalTarget: activity.totalTarget,
      });
      setIsLoading(false);
    } else if (!isLoading) {
      // If activity not found and projects are loaded, redirect
      router.push('/activities');
    }
  }, [activityId, projects]);

  const validateForm = (): boolean => {
    const newErrors: Partial<Record<keyof UpdateActivityData, string>> = {};

    if (!formData.name?.trim()) {
      newErrors.name = 'Activity name is required';
    } else if (formData.name.length > 50) {
      newErrors.name = 'Activity name must be less than 50 characters';
    }

    if (formData.description && formData.description.length > 200) {
      newErrors.description = 'Description must be less than 200 characters';
    }

    if (
      formData.weeklyTarget &&
      (formData.weeklyTarget < 0 || formData.weeklyTarget > 168)
    ) {
      newErrors.weeklyTarget = 'Weekly target must be between 0 and 168 hours';
    }

    if (
      formData.totalTarget &&
      (formData.totalTarget < 0 || formData.totalTarget > 10000)
    ) {
      newErrors.totalTarget = 'Total target must be between 0 and 10,000 hours';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      setIsSubmitting(true);
      setSuccessMessage('');

      if (!updateProject) {
        throw new Error('Update project function is not available');
      }

      // Convert icon name to full Iconify string and color name to hex
      const selectedIcon = AVAILABLE_ICONS.find(i => i.name === formData.icon);
      const selectedColor = AVAILABLE_COLORS.find(
        c => c.name === formData.color
      );

      await updateProject.mutateAsync({
        id: activityId,
        data: {
          ...formData,
          name: formData.name?.trim(),
          description: formData.description?.trim(),
          icon: selectedIcon?.icon || formData.icon,
          color: selectedColor?.hex || formData.color,
          weeklyTarget: formData.weeklyTarget || undefined,
          totalTarget: formData.totalTarget || undefined,
        },
      });

      setSuccessMessage('Activity updated successfully!');

      setTimeout(() => {
        router.push(`/activities/${activityId}`);
      }, 1500);
    } catch (error) {
      console.error('Failed to update activity:', error);
      const errorMessage =
        error instanceof Error
          ? error.message
          : 'Failed to update activity. Please try again.';
      setErrors({ name: errorMessage });
      setSuccessMessage('');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleInputChange = (
    field: keyof UpdateActivityData,
    value: string | number | undefined
  ) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: undefined }));
    }
    if (successMessage) {
      setSuccessMessage('');
    }
  };

  // Get current icon string - always has a fallback to first icon
  const iconData =
    AVAILABLE_ICONS.find(i => i.icon === formData.icon) ||
    AVAILABLE_ICONS.find(i => i.name === formData.icon) ||
    AVAILABLE_ICONS[0]!;

  // Get current color - always has a fallback to first color
  const colorData =
    AVAILABLE_COLORS.find(
      c => c.name === formData.color || c.hex === formData.color
    ) || AVAILABLE_COLORS[0]!;

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="max-w-2xl mx-auto px-4 py-8">
          <div className="bg-white rounded-lg shadow-md p-6 md:p-8">
            <div className="animate-pulse space-y-6">
              <div className="h-8 bg-gray-200 rounded w-1/3"></div>
              <div className="h-4 bg-gray-200 rounded w-2/3"></div>
              <div className="h-24 bg-gray-200 rounded"></div>
              <div className="space-y-4">
                <div className="h-12 bg-gray-200 rounded"></div>
                <div className="h-12 bg-gray-200 rounded"></div>
                <div className="h-12 bg-gray-200 rounded"></div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="max-w-2xl mx-auto px-4 py-8">
        <div className="bg-white rounded-lg shadow-md p-6 md:p-8">
          {/* Header */}
          <div className="mb-6">
            <h1 className="text-3xl font-bold text-gray-900">Edit Activity</h1>
            <p className="text-gray-600 mt-2">
              Update your activity settings and goals
            </p>
          </div>

          {/* Success Message */}
          {successMessage && (
            <div className="mb-6 p-4 bg-green-50 border border-green-200 rounded-lg flex items-center gap-3">
              <svg
                className="w-5 h-5 text-green-600 flex-shrink-0"
                fill="currentColor"
                viewBox="0 0 20 20"
              >
                <path
                  fillRule="evenodd"
                  d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z"
                  clipRule="evenodd"
                />
              </svg>
              <p className="text-sm font-medium text-green-800">
                {successMessage}
              </p>
            </div>
          )}

          {/* Preview Card */}
          <div className="mb-8 p-6 bg-gradient-to-br from-gray-50 to-gray-100 rounded-xl border border-gray-200">
            <div className="flex items-center gap-4">
              <div
                className="w-16 h-16 rounded-xl flex items-center justify-center shadow-lg p-2"
                style={{ backgroundColor: colorData?.hex }}
              >
                <Icon icon={iconData?.icon} width={48} height={48} />
              </div>
              <div className="flex-1">
                <h2 className="text-xl font-bold text-gray-900">
                  {formData.name || 'Activity Name'}
                </h2>
                <p className="text-sm text-gray-600 mt-1">
                  {formData.description || 'Activity description'}
                </p>
              </div>
            </div>
          </div>

          {/* Form */}
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Activity Name */}
            <div>
              <label
                htmlFor="name"
                className="block text-sm font-semibold text-gray-900 mb-2"
              >
                Activity Name *
              </label>
              <input
                type="text"
                id="name"
                value={formData.name}
                onChange={e => handleInputChange('name', e.target.value)}
                className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-[#007AFF] focus:border-[#007AFF] transition-colors ${
                  errors.name ? 'border-red-500' : 'border-gray-300'
                }`}
                placeholder="Enter activity name"
                maxLength={50}
              />
              {errors.name && (
                <p className="mt-1 text-sm text-red-600">{errors.name}</p>
              )}
            </div>

            {/* Description */}
            <div>
              <label
                htmlFor="description"
                className="block text-sm font-semibold text-gray-900 mb-2"
              >
                Description
              </label>
              <textarea
                id="description"
                value={formData.description}
                onChange={e => handleInputChange('description', e.target.value)}
                className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-[#007AFF] focus:border-[#007AFF] resize-none transition-colors ${
                  errors.description ? 'border-red-500' : 'border-gray-300'
                }`}
                placeholder="Describe your activity (optional)"
                rows={3}
                maxLength={200}
              />
              {errors.description && (
                <p className="mt-1 text-sm text-red-600">
                  {errors.description}
                </p>
              )}
              <p className="mt-1 text-xs text-gray-500">
                {(formData.description || '').length}/200 characters
              </p>
            </div>

            {/* Icon Selector */}
            <div>
              <label
                htmlFor="icon"
                className="block text-sm font-semibold text-gray-900 mb-2"
              >
                Icon
              </label>
              <IconSelector
                icons={AVAILABLE_ICONS}
                value={formData.icon || 'briefcase'}
                onChange={iconName => handleInputChange('icon', iconName)}
              />
              <p className="mt-1 text-xs text-gray-500">
                Choose an icon that represents this activity
              </p>
            </div>

            {/* Color Selector */}
            <div>
              <label
                htmlFor="color"
                className="block text-sm font-semibold text-gray-900 mb-2"
              >
                Color
              </label>
              <ColorSelector
                colors={AVAILABLE_COLORS}
                value={formData.color || 'orange'}
                onChange={colorName => handleInputChange('color', colorName)}
              />
              <p className="mt-1 text-xs text-gray-500">
                Pick a color to identify this activity
              </p>
            </div>

            {/* Weekly Target */}
            <div>
              <label
                htmlFor="weeklyTarget"
                className="block text-sm font-semibold text-gray-900 mb-2"
              >
                Weekly Target (hours)
              </label>
              <input
                type="number"
                id="weeklyTarget"
                value={formData.weeklyTarget || ''}
                onChange={e =>
                  handleInputChange(
                    'weeklyTarget',
                    e.target.value ? Number(e.target.value) : undefined
                  )
                }
                className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-[#007AFF] focus:border-[#007AFF] transition-colors ${
                  errors.weeklyTarget ? 'border-red-500' : 'border-gray-300'
                }`}
                placeholder="Optional"
                min="0"
                max="168"
                step="0.5"
              />
              {errors.weeklyTarget && (
                <p className="mt-1 text-sm text-red-600">
                  {errors.weeklyTarget}
                </p>
              )}
              <p className="mt-1 text-xs text-gray-500">
                Set a weekly goal for this activity
              </p>
            </div>

            {/* Total Target */}
            <div>
              <label
                htmlFor="totalTarget"
                className="block text-sm font-semibold text-gray-900 mb-2"
              >
                Total Target (hours)
              </label>
              <input
                type="number"
                id="totalTarget"
                value={formData.totalTarget || ''}
                onChange={e =>
                  handleInputChange(
                    'totalTarget',
                    e.target.value ? Number(e.target.value) : undefined
                  )
                }
                className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-[#007AFF] focus:border-[#007AFF] transition-colors ${
                  errors.totalTarget ? 'border-red-500' : 'border-gray-300'
                }`}
                placeholder="Optional"
                min="0"
                max="10000"
                step="1"
              />
              {errors.totalTarget && (
                <p className="mt-1 text-sm text-red-600">
                  {errors.totalTarget}
                </p>
              )}
              <p className="mt-1 text-xs text-gray-500">
                Set an overall activity goal
              </p>
            </div>

            {/* Actions */}
            <div className="flex flex-col sm:flex-row gap-3 pt-6 border-t border-gray-200">
              <button
                type="button"
                onClick={() => router.push(`/activities/${activityId}`)}
                className="flex-1 px-6 py-3 text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors font-medium"
                disabled={isSubmitting}
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={isSubmitting}
                className="flex-1 px-6 py-3 bg-[#007AFF] text-white rounded-lg hover:bg-[#0056D6] transition-colors disabled:opacity-50 disabled:cursor-not-allowed font-medium shadow-md"
              >
                {isSubmitting ? 'Saving...' : 'Save Changes'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default function EditActivityPageWrapper({
  params,
}: EditActivityPageProps) {
  const [activityId, setActivityId] = React.useState<string>('');

  React.useEffect(() => {
    params.then(({ id }) => setActivityId(id));
  }, [params]);

  return (
    <ProtectedRoute>
      {!activityId ? (
        <div className="min-h-screen bg-gray-50">
          <Header />
          <div className="max-w-2xl mx-auto px-4 py-8">
            <div className="bg-white rounded-lg shadow-md p-6 md:p-8">
              <div className="animate-pulse space-y-6">
                <div className="h-8 bg-gray-200 rounded w-1/3"></div>
                <div className="h-4 bg-gray-200 rounded w-2/3"></div>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <EditActivityContent activityId={activityId} />
      )}
    </ProtectedRoute>
  );
}

