                                </linearGradient>
                              </defs>
                              <XAxis
                                dataKey="name"
                                tick={{ fontSize: 11, fill: '#666' }}
                                axisLine={false}
                                tickLine={false}
                              />
                              <YAxis
                                tick={{ fontSize: 11, fill: '#666' }}
                                axisLine={false}
                                tickLine={false}
                              />
                              <Tooltip content={<CustomTooltip />} />
                              <Area
                                type="monotone"
                                dataKey="avgDuration"
                                stroke="#00BA7C"
                                strokeWidth={2}
                                fill="url(#colorAvgDuration)"
                                name="Minutes"
                              />
                            </ComposedChart>
                          )}
                        </ResponsiveContainer>
                      </div>
                    </div>

                    {/* Sessions completed */}
                    <div className="bg-white border border-gray-200 rounded-xl p-6">
                      <div className="mb-4">
                        <h3 className="font-semibold text-gray-900">
                          Sessions completed
                        </h3>
                      </div>
                      <div className="h-48">
                        <ResponsiveContainer width="100%" height="100%">
                          {chartType === 'bar' ? (
                            <BarChart
                              data={chartData}
                              margin={{
                                top: 5,
                                right: 5,
                                left: -30,
                                bottom: 0,
                              }}
                            >
                              <XAxis
                                dataKey="name"
                                tick={{ fontSize: 11, fill: '#666' }}
                                axisLine={false}
                                tickLine={false}
                              />
                              <YAxis
                                tick={{ fontSize: 11, fill: '#666' }}
                                axisLine={false}
                                tickLine={false}
                              />
                              <Tooltip content={<CustomTooltip />} />
                              <Bar
                                dataKey="sessions"
                                fill="#00BA7C"
                                radius={[4, 4, 0, 0]}
                                name="Sessions"
                              />
                            </BarChart>
                          ) : (
                            <ComposedChart
                              data={chartData}
                              margin={{
                                top: 5,
                                right: 5,
                                left: -30,
                                bottom: 0,
                              }}
                            >
                              <defs>
                                <linearGradient
                                  id="colorSessionsSmall"
                                  x1="0"
                                  y1="0"
                                  x2="0"
                                  y2="1"
                                >
                                  <stop
                                    offset="5%"
                                    stopColor="#00BA7C"
                                    stopOpacity={0.3}
                                  />
                                  <stop
                                    offset="95%"
                                    stopColor="#00BA7C"
                                    stopOpacity={0}
                                  />
                                </linearGradient>
                              </defs>
                              <XAxis
                                dataKey="name"
                                tick={{ fontSize: 11, fill: '#666' }}
                                axisLine={false}
                                tickLine={false}
                              />
                              <YAxis
                                tick={{ fontSize: 11, fill: '#666' }}
                                axisLine={false}
                                tickLine={false}
                              />
                              <Tooltip content={<CustomTooltip />} />
                              <Area
                                type="monotone"
                                dataKey="sessions"
                                stroke="#00BA7C"
                                strokeWidth={2}
                                fill="url(#colorSessionsSmall)"
                                name="Sessions"
                              />
                            </ComposedChart>
                          )}
                        </ResponsiveContainer>
                      </div>
                    </div>
                  </div>

                  {/* Stats Grid - 4 columns */}
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    <div className="bg-white border border-gray-200 rounded-xl p-4">
                      <div className="text-sm text-gray-600 mb-2">
                        Total Hours
                      </div>
                      <div className="text-2xl font-bold mb-1">
                        {calculatedStats.totalHours.toFixed(1)}
                      </div>
                    </div>

                    <div className="bg-white border border-gray-200 rounded-xl p-4">
                      <div className="text-sm text-gray-600 mb-2">
                        Avg Duration
                      </div>
                      <div className="text-2xl font-bold mb-1">
                        {calculatedStats.avgDuration}m
                      </div>
                    </div>

                    <div className="bg-white border border-gray-200 rounded-xl p-4">
                      <div className="text-sm text-gray-600 mb-2">Sessions</div>
                      <div className="text-2xl font-bold mb-1">
                        {calculatedStats.sessions}
                      </div>
                    </div>

                    <div className="bg-white border border-gray-200 rounded-xl p-4">
                      <div className="text-sm text-gray-600 mb-2">
                        Active Days
                      </div>
                      <div className="text-2xl font-bold mb-1">
                        {calculatedStats.activeDays}
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'sessions' && (
              <div className="max-w-3xl mx-auto">
                {isLoadingSessions ? (
                  <div className="space-y-4">
                    {[1, 2, 3].map(i => (
                      <div
                        key={i}
                        className="bg-white rounded-lg border border-gray-200 p-4 animate-pulse"
                      >
                        <div className="h-20 bg-gray-100 rounded"></div>
                      </div>
                    ))}
                  </div>
                ) : sessions.length === 0 ? (
                  <div className="bg-white rounded-xl border border-gray-200 p-12 text-center">
                    <div className="text-gray-400 mb-2">
                      <Clock className="w-12 h-12 mx-auto mb-4" />
                    </div>
                    <h3 className="text-lg font-semibold text-gray-900 mb-2">
                      No sessions yet
                    </h3>
                    <p className="text-gray-600">
                      Start tracking time on this activity to see sessions here.
                    </p>
                  </div>
                ) : (
                  <div className="space-y-0">
                    {sessions.map(session => (
                      <SessionCard
                        key={session.id}
                        session={session}
                        onSupport={handleSupport}
                        onRemoveSupport={handleRemoveSupport}
                        onShare={handleShare}
                      />
                    ))}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default function ProjectDetailPageWrapper({
  params,
}: ProjectDetailPageProps) {
  const [projectId, setProjectId] = React.useState<string>('');

  React.useEffect(() => {
    params.then(({ id }) => setProjectId(id));
  }, [params]);

  return (
    <ProtectedRoute>
      {!projectId ? (
        <div className="min-h-screen bg-gray-50">
          <Header />
          <div className="max-w-[1400px] mx-auto px-4 py-6">
            <div className="animate-pulse">
              <div className="h-8 bg-gray-200 rounded w-48 mb-4"></div>
              <div className="h-4 bg-gray-200 rounded w-64 mb-8"></div>
              <div className="h-64 bg-gray-200 rounded"></div>
            </div>
          </div>
        </div>
      ) : (
        <ProjectDetailContent projectId={projectId} />
      )}
    </ProtectedRoute>
  );
}

