          <div className="flex flex-wrap gap-3">
            <Button variant="outline" asChild>
              <Link href="/help">Help Center</Link>
            </Button>
            <Button variant="outline" asChild>
              <Link href="/about">About Ambira</Link>
            </Button>
            <Button variant="outline" asChild>
              <Link href="/privacy">Privacy Policy</Link>
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import Header from '@/components/HeaderComponent';
import MobileHeader from '@/components/MobileHeader';
import BottomNavigation from '@/components/BottomNavigation';
import { firebaseUserApi } from '@/lib/api';
import { useAuth } from '@/hooks/useAuth';
import { UserCardCompact } from '@/components/UserCard';
import { ArrowLeft, Users as UsersIcon } from 'lucide-react';

export default function DiscoverPeoplePage() {
  const router = useRouter();
  const { user } = useAuth();
  const [suggestedUsers, setSuggestedUsers] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [followingUsers, setFollowingUsers] = useState<Set<string>>(new Set());

  useEffect(() => {
    const loadUsers = async () => {
      if (!user) {
        setIsLoading(false);
        return;
      }

      try {
        setIsLoading(true);

        // Load the list of users we're already following
        try {
          const following = await firebaseUserApi.getFollowing(user.id);
          const followingIds = new Set(following.map(u => u.id));
          setFollowingUsers(followingIds);
        } catch (error) {
          console.error('Failed to load following list:', error);
        }

        // Load suggested users (filters by profileVisibility and excludes following)
        // Limit to 5 to avoid revealing total user count
        const suggestions = await firebaseUserApi.getSuggestedUsers(5);

        setSuggestedUsers(suggestions);
      } catch (error) {
        console.error('Error loading users:', error);
        setSuggestedUsers([]);
      } finally {
        setIsLoading(false);
      }
    };

    loadUsers();
  }, [user]);

  const handleFollowChange = (userId: string, isFollowing: boolean) => {
    // Update following state
    setFollowingUsers(prev => {
      const next = new Set(prev);
      if (isFollowing) {
        next.add(userId);
      } else {
        next.delete(userId);
      }
      return next;
    });

    // Update user data
    setSuggestedUsers(prev =>
      prev.map(u =>
        u.id === userId
          ? {
              ...u,
              isFollowing,
              followersCount: isFollowing
                ? (u.followersCount || 0) + 1
                : Math.max(0, (u.followersCount || 0) - 1)
            }
          : u
      )
    );

    // Remove from suggestions after following
    if (isFollowing) {
      setSuggestedUsers(prev => prev.filter(u => u.id !== userId));
    }
  };

  return (
    <div className="min-h-screen bg-white">
      {/* Header - desktop */}
      <div className="hidden md:block">
        <Header />
      </div>

      {/* Mobile Header */}
      <MobileHeader title="Discover People" />

      <div className="max-w-4xl mx-auto px-0 md:px-6 md:py-8 md:pt-24">
        {/* Page Header - Desktop */}
        <div className="mb-8 hidden md:block px-6">
          <div className="flex items-center gap-3 mb-4">
            <button
              onClick={() => router.back()}
              className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
            >
              <ArrowLeft className="w-5 h-5 text-gray-600" />
            </button>
          </div>
          <div>
            <h1 className="text-3xl font-bold text-gray-900 mb-2">Discover People</h1>
            <p className="text-gray-600">
              Here are some people you might want to follow
            </p>
          </div>
        </div>

        {/* Content */}
        {isLoading ? (
          <div className="p-12 text-center">
            <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-[#007AFF]"></div>
            <p className="text-gray-600 mt-4">Finding people for you...</p>
          </div>
        ) : suggestedUsers.length === 0 ? (
          <div className="p-16 text-center">
            <div className="w-20 h-20 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <UsersIcon className="w-10 h-10 text-[#007AFF]" />
            </div>
            <h3 className="text-xl font-bold text-gray-900 mb-2">No suggestions yet</h3>
            <p className="text-gray-600 max-w-md mx-auto">
              We'll show you people to connect with as the community grows. Check back later!
            </p>
          </div>
        ) : (
          <div>
            {/* Section Header */}
            <div className="px-6 py-4 border-b border-gray-200 bg-gray-50">
              <h2 className="text-sm font-semibold text-gray-700 uppercase tracking-wide">
                Who to Follow
              </h2>
            </div>

            {/* People List */}
            <div className="divide-y divide-gray-200">
              {suggestedUsers.map((suggestedUser) => (
                <div key={suggestedUser.id}>
                  <UserCardCompact
                    user={suggestedUser}
                    variant="search"
                    onFollowChange={handleFollowChange}
                  />
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Bottom padding for mobile navigation */}
      <div className="h-20 md:hidden" />

      <BottomNavigation />
    </div>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import { useParams, useRouter } from 'next/navigation';
import { useAuth } from '@/hooks/useAuth';
import Header from '@/components/HeaderComponent';
import { ImageUpload } from '@/components/ImageUpload';
import { Group, UpdateGroupData } from '@/types';
import { firebaseApi } from '@/lib/api';
import { uploadImage } from '@/lib/imageUpload';
import { Upload } from 'lucide-react';

export default function GroupSettingsPage() {
  const params = useParams();
  const router = useRouter();
  const { user } = useAuth();
  const [group, setGroup] = useState<Group | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isSaving, setIsSaving] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const groupId = params.id as string;

  // Form state
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    location: '',
    category: 'other' as
      | 'work'
      | 'study'
      | 'side-project'
      | 'learning'
      | 'other',
    type: 'other' as 'just-for-fun' | 'professional' | 'competitive' | 'other',
    privacySetting: 'public' as 'public' | 'approval-required',
    imageUrl: '',
  });

  // Image upload state
  const [groupImages, setGroupImages] = useState<File[]>([]);
  const [groupImagePreviews, setGroupImagePreviews] = useState<string[]>([]);

  useEffect(() => {
    if (groupId && user) {
      loadGroup();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [groupId, user]);

  const loadGroup = async () => {
    try {
      setIsLoading(true);
      const groupData = await firebaseApi.group.getGroup(groupId);

      if (!groupData) {
        router.push('/groups');
        return;
      }

      // Check if user is admin
      if (!groupData.adminUserIds.includes(user!.id)) {
        router.push(`/groups/${groupId}`);
        return;
      }

      setGroup(groupData);
      setFormData({
        name: groupData.name,
        description: groupData.description,
        location: groupData.location || '',
        category: groupData.category,
        type: groupData.type,
        privacySetting: groupData.privacySetting,
        imageUrl: groupData.imageUrl || '',
      });

      // Set existing image as preview if it exists
      if (groupData.imageUrl) {
        setGroupImagePreviews([groupData.imageUrl]);
      }
    } catch (error) {
      console.error('Error loading group:', error);
      setError('Failed to load group');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!group || !user) return;

    setIsSaving(true);
    setError(null);

    try {
      // Upload new group image if provided
      let imageUrl = formData.imageUrl;
      if (groupImages.length > 0) {
        try {
          const imageFile = groupImages[0];
          if (imageFile !== undefined) {
            const result = await uploadImage(imageFile, 'group-images');
            imageUrl = result.url;
          }
        } catch (uploadError) {
          console.error('Error uploading group image:', uploadError);
          setError(
            uploadError instanceof Error
              ? uploadError.message
              : 'Failed to upload group image'
          );
          setIsSaving(false);
          return;
        }
      }

      const updateData: UpdateGroupData = {
        name: formData.name,
        description: formData.description,
        location: formData.location || undefined,
        category: formData.category,
        type: formData.type,
        privacySetting: formData.privacySetting,
        imageUrl: imageUrl || undefined,
      };

      await firebaseApi.group.updateGroup(groupId, updateData);
      router.push(`/groups/${groupId}`);
    } catch (error) {
      console.error('Error updating group:', error);
      setError('Failed to update group. Please try again.');
      setIsSaving(false);
    }
  };

  const handleCancel = () => {
    router.push(`/groups/${groupId}`);
  };

  if (!user) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-gray-900 mb-4">
            Please log in
          </h1>
          <p className="text-gray-600">
            You need to be logged in to edit groups.
          </p>
        </div>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="max-w-3xl mx-auto px-4 py-8">
          <div className="animate-pulse">
            <div className="h-8 bg-gray-200 rounded w-64 mb-6"></div>
            <div className="h-96 bg-gray-200 rounded"></div>
          </div>
        </div>
      </div>
    );
  }

  if (!group) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="max-w-3xl mx-auto px-4 py-8">
          <div className="text-center">
            <h1 className="text-2xl font-bold text-gray-900 mb-4">
              Group not found
            </h1>
            <button
              onClick={() => router.push('/groups')}
              className="bg-[#007AFF] text-white px-4 py-2 rounded-lg hover:bg-[#0051D5]"
            >
              Back to Groups
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Edit Group</h1>
        </div>

        <form
          onSubmit={handleSubmit}
          className="bg-white rounded-lg border border-gray-200 p-8"
        >
          {/* Image Upload */}
          <div className="mb-8">
            <label className="block text-sm font-semibold text-gray-900 mb-2">
              Group Picture
            </label>
            <ImageUpload
              singleImage={true}
              maxSizeMB={5}
              images={groupImages}
              previewUrls={groupImagePreviews}
              onImagesChange={(images, previews) => {
                setGroupImages(images);
                setGroupImagePreviews(previews);
                // Clear the imageUrl from formData when user uploads a new image
                if (images.length > 0) {
                  setFormData({ ...formData, imageUrl: '' });
                }
              }}
              placeholder="Upload group picture"
              disabled={isSaving}
            />
            <p className="text-xs text-gray-500 mt-2">
              Recommended size: 248√ó248 px (max 5MB)
            </p>
          </div>

          <p className="text-sm text-gray-600 mb-6 italic">
            Fields marked with * are required
          </p>

          {/* Group Name */}
          <div className="mb-6">
            <label
              htmlFor="name"
              className="block text-sm font-semibold text-gray-900 mb-2"
            >
              Group Name *
            </label>
            <input
              type="text"
              id="name"
              required
              value={formData.name}
              onChange={e => setFormData({ ...formData, name: e.target.value })}
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-[#007AFF] focus:border-transparent"
              placeholder="Enter group name"
            />
          </div>

          {/* Location */}
          <div className="mb-6">
            <label
              htmlFor="location"
              className="block text-sm font-semibold text-gray-900 mb-2"
            >
              Location
            </label>
            <input
              type="text"
              id="location"
              value={formData.location}
              onChange={e =>
                setFormData({ ...formData, location: e.target.value })
              }
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-[#007AFF] focus:border-transparent"
              placeholder="Enter location"
            />
          </div>

          {/* Category */}
          <div className="mb-6">
            <label
              htmlFor="category"
              className="block text-sm font-semibold text-gray-900 mb-2"
            >
              Category
            </label>
            <select
              id="category"
              value={formData.category}
              onChange={e =>
                setFormData({ ...formData, category: e.target.value as any })
              }
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-[#007AFF] focus:border-transparent bg-white"
            >
              <option value="work">Work</option>
              <option value="study">Study</option>
              <option value="side-project">Side Project</option>
              <option value="learning">Learning</option>
              <option value="other">Other</option>
            </select>
          </div>

          {/* Type */}
          <div className="mb-6">
            <label
              htmlFor="type"
              className="block text-sm font-semibold text-gray-900 mb-2"
            >
              Group Type
            </label>
            <select
              id="type"
              value={formData.type}
              onChange={e =>
                setFormData({ ...formData, type: e.target.value as any })
              }
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-[#007AFF] focus:border-transparent bg-white"
            >
              <option value="just-for-fun">Just For Fun</option>
              <option value="professional">Professional</option>
              <option value="competitive">Competitive</option>
              <option value="other">Other</option>
            </select>
          </div>

          {/* Description */}
          <div className="mb-8">
            <label
              htmlFor="description"
              className="block text-sm font-semibold text-gray-900 mb-2"
            >
              Description *
            </label>
            <textarea
              id="description"
              required
              value={formData.description}
              onChange={e => {
                // Limit to 4 lines (3 newlines)
                const newlineCount = (e.target.value.match(/\n/g) || []).length;
                if (newlineCount <= 3) {
                  setFormData({ ...formData, description: e.target.value });
                }
              }}
              rows={4}
              maxLength={200}
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-[#007AFF] focus:border-transparent resize-none"
              placeholder="Describe your group"
            />
            <p className="text-xs text-gray-500 mt-1">
              {formData.description.length}/200 characters
            </p>
          </div>

          {/* Error Message */}
          {error && (
            <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg">
              <p className="text-sm text-red-600">{error}</p>
            </div>
          )}

          {/* Action Buttons */}
          <div className="flex gap-4">
            <button
              type="submit"
              disabled={isSaving}
              className="px-6 py-2.5 bg-[#FC4C02] text-white font-medium rounded-md hover:bg-[#FC4C02]/90 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {isSaving ? 'Saving...' : 'Save Changes'}
            </button>
            <button
              type="button"
              onClick={handleCancel}
              disabled={isSaving}
              className="px-6 py-2.5 text-gray-700 font-medium hover:text-gray-900 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

'use client';

import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { useAuth } from '@/hooks/useAuth';
import {
  useUserGroups,
  usePublicGroups,
  useJoinGroup,
} from '@/features/groups/hooks';
import Header from '@/components/HeaderComponent';
import MobileHeader from '@/components/MobileHeader';
import BottomNavigation from '@/components/BottomNavigation';
import GroupCard from '@/components/GroupCard';
import { GroupListItem } from '@/components/GroupListItem';
import { Group } from '@/types';
import {
  Users,
  Search,
  ChevronLeft,
  ChevronRight,
  Loader2,
  X,
} from 'lucide-react';
import Link from 'next/link';

// Category filter options
const categoryOptions = [
  { value: '', label: 'All Categories' },
  { value: 'fitness', label: 'Fitness' },
  { value: 'study', label: 'Study' },
  { value: 'work', label: 'Work' },
  { value: 'health', label: 'Health' },
  { value: 'other', label: 'Other' },
];

// Pagination and layout constants
const GROUPS_PER_PAGE = 10;
const SEARCH_RESULTS_LIMIT = 50;
const MY_GROUPS_GRID_COLS = {
  mobile: 4,
  tablet: 6,
  desktop: 8,
} as const;

export default function GroupsPage() {
  const { user } = useAuth();
  const [searchResults, setSearchResults] = useState<Group[]>([]);
  const [isSearching, setIsSearching] = useState(false);
  const [currentPage, setCurrentPage] = useState(0);
  const [hasSearched, setHasSearched] = useState(false);
  const [searchStatus, setSearchStatus] = useState('');

  // Search filters
  const [searchFilters, setSearchFilters] = useState({
    name: '',
    location: '',
    category: '',
  });

  // Use new group hooks
  const { data: userGroups = [], isLoading: isLoadingUserGroups } =
    useUserGroups(user?.id || '', {
      enabled: !!user?.id,
    });

  const { data: allPublicGroups = [], isLoading: isLoadingSuggested } =
    usePublicGroups(undefined, {
      enabled: !!user,
    });

  const joinGroupMutation = useJoinGroup();
  const isLoading = isLoadingUserGroups || isLoadingSuggested;

  // Client-side filtering for search
  const hasActiveFilters = !!(
    searchFilters.name ||
    searchFilters.location ||
    searchFilters.category
  );

  const filteredSearchResults = useMemo(() => {
    if (!hasActiveFilters) return [];

    return allPublicGroups
      .filter(group => {
        const matchesName =
          searchFilters.name === '' ||
          group.name.toLowerCase().includes(searchFilters.name.toLowerCase());
        const matchesLocation =
          searchFilters.location === '' ||
          (group.location
            ?.toLowerCase()
            .includes(searchFilters.location.toLowerCase()) ??
            false);
        const matchesCategory =
          searchFilters.category === '' ||
          group.category === searchFilters.category;

        return matchesName && matchesLocation && matchesCategory;
      })
      .slice(0, SEARCH_RESULTS_LIMIT);
  }, [allPublicGroups, searchFilters]);

  // Update search results when filtered results change
  useEffect(() => {
    if (hasSearched && filteredSearchResults.length >= 0) {
      setSearchResults(filteredSearchResults);
    }
  }, [filteredSearchResults, hasSearched]);

  /**
   * Executes a group search based on current filter criteria.
   * Updates search results state and resets pagination to first page.
   */
  const handleSearch = useCallback(async () => {
    if (!user) return;

    try {
      setIsSearching(true);
      setHasSearched(true);
      setCurrentPage(0);
      setSearchStatus('Searching for groups...');

      // Use client-side filtered results
      if (filteredSearchResults.length > 0) {
        setSearchResults(filteredSearchResults);
        setSearchStatus(
          `Found ${filteredSearchResults.length} ${filteredSearchResults.length === 1 ? 'group' : 'groups'}`
        );
      } else {
        setSearchStatus('No groups found');
      }
    } catch (error) {
      console.error('Error searching groups:', error);
      setSearchStatus('Error searching groups. Please try again.');
    } finally {
      setIsSearching(false);
    }
  }, [user, filteredSearchResults]);

  /**
   * Clears a specific search filter and updates the filter state.
   * @param filterKey - The key of the filter to clear (name, location, or category)
   */
  const clearFilter = (filterKey: keyof typeof searchFilters) => {
    setSearchFilters({ ...searchFilters, [filterKey]: '' });
  };

  /**
   * Clears all active search filters and resets search state.
   * Returns to default discover groups view.
   */
  const clearAllFilters = () => {
    setSearchFilters({ name: '', location: '', category: '' });
    setHasSearched(false);
    setSearchResults([]);
  };

  // Memoized pagination calculations to avoid unnecessary recalculations
  const { totalPages, paginatedGroups } = useMemo(() => {
    const pages = Math.ceil(allPublicGroups.length / GROUPS_PER_PAGE);
    const startIndex = currentPage * GROUPS_PER_PAGE;
    const endIndex = startIndex + GROUPS_PER_PAGE;
    const groups = allPublicGroups.slice(startIndex, endIndex);

    return {
      totalPages: pages,
      paginatedGroups: groups,
    };
  }, [allPublicGroups, currentPage]);

  /**
   * Advances to the next page of groups if available.
   */
  const goToNextPage = () => {
    if (currentPage < totalPages - 1) {
      setCurrentPage(prev => prev + 1);
    }
  };

  /**
   * Returns to the previous page of groups if available.
   */
  const goToPreviousPage = () => {
    if (currentPage > 0) {
      setCurrentPage(prev => prev - 1);
    }
  };

  /**
   * Handles joining a group for the current user.
   * Uses the new joinGroupMutation hook for automatic cache invalidation.
   * @param groupId - The ID of the group to join
   * @param e - The click event (prevented to avoid navigation)
   */
  const handleJoinGroup = async (groupId: string, e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    if (!user) return;

    try {
      await joinGroupMutation.mutateAsync({ groupId, userId: user.id });
      // Automatic cache invalidation handled by mutation
    } catch (error) {
      console.error('Failed to join group:', error);
    }
  };

  if (!user) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-gray-900 mb-4">
            Please log in to view groups
          </h1>
          <p className="text-gray-600">
            You need to be logged in to join groups and challenges.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="hidden md:block">
        <Header />
      </div>
      <MobileHeader title="Groups" />

      <div className="max-w-5xl mx-auto px-4 md:px-6 py-4">
        {/* Page Header */}
        <div className="flex items-center justify-between mb-6">
          <div>
