              ) : (
                notifications.map(notification => (
                  <SwipeableNotificationItem
                    key={notification.id}
                    notification={notification}
                    onDelete={handleDelete}
                    onMarkRead={handleMarkRead}
                    onClick={handleNotificationClick}
