                      {renderPercentageChange(calculatedStats.activitiesChange)}
                    </div>
                  </div>

                  {/* Secondary Stats Grid - Streaks */}
                  <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
                    <div className="bg-white border border-gray-200 rounded-xl p-4">
                      <div className="text-sm text-gray-600 mb-2 uppercase tracking-wide">
                        Current Streak
                      </div>
                      <div className="text-2xl font-bold mb-1">
                        {calculatedStats.currentStreak}
                      </div>
                      {renderPercentageChange(calculatedStats.streakChange)}
                    </div>

                    <div className="bg-white border border-gray-200 rounded-xl p-4">
                      <div className="text-sm text-gray-600 mb-2 uppercase tracking-wide">
                        Longest Streak
                      </div>
                      <div className="text-2xl font-bold mb-1">
                        {calculatedStats.longestStreak}
                      </div>
                      {renderPercentageChange(calculatedStats.streakChange)}
                    </div>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'sessions' && (
              <div className="max-w-4xl mx-auto">
                <Feed
                  filters={{ type: 'user', userId: profile.id }}
                  showEndMessage={true}
                />
              </div>
            )}

            {activeTab === 'followers' && (
              <div>
                <FollowersList userId={profile.id} />
              </div>
            )}

            {activeTab === 'following' && (
              <div>
                <FollowingList userId={profile.id} />
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useSearchParams } from 'next/navigation';
import { useState, useEffect, Suspense, useMemo } from 'react';
import Link from 'next/link';
import Header from '@/components/HeaderComponent';
import BottomNavigation from '@/components/BottomNavigation';
import GroupAvatar from '@/components/GroupAvatar';
import { useAuth } from '@/hooks/useAuth';
import { useDebounce } from '@/hooks/useDebounce';
import { UserCardCompact } from '@/components/UserCard';
import { Users } from 'lucide-react';
import { firebaseApi } from '@/lib/api';
import { useQueryClient } from '@tanstack/react-query';
import { CACHE_KEYS } from '@/lib/queryClient';

// Optimized hooks
import {
  useSearchUsers,
  useSearchGroups,
  useSuggestedUsers,
  useSuggestedGroups,
  useFollowingList,
  useUserGroups,
} from '@/features/search/hooks';

// Loading skeletons
import {
  SearchLoadingSkeleton,
  SuggestionsLoadingSkeleton,
} from '@/features/search/components/SearchLoadingSkeleton';

function SearchContent() {
  const searchParams = useSearchParams();
  const initialQuery = searchParams.get('q') || '';
  const type = (searchParams.get('type') || 'people') as 'people' | 'groups';

  const [query, setQuery] = useState(initialQuery);
  const [joiningGroup, setJoiningGroup] = useState<string | null>(null);
  const { user } = useAuth();
  const queryClient = useQueryClient();

  // Debounce search input to reduce API calls
  const debouncedQuery = useDebounce(query, 300);

  // Prefetch following list and user groups in parallel when page loads
  const { followingIds, isLoading: isLoadingFollowing } = useFollowingList({
    userId: user?.id,
    enabled: !!user,
  });

  const { groups: userGroups, isLoading: isLoadingUserGroups } = useUserGroups({
    userId: user?.id,
    enabled: !!user && type === 'groups',
  });

  const joinedGroupIds = useMemo(
    () => new Set(userGroups.map(g => g.id)),
    [userGroups]
  );

  // Search hooks - only enabled when there's a search query
  const hasSearchQuery = debouncedQuery.trim().length > 0;

  const { users: searchUsers, isLoading: isSearchingUsers } = useSearchUsers({
    searchTerm: debouncedQuery,
    enabled: hasSearchQuery && type === 'people',
  });

  const { groups: searchGroups, isLoading: isSearchingGroups } =
    useSearchGroups({
      searchTerm: debouncedQuery,
      enabled: hasSearchQuery && type === 'groups',
    });

  // Suggestions hooks - only enabled when there's no search query
  const { suggestedUsers, isLoading: isLoadingSuggestedUsers } =
    useSuggestedUsers({
      enabled: !hasSearchQuery && type === 'people' && !!user,
      limit: 5,
    });

  const { suggestedGroups, isLoading: isLoadingSuggestedGroups } =
    useSuggestedGroups({
      userId: user?.id,
      enabled: !hasSearchQuery && type === 'groups' && !!user,
      limit: 20,
    });

  // Enhance search results with following/joined status
  const enhancedUsers = useMemo(() => {
    if (!hasSearchQuery) return [];

    return searchUsers
      .map(u => ({
        ...u,
        isFollowing: followingIds.has(u.id),
        isSelf: user && u.id === user.id,
      }))
      .sort((a, b) => (b.isSelf ? 1 : 0) - (a.isSelf ? 1 : 0));
  }, [searchUsers, followingIds, user, hasSearchQuery]);

  const enhancedSuggestedUsers = useMemo(() => {
    return suggestedUsers.map(u => ({
      ...u,
      isFollowing: followingIds.has(u.id),
    }));
  }, [suggestedUsers, followingIds]);

  // Determine loading state
  const isLoading = hasSearchQuery
    ? type === 'people'
      ? isSearchingUsers
      : isSearchingGroups
    : type === 'people'
      ? isLoadingSuggestedUsers
      : isLoadingSuggestedGroups;

  const handleFollowChange = (userId: string, isFollowing: boolean) => {
    // Optimistically update the following IDs set
    queryClient.setQueryData(
      ['following-ids', user!.id],
      (old: Set<string> = new Set()) => {
        const newSet = new Set(old);
        if (isFollowing) {
          newSet.add(userId);
        } else {
          newSet.delete(userId);
        }
        return newSet;
      }
    );
  };

  const handleJoinGroup = async (groupId: string, e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    if (!user) return;

    const isJoined = joinedGroupIds.has(groupId);

    try {
      setJoiningGroup(groupId);

      if (isJoined) {
        await firebaseApi.group.leaveGroup(groupId, user.id);
      } else {
        await firebaseApi.group.joinGroup(groupId, user.id);
      }

      // Invalidate user groups cache to refetch
      queryClient.invalidateQueries({
        queryKey: CACHE_KEYS.USER_GROUPS(user.id),
      });
    } catch (error) {
      console.error('Failed to join/leave group:', error);
    } finally {
      setJoiningGroup(null);
    }
  };

  const renderUserResult = (user: any) => {
    if (user.isSelf) {
      return (
        <Link
          key={user.id}
          href={`/profile/${user.username}`}
          className="block border-b border-gray-100 last:border-0"
        >
          <div className="p-4 hover:bg-gray-50 transition-colors">
            <div className="flex items-center space-x-4">
              <div className="w-12 h-12 rounded-full flex items-center justify-center text-base font-semibold bg-green-100 text-green-800 ring-2 ring-green-400">
                {(user.username?.[0] || 'U').toUpperCase()}
              </div>
              <div>
                <h3 className="font-semibold text-gray-900">{user.name}</h3>
                <p className="text-sm text-gray-600">@{user.username}</p>
                {user.bio && (
                  <p className="text-sm text-gray-700 mt-1">{user.bio}</p>
                )}
                <span className="inline-flex items-center px-2 py-0.5 mt-1 text-xs font-medium rounded-full bg-green-100 text-green-800">
                  This is you
                </span>
              </div>
            </div>
          </div>
        </Link>
      );
    }

    return (
      <div key={user.id} className="border-b border-gray-100 last:border-0">
        <UserCardCompact
          user={user}
          variant="search"
          onFollowChange={handleFollowChange}
        />
      </div>
    );
  };

  const renderGroupResult = (group: any) => {
    const isJoined = joinedGroupIds.has(group.id);
    const isLoading = joiningGroup === group.id;

    return (
      <div className="p-3 transition-colors">
        <div className="flex items-center gap-3">
          {/* Group Icon */}
          <Link href={`/groups/${group.id}`}>
            <GroupAvatar
              imageUrl={group.imageUrl}
              name={group.name}
              size="md"
            />
          </Link>

          {/* Group Info */}
          <div className="flex-1 min-w-0">
            <Link href={`/groups/${group.id}`}>
              <p className="font-semibold text-sm text-gray-900 hover:text-[#007AFF] truncate mb-0.5 transition-colors">
                {group.name}
              </p>
            </Link>
            <div className="text-xs text-gray-500">
              {group.memberCount || group.members || 0}{' '}
              {(group.memberCount || group.members) === 1
                ? 'member'
                : 'members'}
            </div>
          </div>

          {/* Join Button */}
          <button
            onClick={e => handleJoinGroup(group.id, e)}
            disabled={isLoading}
            className={`text-sm font-semibold transition-colors whitespace-nowrap flex-shrink-0 ${
              isJoined
                ? 'text-gray-600 hover:text-gray-900'
                : 'text-[#007AFF] hover:text-[#0051D5]'
            } ${isLoading ? 'opacity-50 cursor-not-allowed' : ''}`}
          >
            {isLoading ? 'Joining...' : isJoined ? 'Joined' : 'Join'}
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header - only show on desktop */}
      <div className="hidden md:block">
        <Header />
      </div>

      {/* Mobile search header */}
      <div className="md:hidden bg-white border-b border-gray-200 px-4 py-3 sticky top-0 z-40">
        <div className="flex items-center space-x-3">
          <svg
            className="w-6 h-6 text-gray-600"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
            />
          </svg>
          <h1 className="text-xl font-semibold text-gray-900">Discover</h1>
        </div>
      </div>

      <div className="max-w-4xl mx-auto px-4 pt-6 pb-4 md:py-8 md:pt-24">
        {/* Search Info - only show if there's a query */}
        {initialQuery && (
          <div className="mb-6 hidden md:block">
            <h1 className="text-2xl font-bold text-gray-900">
              Search Results for "{initialQuery}"
            </h1>
            <p className="text-gray-600 mt-1">
              Searching in {type.charAt(0).toUpperCase() + type.slice(1)}
            </p>
          </div>
        )}

        {/* Mobile Search Form */}
        <div className="md:hidden mb-6">
          <form
            onSubmit={e => {
              e.preventDefault();
              if (query.trim())
                window.location.href = `/search?q=${encodeURIComponent(query.trim())}&type=${type}`;
            }}
          >
            <div className="space-y-4">
              {/* Filter Tabs */}
              <div className="flex bg-white border border-gray-200 rounded-xl overflow-hidden shadow-sm">
                <button
                  type="button"
                  onClick={() =>
                    (window.location.href = `/search?q=${encodeURIComponent(initialQuery)}&type=people`)
                  }
                  className={`flex-1 py-3 px-4 text-sm font-medium transition-all border-r border-gray-200 last:border-r-0 ${
                    type === 'people'
                      ? 'bg-[#007AFF] text-white'
                      : 'text-gray-700 hover:bg-gray-50'
                  }`}
                >
                  People
                </button>
                <button
                  type="button"
                  onClick={() =>
                    (window.location.href = `/search?q=${encodeURIComponent(initialQuery)}&type=groups`)
                  }
                  className={`flex-1 py-3 px-4 text-sm font-medium transition-all ${
                    type === 'groups'
                      ? 'bg-[#007AFF] text-white'
                      : 'text-gray-700 hover:bg-gray-50'
                  }`}
                >
                  Groups
                </button>
              </div>

              {/* Search Input */}
              <div className="relative">
                <input
                  type="text"
                  value={query}
                  onChange={e => setQuery(e.target.value)}
                  placeholder={`Search ${type}...`}
                  className="w-full px-4 py-3 pr-12 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#FC4C02] focus:border-transparent text-base"
                />
                <button
                  type="submit"
                  className="absolute right-3 top-1/2 -translate-y-1/2 p-1.5 text-gray-400 hover:text-[#FC4C02] transition-colors"
                >
                  <svg
                    className="w-5 h-5"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                    />
                  </svg>
                </button>
              </div>
            </div>
          </form>
        </div>

        {/* Results */}
        <div>
          {!hasSearchQuery ? (
            isLoading ? (
              <SuggestionsLoadingSkeleton />
            ) : (
              <div className="space-y-6">
                {/* Suggested People - only show on People tab */}
                {type === 'people' && (
                  <>
                    {enhancedSuggestedUsers.length > 0 ? (
                      <div>
                        <div className="flex items-center justify-between mb-3 px-1">
                          <h3 className="text-lg font-semibold text-gray-900">
                            People you might like
                          </h3>
                        </div>
                        <div className="space-y-1">
                          {enhancedSuggestedUsers.map(suggestedUser => (
                            <div
                              key={suggestedUser.id}
                              className="bg-white rounded-lg overflow-hidden"
                            >
                              <UserCardCompact
                                user={suggestedUser}
                                variant="search"
                                onFollowChange={handleFollowChange}
                              />
                            </div>
                          ))}
                        </div>
                      </div>
                    ) : (
                      <div className="p-12 text-center">
                        <div className="w-16 h-16 mx-auto bg-gray-100 rounded-full flex items-center justify-center mb-4">
                          <Users className="w-8 h-8 text-gray-400" />
                        </div>
                        <h3 className="text-lg font-medium text-gray-900 mb-2">
                          No people to suggest
                        </h3>
                        <p className="text-gray-600 text-sm">
                          There are no people available at the moment. Check
                          back later!
                        </p>
                      </div>
                    )}
                  </>
                )}

                {/* Suggested Groups - only show on Groups tab */}
                {type === 'groups' && (
                  <>
                    {suggestedGroups.length > 0 ? (
                      <div>
                        <div className="flex items-center justify-between mb-3 px-1">
                          <h3 className="text-lg font-semibold text-gray-900">
                            Suggested Groups
                          </h3>
                        </div>
                        <div className="space-y-1">
                          {suggestedGroups.slice(0, 5).map(group => (
                            <div
                              key={group.id}
                              className="bg-white rounded-lg overflow-hidden hover:bg-gray-50 transition-colors"
                            >
                              {renderGroupResult(group)}
                            </div>
                          ))}
                        </div>
                      </div>
                    ) : (
                      <div className="p-12 text-center">
                        <div className="w-16 h-16 mx-auto bg-gray-100 rounded-full flex items-center justify-center mb-4">
                          <Users className="w-8 h-8 text-gray-400" />
                        </div>
                        <h3 className="text-lg font-medium text-gray-900 mb-2">
                          No groups to suggest
                        </h3>
                        <p className="text-gray-600 text-sm">
                          There are no groups available at the moment. Check
                          back later!
                        </p>
                      </div>
                    )}
                  </>
                )}
              </div>
            )
          ) : isLoading ? (
            <SearchLoadingSkeleton type={type} count={5} />
          ) : (
            <>
              {type === 'people' && enhancedUsers.length === 0 && (
                <div className="bg-white rounded-lg border border-gray-200 p-12 text-center">
                  <svg
                    className="w-20 h-20 mx-auto text-gray-300"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                    />
                  </svg>
                  <h3 className="mt-4 text-lg font-medium text-gray-900">
                    No results found
                  </h3>
                  <p className="text-gray-600 mt-2">
                    No {type} found matching "{debouncedQuery}"
                  </p>
                  <p className="text-gray-500 text-sm mt-1">
                    Try a different search term or filter
                  </p>
                </div>
              )}

              {type === 'groups' && searchGroups.length === 0 && (
                <div className="bg-white rounded-lg border border-gray-200 p-12 text-center">
                  <svg
                    className="w-20 h-20 mx-auto text-gray-300"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                    />
                  </svg>
                  <h3 className="mt-4 text-lg font-medium text-gray-900">
                    No results found
                  </h3>
                  <p className="text-gray-600 mt-2">
                    No {type} found matching "{debouncedQuery}"
                  </p>
                  <p className="text-gray-500 text-sm mt-1">
                    Try a different search term or filter
                  </p>
                </div>
              )}

              {type === 'people' && enhancedUsers.length > 0 && (
                <div>
                  <div className="mb-2">
                    <p className="text-sm text-gray-600">
                      Found {enhancedUsers.length}{' '}
                      {enhancedUsers.length === 1 ? 'result' : 'results'}
                    </p>
                  </div>
                  <div className="bg-white rounded-lg border border-gray-200 overflow-hidden">
                    {enhancedUsers.map(renderUserResult)}
                  </div>
                </div>
              )}

              {type === 'groups' && searchGroups.length > 0 && (
                <div>
                  <div className="mb-2">
                    <p className="text-sm text-gray-600">
                      Found {searchGroups.length}{' '}
                      {searchGroups.length === 1 ? 'result' : 'results'}
                    </p>
                  </div>
                  <div className="bg-white rounded-lg border border-gray-200 overflow-hidden">
                    {searchGroups.map(renderGroupResult)}
                  </div>
                </div>
              )}
            </>
          )}
        </div>
      </div>

      {/* Bottom padding for mobile navigation */}
      <div className="h-20 md:hidden" />

      <BottomNavigation />
    </div>
  );
}

export default function SearchPage() {
  return (
    <Suspense
      fallback={
        <div className="min-h-screen bg-gray-50 flex items-center justify-center">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-[#007AFF]"></div>
        </div>
      }
    >
      <SearchContent />
    </Suspense>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { ArrowLeft } from 'lucide-react';
import { Session } from '@/types';
import { firebaseApi } from '@/lib/api';
import { useAuth } from '@/hooks/useAuth';
import { EditSessionModal } from '@/components/EditSessionModal';
import Header from '@/components/HeaderComponent';
import MobileHeader from '@/components/MobileHeader';

interface SessionEditPageProps {
  params: Promise<{
    id: string;
  }>;
}

function SessionEditContent({ sessionId }: { sessionId: string }) {
  const router = useRouter();
  const { user } = useAuth();
  const [session, setSession] = useState<Session | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadSession();
  }, [sessionId]);

  const loadSession = async () => {
    try {
      setIsLoading(true);
      setError(null);

      // Fetch the session
      const sessionData = await firebaseApi.session.getSession(sessionId);

      // Check if user owns this session
      if (!user || sessionData.userId !== user.id) {
        setError('You do not have permission to edit this session');
        return;
      }

      setSession(sessionData);
    } catch (err) {
      console.error('Error loading session:', err);
      setError(err instanceof Error ? err.message : 'Failed to load session');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSave = async (sessionId: string, data: {
    title: string;
    description?: string;
    projectId?: string;
    tags?: string[];
    visibility?: 'everyone' | 'followers' | 'private';
    images?: string[];
    startTime?: Date;
    duration?: number;
  }) => {
    try {
      await firebaseApi.session.updateSession(sessionId, data);
      // Navigate back to the session detail page
      router.push(`/sessions/${sessionId}`);
    } catch (err) {
      console.error('Failed to update session:', err);
      throw err; // Let the modal handle the error
    }
  };

  const handleClose = () => {
    router.back();
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="hidden md:block">
          <Header />
        </div>
        <MobileHeader title="Edit Session" />

        <div className="max-w-[600px] mx-auto px-4 py-6">
          <div className="bg-white border border-gray-200 rounded-lg p-6 animate-pulse">
            <div className="space-y-4">
              <div className="h-10 bg-gray-300 rounded"></div>
              <div className="h-24 bg-gray-300 rounded"></div>
              <div className="h-10 bg-gray-300 rounded"></div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error || !session) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="hidden md:block">
          <Header />
        </div>
        <MobileHeader title="Edit Session" />

        <div className="max-w-[600px] mx-auto px-4 py-6">
          <div className="text-center py-12">
            <div className="text-red-600 mb-4">
              <svg className="w-12 h-12 mx-auto mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.732-.833-2.5 0L4.268 18.5c-.77.833.192 2.5 1.732 2.5z" />
              </svg>
              <p className="font-medium text-lg">Cannot Edit Session</p>
              <p className="text-sm text-gray-600 mt-1">
                {error || 'This session may have been deleted or you may not have permission to edit it.'}
              </p>
            </div>
            <button
              onClick={() => router.push('/')}
              className="px-4 py-2 bg-[#007AFF] text-white rounded-lg hover:bg-[#0051D5] transition-colors"
            >
              Back to Feed
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="hidden md:block">
        <Header />
      </div>
      <MobileHeader title="Edit Session" />

      <div className="max-w-[600px] mx-auto md:px-4 md:py-6">
        {/* Back button for desktop */}
        <button
          onClick={() => router.back()}
          className="hidden md:flex items-center gap-2 text-gray-600 hover:text-gray-900 mb-4 transition-colors"
        >
          <ArrowLeft className="w-5 h-5" />
          <span>Back</span>
        </button>

        {/* Edit Form - Not in modal, just as page content */}
        <EditSessionModal
          session={session}
          onClose={handleClose}
          onSave={handleSave}
          isPage={true}
        />
      </div>
    </div>
  );
}

export default function SessionEditPageWrapper({ params }: SessionEditPageProps) {
  const [sessionId, setSessionId] = React.useState<string>('');

  React.useEffect(() => {
    params.then(({ id }) => setSessionId(id));
  }, [params]);

  return (
    <>
      {!sessionId ? (
        <div className="min-h-screen bg-gray-50">
          <div className="hidden md:block">
            <Header />
          </div>
          <MobileHeader title="Edit Session" />
          <div className="max-w-[600px] mx-auto px-4 py-6">
            <div className="animate-pulse">
              <div className="h-8 bg-gray-200 rounded w-48 mb-4"></div>
              <div className="h-64 bg-gray-200 rounded"></div>
            </div>
          </div>
        </div>
      ) : (
        <SessionEditContent sessionId={sessionId} />
      )}
    </>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { ArrowLeft } from 'lucide-react';
import { SessionWithDetails } from '@/types';
import { firebaseApi } from '@/lib/api';
import { useAuth } from '@/hooks/useAuth';
import SessionCard from '@/components/SessionCard';
import Header from '@/components/HeaderComponent';
import MobileHeader from '@/components/MobileHeader';
import { ErrorBoundary } from '@/components/ErrorBoundary';

interface SessionDetailPageProps {
  params: Promise<{
    id: string;
  }>;
}

function SessionDetailContent({ sessionId }: { sessionId: string }) {
  const router = useRouter();
  const { user } = useAuth();
  const [session, setSession] = useState<SessionWithDetails | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadSession();
  }, [sessionId]);

  const loadSession = async () => {
    try {
      setIsLoading(true);
      setError(null);

      // Fetch the session with details
      const sessionData = await firebaseApi.session.getSessionWithDetails(sessionId);
      setSession(sessionData as unknown as SessionWithDetails);
    } catch (err) {
      console.error('Error loading session:', err);
      setError(err instanceof Error ? err.message : 'Failed to load session');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSupport = async (sessionId: string) => {
    try {
      await firebaseApi.post.supportSession(sessionId);

      // Optimistic update
      setSession(prev => prev ? {
        ...prev,
        isSupported: true,
        supportCount: prev.supportCount + 1,
        supportedBy: [...(prev.supportedBy || []), user?.id || ''].filter(Boolean)
      } : null);
    } catch (err) {
      console.error('Failed to support session:', err);
    }
  };

  const handleRemoveSupport = async (sessionId: string) => {
    try {
      await firebaseApi.post.removeSupportFromSession(sessionId);

      // Optimistic update
      setSession(prev => prev ? {
        ...prev,
        isSupported: false,
        supportCount: Math.max(0, prev.supportCount - 1),
        supportedBy: (prev.supportedBy || []).filter(id => id !== user?.id)
      } : null);
    } catch (err) {
      console.error('Failed to remove support:', err);
    }
  };

  const handleShare = async (sessionId: string) => {
    try {
      const sessionUrl = `${window.location.origin}/sessions/${sessionId}`;

      if (navigator.share) {
        await navigator.share({
          title: session?.title || 'Check out this session on Ambira',
          text: session?.description || 'Look at this productive session!',
          url: sessionUrl
        });
      } else {
        await navigator.clipboard.writeText(sessionUrl);
        alert('Link copied to clipboard!');
      }
    } catch (err) {
      if (err instanceof Error && err.name === 'AbortError') {
        return;
      }
      console.error('Failed to share session:', err);
    }
  };

  const handleDelete = async (sessionId: string) => {
    if (!confirm('Are you sure you want to delete this session?')) {
      return;
    }

    try {
      await firebaseApi.session.deleteSession(sessionId);
      router.push('/');
    } catch (err) {
      console.error('Failed to delete session:', err);
      alert('Failed to delete session. Please try again.');
    }
  };

  // Dynamic metadata using useEffect for client component
  // MUST be called before any conditional returns
  React.useEffect(() => {
    if (session) {
      const title = session.title || 'Session';
      document.title = `${title} by ${session.user?.name || 'User'} - Ambira`;

      const description = session.description || `Check out this productive session on Ambira`;

      let metaDescription = document.querySelector('meta[name="description"]');
      if (!metaDescription) {
        metaDescription = document.createElement('meta');
        metaDescription.setAttribute('name', 'description');
        document.head.appendChild(metaDescription);
      }
      metaDescription.setAttribute('content', description);

      // Open Graph tags
      let ogTitle = document.querySelector('meta[property="og:title"]');
      if (!ogTitle) {
        ogTitle = document.createElement('meta');
        ogTitle.setAttribute('property', 'og:title');
        document.head.appendChild(ogTitle);
      }
      ogTitle.setAttribute('content', `${title} - Ambira`);

      let ogDescription = document.querySelector('meta[property="og:description"]');
      if (!ogDescription) {
        ogDescription = document.createElement('meta');
        ogDescription.setAttribute('property', 'og:description');
        document.head.appendChild(ogDescription);
      }
      ogDescription.setAttribute('content', description);

      let ogType = document.querySelector('meta[property="og:type"]');
      if (!ogType) {
        ogType = document.createElement('meta');
        ogType.setAttribute('property', 'og:type');
        document.head.appendChild(ogType);
      }
      ogType.setAttribute('content', 'article');

      // Twitter card tags
      let twitterCard = document.querySelector('meta[name="twitter:card"]');
      if (!twitterCard) {
        twitterCard = document.createElement('meta');
        twitterCard.setAttribute('name', 'twitter:card');
        document.head.appendChild(twitterCard);
      }
      twitterCard.setAttribute('content', 'summary_large_image');

      let twitterTitle = document.querySelector('meta[name="twitter:title"]');
      if (!twitterTitle) {
        twitterTitle = document.createElement('meta');
        twitterTitle.setAttribute('name', 'twitter:title');
        document.head.appendChild(twitterTitle);
      }
      twitterTitle.setAttribute('content', `${title} - Ambira`);

      let twitterDescription = document.querySelector('meta[name="twitter:description"]');
      if (!twitterDescription) {
        twitterDescription = document.createElement('meta');
        twitterDescription.setAttribute('name', 'twitter:description');
        document.head.appendChild(twitterDescription);
      }
      twitterDescription.setAttribute('content', description);
    }
  }, [session]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="hidden md:block">
          <Header />
        </div>
        <MobileHeader title="Session" />

        <div className="max-w-[600px] mx-auto px-4 py-6">
          <div className="bg-white border border-gray-200 rounded-lg p-4 animate-pulse">
            <div className="flex items-center space-x-3 mb-4">
              <div className="w-10 h-10 bg-gray-300 rounded-full"></div>
              <div className="space-y-2 flex-1">
                <div className="h-4 bg-gray-300 rounded w-32"></div>
                <div className="h-3 bg-gray-300 rounded w-24"></div>
              </div>
            </div>
            <div className="space-y-3">
              <div className="h-4 bg-gray-300 rounded w-full"></div>
              <div className="h-4 bg-gray-300 rounded w-3/4"></div>
              <div className="h-48 bg-gray-300 rounded"></div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error || !session) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="hidden md:block">
          <Header />
        </div>
        <MobileHeader title="Session" />

        <div className="max-w-[600px] mx-auto px-4 py-6">
          <div className="text-center py-12">
            <div className="text-red-600 mb-4">
              <svg className="w-12 h-12 mx-auto mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.732-.833-2.5 0L4.268 18.5c-.77.833.192 2.5 1.732 2.5z" />
              </svg>
              <p className="font-medium text-lg">Session not found</p>
              <p className="text-sm text-gray-600 mt-1">
                {error || 'This session may have been deleted or you may not have permission to view it.'}
              </p>
            </div>
            <button
              onClick={() => router.push('/')}
              className="px-4 py-2 bg-[#007AFF] text-white rounded-lg hover:bg-[#0051D5] transition-colors"
            >
              Back to Feed
            </button>
          </div>
        </div>
      </div>
    );
  }

  const isOwnSession = user && session.userId === user.id;

  return (
    <ErrorBoundary
      fallback={
        <div className="min-h-screen bg-gray-50">
          <div className="hidden md:block">
            <Header />
          </div>
          <MobileHeader title="Session" />
          <div className="max-w-[600px] mx-auto px-4 py-6 text-center">
            <h2 className="text-2xl font-bold text-gray-900 mb-2">Error loading session</h2>
            <p className="text-gray-600 mb-6">Something went wrong while loading this session.</p>
            <button
              onClick={() => router.push('/')}
              className="px-4 py-2 bg-[#007AFF] text-white rounded-lg hover:bg-[#0051D5] transition-colors"
            >
              Back to Feed
            </button>
          </div>
        </div>
      }
    >
      <div className="min-h-screen bg-gray-50">
        <div className="hidden md:block">
          <Header />
        </div>
        <MobileHeader title="Session" />

        <div className="max-w-[600px] mx-auto md:px-4 md:py-6">
          {/* Back button for desktop */}
          <button
            onClick={() => router.back()}
            className="hidden md:flex items-center gap-2 text-gray-600 hover:text-gray-900 mb-4 transition-colors"
          >
            <ArrowLeft className="w-5 h-5" />
            <span>Back</span>
          </button>

          {/* Session Card */}
          <SessionCard
            session={session}
            onSupport={handleSupport}
            onRemoveSupport={handleRemoveSupport}
            onShare={handleShare}
            onEdit={isOwnSession ? (sessionId) => router.push(`/sessions/${sessionId}/edit`) : undefined}
            onDelete={isOwnSession ? handleDelete : undefined}
            showComments={true}
          />
        </div>
      </div>
    </ErrorBoundary>
  );
}

export default function SessionDetailPageWrapper({ params }: SessionDetailPageProps) {
  const [sessionId, setSessionId] = React.useState<string>('');

  React.useEffect(() => {
    params.then(({ id }) => setSessionId(id));
  }, [params]);

  return (
    <>
      {!sessionId ? (
        <div className="min-h-screen bg-gray-50">
          <div className="hidden md:block">
            <Header />
          </div>
          <MobileHeader title="Session" />
          <div className="max-w-[600px] mx-auto px-4 py-6">
            <div className="animate-pulse">
              <div className="h-8 bg-gray-200 rounded w-48 mb-4"></div>
              <div className="h-64 bg-gray-200 rounded"></div>
            </div>
          </div>
        </div>
      ) : (
        <SessionDetailContent sessionId={sessionId} />
      )}
    </>
  );
}

'use client';

import React, { useState, useEffect, useRef } from 'react';
import { useRouter } from 'next/navigation';
import Image from 'next/image';
import { ArrowLeft, Download, Share2 } from 'lucide-react';
import { SessionWithDetails, User } from '@/types';
import { firebaseApi } from '@/lib/api';
import { useAuth } from '@/hooks/useAuth';
import Header from '@/components/HeaderComponent';
import MobileHeader from '@/components/MobileHeader';
import { toPng } from 'html-to-image';

interface SessionSharePageProps {
  params: Promise<{
    id: string;
  }>;
}

type LayoutType = 'minimal' | 'square';

function SessionShareContent({ sessionId }: { sessionId: string }) {
  const router = useRouter();
  const { user } = useAuth();
  const [session, setSession] = useState<SessionWithDetails | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedLayout, setSelectedLayout] = useState<LayoutType | null>(
    'square'
  );
  const [isExporting, setIsExporting] = useState(false);
  const [exportError, setExportError] = useState<string | null>(null);

  const minimalRef = useRef<HTMLDivElement>(null);
  const squareRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (sessionId && user) {
      loadSession();
    }
  }, [sessionId, user]);

  const loadSession = async () => {
    try {
      setIsLoading(true);
      setError(null);

      const sessionData =
        await firebaseApi.session.getSessionWithDetails(sessionId);
      setSession(sessionData as unknown as SessionWithDetails);
    } catch (err: any) {
      console.error('Error loading session:', err);
      setError(err.message || 'Failed to load session');
    } finally {
      setIsLoading(false);
    }
  };

  const formatTime = (seconds: number): string => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);

    if (hours > 0) {
      return `${hours}h ${minutes}m`;
    }
    return `${minutes}m`;
  };

  const formatTimeAgo = (date: Date): string => {
    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    const sessionDate = new Date(
      date.getFullYear(),
      date.getMonth(),
      date.getDate()
    );

    const timeStr = date.toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true,
    });

    if (sessionDate.getTime() === today.getTime()) {
      return `Today at ${timeStr}`;
    }

    if (sessionDate.getTime() === yesterday.getTime()) {
      return `Yesterday at ${timeStr}`;
    }

    const dateStr = date.toLocaleDateString('en-US', {
      month: 'long',
      day: 'numeric',
      year: 'numeric',
    });

    return `${dateStr} at ${timeStr}`;
  };

  const getUserInitials = (user: User): string => {
    return user.name
      .split(' ')
      .map(word => word.charAt(0))
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  const formatDate = (date: Date): string => {
    const dateStr = new Intl.DateTimeFormat('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
    }).format(date);

    const timeStr = new Intl.DateTimeFormat('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true,
    }).format(date);

    return `${dateStr} at ${timeStr}`;
  };

  const getCurrentRef = () => {
    if (selectedLayout === 'square') return squareRef;
    if (selectedLayout === 'minimal') return minimalRef;
    return null;
  };

  const handleExport = async () => {
    const imageRef = getCurrentRef();
    if (!imageRef || !imageRef.current) return;

    setIsExporting(true);
    setExportError(null);

    try {
      // Create a temporary container off-screen with the full-size layout
      const tempContainer = document.createElement('div');
      tempContainer.style.position = 'fixed';
      tempContainer.style.left = '-9999px';
      tempContainer.style.top = '0';
      document.body.appendChild(tempContainer);

      // Clone the layout content
      const clonedLayout = imageRef.current.cloneNode(true) as HTMLElement;
      tempContainer.appendChild(clonedLayout);

      // Generate the image from the full-size layout
      const dataUrl = await toPng(clonedLayout, {
        quality: 1.0,
        pixelRatio: 2,
        cacheBust: true,
        backgroundColor: '#ffffff',
        width: selectedLayout === 'square' ? 1080 : 1080,
        height: selectedLayout === 'square' ? 1110 : 1080,
      });

      // Clean up
      document.body.removeChild(tempContainer);

      const link = document.createElement('a');
      link.download = `ambira-${session?.title?.toLowerCase().replace(/\s+/g, '-') || 'session'}-${selectedLayout}.png`;
      link.href = dataUrl;
      link.click();
    } catch (error) {
      console.error('Failed to export image:', error);
      setExportError('Failed to export image. Please try again.');
    } finally {
      setIsExporting(false);
    }
  };

  const handleShare = async () => {
    const imageRef = getCurrentRef();
    if (!imageRef || !imageRef.current) return;

    setIsExporting(true);
    setExportError(null);

    try {
      // Create a temporary container off-screen with the full-size layout
      const tempContainer = document.createElement('div');
      tempContainer.style.position = 'fixed';
      tempContainer.style.left = '-9999px';
      tempContainer.style.top = '0';
      document.body.appendChild(tempContainer);

      // Clone the layout content
      const clonedLayout = imageRef.current.cloneNode(true) as HTMLElement;
      tempContainer.appendChild(clonedLayout);

      // Generate the image from the full-size layout
      const dataUrl = await toPng(clonedLayout, {
        quality: 1.0,
        pixelRatio: 2,
        cacheBust: true,
        backgroundColor: '#ffffff',
        width: selectedLayout === 'square' ? 1080 : 1080,
        height: selectedLayout === 'square' ? 1110 : 1080,
      });

      // Clean up
      document.body.removeChild(tempContainer);

      const response = await fetch(dataUrl);
      const blob = await response.blob();
      const file = new File([blob], `ambira-session.png`, {
        type: 'image/png',
      });

      if (navigator.share && navigator.canShare({ files: [file] })) {
        await navigator.share({
          files: [file],
          title: session?.title || 'My Ambira Session',
          text: `Check out my productivity session on Ambira!`,
        });
      } else {
        await handleExport();
      }
    } catch (error) {
      console.error('Failed to share image:', error);
      if ((error as Error).name !== 'AbortError') {
        setExportError('Failed to share. Downloading instead...');
        await handleExport();
      }
    } finally {
      setIsExporting(false);
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="hidden md:block">
          <Header />
        </div>
        <MobileHeader title="Share Session" />

        <div className="max-w-4xl mx-auto px-4 py-6">
          <div className="animate-pulse space-y-4">
            <div className="h-6 bg-gray-300 rounded w-32"></div>
            <div className="grid grid-cols-3 gap-3">
              <div className="aspect-square bg-gray-300 rounded"></div>
              <div className="aspect-square bg-gray-300 rounded"></div>
              <div className="aspect-square bg-gray-300 rounded"></div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error || !session) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="hidden md:block">
          <Header />
        </div>
        <MobileHeader title="Share Session" />

        <div className="max-w-4xl mx-auto px-4 py-6">
          <div className="text-center py-12">
            <div className="text-red-600 mb-4">
              <svg
                className="w-12 h-12 mx-auto mb-2"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.732-.833-2.5 0L4.268 18.5c-.77.833.192 2.5 1.732 2.5z"
                />
              </svg>
              <p className="font-medium text-lg">Cannot Share Session</p>
              <p className="text-sm text-gray-600 mt-1">
                {error ||
                  'This session may have been deleted or you may not have permission to view it.'}
              </p>
            </div>
            <button
              onClick={() => router.push('/')}
              className="px-4 py-2 bg-[#007AFF] text-white rounded-lg hover:bg-[#0051D5] transition-colors"
            >
              Back to Feed
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Check if the current user owns this session
  if (session.userId !== user?.id) {
    return (
      <div className="min-h-screen bg-gray-50">
        <div className="hidden md:block">
          <Header />
        </div>
        <MobileHeader title="Share Session" />

        <div className="max-w-4xl mx-auto px-4 py-6">
          <div className="text-center py-12">
            <div className="text-gray-600 mb-4">
              <svg
                className="w-12 h-12 mx-auto mb-2"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z"
                />
              </svg>
              <p className="font-medium text-lg">Cannot Share This Session</p>
              <p className="text-sm text-gray-600 mt-1">
                You can only create share images for your own sessions.
              </p>
            </div>
            <button
              onClick={() => router.back()}
              className="px-4 py-2 bg-[#007AFF] text-white rounded-lg hover:bg-[#0051D5] transition-colors"
            >
              Go Back
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Square Mobile Post Layout
  // Note: Using native <img> tags here instead of Next.js <Image> because this component
  // is rendered to PNG via html-to-image, which doesn't support Next.js image optimization
  /* eslint-disable @next/next/no-img-element */
  const SquareLayout = () => (
    <div
      ref={squareRef}
      style={{
        width: '1080px',
        height: '1110px',
        backgroundColor: '#ffffff',
        overflow: 'hidden',
        fontFamily:
          'system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", sans-serif',
      }}
    >
      <div
        style={{
          height: '100%',
          paddingTop: '48px',
