}: BrowseGroupsProps) {
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [showFilters, setShowFilters] = useState(false);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [filters, setFilters] = useState<GroupFilters>({
    search: '',
    category: undefined,
    type: undefined,
    privacySetting: undefined,
    location: ''
  });
  const [sortBy, setSortBy] = useState('recent');
  const [filteredGroups, setFilteredGroups] = useState<Group[]>(groups);

  useEffect(() => {
    let filtered = [...groups];

    // Apply search filter
    if (filters.search) {
      const searchLower = filters.search.toLowerCase();
      filtered = filtered.filter(group => 
        group.name.toLowerCase().includes(searchLower) ||
        group.description.toLowerCase().includes(searchLower)
      );
    }

    // Apply category filter
    if (filters.category) {
      filtered = filtered.filter(group => group.category === filters.category);
    }

    // Apply type filter
    if (filters.type) {
      filtered = filtered.filter(group => group.type === filters.type);
    }

    // Apply privacy filter
    if (filters.privacySetting) {
      filtered = filtered.filter(group => group.privacySetting === filters.privacySetting);
    }

    // Apply location filter
    if (filters.location) {
      const locationLower = filters.location.toLowerCase();
      filtered = filtered.filter(group => 
        group.location?.toLowerCase().includes(locationLower)
      );
    }

    // Apply sorting
    switch (sortBy) {
      case 'members':
        filtered.sort((a, b) => b.memberCount - a.memberCount);
        break;
      case 'name':
        filtered.sort((a, b) => a.name.localeCompare(b.name));
        break;
      case 'activity':
        // For now, sort by member count as a proxy for activity
        filtered.sort((a, b) => b.memberCount - a.memberCount);
        break;
      case 'recent':
      default:
        filtered.sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
        break;
    }

    setFilteredGroups(filtered);
  }, [groups, filters, sortBy]);

  const handleFilterChange = (key: keyof GroupFilters, value: string) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  };

  const clearFilters = () => {
    setFilters({
      search: '',
      category: undefined,
      type: undefined,
      privacySetting: undefined,
      location: ''
    });
  };

  const hasActiveFilters = Object.values(filters).some(value => value !== '');

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Discover Groups</h1>
          <p className="text-gray-600 mt-1">
            Join productivity groups and connect with like-minded people
          </p>
        </div>
        <Button onClick={() => setShowCreateModal(true)}>
          <Plus className="w-4 h-4 mr-2" />
          Create Group
        </Button>
      </div>

      {/* Search and Filters */}
      <div className="bg-white rounded-lg border border-gray-200 p-4">
        <div className="flex flex-col lg:flex-row gap-4">
          {/* Search */}
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
              <Input
                placeholder="Search groups..."
                value={filters.search}
                onChange={(e) => handleFilterChange('search', e.target.value)}
                className="pl-10"
              />
            </div>
          </div>

          {/* Location */}
          <div className="lg:w-48">
            <div className="relative">
              <MapPin className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
              <Input
                placeholder="Location..."
                value={filters.location}
                onChange={(e) => handleFilterChange('location', e.target.value)}
                className="pl-10"
              />
            </div>
          </div>

          {/* Filter Toggle */}
          <Button
            variant="outline"
            onClick={() => setShowFilters(!showFilters)}
            className="lg:hidden"
          >
            <Filter className="w-4 h-4 mr-2" />
            Filters
          </Button>
        </div>

        {/* Advanced Filters */}
        {(showFilters || window.innerWidth >= 1024) && (
          <div className="mt-4 pt-4 border-t border-gray-200">
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <Select
                value={filters.category}
                onChange={(e) => handleFilterChange('category', e.target.value)}
              >
                {categoryOptions.map((option) => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </Select>

              <Select
                value={filters.type}
                onChange={(e) => handleFilterChange('type', e.target.value)}
              >
                {typeOptions.map((option) => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </Select>

              <Select
                value={filters.privacySetting}
                onChange={(e) => handleFilterChange('privacySetting', e.target.value)}
              >
                {privacyOptions.map((option) => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </Select>

              <Select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
              >
                {sortOptions.map((option) => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </Select>
            </div>

            {hasActiveFilters && (
              <div className="mt-4 flex items-center gap-2">
                <Button variant="outline" size="sm" onClick={clearFilters}>
                  Clear Filters
                </Button>
                <span className="text-sm text-gray-500">
                  {filteredGroups.length} of {groups.length} groups
                </span>
              </div>
            )}
          </div>
        )}
      </div>

      {/* View Mode Toggle */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <span className="text-sm text-gray-600">
            {filteredGroups.length} groups found
          </span>
        </div>

        <div className="flex items-center gap-2">
          <span className="text-sm text-gray-600">View:</span>
          <div className="flex border border-gray-300 rounded-md">
            <Button
              variant={viewMode === 'grid' ? 'default' : 'ghost'}
              size="sm"
              onClick={() => setViewMode('grid')}
              className="rounded-r-none border-r border-gray-300"
            >
              <Grid3X3 className="w-4 h-4" />
            </Button>
            <Button
              variant={viewMode === 'list' ? 'default' : 'ghost'}
              size="sm"
              onClick={() => setViewMode('list')}
              className="rounded-l-none"
            >
              <List className="w-4 h-4" />
            </Button>
          </div>
        </div>
      </div>

      {/* Groups Grid/List */}
      {isLoading && filteredGroups.length === 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {[...Array(6)].map((_, i) => (
            <div key={i} className="bg-white rounded-lg border border-gray-200 p-4 animate-pulse">
              <div className="h-32 bg-gray-200 rounded-lg mb-4"></div>
              <div className="h-4 bg-gray-200 rounded mb-2"></div>
              <div className="h-3 bg-gray-200 rounded mb-4"></div>
              <div className="h-8 bg-gray-200 rounded"></div>
            </div>
          ))}
        </div>
      ) : filteredGroups.length === 0 ? (
        <div className="text-center py-12">
          <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <Search className="w-8 h-8 text-gray-400" />
          </div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No groups found</h3>
          <p className="text-gray-600 mb-4">
            {hasActiveFilters 
              ? 'Try adjusting your filters or search terms'
              : 'Be the first to create a group in this category'
            }
          </p>
          {hasActiveFilters ? (
            <Button variant="outline" onClick={clearFilters}>
              Clear Filters
            </Button>
          ) : (
            <Button onClick={() => setShowCreateModal(true)}>
              Create Group
            </Button>
          )}
        </div>
      ) : (
        <>
          <div className={
            viewMode === 'grid' 
              ? 'grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6'
              : 'space-y-4'
          }>
            {filteredGroups.map((group) => (
              <GroupCard
                key={group.id}
                group={group}
                currentUserId="current-user-id" // This should come from auth context
                isJoined={userGroups.includes(group.id)}
                onJoin={onJoinGroup}
                onLeave={onLeaveGroup}
                isLoading={isLoading}
              />
            ))}
          </div>

          {/* Load More */}
          {hasMore && (
            <div className="text-center pt-6">
              <Button variant="outline" onClick={onLoadMore} disabled={isLoading}>
                {isLoading ? 'Loading...' : 'Load More Groups'}
              </Button>
            </div>
          )}
        </>
      )}

      {/* Create Group Modal */}
      <CreateGroupModal
        isOpen={showCreateModal}
        onClose={() => setShowCreateModal(false)}
        onSubmit={onCreateGroup}
        isLoading={isLoading}
      />
    </div>
  );
}

'use client';

import React, { useState } from 'react';
import { Challenge, ChallengeStats, ChallengeLeaderboard as ChallengeLeaderboardType, ChallengeProgress } from '@/types';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { 
  Calendar, 
  Target,
  Zap,
  Timer,
  TrendingUp,
  Award,
  Settings,
  Share2
} from 'lucide-react';
import ChallengeLeaderboard from './ChallengeLeaderboard';

interface ChallengeDetailProps {
  challenge: Challenge;
  stats: ChallengeStats;
  leaderboard: ChallengeLeaderboardType;
  userProgress?: ChallengeProgress | null;
  isParticipating: boolean;
  isAdmin: boolean;
  onJoin: () => Promise<void>;
  onLeave: () => Promise<void>;
  onEdit?: () => void;
  onDelete?: () => void;  
  isLoading: boolean;
}

const challengeTypeConfig = {
  'most-activity': {
    label: 'Most Activity',
    icon: TrendingUp,
    color: 'bg-blue-100 text-blue-800',
    description: 'Compete to log the most productive hours during the challenge period'
  },
  'fastest-effort': {
    label: 'Fastest Effort',
    icon: Zap,
    color: 'bg-yellow-100 text-yellow-800',
    description: 'Achieve the best tasks-per-hour ratio in your sessions'
  },
  'longest-session': {
    label: 'Longest Session',
    icon: Timer,
    color: 'bg-purple-100 text-purple-800',
    description: 'Record the longest single continuous work session'
  },
  'group-goal': {
    label: 'Group Goal',
    icon: Target,
    color: 'bg-green-100 text-green-800',
    description: 'Work together to reach a collective productivity target'
  }
};

export default function ChallengeDetail({
  challenge,
  stats,
  leaderboard,
  userProgress,
  isParticipating,
  isAdmin,
  onJoin,
  onLeave,
  onEdit,
  onDelete,
  isLoading
}: ChallengeDetailProps) {
  const [activeSection, setActiveSection] = useState<'overview' | 'leaderboard'>('overview');

  const typeConfig = challengeTypeConfig[challenge.type];
  const TypeIcon = typeConfig.icon;

  const now = new Date();
  const startDate = new Date(challenge.startDate);
  const endDate = new Date(challenge.endDate);
  const isActive = now >= startDate && now <= endDate && challenge.isActive;
  const isUpcoming = now < startDate && challenge.isActive;
  const isCompleted = now > endDate || !challenge.isActive;

  const timeRemaining = isActive ? endDate.getTime() - now.getTime() : 0;
  const daysRemaining = Math.ceil(timeRemaining / (1000 * 60 * 60 * 24));
  const hoursRemaining = Math.ceil(timeRemaining / (1000 * 60 * 60));

  const getStatusBadge = () => {
    if (isUpcoming) {
      return <Badge className="bg-blue-100 text-blue-800">Upcoming</Badge>;
    }
    if (isActive) {
      return <Badge className="bg-green-100 text-green-800">Active</Badge>;
    }
    return <Badge className="bg-gray-100 text-gray-800">Completed</Badge>;
  };

  const formatProgress = (progress: number) => {
    if (challenge.type === 'most-activity' || challenge.type === 'group-goal' || challenge.type === 'longest-session') {
      return `${progress.toFixed(1)} hours`;
    }
    if (challenge.type === 'fastest-effort') {
      return `${progress.toFixed(1)} tasks/hour`;
    }
    return progress.toString();
  };

  const getProgressPercentage = () => {
    if (!challenge.goalValue || !userProgress?.currentValue) return 0;
    return Math.min((userProgress.currentValue / challenge.goalValue) * 100, 100);
  };

  const formatTimeRemaining = () => {
    if (daysRemaining > 1) {
      return `${daysRemaining} days remaining`;
    } else if (hoursRemaining > 1) {
      return `${hoursRemaining} hours remaining`;
    } else if (timeRemaining > 0) {
      return 'Less than 1 hour remaining';
    }
    return 'Challenge ended';
  };

  return (
    <div className="max-w-4xl mx-auto">
      {/* Hero Section */}
      <div className="bg-gradient-to-r from-[#007AFF] to-[#0051D5] text-white rounded-lg shadow-sm p-8 mb-8">
        <div className="flex items-start justify-between mb-6">
          <div className="flex items-center gap-4 flex-1 min-w-0">
            <div className="p-3 bg-white/20 rounded-lg flex-shrink-0">
              <TypeIcon className="w-8 h-8" />
            </div>
            <div className="min-w-0 flex-1">
              <h1 className="text-3xl font-bold mb-2">{challenge.name}</h1>
              <p className="text-white/80 text-lg">{typeConfig.description}</p>
            </div>
          </div>
          <div className="flex items-center gap-2">
            {getStatusBadge()}
            {isAdmin && (
              <Button variant="secondary" size="sm" onClick={onEdit}>
                <Settings className="w-4 h-4 mr-1" />
                Settings
              </Button>
            )}
          </div>
        </div>

        {/* Challenge Meta */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          <div className="text-center p-4 bg-white/10 rounded-lg">
            <div className="text-3xl font-bold">{challenge.participantCount}</div>
            <div className="text-white/80 text-sm mt-1">Participants</div>
          </div>
          <div className="text-center p-4 bg-white/10 rounded-lg">
            <div className="text-2xl font-bold">
              {isActive ? formatTimeRemaining() :
               isUpcoming ? `Starts ${startDate.toLocaleDateString()}` :
               `Ended ${endDate.toLocaleDateString()}`}
            </div>
            <div className="text-white/80 text-sm mt-1">
              {isActive ? 'Time Remaining' : isUpcoming ? 'Start Date' : 'End Date'}
            </div>
          </div>
          <div className="text-center p-4 bg-white/10 rounded-lg">
            <div className="text-3xl font-bold">
              {challenge.goalValue ? formatProgress(challenge.goalValue) : 'No limit'}
            </div>
            <div className="text-white/80 text-sm mt-1">Target Goal</div>
          </div>
        </div>

        {/* User Progress */}
        {isParticipating && userProgress && (
          <div className="bg-white/10 rounded-lg p-4">
            <div className="flex items-center justify-between mb-2">
              <span className="font-medium">Your Progress</span>
              <span className="text-lg font-bold">
                {formatProgress(userProgress.currentValue)}
                {challenge.goalValue && ` / ${formatProgress(challenge.goalValue)}`}
              </span>
            </div>
            {challenge.goalValue && (
              <div className="w-full bg-white/20 rounded-full h-3 mb-2">
                <div 
                  className="bg-white h-3 rounded-full transition-all duration-300"
                  style={{ width: `${getProgressPercentage()}%` }}
                />
              </div>
            )}
            <div className="flex items-center justify-between text-sm text-blue-100">
              <span>Rank #{userProgress.rank}</span>
              <span>{userProgress.percentage.toFixed(1)}% complete</span>
            </div>
          </div>
        )}

        {/* Action Buttons */}
        <div className="flex flex-wrap items-center gap-3 mt-6">
          {!isParticipating && !isCompleted && (
            <Button
              onClick={onJoin}
              disabled={isLoading}
              size="lg"
              className="bg-white text-[#007AFF] hover:bg-gray-50 font-semibold"
            >
              {isLoading ? 'Joining...' : 'Join Challenge'}
            </Button>
          )}
          {isParticipating && !isCompleted && (
            <Button
              variant="outline"
              onClick={onLeave}
              disabled={isLoading}
              size="lg"
              className="border-white text-white hover:bg-white/10 font-semibold"
            >
              {isLoading ? 'Leaving...' : 'Leave Challenge'}
            </Button>
          )}
          <Button
            variant="outline"
            size="lg"
            className="border-white text-white hover:bg-white/10 font-semibold"
          >
            <Share2 className="w-4 h-4 mr-2" />
            Share
          </Button>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="bg-white rounded-lg border border-gray-200 shadow-sm mb-6">
        <div className="flex border-b border-gray-200">
          <button
            onClick={() => setActiveSection('overview')}
            className={`px-6 py-4 font-semibold text-sm border-b-2 transition-colors ${
              activeSection === 'overview'
                ? 'border-[#007AFF] text-[#007AFF]'
                : 'border-transparent text-gray-600 hover:text-gray-900'
            }`}
          >
            Overview
          </button>
          <button
            onClick={() => setActiveSection('leaderboard')}
            className={`px-6 py-4 font-semibold text-sm border-b-2 transition-colors ${
              activeSection === 'leaderboard'
                ? 'border-[#007AFF] text-[#007AFF]'
                : 'border-transparent text-gray-600 hover:text-gray-900'
            }`}
          >
            Leaderboard
          </button>
        </div>

        <div className="p-6">
          {activeSection === 'overview' && (
            <div className="space-y-6">
              {/* Description */}
              <div>
                <h2 className="text-xl font-bold text-gray-900 mb-3">About This Challenge</h2>
                <p className="text-gray-600 leading-relaxed">{challenge.description}</p>
              </div>

              {/* Rules */}
              {challenge.rules && (
                <div>
                  <h2 className="text-xl font-bold text-gray-900 mb-3">Rules & Requirements</h2>
                  <div className="bg-gray-50 rounded-lg p-4">
                    <p className="text-gray-600 whitespace-pre-wrap">{challenge.rules}</p>
                  </div>
                </div>
              )}

              {/* Challenge Details */}
              <div>
                <h2 className="text-xl font-bold text-gray-900 mb-3">Challenge Details</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="bg-gray-50 rounded-lg p-4">
                    <div className="flex items-center gap-2 mb-2">
                      <Calendar className="w-5 h-5 text-gray-500" />
                      <span className="font-medium text-gray-900">Duration</span>
                    </div>
                    <p className="text-gray-600">
                      {startDate.toLocaleDateString()} - {endDate.toLocaleDateString()}
                    </p>
                  </div>
                  <div className="bg-gray-50 rounded-lg p-4">
                    <div className="flex items-center gap-2 mb-2">
                      <Target className="w-5 h-5 text-gray-500" />
                      <span className="font-medium text-gray-900">Challenge Type</span>
                    </div>
                    <Badge className={typeConfig.color}>
                      {typeConfig.label}
                    </Badge>
                  </div>
                </div>
              </div>

              {/* Rewards */}
              {challenge.rewards && challenge.rewards.length > 0 && (
                <div>
                  <h2 className="text-xl font-bold text-gray-900 mb-3">Rewards</h2>
                  <div className="bg-yellow-50 rounded-lg p-4 border border-yellow-200">
                    <div className="flex items-center gap-2 mb-2">
                      <Award className="w-5 h-5 text-yellow-600" />
                      <span className="font-semibold text-yellow-900">What You'll Earn</span>
                    </div>
                    <ul className="space-y-1">
                      {challenge.rewards.map((reward, index) => (
                        <li key={index} className="text-yellow-800">‚Ä¢ {reward}</li>
                      ))}
                    </ul>
                  </div>
                </div>
              )}

              {/* Statistics */}
              <div>
                <h2 className="text-xl font-bold text-gray-900 mb-3">Challenge Statistics</h2>
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                  <div className="bg-blue-50 rounded-lg p-4 text-center">
                    <div className="text-2xl font-bold text-blue-600">{stats.totalParticipants}</div>
                    <div className="text-sm text-blue-600">Total Participants</div>
                  </div>
                  <div className="bg-green-50 rounded-lg p-4 text-center">
                    <div className="text-2xl font-bold text-green-600">{stats.completedParticipants}</div>
                    <div className="text-sm text-green-600">Completed</div>
                  </div>
                  <div className="bg-purple-50 rounded-lg p-4 text-center">
                    <div className="text-2xl font-bold text-purple-600">
                      {formatProgress(stats.averageProgress)}
                    </div>
                    <div className="text-sm text-purple-600">Average Progress</div>
                  </div>
                  <div className="bg-orange-50 rounded-lg p-4 text-center">
                    <div className="text-2xl font-bold text-orange-600">
                      {Math.round((stats.completedParticipants / stats.totalParticipants) * 100) || 0}%
                    </div>
                    <div className="text-sm text-orange-600">Completion Rate</div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeSection === 'leaderboard' && (
            <ChallengeLeaderboard
              leaderboard={leaderboard}
              challengeType={challenge.type}
              currentUserId={userProgress?.userId}
            />
          )}
        </div>
      </div>
    </div>
  );
}

'use client';

import React, { useState } from 'react';
import Image from 'next/image';
import Link from 'next/link';
import { ChallengeLeaderboard as ChallengeLeaderboardType, ChallengeLeaderboardEntry } from '@/types';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { 
  Trophy, 
  Medal, 
  Award,
  Crown,
  User,
  MapPin,
  CheckCircle
} from 'lucide-react';

interface ChallengeLeaderboardProps {
  leaderboard: ChallengeLeaderboardType;
  challengeType: 'most-activity' | 'fastest-effort' | 'longest-session' | 'group-goal';
  currentUserId?: string;
  showFilters?: boolean;
}

const getRankIcon = (rank: number) => {
  switch (rank) {
    case 1:
      return <Crown className="w-5 h-5 text-yellow-500" />;
    case 2:
      return <Medal className="w-5 h-5 text-gray-400" />;
    case 3:
      return <Award className="w-5 h-5 text-amber-600" />;
    default:
      return <span className="w-5 h-5 flex items-center justify-center text-sm font-medium text-gray-500">#{rank}</span>;
  }
};

const getRankBadgeColor = (rank: number) => {
  switch (rank) {
    case 1:
      return 'bg-yellow-100 text-yellow-800 border-yellow-200';
    case 2:
      return 'bg-gray-100 text-gray-800 border-gray-200';
    case 3:
      return 'bg-amber-100 text-amber-800 border-amber-200';
    default:
      return 'bg-blue-50 text-blue-700 border-blue-200';
  }
};

export default function ChallengeLeaderboard({
  leaderboard,
  challengeType,
  currentUserId,
  showFilters = true
}: ChallengeLeaderboardProps) {
  const [filter, setFilter] = useState<'all' | 'following' | 'completed'>('all');

  const formatProgress = (progress: number) => {
    if (challengeType === 'most-activity' || challengeType === 'group-goal' || challengeType === 'longest-session') {
      return `${progress.toFixed(1)} hours`;
    }
    if (challengeType === 'fastest-effort') {
      return `${progress.toFixed(1)} tasks/hour`;
    }
    return progress.toString();
  };

  const getProgressLabel = () => {
    switch (challengeType) {
      case 'most-activity':
        return 'Total Hours';
      case 'fastest-effort':
        return 'Best Ratio';
      case 'longest-session':
        return 'Longest Session';
      case 'group-goal':
        return 'Contributed Hours';
      default:
        return 'Progress';
    }
  };

  // Filter entries based on selected filter
  const filteredEntries = leaderboard.entries.filter(entry => {
    if (filter === 'completed') {
      return entry.isCompleted;
    }
    // TODO: Implement following filter when we have following data
    return true;
  });

  const currentUserEntry = leaderboard.entries.find(entry => entry.userId === currentUserId);

  return (
    <div className="space-y-6">
      {/* Filters */}
      {showFilters && (
        <div className="flex items-center gap-2">
          <Button
            variant={filter === 'all' ? 'default' : 'outline'}
            size="sm"
            onClick={() => setFilter('all')}
          >
            All Participants
          </Button>
          <Button
            variant={filter === 'following' ? 'default' : 'outline'}
            size="sm"
            onClick={() => setFilter('following')}
            disabled // TODO: Implement when following data is available
          >
            Following
          </Button>
          <Button
            variant={filter === 'completed' ? 'default' : 'outline'}
            size="sm"
            onClick={() => setFilter('completed')}
          >
            Completed
          </Button>
        </div>
      )}

      {/* Current User Highlight */}
      {currentUserEntry && filter === 'all' && currentUserEntry.rank > 3 && (
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
          <div className="flex items-center gap-3">
            <div className="flex items-center justify-center w-10 h-10 bg-blue-100 rounded-full">
              <span className="text-sm font-bold text-blue-700">#{currentUserEntry.rank}</span>
            </div>
            <div className="flex-1">
              <div className="flex items-center gap-2">
                <span className="font-medium text-blue-900">Your Position</span>
                {currentUserEntry.isCompleted && (
                  <CheckCircle className="w-4 h-4 text-green-500" />
