 * This component doesn't render anything - it just triggers data fetching.
 */

import { useEffect } from 'react';
import { useAuth } from '@/hooks/useAuth';
import { useFollowingList } from '@/features/search/hooks/useFollowingList';

export function DataPrefetcher() {
  const { user } = useAuth();

  // Prefetch following list immediately when user is authenticated
  // This ensures it's available instantly when they navigate to search
  useFollowingList({
    userId: user?.id,
    enabled: !!user,
  });

  return null; // This component doesn't render anything
}

'use client';

import React, { useState, useEffect } from 'react';
import { useAuth } from '@/hooks/useAuth';
import { firebaseSessionApi, firebaseUserApi } from '@/lib/api';
import { Clock, Flame, Target, TrendingUp } from 'lucide-react';
import Link from 'next/link';

export default function DayOverview() {
  const { user } = useAuth();
  const [todayStats, setTodayStats] = useState({
    totalTime: 0,
    sessionsCount: 0,
    currentStreak: 0,
  });
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (user) {
      loadTodayStats();
    }
  }, [user]);

  const loadTodayStats = async () => {
    if (!user) return;

    try {
      setIsLoading(true);

      // Get today's sessions
      const sessions = await firebaseSessionApi.getUserSessions(user.id, 50, true);
      const today = new Date();
      today.setHours(0, 0, 0, 0);

      const todaySessions = sessions.filter(session => {
        const sessionDate = session.createdAt instanceof Date
          ? session.createdAt
          : new Date(session.createdAt);
        const sessionDay = new Date(sessionDate);
        sessionDay.setHours(0, 0, 0, 0);
        return sessionDay.getTime() === today.getTime();
      });

      // Calculate total time
      const totalSeconds = todaySessions.reduce((sum, session) => sum + session.duration, 0);

      // Get streak
      const stats = await firebaseUserApi.getUserStats(user.id);

      setTodayStats({
        totalTime: totalSeconds,
        sessionsCount: todaySessions.length,
        currentStreak: stats?.currentStreak || 0,
      });
    } catch (error) {
      console.error('Failed to load today stats:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const formatTime = (seconds: number) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);

    if (hours > 0) {
      return `${hours}h ${minutes}m`;
    }
    return `${minutes}m`;
  };

  if (isLoading) {
    return (
      <div className="md:hidden bg-white border-b border-gray-200 px-4 py-3">
        <div className="animate-pulse flex gap-3">
          <div className="h-16 bg-gray-200 rounded-lg flex-1"></div>
          <div className="h-16 bg-gray-200 rounded-lg flex-1"></div>
          <div className="h-16 bg-gray-200 rounded-lg flex-1"></div>
        </div>
      </div>
    );
  }

  return (
    <Link href="/you?tab=progress" className="md:hidden block bg-white border-b border-gray-200 px-4 py-4">
      <div className="flex items-center justify-between mb-3">
        <h3 className="text-sm font-semibold text-gray-900">Today's Progress</h3>
        <TrendingUp className="w-5 h-5 text-[#007AFF]" />
      </div>

      <div className="grid grid-cols-3 gap-3">
        {/* Total Time */}
        <div className="bg-gray-50 rounded-lg p-3 border border-gray-200">
          <Clock className="w-5 h-5 text-[#007AFF] mb-2" />
          <div className="text-lg font-bold text-gray-900">
            {formatTime(todayStats.totalTime)}
          </div>
          <div className="text-xs text-gray-500">Time</div>
        </div>

        {/* Sessions */}
        <div className="bg-gray-50 rounded-lg p-3 border border-gray-200">
          <Target className="w-5 h-5 text-[#22C55E] mb-2" />
          <div className="text-lg font-bold text-gray-900">
            {todayStats.sessionsCount}
          </div>
          <div className="text-xs text-gray-500">Sessions</div>
        </div>

        {/* Streak */}
        <div className="bg-gray-50 rounded-lg p-3 border border-gray-200">
          <Flame className="w-5 h-5 text-[#FC4C02] mb-2" />
          <div className="text-lg font-bold text-gray-900">
            {todayStats.currentStreak}
          </div>
          <div className="text-xs text-gray-500">Day Streak</div>
        </div>
      </div>
    </Link>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import { UserProfile } from '@/types';
import { firebaseUserApi } from '@/lib/api';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectItem } from '@/components/ui/select';
import { X, User, MapPin, FileText, Globe } from 'lucide-react';
import { toast } from 'sonner';
import { ImageUpload } from '@/components/ImageUpload';

interface EditProfileModalProps {
  profile: UserProfile;
  isOpen: boolean;
  onClose: () => void;
  onProfileUpdate: (updatedProfile: UserProfile) => void;
}

export const EditProfileModal: React.FC<EditProfileModalProps> = ({
  profile,
  isOpen,
  onClose,
  onProfileUpdate,
}) => {
  const [formData, setFormData] = useState({
    name: profile.name,
    bio: profile.bio || '',
    location: profile.location || '',
    profilePicture: profile.profilePicture || '',
  });
  const [isLoading, setIsLoading] = useState(false);
  const [profileVisibility, setProfileVisibility] = useState<'everyone' | 'followers' | 'private'>('everyone');
  const [profileImageFile, setProfileImageFile] = useState<File[]>([]);
  const [profileImagePreview, setProfileImagePreview] = useState<string[]>(
    profile.profilePicture ? [profile.profilePicture] : []
  );

  // Handle ESC key to close modal
  useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape' && isOpen && !isLoading) {
        // Reset form data to original values
        setFormData({
          name: profile.name,
          bio: profile.bio || '',
          location: profile.location || '',
          profilePicture: profile.profilePicture || '',
        });
        onClose();
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleEscape);
    }

    return () => {
      document.removeEventListener('keydown', handleEscape);
    };
  }, [isOpen, isLoading, onClose, profile]);

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handleProfileImageChange = (images: File[], previewUrls: string[]) => {
    setProfileImageFile(images);
    setProfileImagePreview(previewUrls);
  };

  const handleProfileImageUpload = async (files: File[]): Promise<string[]> => {
    const file = files[0];
    if (!file) return [];

    try {
      toast.loading('Uploading profile picture...', { id: 'upload-profile-pic' });

      // Upload to Firebase Storage
      const downloadURL = await firebaseUserApi.uploadProfilePicture(file);

      // Delete old profile picture if it exists and is a Firebase Storage URL
      if (profile.profilePicture && profile.profilePicture.includes('firebasestorage.googleapis.com')) {
        try {
          await firebaseUserApi.deleteProfilePicture(profile.profilePicture);
        } catch (error) {
          // Silently fail - old picture deletion is not critical
        }
      }

      setFormData(prev => ({ ...prev, profilePicture: downloadURL }));
      toast.success('Profile picture uploaded successfully', { id: 'upload-profile-pic' });

      return [downloadURL];
    } catch (error) {
      console.error('File upload error:', error);
      toast.error('Failed to upload image', { id: 'upload-profile-pic' });
      throw error;
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      setIsLoading(true);

      const updatedProfile = await firebaseUserApi.updateProfile(formData);
      onProfileUpdate(updatedProfile);
      onClose();
      toast.success('Profile updated successfully');
    } catch (error: unknown) {
      console.error('Profile update error:', error);
      toast.error('Failed to update profile');
    } finally {
      setIsLoading(false);
    }
  };

  const handleClose = () => {
    if (!isLoading) {
      // Reset form data to original values
      setFormData({
        name: profile.name,
        bio: profile.bio || '',
        location: profile.location || '',
        profilePicture: profile.profilePicture || '',
      });
      onClose();
    }
  };

  if (!isOpen) return null;

  return (
    <div className="max-w-2xl w-full mx-auto p-4">
      <div className="bg-background rounded-lg shadow-xl w-full">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-border">
          <h2 className="text-xl font-semibold text-foreground">Edit Profile</h2>
          <Button
            variant="ghost"
            size="sm"
            onClick={handleClose}
            disabled={isLoading}
            className="h-8 w-8 p-0"
          >
            <X className="h-4 w-4" />
          </Button>
        </div>

        <form onSubmit={handleSubmit} className="p-6 space-y-6">
          {/* Profile Picture */}
          <div className="space-y-4">
            <ImageUpload
              label="Profile Picture"
              singleImage={true}
              maxSizeMB={5}
              images={profileImageFile}
              previewUrls={profileImagePreview}
              onImagesChange={handleProfileImageChange}
              uploadMode="instant"
              onUpload={handleProfileImageUpload}
              showProgress={true}
              placeholder="Upload profile picture"
              disabled={isLoading}
            />
          </div>

          {/* Name */}
          <div className="space-y-2">
            <Label htmlFor="name" className="text-base font-medium">
              <User className="w-4 h-4 inline mr-2" />
              Name
            </Label>
            <Input
              id="name"
              type="text"
              value={formData.name}
              onChange={(e) => handleInputChange('name', e.target.value)}
              placeholder="Your full name"
              required
              disabled={isLoading}
              className="text-base"
            />
          </div>

          {/* Bio */}
          <div className="space-y-2">
            <Label htmlFor="bio" className="text-base font-medium">
              <FileText className="w-4 h-4 inline mr-2" />
              Bio
            </Label>
            <Textarea
              id="bio"
              value={formData.bio}
              onChange={(e) => handleInputChange('bio', e.target.value)}
              placeholder="Tell us about yourself..."
              rows={4}
              maxLength={160}
              disabled={isLoading}
              className="text-base resize-none"
            />
            <div className="text-sm text-muted-foreground text-right">
              {formData.bio.length}/160
            </div>
          </div>

          {/* Location */}
          <div className="space-y-2">
            <Label htmlFor="location" className="text-base font-medium">
              <MapPin className="w-4 h-4 inline mr-2" />
              Location
            </Label>
            <Input
              id="location"
              type="text"
              value={formData.location}
              onChange={(e) => handleInputChange('location', e.target.value)}
              placeholder="City, Country"
              disabled={isLoading}
              className="text-base"
            />
          </div>

          {/* Privacy Settings */}
          <div className="space-y-4 pt-4 border-t border-border">
            <h3 className="text-lg font-semibold text-foreground">Privacy Settings</h3>

          <div className="space-y-2">
              <Label className="text-base font-medium">
                <Globe className="w-4 h-4 inline mr-2" />
                Profile Visibility
              </Label>
            <Select value={profileVisibility} onChange={(e) => setProfileVisibility(e.target.value as 'everyone' | 'followers' | 'private')}>
              <SelectItem value="everyone">Everyone</SelectItem>
              <SelectItem value="followers">Followers only</SelectItem>
              <SelectItem value="private">Private</SelectItem>
            </Select>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex justify-end gap-3 pt-6 border-t border-border">
            <Button
              type="button"
              variant="outline"
              onClick={handleClose}
              disabled={isLoading}
            >
              Cancel
            </Button>
            <Button
              type="submit"
              disabled={isLoading}
              className="min-w-[100px]"
            >
              {isLoading ? (
                <div className="flex items-center gap-2">
                  <div className="w-4 h-4 border-2 border-current border-t-transparent rounded-full animate-spin" />
                  Saving...
                </div>
              ) : (
                'Save Changes'
              )}
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
};

'use client';

import React, { useState, useEffect } from 'react';
import Image from 'next/image';
import { Session } from '@/types';
import { X, XCircle, Image as ImageIcon } from 'lucide-react';
import { uploadImages } from '@/lib/imageUpload';
import { useAuth } from '@/hooks/useAuth';
import { useActivities } from '@/hooks/useActivitiesQuery';
import { parseLocalDateTime, cn } from '@/lib/utils';

interface EditSessionModalProps {
  session: Session;
  onClose: () => void;
  onSave: (
    sessionId: string,
    data: {
      title: string;
      description?: string;
      projectId?: string;
      tags?: string[];
      visibility?: 'everyone' | 'followers' | 'private';
      images?: string[];
      startTime?: Date;
      duration?: number;
    }
  ) => Promise<void>;
  isPage?: boolean;
}

export const EditSessionModal: React.FC<EditSessionModalProps> = ({
  session,
  onClose,
  onSave,
  isPage = false,
}) => {
  const { user } = useAuth();
  const { data: projects = [] } = useActivities(user?.id);

  const [title, setTitle] = useState(session.title || '');
  const [description, setDescription] = useState(session.description || '');
  const [selectedProjectId, setSelectedProjectId] = useState(
    session.projectId || ''
  );
  const [visibility, setVisibility] = useState<
    'everyone' | 'followers' | 'private'
  >(session.visibility || 'everyone');
  const [existingImages, setExistingImages] = useState<string[]>(
    session.images || []
  );
  const [selectedImages, setSelectedImages] = useState<File[]>([]);
  const [imagePreviewUrls, setImagePreviewUrls] = useState<string[]>([]);
  const [isUploading, setIsUploading] = useState(false);
  const [isSaving, setIsSaving] = useState(false);

  // Date/time state
  const [sessionDate, setSessionDate] = useState(() => {
    const date = new Date(session.startTime);
    return date.toISOString().split('T')[0];
  });
  const [startTime, setStartTime] = useState(() => {
    const date = new Date(session.startTime);
    const hours = String(date.getHours()).padStart(2, '0');
    const minutes = String(date.getMinutes()).padStart(2, '0');
    return `${hours}:${minutes}`;
  });
  const [durationHours, setDurationHours] = useState(() => {
    return String(Math.floor(session.duration / 3600));
  });
  const [durationMinutes, setDurationMinutes] = useState(() => {
    return String(Math.floor((session.duration % 3600) / 60));
  });

  // Update state when session prop changes (important for modal reuse)
  useEffect(() => {
    setTitle(session.title || '');
    setDescription(session.description || '');
    setSelectedProjectId(session.projectId || '');
    setVisibility(session.visibility || 'everyone');
    setExistingImages(session.images || []);
    setSelectedImages([]);
    setImagePreviewUrls([]);

    // Update date/time fields
    const date = new Date(session.startTime);
    setSessionDate(date.toISOString().split('T')[0]);
    const hours = String(date.getHours()).padStart(2, '0');
    const minutes = String(date.getMinutes()).padStart(2, '0');
    setStartTime(`${hours}:${minutes}`);
    setDurationHours(String(Math.floor(session.duration / 3600)));
    setDurationMinutes(String(Math.floor((session.duration % 3600) / 60)));
  }, [session.id]);

  // Handle ESC key to close modal
  useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape') {
        onClose();
      }
    };

    document.addEventListener('keydown', handleEscape);
    return () => {
      document.removeEventListener('keydown', handleEscape);
    };
  }, [onClose]);

  const handleImageSelect = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);

    const totalImages =
      existingImages.length + selectedImages.length + files.length;

    if (totalImages > 3) {
      alert('Maximum 3 images allowed');
      return;
    }

    const maxSize = 5 * 1024 * 1024; // 5MB
    const validFiles: File[] = [];
    const previewUrls: string[] = [];

    for (const file of files) {
      // Check if it's HEIC - we'll handle conversion during upload
      const isHeic =
        file.type === 'image/heic' ||
        file.type === 'image/heif' ||
        file.name.toLowerCase().endsWith('.heic') ||
        file.name.toLowerCase().endsWith('.heif');

      if (file.size > maxSize) {
        const sizeMB = (file.size / 1024 / 1024).toFixed(1);
        alert(
          `Image "${file.name}" is too large (${sizeMB}MB). Maximum size is 5MB.`
        );
        continue;
      }

      // Allow HEIC files as well as regular images
      if (!file.type.startsWith('image/') && !isHeic) {
        alert(`"${file.name}" is not an image file.`);
        continue;
      }

      const previewUrl = URL.createObjectURL(file);
      validFiles.push(file);
      previewUrls.push(previewUrl);
    }

    if (validFiles.length > 0) {
      setSelectedImages(prev => [...prev, ...validFiles]);
      setImagePreviewUrls(prev => [...prev, ...previewUrls]);
    }
  };

  const handleRemoveExistingImage = (index: number) => {
    setExistingImages(prev => prev.filter((_, i) => i !== index));
  };

  const handleRemoveNewImage = (index: number) => {
    setSelectedImages(prev => prev.filter((_, i) => i !== index));
    setImagePreviewUrls(prev => {
      const newUrls = prev.filter((_, i) => i !== index);
      const urlToRevoke = prev[index];
      if (urlToRevoke) {
        URL.revokeObjectURL(urlToRevoke);
      }
      return newUrls;
    });
  };

  const handleSave = async () => {
    if (!title.trim()) {
      alert('Please enter a title');
      return;
    }

    // Validate duration
    const hours = parseInt(durationHours) || 0;
    const minutes = parseInt(durationMinutes) || 0;
    const totalDuration = hours * 3600 + minutes * 60;

    if (totalDuration <= 0) {
      alert('Duration must be greater than 0');
      return;
    }

    setIsSaving(true);
    try {
      // Upload new images if any
      let newImageUrls: string[] = [];
      if (selectedImages.length > 0) {
        setIsUploading(true);
        try {
          const uploadResults = await uploadImages(selectedImages);
          newImageUrls = uploadResults.map(result => result.url);
        } catch (error) {
          console.error('Failed to upload images:', error);
          alert('Failed to upload images. Please try again.');
          setIsSaving(false);
          setIsUploading(false);
          return;
        } finally {
          setIsUploading(false);
        }
      }

      // Combine existing and new images
      const allImages = [...existingImages, ...newImageUrls];

      // Parse the new start time
      const newStartTime = parseLocalDateTime(
        sessionDate || '',
        startTime || ''
      );

      await onSave(session.id, {
        title,
        description: description || undefined,
        projectId: selectedProjectId || undefined,
        visibility,
        images: allImages.length > 0 ? allImages : undefined,
        startTime: newStartTime,
        duration: totalDuration,
      });

      onClose();
    } catch (error) {
      console.error('Failed to save session:', error);
      alert('Failed to save session. Please try again.');
    } finally {
      setIsSaving(false);
    }
  };

  // Clean up preview URLs on unmount
  useEffect(() => {
    return () => {
      imagePreviewUrls.forEach(url => URL.revokeObjectURL(url));
    };
  }, []);

  const totalImages = existingImages.length + selectedImages.length;

  const formContent = (
    <>
      {/* Header */}
      {!isPage && (
        <div className="sticky top-0 bg-white border-b border-gray-200 px-6 py-4 flex items-center justify-between">
          <h2 className="text-xl font-bold text-gray-900">Edit Session</h2>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
            aria-label="Close modal"
          >
            <X className="w-6 h-6" aria-hidden="true" />
          </button>
        </div>
      )}
      {isPage && (
        <div className="px-4 md:px-0 py-4">
          <h2 className="text-2xl font-bold text-gray-900">Edit Session</h2>
        </div>
      )}

      {/* Content */}
      <div className={cn('px-4 md:px-0 space-y-6')}>
        {/* Title */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Session Title *
          </label>
          <input
            type="text"
            value={title}
            onChange={e => setTitle(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#007AFF] focus:border-[#007AFF]"
            placeholder="Enter session title"
          />
        </div>

        {/* Description */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Description
          </label>
          <textarea
            value={description}
            onChange={e => setDescription(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#007AFF] focus:border-[#007AFF]"
            rows={3}
            placeholder="How did the session go? What did you accomplish?"
          />
        </div>

        {/* Date and Time Section */}
        <div>
          <h3 className="text-sm font-semibold text-gray-900 mb-3">
            Session Timing
          </h3>

          <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 sm:gap-4">
            {/* Date */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Date *
              </label>
              <input
                type="date"
                value={sessionDate}
                onChange={e => setSessionDate(e.target.value)}
                max={new Date().toISOString().split('T')[0]}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#007AFF]"
                required
              />
              <p className="text-xs text-gray-500 mt-1">Date of your session</p>
            </div>

            {/* Start Time */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Start Time
              </label>
              <input
                type="time"
                value={startTime}
                onChange={e => setStartTime(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#007AFF]"
              />
              <p className="text-xs text-gray-500 mt-1">When you started</p>
            </div>
          </div>

          {/* Duration */}
          <div className="mt-3 sm:mt-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Duration *
            </label>
            <div className="flex gap-2 sm:gap-3">
              <div className="flex-1">
                <input
                  type="number"
                  min="0"
                  max="23"
                  value={durationHours}
                  onChange={e => setDurationHours(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#007AFF]"
                  placeholder="0"
                />
                <p className="text-xs text-gray-500 mt-1">Hours</p>
              </div>
              <div className="flex-1">
                <input
                  type="number"
                  min="0"
                  max="59"
                  value={durationMinutes}
                  onChange={e => setDurationMinutes(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#007AFF]"
                  placeholder="0"
                />
                <p className="text-xs text-gray-500 mt-1">Minutes</p>
              </div>
            </div>
          </div>
        </div>

        {/* Images */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Images (Optional, max 3)
            {/* Debug info */}
            <span className="ml-2 text-xs text-gray-400">
              ({existingImages.length} existing, {selectedImages.length} new)
            </span>
          </label>
          <div className="space-y-3">
            {/* Existing & New Image Previews */}
            {(existingImages.length > 0 || imagePreviewUrls.length > 0) && (
              <div className="grid grid-cols-3 gap-2">
                {/* Existing images */}
                {existingImages.map((url, index) => (
                  <div
                    key={`existing-${index}`}
                    className="relative aspect-square rounded-lg overflow-hidden bg-gray-100"
                  >
                    <Image
                      src={url}
                      alt={`Image ${index + 1}`}
                      width={300}
                      height={300}
                      quality={90}
                      className="w-full h-full object-cover"
                    />
                    <button
                      type="button"
                      onClick={() => handleRemoveExistingImage(index)}
                      className="absolute top-2 right-2 w-6 h-6 flex items-center justify-center bg-red-500 text-white rounded-full hover:bg-red-600 transition-colors shadow-md"
                      aria-label="Remove image"
                    >
                      <X className="w-4 h-4 flex-shrink-0" aria-hidden="true" />
                    </button>
                  </div>
                ))}
                {/* New images */}
                {imagePreviewUrls.map((url, index) => (
                  <div
                    key={`new-${index}`}
                    className="relative aspect-square rounded-lg overflow-hidden bg-gray-100"
                  >
                    <Image
                      src={url}
                      alt={`Preview ${index + 1}`}
                      width={300}
                      height={300}
                      quality={90}
                      className="w-full h-full object-cover"
                      unoptimized
                    />
                    <button
                      type="button"
                      onClick={() => handleRemoveNewImage(index)}
                      className="absolute top-2 right-2 w-6 h-6 flex items-center justify-center bg-red-500 text-white rounded-full hover:bg-red-600 transition-colors shadow-md"
                      aria-label="Remove image"
                    >
                      <X className="w-4 h-4 flex-shrink-0" aria-hidden="true" />
                    </button>
                  </div>
                ))}
              </div>
            )}

            {/* Upload Button */}
            {totalImages < 3 && (
              <label className="flex flex-col items-center justify-center gap-2 px-4 py-8 border-2 border-dashed border-gray-300 rounded-lg cursor-pointer hover:border-[#007AFF] hover:bg-gray-50 transition-colors min-h-[120px]">
                <ImageIcon className="w-8 h-8 text-gray-400" />
                <span className="text-sm font-medium text-gray-600">
                  {totalImages === 0
                    ? 'Add images'
                    : `Add ${3 - totalImages} more`}
                </span>
                <span className="text-xs text-gray-400">
                  JPG, PNG, HEIC (max 5MB each)
                </span>
                <input
                  type="file"
                  accept="image/*,.heic,.heif"
                  multiple
                  onChange={handleImageSelect}
                  className="hidden"
                />
              </label>
            )}
          </div>
        </div>

        {/* Project Selection */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Project
          </label>
          <select
            value={selectedProjectId}
            onChange={e => setSelectedProjectId(e.target.value)}
            className="w-full pl-3 pr-8 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#007AFF] focus:border-[#007AFF] bg-white text-sm appearance-none"
          >
            <option value="">Unassigned</option>
            {projects?.map(project => (
              <option key={project.id} value={project.id}>
                {project.name}
              </option>
            ))}
          </select>
        </div>

        {/* Visibility */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Visibility
          </label>
          <select
            value={visibility}
            onChange={e => setVisibility(e.target.value as any)}
            className="w-full pl-3 pr-8 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#007AFF] focus:border-[#007AFF] bg-white appearance-none"
          >
            <option value="everyone">Everyone</option>
            <option value="followers">Followers</option>
            <option value="private">Only You</option>
          </select>
        </div>
      </div>

      {/* Footer */}
      <div
        className={cn(
          'flex gap-3',
          isPage
            ? 'px-4 md:px-0 py-6'
            : 'sticky bottom-0 bg-white border-t border-gray-200 px-6 py-4'
        )}
      >
        <button
          onClick={onClose}
          disabled={isSaving || isUploading}
          className="flex-1 px-4 py-2 text-gray-700 bg-gray-200 rounded-lg hover:bg-gray-300 transition-colors disabled:opacity-50"
        >
          Cancel
        </button>
        <button
          onClick={handleSave}
          disabled={!title.trim() || isSaving || isUploading}
          className="flex-1 px-4 py-2 bg-[#007AFF] text-white rounded-lg hover:bg-[#0056D6] transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
        >
          {isUploading
            ? 'Uploading Images...'
            : isSaving
              ? 'Saving...'
              : 'Save Changes'}
        </button>
      </div>
    </>
  );

  if (isPage) {
    return <>{formContent}</>;
  }

  return (
    <div className="fixed inset-0 bg-black/50 z-[9999] flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        {formContent}
      </div>
    </div>
  );
};

'use client';

import React, { useState } from 'react';
import Link from 'next/link';
import { ManualEntry } from './ManualEntry';
import { firebaseApi } from '@/lib/api';

export const FABMenu: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [showManualEntry, setShowManualEntry] = useState(false);
  const [isLoading, setIsLoading] = useState(false);


  const handleManualEntry = () => {
    setShowManualEntry(true);
    setIsOpen(false);
  };

  const handleSaveSession = async (data: any) => {
    try {
      setIsLoading(true);

      // Create session and post if visibility allows
      if (data.visibility !== 'private') {
        // Create session with post for non-private sessions
        const { session, post } = await firebaseApi.session.createSessionWithPost(
          data,
          data.description || `Completed ${data.title}`,
          data.visibility
        );
      } else {
        // Create private session only
        const session = await firebaseApi.session.createSession(data);
      }

      setShowManualEntry(false);
    } catch (error) {
      console.error('Failed to save session:', error);
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <>
      {/* Floating Action Button Menu */}
      <div className="fixed bottom-6 right-6 z-40">
        {/* Menu Items */}
        <div className={`absolute bottom-16 right-0 mb-2 space-y-2 transition-all duration-200 ${
          isOpen ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-2 pointer-events-none'
        }`}>
          {/* Manual Entry */}
          <button
            onClick={handleManualEntry}
            className="flex items-center space-x-3 bg-white text-gray-700 px-4 py-3 rounded-lg shadow-lg hover:bg-gray-50 transition-colors"
            aria-label="Create manual session entry"
          >
            <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
              <svg className="w-5 h-5 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24" aria-hidden="true">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
              </svg>
            </div>
            <span className="font-medium">Manual Entry</span>
          </button>

          {/* Session History */}
          <Link
            href="/sessions"
            className="flex items-center space-x-3 bg-white text-gray-700 px-4 py-3 rounded-lg shadow-lg hover:bg-gray-50 transition-colors"
            aria-label="View session history"
          >
            <div className="w-10 h-10 bg-green-100 rounded-full flex items-center justify-center">
              <svg className="w-5 h-5 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24" aria-hidden="true">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <span className="font-medium">Session History</span>
          </Link>

          {/* Timer */}
          <Link
            href="/timer"
            className="flex items-center space-x-3 bg-white text-gray-700 px-4 py-3 rounded-lg shadow-lg hover:bg-gray-50 transition-colors"
            aria-label="Start new session timer"
          >
            <div className="w-10 h-10 bg-purple-100 rounded-full flex items-center justify-center">
              <svg className="w-5 h-5 text-purple-600" fill="none" stroke="currentColor" viewBox="0 0 24 24" aria-hidden="true">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <span className="font-medium">Start Timer</span>
          </Link>
        </div>

        {/* Main FAB Button */}
        <button
          onClick={() => setIsOpen(!isOpen)}
          className={`w-14 h-14 bg-blue-600 text-white rounded-full shadow-lg hover:bg-blue-700 transition-all duration-200 flex items-center justify-center ${
            isOpen ? 'rotate-45' : ''
          }`}
          aria-label={isOpen ? 'Close quick actions menu' : 'Open quick actions menu'}
          aria-expanded={isOpen}
          aria-haspopup="true"
        >
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" aria-hidden="true">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
          </svg>
        </button>
      </div>

      {/* Manual Entry Modal */}
      {showManualEntry && (
        <ManualEntry
          onSave={handleSaveSession}
          onCancel={() => setShowManualEntry(false)}
          isLoading={isLoading}
        />
      )}
    </>
  );
};

'use client';

import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useRouter, useSearchParams } from 'next/navigation';
import { SessionWithDetails, FeedFilters } from '@/types';
import { firebaseApi } from '@/lib/api';
import SessionCard from './SessionCard';
import { useFeedInfinite, FeedResult } from '@/features/feed/hooks';
import { useSupportSession, useDeleteSession } from '@/features/sessions/hooks';
import { useAuth } from '@/hooks/useAuth';
import ConfirmDialog from './ConfirmDialog';
import { useQueryClient } from '@tanstack/react-query';
import { AlertTriangle, Users, Search, ChevronUp } from 'lucide-react';

// Session Card Skeleton Component
const SessionCardSkeleton: React.FC = () => (
  <div className="bg-white border-b md:border md:border-gray-200 md:rounded-lg p-4 animate-pulse">
    {/* Header - User Info */}
    <div className="flex items-center space-x-3 mb-4">
      <div className="w-10 h-10 bg-gray-200 rounded-full flex-shrink-0"></div>
      <div className="flex-1 space-y-2">
        <div className="h-4 bg-gray-200 rounded w-32"></div>
        <div className="h-3 bg-gray-200 rounded w-24"></div>
      </div>
    </div>

    {/* Session Title */}
    <div className="mb-3">
      <div className="h-5 bg-gray-200 rounded w-3/4 mb-2"></div>
    </div>

    {/* Description */}
    <div className="space-y-2 mb-4">
      <div className="h-4 bg-gray-200 rounded w-full"></div>
      <div className="h-4 bg-gray-200 rounded w-5/6"></div>
    </div>

    {/* Stats Bar */}
    <div className="flex items-center gap-4 py-3 mb-3 border-t border-b border-gray-100">
      <div className="h-4 bg-gray-200 rounded w-20"></div>
      <div className="h-4 bg-gray-200 rounded w-24"></div>
      <div className="h-4 bg-gray-200 rounded w-16"></div>
    </div>

    {/* Action Buttons */}
    <div className="flex items-center justify-between">
      <div className="flex gap-4">
        <div className="h-8 bg-gray-200 rounded w-16"></div>
        <div className="h-8 bg-gray-200 rounded w-20"></div>
      </div>
      <div className="h-8 bg-gray-200 rounded w-12"></div>
    </div>
  </div>
);

interface FeedProps {
  filters?: FeedFilters;
  className?: string;
  initialLimit?: number;
  showEndMessage?: boolean;
  showGroupInfo?: boolean;
}

export const Feed: React.FC<FeedProps> = ({
  filters = {},
  className = '',
  initialLimit = 10,
  showEndMessage = true,
  showGroupInfo = false,
}) => {
  const router = useRouter();
  const searchParams = useSearchParams();
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [hasNewSessions, setHasNewSessions] = useState(false);
  const [newSessionsCount, setNewSessionsCount] = useState(0);
  const [deleteConfirmSession, setDeleteConfirmSession] = useState<
    string | null
  >(null);
  const [isDeleting, setIsDeleting] = useState(false);

  // Ref for infinite scroll trigger element
  const loadMoreTriggerRef = React.useRef<HTMLDivElement>(null);

  // Use new infinite scroll hook
  const {
    data,
    isLoading,
    error,
    fetchNextPage,
    hasNextPage,
    isFetchingNextPage,
    refetch,
  } = useFeedInfinite(user?.id || '', filters);

  // Flatten pages into allSessions
  // Note: Casting to SessionWithDetails[] for compatibility during architecture migration
  // The data structure comes from useFeedInfinite which returns InfiniteData<FeedResult>
  // where each page is a FeedResult with sessions: Session[] property
  const allSessions = useMemo(() => {
    if (!data?.pages) return [];
    // TypeScript can't infer the page type from useInfiniteQuery, so we cast
    return (data.pages as FeedResult[]).flatMap(page => {
      return page.sessions || [];
    }) as unknown as SessionWithDetails[];
  }, [data]);

  const hasMore = hasNextPage || false;
  const isLoadingMore = isFetchingNextPage;

  // New mutations using feature hooks
  const supportMutation = useSupportSession(user?.id);
  const deleteSessionMutation = useDeleteSession();

  // Refresh sessions and invalidate cache
  const refreshSessions = useCallback(() => {
    setHasNewSessions(false);
    setNewSessionsCount(0);
    // Invalidate all feed caches to force refetch
    queryClient.invalidateQueries({ queryKey: ['feed'] });
    refetch();
  }, [refetch, queryClient]);

  // Auto-refresh feed when coming from session creation
  useEffect(() => {
    const shouldRefresh = searchParams?.get('refresh');
    if (shouldRefresh === 'true') {
      // Clear the URL parameter
      router.replace('/', { scroll: false });
      // Trigger immediate refresh
      refreshSessions();
    }
  }, [searchParams, router, refreshSessions]);

  // Load more sessions - now uses React Query infinite scroll
  const loadMore = useCallback(() => {
    if (!isLoadingMore && hasMore) {
      fetchNextPage();
    }
  }, [isLoadingMore, hasMore, fetchNextPage]);

  // Check for new sessions periodically - only when page is visible
  useEffect(() => {
    if (allSessions.length === 0) return;

    // Track if page is currently visible
    let isPageVisible = !document.hidden;

    const checkForNewSessions = async () => {
      // Skip check if page is not visible
      if (!isPageVisible) return;

      try {
        // Use queryClient to check cache first, then fetch if stale
        const cachedData = queryClient.getQueryData([
          'feed',
          'sessions',
          5,
          undefined,
          filters,
        ]);

        let response;
        if (cachedData) {
          response = cachedData as { sessions: any[] };
        } else {
          response = await firebaseApi.post.getFeedSessions(
            5,
            undefined,
            filters
          );
        }

        const newSessionIds = response.sessions.map(s => s.id);
        const currentSessionIds = allSessions.slice(0, 5).map(s => s.id);

        const newCount = newSessionIds.filter(
          id => !currentSessionIds.includes(id)
        ).length;
        if (newCount > 0) {
          setHasNewSessions(true);
          setNewSessionsCount(newCount);
        }
      } catch (err) {
        // Silently fail
      }
    };

    // Update visibility state when page visibility changes
    const handleVisibilityChange = () => {
      isPageVisible = !document.hidden;
      // Check immediately when page becomes visible
      if (isPageVisible) {
        checkForNewSessions();
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);

    // Check every 2 minutes when visible (reduced frequency saves resources)
    const interval = setInterval(checkForNewSessions, 120000);

    return () => {
      clearInterval(interval);
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [allSessions, filters, queryClient]);

  // Handle support with optimistic updates via React Query
  const handleSupport = useCallback(
    async (sessionId: string) => {
      supportMutation.mutate({ sessionId, action: 'support' });
    },
    [supportMutation]
  );

  // Handle remove support with optimistic updates via React Query
  const handleRemoveSupport = useCallback(
    async (sessionId: string) => {
      supportMutation.mutate({ sessionId, action: 'unsupport' });
    },
    [supportMutation]
  );

  // Handle share
  const handleShare = useCallback(async (sessionId: string) => {
    try {
      const sessionUrl = `${window.location.origin}/sessions/${sessionId}`;

      if (navigator.share) {
        // Use native share API on mobile
        await navigator.share({
          title: 'Check out this session on Ambira',
          text: 'Look at this productive session!',
          url: sessionUrl,
        });
      } else {
        // Fallback to clipboard
        await navigator.clipboard.writeText(sessionUrl);
        // Could show success toast here
      }
    } catch (err: any) {
      // Silently ignore if user cancels the share dialog
      if (err.name === 'AbortError') {
        return;
      }
      // Could show error toast here
    }
  }, []);

  // Handle delete
  const handleDelete = useCallback(async (sessionId: string) => {
    setDeleteConfirmSession(sessionId);
  }, []);

  const confirmDelete = useCallback(async () => {
    if (!deleteConfirmSession) return;

    try {
      setIsDeleting(true);
      await deleteSessionMutation.mutateAsync(deleteConfirmSession);
      setDeleteConfirmSession(null);
    } catch (err: any) {
      // Could show error toast here
    } finally {
      setIsDeleting(false);
    }
  }, [deleteConfirmSession, deleteSessionMutation]);

  // Memoize top 10 session IDs string to create stable dependency
  // This prevents useEffect from re-running when sessions array changes but top 10 IDs remain same
  const top10SessionIdsString = useMemo(() => {
    const MAX_LISTENERS = 10;
    return allSessions
      .slice(0, MAX_LISTENERS)
      .map(session => session.id)
      .join(',');
  }, [allSessions]);

  // Real-time updates for support counts (throttled to reduce reads)
  // Only listen to the first 10 sessions to reduce overhead
  // Note: React Query handles optimistic updates through mutations
  // This listener is kept for reference but real-time updates happen via queryClient updates
  useEffect(() => {
    if (allSessions.length === 0 || !top10SessionIdsString) return;

    // Parse session IDs from memoized string
    const sessionIds = top10SessionIdsString.split(',').filter(Boolean);
    if (sessionIds.length === 0) return;

    // Register listener but don't update state directly
    // React Query mutations handle support count updates via cache invalidation
    const unsubscribe = firebaseApi.post.listenToSessionUpdates(
      sessionIds,
      updates => {
        // Updates are handled by React Query's optimistic updates in supportMutation
        // This listener can be used for real-time notifications in the future
      }
    );

    return unsubscribe;
  }, [top10SessionIdsString]); // Only re-run when top 10 IDs change

  // Infinite scroll using IntersectionObserver
  useEffect(() => {
    const trigger = loadMoreTriggerRef.current;
    if (!trigger) return;

    const observer = new IntersectionObserver(
      entries => {
        const entry = entries[0];
        if (!entry) return;

        if (entry.isIntersecting && hasMore && !isLoadingMore) {
          loadMore();
        }
      },
      {
        root: null, // viewport
        rootMargin: '200px', // Trigger 200px before reaching the element
        threshold: 0,
      }
    );

    observer.observe(trigger);

    return () => {
      if (trigger) {
        observer.unobserve(trigger);
      }
    };
  }, [loadMore, hasMore, isLoadingMore]);

  if (isLoading) {
    return (
      <div className={`space-y-4 ${className}`}>
        {[...Array(3)].map((_, i) => (
          <SessionCardSkeleton key={i} />
        ))}
      </div>
    );
  }

  if (error) {
    const errorMessage = String(error);
    const isPermissionError =
      errorMessage.includes('permission') ||
      errorMessage.includes('insufficient');

    return (
      <div
        className={`text-center py-8 px-4 ${className}`}
        role="alert"
        aria-live="polite"
      >
        <div className="text-red-600 mb-4">
          <AlertTriangle
            className="w-12 h-12 mx-auto mb-2"
            aria-hidden="true"
          />
          <p className="font-medium text-sm sm:text-base">
            Failed to load sessions
          </p>
          <p className="text-sm text-gray-600 mt-1">
            {isPermissionError
              ? 'There was a permissions issue loading the feed. This may be due to security rules that need updating.'
              : errorMessage}
          </p>
          {isPermissionError && (
            <p className="text-xs text-gray-500 mt-2">
              If you're the app administrator, try deploying the latest
              Firestore security rules.
            </p>
          )}
        </div>
        <button
          onClick={refreshSessions}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors duration-200"
        >
          Try Again
        </button>
      </div>
    );
  }

  if (allSessions.length === 0) {
    // Determine empty state content based on feed type
    const feedType = filters?.type || 'all';

    let emptyStateContent = {
      title: 'Your feed is empty',
      message:
        'Follow people to see their productive sessions in your feed and get inspired by their work!',
      buttonText: 'Find People to Follow',
      buttonAction: () => router.push('/discover/people'),
    };

    if (feedType === 'group-members-unfollowed') {
      emptyStateContent = {
        title: 'No sessions yet',
        message:
          "When group members you don't follow post sessions, they'll appear here!",
        buttonText: 'Discover Groups',
        buttonAction: () => router.push('/groups'),
      };
    } else if (feedType === 'user') {
      emptyStateContent = {
        title: 'No sessions yet',
        message:
          'Start tracking your productive sessions to build your profile!',
        buttonText: 'Start a Session',
        buttonAction: () => router.push('/timer'),
      };
    } else if (feedType === 'following') {
      emptyStateContent = {
        title: 'Your feed is empty',
        message:
          'Follow people to see their productive sessions in your feed and get inspired by their work!',
        buttonText: 'Find People to Follow',
        buttonAction: () => router.push('/discover/people'),
      };
    }

    return (
      <div className={`text-center py-12 px-4 ${className}`}>
        <div className="max-w-md mx-auto">
          <div className="text-gray-500 mb-8">
            <Users className="w-20 h-20 mx-auto mb-4 text-gray-400" />
            <h3 className="font-bold text-xl text-gray-900 mb-2">
              {emptyStateContent.title}
            </h3>
            <p className="text-base text-gray-600 leading-relaxed">
              {emptyStateContent.message}
            </p>
          </div>

          {/* Action Button */}
          <button
            onClick={emptyStateContent.buttonAction}
            className="inline-flex items-center justify-center gap-2 px-6 py-3 bg-[#007AFF] text-white rounded-lg hover:bg-[#0051D5] transition-colors duration-200 font-semibold text-base shadow-md hover:shadow-lg"
          >
            <Search className="w-5 h-5" />
            {emptyStateContent.buttonText}
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className={className}>
      {/* New sessions indicator */}
      {hasNewSessions && (
        <div className="mb-4 sticky top-0 z-10">
          <button
            onClick={refreshSessions}
            className="w-full py-3 px-3 sm:px-4 bg-gradient-to-r from-[#007AFF] to-[#0051D5] text-white rounded-lg shadow-lg hover:shadow-xl transition-colors duration-200 flex items-center justify-center gap-2 font-medium text-sm sm:text-base"
            aria-label={`${newSessionsCount} new sessions available, click to refresh`}
          >
            <ChevronUp className="w-5 h-5 flex-shrink-0" aria-hidden="true" />
            <span className="truncate">
              {newSessionsCount} new{' '}
              {newSessionsCount === 1 ? 'session' : 'sessions'} - Click to
              refresh
            </span>
          </button>
        </div>
      )}

      {/* Sessions */}
      <div className="space-y-0 md:space-y-0">
        {allSessions.map((session, index) => {
          const isOwnSession = user && session.userId === user.id;
          // First 2 sessions are above the fold on most screens
          const isAboveFold = index < 2;
          return (
            <SessionCard
              key={session.id}
              session={session}
              onSupport={handleSupport}
              onRemoveSupport={handleRemoveSupport}
              onShare={handleShare}
              onEdit={
                isOwnSession
                  ? sessionId => router.push(`/sessions/${sessionId}/edit`)
                  : undefined
              }
              onDelete={isOwnSession ? handleDelete : undefined}
              showGroupInfo={showGroupInfo}
              isAboveFold={isAboveFold}
              priority={isAboveFold}
            />
          );
        })}
      </div>

      {/* Infinite scroll trigger element */}
      {hasMore && !isLoadingMore && (
        <div ref={loadMoreTriggerRef} className="h-4" aria-hidden="true" />
      )}

      {/* Load more indicator */}
      {isLoadingMore && (
        <div className="text-center py-4" role="status" aria-live="polite">
          <div className="inline-flex items-center space-x-2 text-gray-600">
            <div
              className="w-4 h-4 border-2 border-gray-300 border-t-blue-600 rounded-full animate-spin"
              aria-hidden="true"
            ></div>
            <span className="text-sm">Loading more sessions...</span>
          </div>
        </div>
      )}

      {/* Delete Confirmation Dialog */}
      <ConfirmDialog
        isOpen={deleteConfirmSession !== null}
        onClose={() => setDeleteConfirmSession(null)}
        onConfirm={confirmDelete}
        title="Delete Session"
        message="Are you sure you want to delete this session? This action cannot be undone and all associated data will be permanently removed."
        confirmText="Delete Session"
        cancelText="Cancel"
        variant="danger"
        isLoading={isDeleting}
      />

      {/* End of feed */}
      {showEndMessage && !hasMore && allSessions.length > 0 && (
        <div className="text-center py-4 text-gray-500 text-sm">
          You've reached the end of the feed
        </div>
      )}
    </div>
  );
};

export default Feed;

'use client';

import React, { useState } from 'react';
import { useAuth } from '@/hooks/useAuth';
import { firebaseApi } from '@/lib/api';
import { UserStats, SuggestedUser, FeedFilters } from '@/types';
import Feed from './Feed';
import SuggestedUsers from './SuggestedUsers';

type FeedType = 'recent' | 'following' | 'trending';

interface FeedLayoutProps {
  className?: string;
}

export const FeedLayout: React.FC<FeedLayoutProps> = ({ className = '' }) => {
  const { user } = useAuth();
  const [userStats, setUserStats] = useState<UserStats | null>(null);
  const [suggestedUsers, setSuggestedUsers] = useState<SuggestedUser[]>([]);
  const [isLoadingStats, setIsLoadingStats] = useState(true);
  const [feedType, setFeedType] = useState<FeedType>('recent');
  const [filters, setFilters] = useState<FeedFilters>({ type: 'recent' });

  const handleFeedTypeChange = (type: FeedType) => {
    setFeedType(type);
    setFilters({ type });
  };

  // Load user stats and suggestions
  React.useEffect(() => {
    const loadData = async () => {
      if (!user) return;

      try {
        const [stats, suggestions] = await Promise.all([
          firebaseApi.user.getUserStats(user.id),
          firebaseApi.user.getSuggestedUsers(5)
        ]);

        setUserStats(stats);
        setSuggestedUsers(suggestions);
      } catch (error) {
        console.error('Failed to load feed data:', error);
      } finally {
        setIsLoadingStats(false);
      }
    };

    loadData();
  }, [user]);

  const formatTime = (seconds: number): string => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);

    if (hours > 0) {
      return `${hours}h ${minutes}m`;
    }
    return `${minutes}m`;
  };

  return (
    <div className={`max-w-7xl mx-auto px-0 md:px-4 sm:px-6 lg:px-8 py-0 md:py-6 ${className}`}>
      <div className="grid grid-cols-1 lg:grid-cols-12 gap-0 md:gap-6">
        {/* Left Sidebar - Personal Stats */}
        <div className="lg:col-span-3 order-2 lg:order-1 hidden lg:block">
          <div className="sticky top-6 space-y-6">
            {/* Personal Stats Widget */}
            {user && (
              <div className="bg-white rounded-lg border border-gray-200 p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">
                  Your Stats
                </h3>

                {isLoadingStats ? (
                  <div className="space-y-3">
                    {[...Array(4)].map((_, i) => (
                      <div key={i} className="h-4 bg-gray-200 rounded animate-pulse"></div>
                    ))}
                  </div>
                ) : userStats ? (
                  <div className="space-y-4">
                    <div className="flex justify-between items-center">
                      <span className="text-sm text-gray-600">Total Hours</span>
                      <span className="font-semibold text-gray-900">
                        {userStats.totalHours.toFixed(1)}h
                      </span>
                    </div>

                    <div className="flex justify-between items-center">
                      <span className="text-sm text-gray-600">This Week</span>
                      <span className="font-semibold text-gray-900">
                        {userStats.weeklyHours.toFixed(1)}h
                      </span>
                    </div>

                    <div className="flex justify-between items-center">
                      <span className="text-sm text-gray-600">Current Streak</span>
                      <span className="font-semibold text-gray-900">
                        {userStats.currentStreak} days
                      </span>
                    </div>

                    <div className="flex justify-between items-center">
                      <span className="text-sm text-gray-600">Sessions This Week</span>
                      <span className="font-semibold text-gray-900">
                        {userStats.sessionsThisWeek}
                      </span>
                    </div>

                    {userStats.favoriteProject && (
                      <div className="pt-3 border-t border-gray-200">
                        <span className="text-sm text-gray-600 block mb-1">
                          Favorite Project
                        </span>
                        <span className="font-semibold text-gray-900">
                          {userStats.favoriteProject.name}
                        </span>
                        <span className="text-sm text-gray-600 ml-2">
                          ({formatTime(userStats.favoriteProject.hours * 3600)})
                        </span>
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="text-center text-gray-500 text-sm">
                    No stats available
                  </div>
                )}
              </div>
            )}

            {/* Quick Actions */}
            <div className="bg-white rounded-lg border border-gray-200 p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">
                Quick Actions
              </h3>

              <div className="space-y-3">
                <button className="w-full text-left px-4 py-3 bg-blue-50 hover:bg-blue-100 rounded-lg transition-colors">
                  <div className="flex items-center space-x-3">
                    <div className="w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center">
                      <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                      </svg>
                    </div>
                    <div>
                      <div className="font-medium text-gray-900">Start Timer</div>
                      <div className="text-sm text-gray-600">Begin a new session</div>
                    </div>
                  </div>
                </button>

                <button className="w-full text-left px-4 py-3 bg-green-50 hover:bg-green-100 rounded-lg transition-colors">
                  <div className="flex items-center space-x-3">
                    <div className="w-8 h-8 bg-green-500 rounded-full flex items-center justify-center">
                      <svg className="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2m-6 9l2 2 4-4" />
                      </svg>
                    </div>
                    <div>
                      <div className="font-medium text-gray-900">Add Task</div>
                      <div className="text-sm text-gray-600">Create a new task</div>
                    </div>
                  </div>
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Center - Feed */}
        <div className="lg:col-span-6 order-1 lg:order-2 w-full">
          {/* Feed Type Tabs */}
          <div className="bg-white md:rounded-lg md:border border-gray-200 mb-0 md:mb-4">
            <div className="flex gap-0 border-b border-gray-200">
              <button
                onClick={() => setFeedType('recent')}
                className={`flex-1 px-6 py-4 font-medium transition-all ${
                  feedType === 'recent'
                    ? 'text-orange-600 border-b-2 border-orange-600 bg-orange-50'
                    : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                }`}
              >
                Recent
              </button>
              <button
                onClick={() => setFeedType('following')}
                className={`flex-1 px-6 py-4 font-medium transition-all ${
                  feedType === 'following'
                    ? 'text-orange-600 border-b-2 border-orange-600 bg-orange-50'
                    : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                }`}
              >
                Following
              </button>
              <button
                onClick={() => setFeedType('trending')}
                className={`flex-1 px-6 py-4 font-medium transition-all ${
                  feedType === 'trending'
                    ? 'text-orange-600 border-b-2 border-orange-600 bg-orange-50'
                    : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                }`}
              >
                üî• Trending
              </button>
            </div>
          </div>

          <Feed filters={filters} key={feedType} />
        </div>

        {/* Right Sidebar - Suggestions */}
        <div className="lg:col-span-3 order-3 hidden lg:block">
          <div className="sticky top-6 space-y-6">
            {/* Suggested Users */}
            <SuggestedUsers />

            {/* Feed Tips */}
            <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-lg border border-blue-200 p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-2">
                üí° Feed Tips
              </h3>
              <p className="text-sm text-gray-700 mb-3">
                Share your productive sessions to inspire others and build your network!
              </p>
              <ul className="text-sm text-gray-600 space-y-1">
                <li>‚Ä¢ Use meaningful descriptions</li>
                <li>‚Ä¢ Tag relevant skills</li>
                <li>‚Ä¢ Rate how you felt</li>
                <li>‚Ä¢ Support others' work</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FeedLayout;

'use client';

import React, { useState, useEffect } from 'react';
import { GroupStats } from '@/types';
import { BarChart3, TrendingUp, Users, Clock, Calendar } from 'lucide-react';
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  Area,
  AreaChart,
} from 'recharts';

interface GroupAnalyticsProps {
  groupId: string;
  stats: GroupStats | null | undefined;
}

export default function GroupAnalytics({
  groupId,
  stats,
}: GroupAnalyticsProps) {
  const [timeRange, setTimeRange] = useState<'week' | 'month' | 'year'>(
    'month'
  );
  const [analyticsData, setAnalyticsData] = useState<{
    hoursData: Array<{ date: string; hours: number; members: number }>;
    membershipGrowth: Array<{ date: string; members: number }>;
  } | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    loadAnalytics();
  }, [groupId, timeRange]);

  const loadAnalytics = async () => {
    try {
      setIsLoading(true);
      const { firebaseApi } = await import('@/lib/api');
      const data = await firebaseApi.group.getGroupAnalytics(
        groupId,
        timeRange
      );
      setAnalyticsData(data);
    } catch (error) {
      console.error('Failed to load analytics:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const hoursData = analyticsData?.hoursData || [];
  const membershipGrowth = analyticsData?.membershipGrowth || [];

  const projectDistribution =
    stats?.topProjects.slice(0, 5).map(project => ({
      name:
        project.projectName.length > 15
          ? project.projectName.substring(0, 15) + '...'
          : project.projectName,
      hours: project.hours,
      members: project.memberCount,
    })) || [];

  return (
    <div className="space-y-6 pb-20">
      {/* Time Range Selector */}
      <div className="bg-white rounded-xl p-4 border border-gray-200">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-gray-900">
            Activity Over Time
          </h3>
          <div className="flex gap-2">
            {(['week', 'month', 'year'] as const).map(range => (
              <button
                key={range}
                onClick={() => setTimeRange(range)}
                className={`px-3 py-1.5 text-sm font-medium rounded-lg transition-colors ${
                  timeRange === range
                    ? 'bg-[#007AFF] text-white'
                    : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                }`}
              >
                {range.charAt(0).toUpperCase() + range.slice(1)}
              </button>
            ))}
          </div>
        </div>

        {/* Hours Chart */}
        <div className="h-64">
          <ResponsiveContainer width="100%" height="100%">
            <AreaChart data={hoursData}>
              <defs>
                <linearGradient id="colorHours" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#007AFF" stopOpacity={0.3} />
                  <stop offset="95%" stopColor="#007AFF" stopOpacity={0} />
                </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
              <XAxis
                dataKey="date"
                stroke="#9ca3af"
                style={{ fontSize: '12px' }}
              />
              <YAxis stroke="#9ca3af" style={{ fontSize: '12px' }} />
              <Tooltip
                contentStyle={{
                  backgroundColor: '#fff',
                  border: '1px solid #e5e7eb',
                  borderRadius: '8px',
                  fontSize: '12px',
                }}
              />
              <Area
                type="monotone"
                dataKey="hours"
                stroke="#007AFF"
                strokeWidth={2}
                fillOpacity={1}
                fill="url(#colorHours)"
                name="Hours"
              />
            </AreaChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Membership Growth */}
      <div className="bg-white rounded-xl p-4 border border-gray-200">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">
          Membership Growth
        </h3>
        <div className="h-64">
          <ResponsiveContainer width="100%" height="100%">
            <LineChart data={membershipGrowth}>
              <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
              <XAxis
                dataKey="date"
                stroke="#9ca3af"
                style={{ fontSize: '12px' }}
              />
              <YAxis
                stroke="#9ca3af"
                style={{ fontSize: '12px' }}
                allowDecimals={false}
              />
              <Tooltip
                contentStyle={{
                  backgroundColor: '#fff',
                  border: '1px solid #e5e7eb',
                  borderRadius: '8px',
                  fontSize: '12px',
                }}
              />
              <Line
                type="monotone"
                dataKey="members"
                stroke="#10b981"
                strokeWidth={3}
                dot={{ fill: '#10b981', r: 4 }}
                activeDot={{ r: 6 }}
                name="Members"
              />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Top Projects */}
      {projectDistribution.length > 0 && (
        <div className="bg-white rounded-xl p-4 border border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">
            Top Projects by Hours
          </h3>
          <div className="h-64">
            <ResponsiveContainer width="100%" height="100%">
              <BarChart data={projectDistribution}>
                <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                <XAxis
                  dataKey="name"
                  stroke="#9ca3af"
                  style={{ fontSize: '11px' }}
                  angle={-45}
                  textAnchor="end"
                  height={80}
                />
                <YAxis stroke="#9ca3af" style={{ fontSize: '12px' }} />
                <Tooltip
                  contentStyle={{
                    backgroundColor: '#fff',
                    border: '1px solid #e5e7eb',
                    borderRadius: '8px',
                    fontSize: '12px',
                  }}
                />
                <Bar
                  dataKey="hours"
                  fill="#8b5cf6"
                  radius={[8, 8, 0, 0]}
                  name="Hours"
                />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>
      )}

      {/* Insights */}
      <div className="bg-gradient-to-br from-blue-50 to-indigo-50 rounded-xl p-6 border border-blue-100">
        <div className="flex items-start gap-3">
          <div className="p-2 bg-blue-500 rounded-lg">
            <TrendingUp className="w-5 h-5 text-white" />
          </div>
          <div>
            <h4 className="font-semibold text-gray-900 mb-1">Group Insights</h4>
            <ul className="space-y-2 text-sm text-gray-700">
              <li className="flex items-start gap-2">
                <span className="text-blue-500 mt-0.5">‚Ä¢</span>
                <span>
                  Average{' '}
                  {(
                    (stats?.totalHours || 0) / (stats?.totalMembers || 1)
                  ).toFixed(1)}{' '}
                  hours per member
                </span>
              </li>
              <li className="flex items-start gap-2">
                <span className="text-blue-500 mt-0.5">‚Ä¢</span>
                <span>
                  {stats?.activeMembers || 0} active members this month (
                  {Math.round(
                    ((stats?.activeMembers || 0) / (stats?.totalMembers || 1)) *
                      100
                  )}
                  % of total)
                </span>
              </li>
              {stats?.topProjects &&
                stats.topProjects.length > 0 &&
                stats.topProjects[0] && (
                  <li className="flex items-start gap-2">
                    <span className="text-blue-500 mt-0.5">‚Ä¢</span>
                    <span>
                      Most popular project: {stats.topProjects[0]?.projectName}{' '}
                      ({stats.topProjects[0]?.hours.toFixed(1)} hours)
                    </span>
                  </li>
                )}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import { useAuth } from '@/hooks/useAuth';
import { useActivities } from '@/hooks/useActivitiesQuery';
import { firebaseChallengeApi } from '@/lib/api';
import { Challenge, ChallengeProgress, Group } from '@/types';
import ChallengeCard from './ChallengeCard';
import CreateChallengeModal from './CreateChallengeModal';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import {
  Plus,
  Trophy,
  Target,
  Calendar,
  Users,
  TrendingUp,
} from 'lucide-react';

interface GroupChallengesProps {
  group: Group;
  isAdmin: boolean;
}

export default function GroupChallenges({
  group,
  isAdmin,
}: GroupChallengesProps) {
  const { user } = useAuth();
  const { data: projects = [] } = useActivities(user?.id);
  const [challenges, setChallenges] = useState<Challenge[]>([]);
  const [userProgress, setUserProgress] = useState<
    Record<string, ChallengeProgress>
  >({});
  const [participatingChallenges, setParticipatingChallenges] = useState<
    Set<string>
  >(new Set());
  const [isLoading, setIsLoading] = useState(true);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [activeFilter, setActiveFilter] = useState<
    'all' | 'active' | 'upcoming' | 'completed'
  >('all');

  useEffect(() => {
    loadGroupChallenges();
  }, [group.id, activeFilter]);

  const loadGroupChallenges = async () => {
    try {
      setIsLoading(true);

      const filters = {
        groupId: group.id,
        status: activeFilter === 'all' ? undefined : activeFilter,
      };

      const challengesList = await firebaseChallengeApi.getChallenges(filters);
      setChallenges(challengesList);

      // Load user progress for participating challenges
      if (user) {
        const progressMap: Record<string, ChallengeProgress> = {};
        const participatingSet = new Set<string>();

        for (const challenge of challengesList) {
          const progress = await firebaseChallengeApi.getChallengeProgress(
            challenge.id
          );
          if (progress) {
            progressMap[challenge.id] = progress;
            participatingSet.add(challenge.id);
          }
        }

        setUserProgress(progressMap);
        setParticipatingChallenges(participatingSet);
      }
    } catch (error) {
      console.error('Failed to load group challenges:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleCreateChallenge = async (data: any) => {
    try {
      await firebaseChallengeApi.createChallenge({
        ...data,
        groupId: group.id,
      });
      await loadGroupChallenges();
      setShowCreateModal(false);
    } catch (error) {
      console.error('Failed to create challenge:', error);
      throw error;
    }
  };

  const handleJoinChallenge = async (challengeId: string) => {
    try {
      await firebaseChallengeApi.joinChallenge(challengeId);
      await loadGroupChallenges();
    } catch (error) {
      console.error('Failed to join challenge:', error);
      alert('Failed to join challenge. Please try again.');
    }
  };

  const handleLeaveChallenge = async (challengeId: string) => {
    try {
      await firebaseChallengeApi.leaveChallenge(challengeId);
      await loadGroupChallenges();
    } catch (error) {
      console.error('Failed to leave challenge:', error);
      alert('Failed to leave challenge. Please try again.');
    }
  };

  const activeChallenges = challenges.filter(c => {
    const now = new Date();
    const startDate = new Date(c.startDate);
    const endDate = new Date(c.endDate);
    return now >= startDate && now <= endDate && c.isActive;
  });

  const upcomingChallenges = challenges.filter(c => {
    const now = new Date();
    const startDate = new Date(c.startDate);
    return now < startDate && c.isActive;
  });

  const completedChallenges = challenges.filter(c => {
    const now = new Date();
    const endDate = new Date(c.endDate);
    return now > endDate || !c.isActive;
  });

  const getFilteredChallenges = () => {
    switch (activeFilter) {
      case 'active':
        return activeChallenges;
      case 'upcoming':
        return upcomingChallenges;
      case 'completed':
        return completedChallenges;
      default:
        return challenges;
    }
  };

  const filteredChallenges = getFilteredChallenges();

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-semibold text-gray-900">
            Group Challenges
          </h3>
          <p className="text-sm text-gray-600">
            Compete with group members in productivity challenges
          </p>
        </div>
        {isAdmin && (
          <Button onClick={() => setShowCreateModal(true)}>
            <Plus className="w-4 h-4 mr-2" />
            Create Challenge
          </Button>
        )}
      </div>

      {/* Stats Overview */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <div className="bg-blue-50 rounded-lg p-4 text-center">
          <div className="text-2xl font-bold text-blue-600">
            {activeChallenges.length}
          </div>
          <div className="text-sm text-blue-600">Active</div>
        </div>
        <div className="bg-green-50 rounded-lg p-4 text-center">
          <div className="text-2xl font-bold text-green-600">
            {upcomingChallenges.length}
          </div>
          <div className="text-sm text-green-600">Upcoming</div>
        </div>
        <div className="bg-purple-50 rounded-lg p-4 text-center">
          <div className="text-2xl font-bold text-purple-600">
            {completedChallenges.length}
          </div>
          <div className="text-sm text-purple-600">Completed</div>
        </div>
        <div className="bg-orange-50 rounded-lg p-4 text-center">
          <div className="text-2xl font-bold text-orange-600">
            {participatingChallenges.size}
          </div>
          <div className="text-sm text-orange-600">Participating</div>
        </div>
      </div>

      {/* Filter Tabs */}
      <div className="flex flex-wrap gap-2">
        {[
          { key: 'all', label: 'All Challenges', count: challenges.length },
          { key: 'active', label: 'Active', count: activeChallenges.length },
          {
            key: 'upcoming',
            label: 'Upcoming',
            count: upcomingChallenges.length,
          },
          {
            key: 'completed',
            label: 'Completed',
            count: completedChallenges.length,
          },
        ].map(filter => (
          <button
            key={filter.key}
            onClick={() => setActiveFilter(filter.key as any)}
            className={`flex items-center gap-2 px-4 py-2 rounded-lg font-medium text-sm transition-colors ${
              activeFilter === filter.key
                ? 'bg-blue-100 text-blue-700 border border-blue-200'
                : 'bg-gray-50 text-gray-600 hover:bg-gray-100 border border-transparent'
            }`}
          >
            {filter.label}
            <Badge variant="secondary" className="ml-1">
              {filter.count}
            </Badge>
          </button>
        ))}
      </div>

      {/* Challenges List */}
      {isLoading ? (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {[...Array(4)].map((_, i) => (
            <div
              key={i}
              className="bg-white rounded-lg border border-gray-200 p-6 animate-pulse"
            >
              <div className="h-4 bg-gray-200 rounded mb-4"></div>
              <div className="h-3 bg-gray-200 rounded mb-2"></div>
              <div className="h-3 bg-gray-200 rounded mb-4"></div>
              <div className="h-8 bg-gray-200 rounded"></div>
            </div>
          ))}
        </div>
      ) : filteredChallenges.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {filteredChallenges.map(challenge => (
            <ChallengeCard
              key={challenge.id}
              challenge={challenge}
              isParticipating={participatingChallenges.has(challenge.id)}
              userProgress={userProgress[challenge.id]?.currentValue || 0}
              onJoin={() => handleJoinChallenge(challenge.id)}
              onLeave={() => handleLeaveChallenge(challenge.id)}
              showActions={!!user}
            />
          ))}
        </div>
      ) : (
        <div className="bg-white rounded-lg border border-gray-200 p-12 text-center">
          <Trophy className="w-12 h-12 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">
            {activeFilter === 'all'
              ? 'No challenges yet'
              : `No ${activeFilter} challenges`}
          </h3>
          <p className="text-gray-500 mb-6">
            {isAdmin
              ? 'Create the first challenge for your group members to participate in.'
              : 'Check back later for new challenges to join.'}
          </p>
          {isAdmin && activeFilter === 'all' && (
            <Button onClick={() => setShowCreateModal(true)}>
              <Plus className="w-4 h-4 mr-2" />
              Create First Challenge
            </Button>
          )}
        </div>
      )}

      {/* Create Challenge Modal */}
      <CreateChallengeModal
        isOpen={showCreateModal}
        onClose={() => setShowCreateModal(false)}
        onSubmit={handleCreateChallenge}
        groupId={group.id}
        projects={projects || []}
        isLoading={false}
      />
    </div>
  );
}

'use client';

import React from 'react';
import Image from 'next/image';
import { useRouter } from 'next/navigation';
import { Group, GroupStats } from '@/types';
