} from 'lucide-react';

interface GroupHeaderProps {
  group: Group;
  stats?: GroupStats;
  currentUserId?: string;
  isJoined?: boolean;
  onJoin?: () => Promise<void>;
  onLeave?: () => Promise<void>;
  onSettings?: () => void;
  onInvite?: () => void;
  isLoading?: boolean;
}

const categoryLabels = {
  'work': 'Work',
  'study': 'Study',
  'side-project': 'Side Project',
  'learning': 'Learning',
  'other': 'Other'
};

const typeLabels = {
