'use client';

import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { useAuth } from '@/hooks/useAuth';
import { Group } from '@/types';
import { firebaseApi } from '@/lib/api';
import { Users, MapPin, Loader2 } from 'lucide-react';
import Image from 'next/image';

interface GroupInviteLandingProps {
  groupId: string;
}

export default function GroupInviteLanding({ groupId }: GroupInviteLandingProps) {
  const router = useRouter();
  const { user, isLoading: authLoading } = useAuth();
  const [group, setGroup] = useState<Group | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isJoining, setIsJoining] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadGroup();
  }, [groupId]);

  // If user is already logged in, check membership and redirect
  useEffect(() => {
    if (!authLoading && user && group) {
      checkMembershipAndRedirect();
    }
  }, [user, authLoading, group]);

  const loadGroup = async () => {
    try {
      setIsLoading(true);
      const groupData = await firebaseApi.group.getGroup(groupId);
      if (!groupData) {
        setError('Group not found');
        return;
      }
      setGroup(groupData);
    } catch (err) {
      console.error('Error loading group:', err);
      setError('Failed to load group information');
    } finally {
      setIsLoading(false);
    }
  };

  const checkMembershipAndRedirect = async () => {
    if (!user || !group) return;

    // Check if user is already a member
    if (group.memberIds.includes(user.id)) {
      // Already a member, redirect to group
      router.push(`/groups/${groupId}`);
      return;
    }

    // Not a member, auto-join them
    await handleJoinGroup();
  };

  const handleJoinGroup = async () => {
    if (!user || !group) return;

    try {
      setIsJoining(true);
      await firebaseApi.group.joinGroup(group.id, user.id);
      // Redirect to group page
      router.push(`/groups/${groupId}`);
    } catch (err: any) {
      console.error('Error joining group:', err);
      setError(err.message || 'Failed to join group');
    } finally {
      setIsJoining(false);
    }
  };

  const handleSignUp = () => {
    // Store invite context in sessionStorage for post-signup redirect
    if (typeof window !== 'undefined') {
      sessionStorage.setItem('inviteContext', JSON.stringify({
        type: 'group',
        groupId: groupId,
      }));
    }
    router.push('/signup');
  };

  const handleLogin = () => {
    // Store invite context in sessionStorage for post-login redirect
    if (typeof window !== 'undefined') {
      sessionStorage.setItem('inviteContext', JSON.stringify({
        type: 'group',
        groupId: groupId,
      }));
    }
    router.push('/login');
  };

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case 'work':
        return 'üíº';
      case 'study':
        return 'üìö';
      case 'side-project':
        return 'üíª';
      case 'learning':
        return 'üéì';
      default:
        return 'üìå';
    }
  };

  if (isLoading || authLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <div className="text-center">
          <Loader2 className="w-12 h-12 animate-spin text-[#007AFF] mx-auto mb-4" />
          <p className="text-gray-600">Loading group...</p>
        </div>
      </div>
    );
  }

  if (error || !group) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center px-4">
        <div className="max-w-md w-full bg-white rounded-2xl shadow-xl p-8 text-center">
          <div className="w-16 h-16 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <span className="text-3xl">‚ùå</span>
          </div>
          <h1 className="text-2xl font-bold text-gray-900 mb-2">Group Not Found</h1>
          <p className="text-gray-600 mb-6">
            {error || "The group you're looking for doesn't exist or has been deleted."}
          </p>
          <button
            onClick={() => router.push('/')}
            className="bg-[#007AFF] text-white px-6 py-3 rounded-lg hover:bg-[#0051D5] transition-colors font-medium"
          >
            Go to Home
          </button>
        </div>
      </div>
    );
  }

  // If user is logged in and joining, show loading state
  if (user && isJoining) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <div className="text-center">
          <Loader2 className="w-12 h-12 animate-spin text-[#007AFF] mx-auto mb-4" />
          <p className="text-gray-600">Joining {group.name}...</p>
        </div>
      </div>
    );
  }

  // Show invite landing page for non-logged-in users
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center px-4 py-8">
      <div className="max-w-2xl w-full">
        {/* Logo */}
        <div className="text-center mb-8">
          <div className="w-16 h-16 mx-auto mb-4">
            <Image
              src="/logo.svg"
              alt="Ambira"
              width={64}
              height={64}
              className="w-16 h-16"
            />
          </div>
          <h1 className="text-2xl font-bold text-gray-900">
            Join Ambira
          </h1>
        </div>

        {/* Group Card */}
        <div className="bg-white rounded-2xl shadow-xl overflow-hidden mb-6">
          {/* Group Header */}
          <div className="bg-gradient-to-r from-[#007AFF] to-[#0051D5] px-8 py-12 text-center">
            {/* Group Avatar */}
            <div className="w-24 h-24 mx-auto mb-4 bg-white rounded-full flex items-center justify-center">
              {group.imageUrl ? (
                <img
                  src={group.imageUrl}
                  alt={group.name}
                  className="w-full h-full rounded-full object-cover"
                />
              ) : (
                <Users className="w-12 h-12 text-[#007AFF]" />
              )}
            </div>

            <h2 className="text-3xl font-bold text-white mb-2">
              Join {group.name}
            </h2>
            <p className="text-blue-100 text-lg">
              on Ambira
            </p>
          </div>

          {/* Group Details */}
          <div className="px-8 py-6">
            {/* Description */}
            {group.description && (
              <div className="mb-6">
                <p className="text-gray-700 text-center leading-relaxed">
                  {group.description}
                </p>
              </div>
            )}

            {/* Meta Information */}
            <div className="flex items-center justify-center gap-6 mb-8 text-sm text-gray-600">
              {/* Category */}
              <div className="flex items-center gap-2">
                <span className="text-xl">{getCategoryIcon(group.category)}</span>
                <span className="capitalize">{group.category.replace('-', ' ')}</span>
              </div>

              {/* Member Count */}
              <div className="flex items-center gap-2">
                <Users className="w-4 h-4" />
                <span>{group.memberCount} {group.memberCount === 1 ? 'member' : 'members'}</span>
              </div>

              {/* Location */}
              {group.location && (
                <div className="flex items-center gap-2">
                  <MapPin className="w-4 h-4" />
                  <span>{group.location}</span>
                </div>
              )}
            </div>

            {/* CTA Buttons */}
            <div className="space-y-3">
              <button
                onClick={handleSignUp}
                className="w-full bg-[#007AFF] text-white px-6 py-4 rounded-lg hover:bg-[#0051D5] transition-colors font-semibold text-lg shadow-md hover:shadow-lg"
              >
                Sign Up to Join {group.name}
              </button>

              <div className="text-center">
                <p className="text-sm text-gray-600">
                  Already have an account?{' '}
                  <button
                    onClick={handleLogin}
                    className="text-[#007AFF] hover:underline font-medium"
                  >
                    Log in
                  </button>
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* What is Ambira section */}
        <div className="bg-white/80 backdrop-blur rounded-xl p-6 text-center">
          <h3 className="font-semibold text-gray-900 mb-2">What is Ambira?</h3>
          <p className="text-gray-600 text-sm">
            Ambira is a social productivity tracking app that helps you track work sessions,
            build streaks, and compete with friends. Think Strava, but for productivity.
          </p>
        </div>
      </div>
    </div>
  );
}

'use client';

import React, { useState, useRef } from 'react';
import { Group } from '@/types';
import { X, Copy, Check, Share2, Download, QrCode, Image as ImageIcon } from 'lucide-react';
import { QRCodeCanvas } from 'qrcode.react';

interface GroupInviteModalProps {
  group: Group;
  isOpen: boolean;
  onClose: () => void;
}

type ShareTab = 'link' | 'qr' | 'image';

export default function GroupInviteModal({ group, isOpen, onClose }: GroupInviteModalProps) {
  const [copied, setCopied] = useState(false);
  const [activeTab, setActiveTab] = useState<ShareTab>('link');
  const qrCodeRef = useRef<HTMLDivElement>(null);
  const inviteCardRef = useRef<HTMLDivElement>(null);

  if (!isOpen) return null;

  const baseUrl = typeof window !== 'undefined'
    ? window.location.origin
    : process.env.NEXT_PUBLIC_BASE_URL || 'https://ambira.com';

  const inviteLink = `${baseUrl}/invite/group/${group.id}`;

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(inviteLink);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
    }
  };

  const handleShare = async () => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: `Join ${group.name} on Ambira`,
          text: `I'd like to invite you to join ${group.name} on Ambira!`,
          url: inviteLink,
        });
      } catch (err) {
        // User cancelled or error occurred
      }
    } else {
      // Fallback to copy
      handleCopy();
    }
  };

  const handleDownloadQR = () => {
    const canvas = qrCodeRef.current?.querySelector('canvas');
    if (!canvas) return;

    canvas.toBlob((blob) => {
      if (!blob) return;
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `${group.name.replace(/\s+/g, '-').toLowerCase()}-qr-code.png`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);
    });
  };

  const handleDownloadInviteCard = async () => {
    if (!inviteCardRef.current) return;

    try {
      // Use html2canvas for better rendering
      const html2canvas = (await import('html2canvas')).default;
      const canvas = await html2canvas(inviteCardRef.current, {
        backgroundColor: '#ffffff',
        scale: 2,
        logging: false,
      });

      canvas.toBlob((blob) => {
        if (!blob) return;
        const url = URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = `${group.name.replace(/\s+/g, '-').toLowerCase()}-invite.png`;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        URL.revokeObjectURL(url);
      });
    } catch (err) {
      console.error('Failed to download invite card:', err);
    }
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/50 backdrop-blur-sm">
      <div className="bg-white rounded-2xl shadow-2xl max-w-2xl w-full overflow-hidden max-h-[90vh] flex flex-col">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h2 className="text-xl font-bold text-gray-900">Invite People</h2>
          <button
            onClick={onClose}
            className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <X className="w-5 h-5 text-gray-600" />
          </button>
        </div>

        {/* Tabs */}
        <div className="border-b border-gray-200 px-6">
          <div className="flex gap-6">
            <button
              onClick={() => setActiveTab('link')}
              className={`pb-3 px-1 text-sm font-medium border-b-2 transition-colors ${
                activeTab === 'link'
                  ? 'border-[#007AFF] text-[#007AFF]'
                  : 'border-transparent text-gray-600 hover:text-gray-900'
              }`}
            >
              <div className="flex items-center gap-2">
                <Copy className="w-4 h-4" />
                Link
              </div>
            </button>
            <button
              onClick={() => setActiveTab('qr')}
              className={`pb-3 px-1 text-sm font-medium border-b-2 transition-colors ${
                activeTab === 'qr'
                  ? 'border-[#007AFF] text-[#007AFF]'
                  : 'border-transparent text-gray-600 hover:text-gray-900'
              }`}
            >
              <div className="flex items-center gap-2">
                <QrCode className="w-4 h-4" />
                QR Code
              </div>
            </button>
            <button
              onClick={() => setActiveTab('image')}
              className={`pb-3 px-1 text-sm font-medium border-b-2 transition-colors ${
                activeTab === 'image'
                  ? 'border-[#007AFF] text-[#007AFF]'
                  : 'border-transparent text-gray-600 hover:text-gray-900'
              }`}
            >
              <div className="flex items-center gap-2">
                <ImageIcon className="w-4 h-4" />
                Share Image
              </div>
            </button>
          </div>
        </div>

        {/* Content */}
        <div className="p-6 space-y-6 overflow-y-auto">
          {/* Group Info */}
          <div className="flex items-center gap-3 p-4 bg-gray-50 rounded-lg">
            <div className="w-12 h-12 bg-gradient-to-br from-[#007AFF] to-[#0051D5] rounded-full flex items-center justify-center flex-shrink-0">
              {group.imageUrl ? (
                <img
                  src={group.imageUrl}
                  alt={group.name}
                  className="w-full h-full rounded-full object-cover"
                />
              ) : (
                <span className="text-xl">üíº</span>
              )}
            </div>
            <div className="flex-1 min-w-0">
              <h3 className="font-semibold text-gray-900 truncate">{group.name}</h3>
              <p className="text-sm text-gray-600 truncate">
                {group.memberCount} {group.memberCount === 1 ? 'member' : 'members'}
              </p>
            </div>
          </div>

          {/* Tab Content */}
          {activeTab === 'link' && (
            <div className="space-y-4">
              {/* Invite Link */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Share this invite link
                </label>
                <div className="flex gap-2">
                  <input
                    type="text"
                    value={inviteLink}
                    readOnly
                    className="flex-1 px-4 py-3 border border-gray-300 rounded-lg bg-gray-50 text-gray-700 text-sm font-mono focus:outline-none focus:ring-2 focus:ring-[#007AFF]"
                    onClick={(e) => e.currentTarget.select()}
                  />
                  <button
                    onClick={handleCopy}
                    className="px-4 py-3 bg-[#007AFF] text-white rounded-lg hover:bg-[#0051D5] transition-colors flex items-center gap-2 font-medium whitespace-nowrap"
                  >
                    {copied ? (
                      <>
                        <Check className="w-4 h-4" />
                        Copied
                      </>
                    ) : (
                      <>
                        <Copy className="w-4 h-4" />
                        Copy
                      </>
                    )}
                  </button>
                </div>
              </div>

              {/* Share Button (shows on mobile with Web Share API support) */}
              {typeof navigator !== 'undefined' && typeof navigator.share !== 'undefined' && (
                <button
                  onClick={handleShare}
                  className="w-full px-4 py-3 bg-gray-100 text-gray-900 rounded-lg hover:bg-gray-200 transition-colors flex items-center justify-center gap-2 font-medium"
                >
                  <Share2 className="w-4 h-4" />
                  Share Link
                </button>
              )}

              {/* Info Text */}
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                <p className="text-sm text-blue-900">
                  Anyone with this link can join <strong>{group.name}</strong>. They'll need to sign up or log in to Ambira first.
                </p>
              </div>
            </div>
          )}

          {activeTab === 'qr' && (
            <div className="space-y-4">
              {/* QR Code */}
              <div className="flex flex-col items-center">
                <div ref={qrCodeRef} className="p-6 bg-white border-2 border-gray-200 rounded-2xl">
                  <QRCodeCanvas
                    value={inviteLink}
                    size={256}
                    level="H"
                    includeMargin={true}
                    imageSettings={{
                      src: '/logo.png',
                      excavate: true,
                      width: 40,
                      height: 40,
                    }}
                  />
                </div>
                <p className="text-sm text-gray-600 mt-4 text-center">
                  Scan this QR code with a mobile device to join <strong>{group.name}</strong>
                </p>
              </div>

              {/* Download Button */}
              <button
                onClick={handleDownloadQR}
                className="w-full px-4 py-3 bg-[#007AFF] text-white rounded-lg hover:bg-[#0051D5] transition-colors flex items-center justify-center gap-2 font-medium"
              >
                <Download className="w-4 h-4" />
                Download QR Code
              </button>

              {/* Info Text */}
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                <p className="text-sm text-blue-900">
                  Share this QR code in presentations, posters, or print materials. Anyone can scan it to join your group.
                </p>
              </div>
            </div>
          )}

          {activeTab === 'image' && (
            <div className="space-y-4">
              {/* Invite Card Preview */}
              <div className="flex flex-col items-center">
                <div
                  ref={inviteCardRef}
                  className="w-full max-w-lg bg-gradient-to-br from-[#007AFF] to-[#0051D5] rounded-2xl p-8 text-white"
                >
                  {/* Logo/Branding */}
                  <div className="text-center mb-6">
                    <h3 className="text-2xl font-bold mb-2">Join us on Ambira</h3>
                    <div className="h-1 w-16 bg-white/50 mx-auto rounded-full"></div>
                  </div>

                  {/* Group Info */}
                  <div className="bg-white/10 backdrop-blur-sm rounded-xl p-6 mb-6 text-center">
                    <div className="w-20 h-20 bg-white rounded-full flex items-center justify-center mx-auto mb-4">
                      {group.imageUrl ? (
                        <img
                          src={group.imageUrl}
                          alt={group.name}
                          className="w-full h-full rounded-full object-cover"
                        />
                      ) : (
                        <span className="text-4xl">üíº</span>
                      )}
                    </div>
                    <h2 className="text-3xl font-bold mb-2">{group.name}</h2>
                    {group.description && (
                      <p className="text-white/90 text-sm line-clamp-2 mb-3">{group.description}</p>
                    )}
                    <div className="flex items-center justify-center gap-4 text-sm text-white/80">
                      <span>{group.memberCount} members</span>
                      {group.location && (
                        <>
                          <span>‚Ä¢</span>
                          <span>{group.location}</span>
                        </>
                      )}
                    </div>
                  </div>

                  {/* QR Code */}
                  <div className="bg-white rounded-xl p-4 mb-4">
                    <QRCodeCanvas
                      value={inviteLink}
                      size={200}
                      level="H"
                      style={{ width: '100%', height: 'auto' }}
                    />
                  </div>

                  {/* Footer */}
                  <div className="text-center">
                    <p className="text-sm text-white/80 mb-1">Scan to join the group</p>
                    <p className="text-xs text-white/60 font-mono break-all">{inviteLink}</p>
                  </div>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex gap-3">
                <button
                  onClick={handleDownloadInviteCard}
                  className="flex-1 px-4 py-3 bg-[#007AFF] text-white rounded-lg hover:bg-[#0051D5] transition-colors flex items-center justify-center gap-2 font-medium"
                >
                  <Download className="w-4 h-4" />
                  Download Image
                </button>
                {typeof navigator !== 'undefined' && typeof navigator.share !== 'undefined' && (
                  <button
                    onClick={handleShare}
                    className="flex-1 px-4 py-3 bg-gray-100 text-gray-900 rounded-lg hover:bg-gray-200 transition-colors flex items-center justify-center gap-2 font-medium"
                  >
                    <Share2 className="w-4 h-4" />
                    Share
                  </button>
                )}
              </div>

              {/* Info Text */}
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                <p className="text-sm text-blue-900">
                  Download and share this invite card on social media, messaging apps, or email to invite people to join <strong>{group.name}</strong>.
                </p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

'use client';

import React, { useState } from 'react';
import Image from 'next/image';
import { Group, UpdateGroupData, User } from '@/types';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { Switch } from '@/components/ui/switch';
import {
  Settings,
  Users,
  Trash2,
  Upload,
  Save,
  AlertTriangle,
  Image as ImageIcon,
  X,
  ChevronLeft
} from 'lucide-react';
import { useRouter } from 'next/navigation';

interface GroupSettingsProps {
  group: Group;
  admins: User[];
  onUpdate: (data: UpdateGroupData) => Promise<void>;
  onDelete: () => Promise<void>;
  onAddAdmin: (userId: string) => Promise<void>;
  onRemoveAdmin: (userId: string) => Promise<void>;
  isLoading?: boolean;
}

const categoryOptions = [
  { value: 'work', label: 'Work' },
  { value: 'study', label: 'Study' },
  { value: 'side-project', label: 'Side Project' },
  { value: 'learning', label: 'Learning' },
  { value: 'other', label: 'Other' }
];

const typeOptions = [
  { value: 'just-for-fun', label: 'Just for Fun' },
  { value: 'professional', label: 'Professional' },
  { value: 'competitive', label: 'Competitive' },
  { value: 'other', label: 'Other' }
];

export default function GroupSettings({
  group,
  admins,
  onUpdate,
  onDelete,
  onAddAdmin,
  onRemoveAdmin,
  isLoading = false
}: GroupSettingsProps) {
  const router = useRouter();
  const [formData, setFormData] = useState<UpdateGroupData>({
    name: group.name,
    description: group.description,
    category: group.category,
    type: group.type,
    privacySetting: group.privacySetting,
    location: group.location || '',
    imageUrl: group.imageUrl || '',
    bannerUrl: group.bannerUrl || ''
  });

  const [errors, setErrors] = useState<Record<string, string>>({});
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [newAdminUsername, setNewAdminUsername] = useState('');

  const handleInputChange = (field: keyof UpdateGroupData, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    if (!formData.name?.trim()) {
      newErrors.name = 'Group name is required';
    } else if (formData.name.length < 3) {
      newErrors.name = 'Group name must be at least 3 characters';
    } else if (formData.name.length > 50) {
      newErrors.name = 'Group name must be less than 50 characters';
    }

    if (!formData.description?.trim()) {
      newErrors.description = 'Group description is required';
    } else if (formData.description.length < 10) {
      newErrors.description = 'Description must be at least 10 characters';
    } else if (formData.description.length > 500) {
      newErrors.description = 'Description must be less than 500 characters';
    }

    if (formData.location && formData.location.length > 100) {
      newErrors.location = 'Location must be less than 100 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSave = async () => {
    if (!validateForm()) {
      return;
    }

    try {
      await onUpdate(formData);
    } catch (error) {
      console.error('Error updating group:', error);
    }
  };

  const handleImageUpload = (type: 'imageUrl' | 'bannerUrl', file: File) => {
    // In a real implementation, you would upload the file to a storage service
    const url = URL.createObjectURL(file);
    handleInputChange(type, url);
  };

  const handleAddAdmin = async () => {
    if (!newAdminUsername.trim()) return;

    try {
      await onAddAdmin(newAdminUsername.trim());
      setNewAdminUsername('');
    } catch (error) {
      console.error('Error adding admin:', error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Mobile Header */}
      <div className="md:hidden sticky top-0 bg-white border-b border-gray-200 px-4 py-3 z-50 flex items-center gap-3">
        <button
          onClick={() => router.back()}
          className="p-2 -ml-2 hover:bg-gray-100 rounded-full transition-colors"
        >
          <ChevronLeft className="w-6 h-6 text-gray-700" />
        </button>
        <h1 className="text-lg font-bold text-gray-900 flex-1">Edit Group</h1>
        <button
          onClick={handleSave}
          disabled={isLoading}
          className="px-4 py-2 bg-[#007AFF] text-white rounded-lg font-semibold text-sm hover:bg-[#0056D6] transition-colors disabled:opacity-50"
        >
          Save
        </button>
      </div>

      {/* Desktop Header */}
      <div className="hidden md:block bg-white border-b border-gray-200">
        <div className="max-w-4xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <button
                onClick={() => router.back()}
                className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
              >
                <ChevronLeft className="w-6 h-6 text-gray-700" />
              </button>
              <h1 className="text-2xl font-bold text-gray-900">Edit Group</h1>
            </div>
            <button
              onClick={handleSave}
              disabled={isLoading}
              className="px-6 py-2.5 bg-[#007AFF] text-white rounded-xl font-semibold hover:bg-[#0056D6] transition-colors disabled:opacity-50"
            >
              <Save className="w-4 h-4 inline mr-2" />
              Save Changes
            </button>
          </div>
        </div>
      </div>

      <div className="max-w-4xl mx-auto px-4 md:px-6 py-6 space-y-4">

        {/* Basic Information */}
        <div className="bg-white rounded-xl border border-gray-200 p-6">
          <h3 className="text-lg font-bold text-gray-900 mb-4">Basic Information</h3>

        <div className="space-y-4">
          {/* Group Name */}
          <div>
            <Label htmlFor="name">Group Name *</Label>
            <Input
              id="name"
              value={formData.name || ''}
              onChange={(e) => handleInputChange('name', e.target.value)}
              className={errors.name ? 'border-red-500' : ''}
            />
            {errors.name && (
              <p className="text-sm text-red-600 mt-1">{errors.name}</p>
            )}
          </div>

          {/* Description */}
          <div>
            <Label htmlFor="description">Description *</Label>
            <Textarea
              id="description"
              value={formData.description || ''}
              onChange={(e) => handleInputChange('description', e.target.value)}
              rows={4}
              className={errors.description ? 'border-red-500' : ''}
            />
            <div className="flex justify-between mt-1">
              {errors.description && (
                <p className="text-sm text-red-600">{errors.description}</p>
              )}
              <p className="text-sm text-gray-500 ml-auto">
                {(formData.description || '').length}/500
              </p>
            </div>
          </div>

          {/* Category and Type */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <Label htmlFor="category">Category *</Label>
              <select
                id="category"
                value={formData.category || 'other'}
                onChange={(e) => handleInputChange('category', e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#007AFF] focus:border-transparent"
              >
                {categoryOptions.map((option) => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <Label htmlFor="type">Type *</Label>
              <select
                id="type"
                value={formData.type || 'just-for-fun'}
                onChange={(e) => handleInputChange('type', e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#007AFF] focus:border-transparent"
              >
                {typeOptions.map((option) => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </select>
            </div>
          </div>

          {/* Privacy Setting */}
          <div>
            <Label htmlFor="privacySetting">Privacy Setting *</Label>
            <select
              id="privacySetting"
              value={formData.privacySetting || 'public'}
              onChange={(e) => handleInputChange('privacySetting', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#007AFF] focus:border-transparent"
            >
              <option value="public">Public - Anyone can join</option>
              <option value="approval-required">Approval Required - Admins must approve</option>
            </select>
          </div>

          {/* Location */}
          <div>
            <Label htmlFor="location">Location</Label>
            <Input
              id="location"
              value={formData.location || ''}
              onChange={(e) => handleInputChange('location', e.target.value)}
              placeholder="City, Country"
            />
            {errors.location && (
              <p className="text-sm text-red-600 mt-1">{errors.location}</p>
            )}
          </div>
          </div>
        </div>

        {/* Group Images */}
        <div className="bg-white rounded-xl border border-gray-200 p-6">
          <h3 className="text-lg font-bold text-gray-900 mb-4">Group Images</h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Group Avatar */}
          <div>
            <Label>Group Avatar</Label>
            <div className="mt-2">
              {formData.imageUrl ? (
                <div className="relative w-20 h-20 rounded-lg overflow-hidden border">
                  <Image
                    src={formData.imageUrl}
                    alt="Group avatar"
                    fill
                    className="object-cover"
                  />
                  <button
                    type="button"
                    onClick={() => handleInputChange('imageUrl', '')}
                    className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full w-6 h-6 flex items-center justify-center text-xs hover:bg-red-600"
                  >
                    √ó
                  </button>
                </div>
              ) : (
                <label className="flex items-center justify-center w-20 h-20 border-2 border-dashed border-gray-300 rounded-lg cursor-pointer hover:border-gray-400">
                  <input
                    type="file"
                    accept="image/*"
                    onChange={(e) => {
                      const file = e.target.files?.[0];
                      if (file) handleImageUpload('imageUrl', file);
                    }}
                    className="hidden"
                  />
                  <ImageIcon className="w-6 h-6 text-gray-400" />
                </label>
              )}
            </div>
          </div>

          {/* Group Banner */}
          <div>
            <Label>Group Banner</Label>
            <div className="mt-2">
              {formData.bannerUrl ? (
                <div className="relative w-full h-20 rounded-lg overflow-hidden border">
                  <Image
                    src={formData.bannerUrl}
                    alt="Group banner"
                    fill
                    className="object-cover"
                  />
                  <button
                    type="button"
                    onClick={() => handleInputChange('bannerUrl', '')}
                    className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full w-6 h-6 flex items-center justify-center text-xs hover:bg-red-600"
                  >
                    √ó
                  </button>
                </div>
              ) : (
                <label className="flex items-center justify-center w-full h-20 border-2 border-dashed border-gray-300 rounded-lg cursor-pointer hover:border-gray-400">
                  <input
                    type="file"
                    accept="image/*"
                    onChange={(e) => {
                      const file = e.target.files?.[0];
                      if (file) handleImageUpload('bannerUrl', file);
                    }}
                    className="hidden"
                  />
                  <ImageIcon className="w-6 h-6 text-gray-400" />
                </label>
              )}
            </div>
          </div>
          </div>
        </div>

        {/* Administrators */}
        <div className="bg-white rounded-xl border border-gray-200 p-6">
          <h3 className="text-lg font-bold text-gray-900 mb-4">Administrators</h3>

        {/* Current Admins */}
        <div className="space-y-3 mb-4">
          {admins.map((admin) => (
            <div key={admin.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
              <div className="flex items-center gap-3">
                <div className="w-8 h-8 rounded-full bg-gradient-to-br from-blue-400 to-purple-500 flex items-center justify-center text-white text-sm font-medium">
                  {admin.name.charAt(0).toUpperCase()}
                </div>
                <div>
                  <p className="font-medium text-gray-900">{admin.name}</p>
                  <p className="text-sm text-gray-500">@{admin.username}</p>
                </div>
              </div>
              <Button
                variant="outline"
                size="sm"
                onClick={() => onRemoveAdmin(admin.id)}
                disabled={admins.length === 1} // Can't remove the last admin
              >
                Remove
              </Button>
            </div>
          ))}
        </div>

        {/* Add New Admin */}
        <div className="flex gap-2">
          <Input
            placeholder="Enter username to add as admin"
            value={newAdminUsername}
            onChange={(e) => setNewAdminUsername(e.target.value)}
          />
          <Button onClick={handleAddAdmin} disabled={!newAdminUsername.trim()}>
            Add Admin
          </Button>
          </div>
        </div>

        {/* Danger Zone */}
        <div className="bg-white rounded-xl border border-red-200 p-6">
          <h3 className="text-lg font-bold text-red-900 mb-4">Danger Zone</h3>

        <div className="flex items-center justify-between p-4 bg-red-50 rounded-lg">
          <div>
            <h4 className="font-medium text-red-900">Delete Group</h4>
            <p className="text-sm text-red-700 mt-1">
              Once you delete a group, there is no going back. Please be certain.
            </p>
          </div>
          <Button
            variant="destructive"
            onClick={() => setShowDeleteConfirm(true)}
          >
            <Trash2 className="w-4 h-4 mr-2" />
            Delete Group
          </Button>
        </div>

        {showDeleteConfirm && (
          <div className="mt-4 p-4 bg-red-50 border border-red-200 rounded-lg">
            <div className="flex items-start gap-3">
              <AlertTriangle className="w-5 h-5 text-red-600 mt-0.5" />
              <div className="flex-1">
                <h4 className="font-medium text-red-900">Are you absolutely sure?</h4>
                <p className="text-sm text-red-700 mt-1">
                  This action cannot be undone. This will permanently delete the group and remove all associated data.
                </p>
                <div className="flex gap-2 mt-3">
                  <Button
                    variant="destructive"
                    size="sm"
                    onClick={onDelete}
                    disabled={isLoading}
                  >
                    Yes, delete the group
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setShowDeleteConfirm(false)}
                  >
                    Cancel
                  </Button>
                </div>
              </div>
            </div>
          </div>
          )}
        </div>
      </div>
    </div>
  );
}

'use client';

import React, { useState } from 'react';
import { Button } from '@/components/ui/button';
import { 
  MessageSquare, 
  Users, 
  Trophy, 
  Target,
  ChevronDown,
  BarChart3
} from 'lucide-react';

export type GroupTab = 'posts' | 'members' | 'challenges' | 'leaderboard' | 'analytics' | 'about';

interface GroupTabsProps {
  activeTab: GroupTab;
  onTabChange: (tab: GroupTab) => void;
  groupId: string;
  memberCount: number;
  isAdmin?: boolean;
}

const tabConfig = {
  posts: {
    label: 'Posts',
    icon: MessageSquare,
    description: 'Group discussions and updates'
  },
  members: {
    label: 'Members',
    icon: Users,
    description: 'Group members and their activity'
  },
  challenges: {
    label: 'Challenges',
    icon: Target,
    description: 'Group challenges and competitions'
  },
  leaderboard: {
    label: 'Leaderboard',
    icon: Trophy,
    description: 'Member rankings and achievements'
  },
  analytics: {
    label: 'Analytics',
    icon: BarChart3,
    description: 'Group statistics and insights'
  },
  about: {
    label: 'About',
    icon: MessageSquare,
    description: 'Group information and details'
  }
};

export default function GroupTabs({ 
  activeTab, 
  onTabChange, 
  groupId, 
  memberCount,
  isAdmin = false 
}: GroupTabsProps) {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const tabs = Object.entries(tabConfig) as [GroupTab, typeof tabConfig[GroupTab]][];

  return (
    <div className="sticky top-12 md:top-0 bg-white border-b border-gray-200 z-30">
      <div className="max-w-7xl mx-auto px-4 md:px-6 lg:px-8">
        {/* Tabs - Responsive */}
        <div className="flex md:gap-8">
          <nav className="flex md:space-x-8 overflow-x-auto scrollbar-hide" aria-label="Group tabs">
            {tabs.map(([tabKey, config]) => {
              const Icon = config.icon;
              const isActive = activeTab === tabKey;

              return (
                <button
                  key={tabKey}
                  onClick={() => onTabChange(tabKey)}
                  className={`
                    flex-1 md:flex-initial flex items-center justify-center gap-2 py-3 md:py-4 px-1 text-sm md:text-base font-medium border-b-2 transition-colors whitespace-nowrap
                    ${isActive 
                      ? 'border-[#007AFF] text-[#007AFF] md:text-gray-900' 
                      : 'border-transparent text-gray-500 md:text-gray-600 hover:text-gray-700 md:hover:text-gray-900'
                    }
                  `}
                >
                  <Icon className="w-4 h-4 md:inline hidden" />
                  {config.label}
                  {tabKey === 'members' && (
                    <span className="ml-1 bg-gray-100 text-gray-600 text-xs px-2 py-0.5 rounded-full">
                      {memberCount}
                    </span>
                  )}
                </button>
              );
            })}
          </nav>
        </div>
      </div>
    </div>
  );
}

import React from 'react';
import * as Icons from 'lucide-react';
import { Icon } from '@iconify/react';

interface IconRendererProps {
  iconName: string;
  className?: string;
  size?: number;
  style?: React.CSSProperties;
}

/**
 * Dynamically renders an icon based on the icon name string
 * Supports both Iconify icons (e.g., "flat-color-icons:briefcase") and legacy Lucide icons (e.g., "Briefcase")
 * Falls back to Briefcase icon if the icon name is not found
 */
export const IconRenderer: React.FC<IconRendererProps> = ({ iconName, className = '', size = 24, style }) => {
  // Check if it's an Iconify icon (contains a colon, e.g., "flat-color-icons:briefcase")
  if (iconName && iconName.includes(':')) {
    return <Icon icon={iconName} width={size} height={size} className={className} style={style} />;
  }

  // Legacy Lucide icon support (e.g., "Briefcase")
  // If iconName is empty or invalid, fall back to Briefcase
  const IconComponent = (iconName && (Icons as any)[iconName]) || Icons.Briefcase;
  return <IconComponent className={className} size={size} style={style} />;
};

export default IconRenderer;

