interface ImageUploadProps {
  /** Maximum number of images allowed */
  maxImages?: number;
  /** Maximum file size in MB */
  maxSizeMB?: number;
  /** Accepted file types (e.g., ['image/jpeg', 'image/png']) */
  acceptedTypes?: string[];
  /** Show accepted types hint */
  showTypeHint?: boolean;
  /** Current images (for controlled component) */
  images?: File[];
  /** Preview URLs (for controlled component) */
  previewUrls?: string[];
  /** Callback when images change */
  onImagesChange?: (images: File[], previewUrls: string[]) => void;
  /** Upload mode: 'instant' uploads immediately, 'deferred' waits for form submission */
  uploadMode?: 'instant' | 'deferred';
  /** Upload function for instant mode */
  onUpload?: (files: File[]) => Promise<string[]>;
  /** Label text */
  label?: string;
  /** Show upload progress */
  showProgress?: boolean;
  /** Custom placeholder text */
  placeholder?: string;
  /** Disabled state */
  disabled?: boolean;
  /** Show as single image picker (for profile pictures) */
  singleImage?: boolean;
}

export const ImageUpload: React.FC<ImageUploadProps> = ({
  maxImages = 3,
  maxSizeMB = 5,
  acceptedTypes = [
    'image/jpeg',
    'image/png',
    'image/gif',
    'image/webp',
    'image/heic',
    'image/heif',
  ],
  showTypeHint = true,
  images = [],
  previewUrls = [],
  onImagesChange,
  uploadMode = 'deferred',
  onUpload,
  label,
  showProgress = true,
  placeholder,
  disabled = false,
  singleImage = false,
}) => {
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [error, setError] = useState<string>('');
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState<number>(0);
  const [actionSheetIndex, setActionSheetIndex] = useState<number | null>(null);

  const maxSize = maxSizeMB * 1024 * 1024;
  const effectiveMaxImages = singleImage ? 1 : maxImages;

  const formatFileTypes = () => {
    const types = acceptedTypes.map(type => {
      const ext = type.split('/')[1]?.toUpperCase();
      if (ext === 'JPEG') return 'JPG';
      return ext;
    });
    return types.join(', ');
  };

  const validateFile = (file: File): { valid: boolean; error?: string } => {
    // Check file size
    if (file.size > maxSize) {
      const sizeMB = (file.size / 1024 / 1024).toFixed(1);
      return {
        valid: false,
        error: `"${file.name}" is too large (${sizeMB}MB). Maximum size is ${maxSizeMB}MB.`,
      };
    }

    // Check file type
    const isHeic =
      file.type === 'image/heic' ||
      file.type === 'image/heif' ||
      file.name.toLowerCase().endsWith('.heic') ||
      file.name.toLowerCase().endsWith('.heif');

    if (
      !acceptedTypes.includes(file.type) &&
      !isHeic &&
      !file.type.startsWith('image/')
    ) {
      return {
        valid: false,
        error: `"${file.name}" is not a supported file type. Please use ${formatFileTypes()}.`,
      };
    }

    return { valid: true };
  };

  const handleFileSelect = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    setError('');

    if (files.length + images.length > effectiveMaxImages) {
      setError(
        `Maximum ${effectiveMaxImages} ${effectiveMaxImages === 1 ? 'image' : 'images'} allowed`
      );
      return;
    }

    const validFiles: File[] = [];
    const newPreviewUrls: string[] = [];

    // Validate each file
    for (const file of files) {
      const validation = validateFile(file);
      if (!validation.valid) {
        setError(validation.error || 'Invalid file');
        continue;
      }

      validFiles.push(file);

      // Create preview URL
      const previewUrl = URL.createObjectURL(file);
      newPreviewUrls.push(previewUrl);
    }

    if (validFiles.length === 0) {
      return;
    }

    // Handle instant upload mode
    if (uploadMode === 'instant' && onUpload) {
      try {
        setIsUploading(true);
        setUploadProgress(0);

        // Simulate progress (you can replace with actual upload progress)
        const progressInterval = setInterval(() => {
          setUploadProgress(prev => Math.min(prev + 10, 90));
        }, 200);

        const uploadedUrls = await onUpload(validFiles);

        clearInterval(progressInterval);
        setUploadProgress(100);

        // Call onChange with uploaded URLs (you might want to handle this differently)
        onImagesChange?.(
          [...images, ...validFiles],
          [...previewUrls, ...uploadedUrls]
        );

        setTimeout(() => {
          setIsUploading(false);
          setUploadProgress(0);
        }, 500);
      } catch (err: unknown) {
        setError((err as Error).message || 'Failed to upload images');
        setIsUploading(false);
        setUploadProgress(0);
        // Clean up preview URLs on error
        newPreviewUrls.forEach(url => URL.revokeObjectURL(url));
      }
    } else {
      // Deferred mode - just store files and previews
      onImagesChange?.(
        [...images, ...validFiles],
        [...previewUrls, ...newPreviewUrls]
      );
    }

    // Reset file input
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const handleRemoveImage = (index: number) => {
    const newImages = images.filter((_, i) => i !== index);
    const newPreviewUrls = previewUrls.filter((_, i) => {
      if (i === index) {
        // Revoke object URL to free memory
        const url = previewUrls[i];
        if (url) {
          URL.revokeObjectURL(url);
        }
        return false;
      }
      return true;
    });

    onImagesChange?.(newImages, newPreviewUrls);
    setError('');
  };

  const getPlaceholderText = () => {
    if (placeholder) return placeholder;
    if (singleImage) return 'Upload profile picture';
    if (previewUrls.length === 0) return 'Add images';
    return `Add ${effectiveMaxImages - previewUrls.length} more`;
  };

  return (
    <div className="space-y-3">
      {label && (
        <label className="block text-sm font-medium text-gray-700">
          {label}
        </label>
      )}

      {/* Error Message */}
      {error && (
        <div className="flex items-center gap-2 p-3 bg-red-50 border border-red-200 rounded-lg text-sm text-red-700">
          <AlertCircle className="w-4 h-4 flex-shrink-0" />
          <span>{error}</span>
        </div>
      )}

      {/* Image Previews */}
      {previewUrls.length > 0 && (
        <div
          className={`grid gap-2 ${singleImage ? 'grid-cols-1' : 'grid-cols-3'}`}
        >
          {previewUrls.map((url, index) => (
            <div
              key={index}
              className={`relative rounded-lg overflow-hidden bg-gray-100 ${
                singleImage ? 'aspect-square max-w-[200px]' : 'aspect-square'
              }`}
            >
              <Image
                src={url}
                alt={`Preview ${index + 1}`}
                width={300}
                height={300}
                quality={90}
                className="w-full h-full object-cover"
                unoptimized
                onClick={() => !disabled && setActionSheetIndex(index)}
              />
              {!disabled && (
                <>
                  {/* Desktop X button */}
                  <button
                    type="button"
                    onClick={() => handleRemoveImage(index)}
                    className="hidden md:block absolute top-1 right-1 p-0.5 text-white hover:text-red-500 transition-colors"
                    aria-label="Remove image"
                    style={{
                      filter: 'drop-shadow(0 1px 2px rgb(0 0 0 / 0.9))',
                    }}
                  >
                    <X className="w-5 h-5" strokeWidth={3} />
                  </button>

                  {/* Mobile tap indicator */}
                  <div className="md:hidden absolute inset-0 flex items-center justify-center pointer-events-none">
                    <div className="opacity-0 hover:opacity-100 transition-opacity">
                      <div className="bg-black/20 rounded-full p-2">
                        <X className="w-6 h-6 text-white" strokeWidth={2} />
                      </div>
                    </div>
                  </div>
                </>
              )}
              {/* File size indicator */}
              {images[index] && (
                <div className="absolute bottom-1 left-1 px-2 py-0.5 bg-black/60 text-white text-xs rounded">
                  {(images[index].size / 1024 / 1024).toFixed(1)}MB
                </div>
              )}
            </div>
          ))}
        </div>
      )}

      {/* Delete confirmation modal */}
      <DeleteConfirm
        isOpen={actionSheetIndex !== null}
        onClose={() => setActionSheetIndex(null)}
        onDelete={() => {
          if (actionSheetIndex !== null) {
            handleRemoveImage(actionSheetIndex);
            setActionSheetIndex(null);
          }
        }}
      />

      {/* Upload Progress */}
      {isUploading && showProgress && (
        <div className="space-y-2">
          <div className="flex items-center justify-between text-sm text-gray-600">
            <span>Uploading...</span>
            <span>{uploadProgress}%</span>
          </div>
          <div className="h-2 bg-gray-200 rounded-full overflow-hidden">
            <div
              className="h-full bg-blue-500 transition-all duration-300 ease-out"
              style={{ width: `${uploadProgress}%` }}
            />
          </div>
        </div>
      )}

      {/* Upload Button */}
      {previewUrls.length < effectiveMaxImages && !disabled && (
        <label
          className={`flex flex-col items-center justify-center gap-2 px-8 py-8 border-[3px] border-dashed rounded-lg cursor-pointer transition-colors max-w-[240px] ${
            isUploading
              ? 'border-blue-300 bg-blue-50 cursor-not-allowed'
              : 'border-[#007AFF] hover:border-[#0051D5] hover:bg-gray-50'
          }`}
        >
          {isUploading ? (
            <>
              <Loader2 className="w-8 h-8 text-blue-500 animate-spin" />
              <span className="text-sm font-medium text-blue-600">
                Uploading...
              </span>
            </>
          ) : (
            <>
              <ImageIcon className="w-8 h-8 text-[#007AFF]" />
              <span className="text-sm font-medium text-[#007AFF]">
                {getPlaceholderText()}
              </span>
            </>
          )}
          <input
            ref={fileInputRef}
            type="file"
            accept={acceptedTypes.join(',') + ',.heic,.heif'}
            multiple={!singleImage}
            onChange={handleFileSelect}
            className="hidden"
            disabled={disabled || isUploading}
          />
        </label>
      )}

      {/* Helper text for max images */}
      {!singleImage &&
        previewUrls.length > 0 &&
        previewUrls.length < effectiveMaxImages && (
          <p className="text-xs text-gray-500 text-center">
            {previewUrls.length} of {effectiveMaxImages} images selected
          </p>
        )}
    </div>
  );
};

'use client';

import React, { useState, useEffect } from 'react';
import Link from 'next/link';
import Image from 'next/image';
import { useAuth } from '@/hooks/useAuth';
import { SignupCredentials } from '@/types';
import { firebaseUserApi } from '@/lib/api';
import Header from './HeaderComponent';
import PWAInstallPrompt from './PWAInstallPrompt';

export const LandingPage: React.FC = () => {
  const { login, signup, signInWithGoogle, user, isAuthenticated, isLoading: authIsLoading } = useAuth();
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [showSignup, setShowSignup] = useState(false);
  const [showLogin, setShowLogin] = useState(false);
  const [loginData, setLoginData] = useState({
    email: '',
    password: '',
  });
  const [signupData, setSignupData] = useState<SignupCredentials>({
    email: '',
    password: '',
    name: '',
    username: '',
  });
  const [confirmPassword, setConfirmPassword] = useState('');
  const [signupErrors, setSignupErrors] = useState<Partial<SignupCredentials & { confirmPassword: string }>>({});
  const [loginErrors, setLoginErrors] = useState<{email?: string; password?: string}>({});
  const [usernameCheckLoading, setUsernameCheckLoading] = useState(false);
  const [usernameAvailable, setUsernameAvailable] = useState<boolean | null>(null);

  const handleGoogleSignIn = async () => {
    try {
      setIsLoading(true);
      setError(null);
      await signInWithGoogle();
      // Navigation is handled by AuthContext after successful authentication
      // No need to manually navigate here - AuthContext will update isAuthenticated
      // and the page component will automatically show the authenticated view
    } catch (err: any) {
      console.error('Google sign-in error:', err);
      // Don't show error or clear loading if redirect is in progress
      // (user is being redirected to Google)
      if (err?.message !== 'REDIRECT_IN_PROGRESS') {
        setError(err?.message || 'Failed to sign in with Google. Please try again.');
        setIsLoading(false);
      }
      // If REDIRECT_IN_PROGRESS, keep loading state - browser will redirect
    }
  };

  const handleLoginWithEmail = () => {
    setShowLogin(true);
    setError(null);
  };

  const handleSignupWithEmail = () => {
    setShowSignup(true);
    setError(null);
  };

  const handleLoginChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setLoginData(prev => ({ ...prev, [name]: value }));

    // Clear field-specific error when user starts typing
    if (loginErrors[name as keyof typeof loginErrors]) {
      setLoginErrors(prev => ({ ...prev, [name]: undefined }));
    }

    // Clear submit error
    if (error) {
      setError('');
    }
  };

  const validateLoginForm = (): boolean => {
    const newErrors: {email?: string; password?: string} = {};

    if (!loginData.email) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(loginData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!loginData.password) {
      newErrors.password = 'Password is required';
    }

    setLoginErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleLoginSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateLoginForm()) {
      return;
    }

    try {
      setIsLoading(true);
      setError(null);
      await login(loginData);
      // Navigation is handled by AuthContext after successful authentication
      // No need to manually navigate here - AuthContext will update isAuthenticated
      // and the page component will automatically show the authenticated view
    } catch (err: any) {
      console.error('Login error:', err);

      // Handle specific Firebase errors with user-friendly messages
      if (err.message?.includes('auth/user-not-found')) {
        setError('No account found with this email address. Please sign up or check your email.');
      } else if (err.message?.includes('auth/wrong-password')) {
        setError('Incorrect password. Please try again.');
      } else if (err.message?.includes('auth/invalid-email')) {
        setError('Please enter a valid email address.');
      } else if (err.message?.includes('auth/too-many-requests')) {
        setError('Too many failed attempts. Please try again later.');
      } else {
        setError('Failed to sign in. Please check your credentials and try again.');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handleSignupChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    if (name === 'confirmPassword') {
      setConfirmPassword(value);
    } else {
      setSignupData(prev => ({ ...prev, [name]: value }));
    }

    // Clear field-specific error when user starts typing
    if (signupErrors[name as keyof (SignupCredentials & { confirmPassword: string })]) {
      setSignupErrors(prev => ({ ...prev, [name]: undefined }));
    }

    // Clear submit error
    if (error) {
      setError('');
    }

    // Reset username availability when username changes
    if (name === 'username') {
      setUsernameAvailable(null);
    }
  };

  // Debounced username availability check
  useEffect(() => {
    const checkUsername = async () => {
      const username = signupData.username.trim();

      // Only check if username meets minimum requirements
      if (username.length < 3) {
        setUsernameAvailable(null);
        return;
      }

      // Validate username format
      if (!/^[a-zA-Z0-9_]+$/.test(username)) {
        setUsernameAvailable(null);
        return;
      }

      setUsernameCheckLoading(true);
      try {
        const available = await firebaseUserApi.checkUsernameAvailability(username);
        setUsernameAvailable(available);
      } catch (error: any) {
        // Set to null to indicate check couldn't be completed
        // Registration will still proceed with server-side validation
        setUsernameAvailable(null);
      } finally {
        setUsernameCheckLoading(false);
      }
    };

    // Debounce: wait 1000ms after user stops typing
    const timeoutId = setTimeout(checkUsername, 1000);
    return () => clearTimeout(timeoutId);
  }, [signupData.username]);

  const validateSignupForm = (): boolean => {
    const newErrors: Partial<SignupCredentials & { confirmPassword: string }> = {};

    if (!signupData.name.trim()) {
      newErrors.name = 'Name is required';
    } else if (signupData.name.trim().length < 2) {
      newErrors.name = 'Name must be at least 2 characters';
    }

    if (!signupData.username.trim()) {
      newErrors.username = 'Username is required';
    } else if (signupData.username.trim().length < 3) {
      newErrors.username = 'Username must be at least 3 characters';
    } else if (!/^[a-zA-Z0-9_]+$/.test(signupData.username.trim())) {
      newErrors.username = 'Username can only contain letters, numbers, and underscores';
    } else if (usernameAvailable === false) {
      newErrors.username = 'Username is already taken';
    } else if (usernameCheckLoading) {
      newErrors.username = 'Checking username availability...';
    }

    if (!signupData.email) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(signupData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!signupData.password) {
      newErrors.password = 'Password is required';
    } else if (signupData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    if (!confirmPassword) {
      newErrors.confirmPassword = 'Please confirm your password';
    } else if (signupData.password !== confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    setSignupErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSignupSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateSignupForm()) {
      return;
    }

    try {
      setIsLoading(true);
      setError(null);
      await signup(signupData);
      // Navigation is handled by AuthContext after successful authentication
      // No need to manually navigate here - AuthContext will update isAuthenticated
      // and the page component will automatically show the authenticated view
    } catch (err: any) {
      console.error('Signup error:', err);

      // Handle specific Firebase errors with user-friendly messages
      if (err.message?.includes('auth/email-already-in-use')) {
        setError('This email address is already registered. Please try logging in instead or use a different email.');
      } else if (err.message?.includes('auth/weak-password')) {
        setError('Password is too weak. Please choose a stronger password with at least 6 characters.');
      } else if (err.message?.includes('auth/invalid-email')) {
        setError('Please enter a valid email address.');
      } else {
        setError('Failed to create account. Please try again.');
      }
    } finally {
      setIsLoading(false);
    }
  };

  const [carouselIndex, setCarouselIndex] = useState(0);

  const benefits = [
    {
      title: "Track your active life in one place",
      description: "Record all your productivity sessions and track your progress over time",
      image: "üìä"
    },
    {
      title: "Stay motivated with friends",
      description: "Share your achievements and compete in challenges with your community",
      image: "üèÜ"
    },
    {
      title: "Build lasting habits",
      description: "Track streaks and celebrate milestones as you reach your goals",
      image: "üî•"
    },
    {
      title: "Join groups & challenges",
      description: "Connect with like-minded people and push each other to succeed",
      image: "üë•"
    }
  ];

  return (
    <div className="min-h-screen bg-white">
      {/* Hide header on mobile */}
      <div className="hidden md:block">
        <Header />
      </div>

      {/* Hero Section - Full screen on mobile, with header space on desktop */}
      <main className="min-h-screen md:h-[calc(100vh-56px)] flex flex-col md:items-center md:justify-center px-4 md:px-8 py-8 md:pt-0">
        {/* Mobile View - Compact version similar to desktop */}
        {!showLogin && !showSignup && (
          <div className="md:hidden w-full max-w-md mx-auto flex flex-col justify-center min-h-[calc(100vh-4rem)]">
