import { SessionFormData, Project } from '@/types';
import { parseLocalDateTime } from '@/lib/utils';
import { ERROR_MESSAGES } from '@/config/errorMessages';

interface ManualEntryProps {
  onSave: (data: SessionFormData) => Promise<void>;
  onCancel: () => void;
  isLoading?: boolean;
}

const TAGS = [
  'Study',
  'Work',
  'Side Project',
  'Reading',
  'Learning',
  'Exercise',
  'Creative',
  'Other',
];

const PRIVACY_OPTIONS = [
  { value: 'everyone', label: 'Everyone', description: 'Visible to all users' },
  {
    value: 'followers',
    label: 'Followers',
    description: 'Visible to your followers',
  },
  { value: 'private', label: 'Only You', description: 'Private to you only' },
];

export const ManualEntry: React.FC<ManualEntryProps> = ({
  onSave,
  onCancel,
  isLoading = false,
}) => {
  const [formData, setFormData] = useState<SessionFormData>({
    activityId: '',
    projectId: '',
    title: '',
    description: '',
    duration: 0,
    startTime: new Date(),
    tags: [],
    visibility: 'everyone',
    howFelt: 3,
    privateNotes: '',
  });

  const [projects, setProjects] = useState<Project[]>([]);
  const [errors, setErrors] = useState<Record<string, string>>({});

  // Time inputs
  const [durationHours, setDurationHours] = useState(0);
  const [durationMinutes, setDurationMinutes] = useState(0);
  const [durationSeconds, setDurationSeconds] = useState(0);
  const [startDate, setStartDate] = useState(
    new Date().toISOString().split('T')[0]
  );
  const [startTime, setStartTime] = useState('12:00');

  // TODO: Implement Firebase API calls
  // Helper function to get auth token
  const getAuthToken = (): string => {
    // For now, return empty string since we're not using Firebase sessions yet
    return '';
  };

  // Load projects on mount
  useEffect(() => {
    const loadProjects = async () => {
      try {
        const token = getAuthToken();
        // TODO: Load projects from Firebase
        const projectList: Project[] = []; // await mockProjectApiLocal.getProjects(token);
        setProjects(projectList);
      } catch (error) {
        console.error('Failed to load projects:', error);
      }
    };

    loadProjects();
  }, []);

  // Update duration when time inputs change
  useEffect(() => {
    const totalSeconds =
      durationHours * 3600 + durationMinutes * 60 + durationSeconds;
    setFormData(prev => ({ ...prev, duration: totalSeconds }));
  }, [durationHours, durationMinutes, durationSeconds]);

  // Update start time when date/time inputs change
  useEffect(() => {
    // Parse date and time in local timezone to avoid UTC interpretation issues
    const dateTime = parseLocalDateTime(startDate || '', startTime || '00:00');
    setFormData(prev => ({ ...prev, startTime: dateTime }));
  }, [startDate, startTime]);

  // Generate smart title based on time of day and project
  useEffect(() => {
    if (!formData.title && formData.projectId) {
      const project = projects.find(p => p.id === formData.projectId);
      const hour = new Date().getHours();

      let timeOfDay = '';
      if (hour < 12) timeOfDay = 'Morning';
      else if (hour < 17) timeOfDay = 'Afternoon';
      else timeOfDay = 'Evening';

      const smartTitle = project
        ? `${timeOfDay} ${project.name} Session`
        : `${timeOfDay} Work Session`;
      setFormData(prev => ({ ...prev, title: smartTitle }));
    }
  }, [formData.projectId, formData.title, projects]);

  const handleInputChange = (field: keyof SessionFormData, value: any) => {
    setFormData(prev => ({ ...prev, [field]: value }));

    // Clear error when user starts typing
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const handleTagToggle = (tag: string) => {
    const tags = formData.tags || [];
    const isSelected = tags.includes(tag);
    const newTags = isSelected ? tags.filter(t => t !== tag) : [...tags, tag];

    setFormData(prev => ({ ...prev, tags: newTags }));
  };

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    if (!formData.projectId) {
      newErrors.projectId = 'Please select a project';
    }

    if (!formData.title.trim()) {
      newErrors.title = 'Please enter a session title';
    }

    if (formData.duration <= 0) {
      newErrors.duration = 'Duration must be greater than 0';
    }

    if (!startDate) {
      newErrors.startDate = 'Please select a date';
    }

    if (!startTime) {
      newErrors.startTime = 'Please select a time';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      await onSave(formData);
    } catch (error) {
      console.error('Failed to save session:', error);
      setErrors({ submit: ERROR_MESSAGES.SESSION_SAVE_FAILED });
    }
  };

  const formatDuration = (seconds: number): string => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;

    if (hours > 0) {
      return `${hours}h ${minutes}m ${secs}s`;
    } else if (minutes > 0) {
      return `${minutes}m ${secs}s`;
    } else {
      return `${secs}s`;
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg w-full max-w-2xl max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <h2 className="text-2xl font-bold mb-6">Add Manual Session</h2>

          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Project Selection */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Project *
              </label>
              <select
                value={formData.projectId}
                onChange={e => handleInputChange('projectId', e.target.value)}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  errors.projectId ? 'border-red-500' : 'border-gray-300'
                }`}
                disabled={isLoading}
              >
                <option value="">Select a project...</option>
                {projects.map(project => (
                  <option key={project.id} value={project.id}>
                    {project.name}
                  </option>
                ))}
              </select>
              {errors.projectId && (
                <p className="text-red-500 text-sm mt-1">{errors.projectId}</p>
              )}
            </div>

            {/* Session Title */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Session Title *
              </label>
              <input
                type="text"
                value={formData.title}
                onChange={e => handleInputChange('title', e.target.value)}
                className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  errors.title ? 'border-red-500' : 'border-gray-300'
                }`}
                placeholder="What did you work on?"
                disabled={isLoading}
              />
              {errors.title && (
                <p className="text-red-500 text-sm mt-1">{errors.title}</p>
              )}
            </div>

            {/* Date and Time */}
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Date *
                </label>
                <input
                  type="date"
                  value={startDate}
                  onChange={e => setStartDate(e.target.value)}
                  className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                    errors.startDate ? 'border-red-500' : 'border-gray-300'
                  }`}
                  disabled={isLoading}
                />
                {errors.startDate && (
                  <p className="text-red-500 text-sm mt-1">
                    {errors.startDate}
                  </p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Time *
                </label>
                <select
                  value={startTime}
                  onChange={e => setStartTime(e.target.value)}
                  className={`w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                    errors.startTime ? 'border-red-500' : 'border-gray-300'
                  }`}
                  disabled={isLoading}
                >
                  {Array.from({ length: 48 }, (_, i) => {
                    const hour = Math.floor(i / 2);
                    const minute = (i % 2) * 30;
                    const timeString = `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;
                    return (
                      <option key={i} value={timeString}>
                        {timeString}
                      </option>
                    );
                  })}
                </select>
                {errors.startTime && (
                  <p className="text-red-500 text-sm mt-1">
                    {errors.startTime}
                  </p>
                )}
              </div>
            </div>

            {/* Duration Input */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Duration *
              </label>
              <div className="grid grid-cols-3 gap-4">
                <div>
                  <label className="block text-xs text-gray-500 mb-1">
                    Hours
                  </label>
                  <input
                    type="number"
                    min="0"
                    max="24"
                    value={durationHours}
                    onChange={e =>
                      setDurationHours(parseInt(e.target.value) || 0)
                    }
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    disabled={isLoading}
                  />
                </div>
                <div>
                  <label className="block text-xs text-gray-500 mb-1">
                    Minutes
                  </label>
                  <input
                    type="number"
                    min="0"
                    max="59"
                    value={durationMinutes}
                    onChange={e =>
                      setDurationMinutes(parseInt(e.target.value) || 0)
                    }
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    disabled={isLoading}
                  />
                </div>
                <div>
                  <label className="block text-xs text-gray-500 mb-1">
                    Seconds
                  </label>
                  <input
                    type="number"
                    min="0"
                    max="59"
                    value={durationSeconds}
                    onChange={e =>
                      setDurationSeconds(parseInt(e.target.value) || 0)
                    }
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    disabled={isLoading}
                  />
                </div>
              </div>
              {formData.duration > 0 && (
                <p className="text-sm text-gray-600 mt-2">
                  Total: {formatDuration(formData.duration)}
                </p>
              )}
              {errors.duration && (
                <p className="text-red-500 text-sm mt-1">{errors.duration}</p>
              )}
            </div>

            {/* Description */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Description
              </label>
              <textarea
                value={formData.description || ''}
                onChange={e => handleInputChange('description', e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                rows={3}
                placeholder="What did you accomplish?"
                disabled={isLoading}
              />
            </div>

            {/* Tags */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Tags
              </label>
              <div className="flex flex-wrap gap-2">
                {TAGS.map(tag => (
                  <button
                    key={tag}
                    type="button"
                    onClick={() => handleTagToggle(tag)}
                    className={`px-3 py-1 rounded-full text-sm transition-colors ${
                      (formData.tags || []).includes(tag)
                        ? 'bg-blue-600 text-white'
                        : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                    }`}
                    disabled={isLoading}
                  >
                    {tag}
                  </button>
                ))}
              </div>
            </div>

            {/* Privacy Settings */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Privacy
              </label>
              <select
                value={formData.visibility}
                onChange={e =>
                  handleInputChange(
                    'visibility',
                    e.target.value as 'everyone' | 'followers' | 'private'
                  )
                }
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                disabled={isLoading}
              >
                {PRIVACY_OPTIONS.map(option => (
                  <option key={option.value} value={option.value}>
                    {option.label} - {option.description}
                  </option>
                ))}
              </select>
            </div>

            {/* How did it feel? */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                How did it feel? (Private)
              </label>
              <div className="flex space-x-2">
                {[1, 2, 3, 4, 5].map(rating => (
                  <button
                    key={rating}
                    type="button"
                    onClick={() => handleInputChange('howFelt', rating)}
                    className={`w-10 h-10 rounded-full border-2 transition-colors ${
                      formData.howFelt === rating
                        ? 'border-blue-500 bg-blue-500 text-white'
                        : 'border-gray-300 text-gray-600 hover:border-gray-400'
                    }`}
                    disabled={isLoading}
                  >
                    {rating}
                  </button>
                ))}
              </div>
            </div>

            {/* Private Notes */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Private Notes
              </label>
              <textarea
                value={formData.privateNotes || ''}
                onChange={e =>
                  handleInputChange('privateNotes', e.target.value)
                }
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                rows={2}
                placeholder="Any private notes about this session..."
                disabled={isLoading}
              />
            </div>

            {/* Error Messages */}
            {errors.submit && (
              <div className="text-red-500 text-sm">{errors.submit}</div>
            )}

            {/* Action Buttons */}
            <div className="flex justify-end space-x-3 pt-4 border-t">
              <button
                type="button"
                onClick={onCancel}
                className="px-4 py-2 text-gray-600 hover:text-gray-800 transition-colors"
                disabled={isLoading}
              >
                Cancel
              </button>
              <button
                type="submit"
                disabled={isLoading}
                className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 disabled:bg-gray-300 transition-colors"
              >
                {isLoading ? 'Saving...' : 'Save Session'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

'use client';

import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { useQueryClient } from '@tanstack/react-query';
import { SessionFormData, Project, CreateSessionData } from '@/types';
import { firebaseApi } from '@/lib/api';
import { useAuth } from '@/hooks/useAuth';
import { toast } from 'sonner';
import { ArrowLeft, Check, Image as ImageIcon, X } from 'lucide-react';
import Link from 'next/link';
import Image from 'next/image';
import { uploadImages, compressImage } from '@/lib/imageUpload';
import { parseLocalDateTime } from '@/lib/utils';
import Header from '@/components/HeaderComponent';
import { debug } from '@/lib/debug';

interface DeleteConfirmProps {
  isOpen: boolean;
  onClose: () => void;
  onDelete: () => void;
}

const DeleteConfirm: React.FC<DeleteConfirmProps> = ({
  isOpen,
  onClose,
  onDelete,
}) => {
  if (!isOpen) return null;

  return (
    <>
      {/* Backdrop */}
      <div
        className="fixed inset-0 bg-black/60 z-50 md:hidden flex items-center justify-center p-4"
        onClick={onClose}
      >
        {/* Modal */}
        <div
          className="bg-white rounded-lg p-5 w-full max-w-sm shadow-xl"
          onClick={e => e.stopPropagation()}
        >
          <h3 className="text-lg font-semibold text-gray-900 mb-2">
            Delete Image?
          </h3>
          <p className="text-sm text-gray-600 mb-6">
            This image will be removed from your session.
          </p>

          <div className="flex gap-3">
            <button
              onClick={onClose}
              className="flex-1 px-4 py-2.5 bg-gray-200 text-gray-800 rounded-lg font-medium hover:bg-gray-300 active:bg-gray-300 transition-colors"
            >
              Cancel
            </button>
            <button
              onClick={() => {
                onDelete();
                onClose();
              }}
              className="flex-1 px-4 py-2.5 bg-red-600 text-white rounded-lg font-medium hover:bg-red-700 active:bg-red-700 transition-colors"
            >
              Delete
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default function ManualSessionRecorder() {
  const router = useRouter();
  const { user } = useAuth();
  const queryClient = useQueryClient();

  const [projects, setProjects] = useState<Project[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [errors, setErrors] = useState<Record<string, string>>({});

  // Form state
  const [projectId, setProjectId] = useState('');
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [visibility, setVisibility] = useState<
    'everyone' | 'followers' | 'private'
  >('everyone');
  const [privateNotes, setPrivateNotes] = useState('');

  // Manual time inputs
  const [sessionDate, setSessionDate] = useState(
    new Date().toISOString().split('T')[0]
  );
  const [startTime, setStartTime] = useState(() => {
    const now = new Date();
    const hours = String(now.getHours()).padStart(2, '0');
    const minutes = String(now.getMinutes()).padStart(2, '0');
    return `${hours}:${minutes}`;
  });
  const [endTime, setEndTime] = useState('10:00');
  const [manualDurationHours, setManualDurationHours] = useState('1');
  const [manualDurationMinutes, setManualDurationMinutes] = useState('0');

  // Image upload state
  const [selectedImages, setSelectedImages] = useState<File[]>([]);
  const [imagePreviewUrls, setImagePreviewUrls] = useState<string[]>([]);
  const [isUploadingImages, setIsUploadingImages] = useState(false);
  const [actionSheetIndex, setActionSheetIndex] = useState<number | null>(null);

  // Load projects on mount
  useEffect(() => {
    const loadProjects = async () => {
      if (!user) return;

      try {
        const projectList = await firebaseApi.project.getProjects();
        setProjects(projectList);
      } catch (error) {
        debug.error('ManualSessionRecorder - Failed to load projects:', error);
      }
    };

    loadProjects();
  }, [user]);

  // Auto-generate title based on time of day and project
  useEffect(() => {
    if (!title && projectId) {
      const project = projects.find(p => p.id === projectId);
      const hour = new Date().getHours();

      let timeOfDay = '';
      if (hour < 12) timeOfDay = 'Morning';
      else if (hour < 17) timeOfDay = 'Afternoon';
      else timeOfDay = 'Evening';

      const smartTitle = project
        ? `${timeOfDay} ${project.name} Session`
        : `${timeOfDay} Work Session`;
      setTitle(smartTitle);
    }
  }, [projectId, projects]);

  const handleImageSelect = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);

    if (files.length + selectedImages.length > 3) {
      alert('Maximum 3 images allowed');
      return;
    }

    const validFiles: File[] = [];
    const previewUrls: string[] = [];

    for (const file of files) {
      try {
        // Convert HEIC to JPEG first
        let processedFile = file;

        // Check if it's HEIC and convert
        const isHeic =
          file.name.toLowerCase().endsWith('.heic') ||
          file.name.toLowerCase().endsWith('.heif') ||
          file.type === 'image/heic' ||
          file.type === 'image/heif';

        if (isHeic) {
          try {
            // Dynamically import heic2any - handle both default and named exports
            const heic2anyModule = await import('heic2any');
            const heic2any = heic2anyModule.default || heic2anyModule;

            const convertedBlob = await heic2any({
              blob: file,
              toType: 'image/jpeg',
              quality: 0.9,
            });
            const blob = Array.isArray(convertedBlob)
              ? convertedBlob[0]
              : convertedBlob;

            if (!blob) {
              throw new Error('Failed to convert HEIC file');
            }

            processedFile = new File(
              [blob],
              file.name.replace(/\.heic$/i, '.jpg').replace(/\.heif$/i, '.jpg'),
              { type: 'image/jpeg' }
            );
          } catch (error) {
            debug.error(
              'ManualSessionRecorder - Error converting HEIC:',
              error
            );
            // More helpful error message
            alert(
              `HEIC conversion is currently unavailable. Please convert ${file.name} to JPG or PNG before uploading, or try refreshing the page.`
            );
            continue;
          }
        }

        if (!processedFile.type.startsWith('image/')) {
          alert(`${file.name} is not an image file`);
          continue;
        }

        if (processedFile.size > 10 * 1024 * 1024) {
          alert(`${file.name} is too large. Maximum size is 10MB`);
          continue;
        }

        validFiles.push(processedFile);
        const previewUrl = URL.createObjectURL(processedFile);
        previewUrls.push(previewUrl);
      } catch (error) {
        debug.error('ManualSessionRecorder - Error processing image:', error);
        alert(`Failed to process ${file.name}`);
      }
    }

    if (validFiles.length > 0) {
      setSelectedImages(prev => [...prev, ...validFiles]);
      setImagePreviewUrls(prev => [...prev, ...previewUrls]);
    }
  };

  const handleRemoveImage = (index: number) => {
    const imageUrl = imagePreviewUrls[index];
    if (imageUrl) {
      URL.revokeObjectURL(imageUrl);
    }
    setSelectedImages(prev => prev.filter((_, i) => i !== index));
    setImagePreviewUrls(prev => prev.filter((_, i) => i !== index));
  };

  const calculateDuration = (): number => {
    // Use manual duration input
    const hours = parseInt(manualDurationHours) || 0;
    const minutes = parseInt(manualDurationMinutes) || 0;
    return hours * 3600 + minutes * 60;
  };

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    if (!projectId) {
      newErrors.projectId = 'Please select a project';
    }

    if (!title.trim()) {
      newErrors.title = 'Please enter a session title';
    }

    const duration = calculateDuration();
    if (duration <= 0) {
      newErrors.duration = 'Duration must be greater than 0';
    }

    if (!sessionDate) {
      newErrors.sessionDate = 'Please select a date';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm() || !user) {
      return;
    }

    setIsLoading(true);

    try {
      const duration = calculateDuration();

      // Parse the session date and start time in local timezone
      // NOTE: Using parseLocalDateTime to avoid UTC interpretation issues
      if (!sessionDate || !startTime) {
        setErrors({ sessionDate: 'Date and time are required' });
        setIsLoading(false);
        return;
      }
      const sessionDateTime = parseLocalDateTime(sessionDate, startTime);

      // Upload images first if any
      let imageUrls: string[] = [];
      if (selectedImages.length > 0) {
        setIsUploadingImages(true);
        try {
          const uploadResults = await uploadImages(selectedImages);
          imageUrls = uploadResults.map(result => result.url);
        } catch (error) {
          debug.error(
            'ManualSessionRecorder - Failed to upload images:',
            error
          );
          setErrors({ submit: 'Failed to upload images. Please try again.' });
          setIsUploadingImages(false);
          setIsLoading(false);
          return;
        }
        setIsUploadingImages(false);
      }

      const formData: CreateSessionData = {
        activityId: '',
        projectId,
        title,
        description,
        duration,
        startTime: sessionDateTime,
        tags: [],
        visibility,
        privateNotes,
        images: imageUrls,
      };

      // Create session with post
      await firebaseApi.session.createSessionWithPost(
        formData,
        description,
        visibility
      );

      // Invalidate caches to refresh UI immediately
      if (user) {
        queryClient.invalidateQueries({
          queryKey: ['user', 'sessions', user.id],
        });
        queryClient.invalidateQueries({ queryKey: ['user', 'stats', user.id] });
        queryClient.invalidateQueries({ queryKey: ['streak', user.id] });
        queryClient.invalidateQueries({ queryKey: ['feed'] });
        queryClient.invalidateQueries({ queryKey: ['sessions', 'feed'] });
      }

      // Show success message
      toast.success('Session created successfully!');

      // Redirect to home feed
      router.push('/');
    } catch (error) {
      debug.error(
        'ManualSessionRecorder - Failed to create manual session:',
        error
      );
      toast.error('Failed to create session. Please try again.');
      setErrors({ submit: 'Failed to create session. Please try again.' });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Main Header - hidden on mobile */}
      <div className="hidden md:block">
        <Header />
      </div>

      {/* Header with Cancel and Save Session title */}
      <div className="max-w-2xl mx-auto px-4 sm:px-6 pt-4">
        <div className="flex justify-between items-center mb-6">
          <button
            onClick={() => router.back()}
            className="text-[#007AFF] hover:text-[#0051D5] font-semibold text-base"
            disabled={isLoading}
          >
            Cancel
          </button>
          <h3 className="text-base font-semibold text-gray-900">
            Log Manual Session
          </h3>
          <div className="w-16"></div> {/* Spacer for centering */}
        </div>
      </div>

      {/* Form */}
      <div className="max-w-2xl mx-auto px-4 sm:px-6">
        <form onSubmit={handleSubmit} className="space-y-3 sm:space-y-4">
          {/* Session Title */}
          <input
            type="text"
            value={title}
            onChange={e => setTitle(e.target.value)}
            className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-[#007AFF] focus:border-[#007AFF] text-base ${
              errors.title ? 'border-red-500' : 'border-gray-300'
            }`}
            placeholder="Afternoon Work Session"
            disabled={isLoading}
          />
          {errors.title && (
            <p className="text-red-500 text-sm -mt-2">{errors.title}</p>
          )}

          {/* Description */}
          <textarea
            value={description}
            onChange={e => setDescription(e.target.value)}
            rows={3}
            className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#007AFF] focus:border-[#007AFF] text-base"
            placeholder="How'd it go? Share more about your session."
            disabled={isLoading}
          />

          {/* Project Selection */}
          <select
            value={projectId}
            onChange={e => setProjectId(e.target.value)}
            className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-[#007AFF] focus:border-[#007AFF] bg-white text-base ${
              errors.projectId ? 'border-red-500' : 'border-gray-300'
            }`}
            disabled={isLoading}
          >
            <option value="">Select an activity</option>
            {projects.map(project => (
              <option key={project.id} value={project.id}>
                {project.name}
              </option>
            ))}
          </select>
          {errors.projectId && (
            <p className="text-red-500 text-sm -mt-2">{errors.projectId}</p>
          )}

          {/* Image Upload */}
          <div className="max-w-md">
            <div className="space-y-3">
              {/* Image Previews */}
              {imagePreviewUrls.length > 0 && (
                <div className="grid grid-cols-3 gap-2">
                  {imagePreviewUrls.map((url, index) => (
                    <div
                      key={index}
