                      }`}
                      placeholder="Minutes"
                      disabled={isLoading}
                    />
                  </div>
                </div>
                {errors.duration && (
                  <p className="text-red-500 text-sm mt-1">{errors.duration}</p>
                )}
              </div>
            </div>
          </div>

          {/* Visibility */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Visibility
            </label>
            <select
              value={visibility}
              onChange={e =>
                setVisibility(
                  e.target.value as 'everyone' | 'followers' | 'private'
