interface NotificationSettingsProps {
  onClose?: () => void;
  isModal?: boolean;
}

const defaultPreferences: NotificationPreferences = {
  email: {
    follows: true,
    supports: true,
    comments: true,
    mentions: true,
    replies: true,
    achievements: true,
    streaks: true,
    groupPosts: false,
    challenges: true,
  },
  inApp: {
    follows: true,
    supports: true,
    comments: true,
    mentions: true,
    replies: true,
    achievements: true,
    streaks: true,
    groupPosts: true,
    challenges: true,
  }
};

export const NotificationSettings: React.FC<NotificationSettingsProps> = ({ 
  onClose, 
  isModal = false 
}) => {
  const [preferences, setPreferences] = useState<NotificationPreferences>(defaultPreferences);
  const [isLoading, setIsLoading] = useState(true);
  const [isSaving, setIsSaving] = useState(false);

  useEffect(() => {
    loadPreferences();
  }, []);

  const loadPreferences = async () => {
    try {
      setIsLoading(true);
      // TODO: Implement notification preferences API in firebaseUserApi
      // Need to create getNotificationPreferences() and updateNotificationPreferences() methods
      // For now, use defaults
      setPreferences(defaultPreferences);
    } catch (error) {
      debug.error('NotificationSettings - Failed to load notification preferences:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleToggle = (
    category: 'email' | 'inApp',
    key: keyof NotificationPreferences['email']
  ) => {
    setPreferences(prev => ({
      ...prev,
      [category]: {
        ...prev[category],
        [key]: !prev[category][key]
      }
    }));
  };

  const handleSave = async () => {
    try {
      setIsSaving(true);
      // TODO: Implement notification preferences save API in firebaseUserApi
      // Need updateNotificationPreferences() method to persist to Firestore
      alert('Notification preferences saved successfully!');
    } catch (error) {
      debug.error('NotificationSettings - Failed to save notification preferences:', error);
      alert('Failed to save notification preferences');
    } finally {
      setIsSaving(false);
    }
  };

  const NotificationToggle = ({
    label,
    description,
    emailEnabled,
    inAppEnabled,
    settingKey
