            <NotificationToggle
              label="Challenges"
              description="Challenge invitations and updates"
              emailEnabled={preferences.email.challenges}
              inAppEnabled={preferences.inApp.challenges}
              settingKey="challenges"
            />
          </SettingsRowGroup>
        </SettingsCardContent>
      </SettingsCard>

      {/* Save Button */}
      <div className="flex justify-end pt-2">
        <Button
          onClick={handleSave}
          disabled={isSaving}
