import PostStats from './PostStats';
import PostInteractions from './PostInteractions';
import CommentList from './CommentList';

interface PostCardProps {
  post: SessionWithDetails;
  onSupport: (postId: string) => Promise<void>;
  onRemoveSupport: (postId: string) => Promise<void>;
  onShare: (postId: string) => Promise<void>;
  className?: string;
  showComments?: boolean;
}

export const PostCard: React.FC<PostCardProps> = ({
  post,
  onSupport,
  onRemoveSupport,
  onShare,
  className = '',
  showComments = false,
}) => {
  const [showMenu, setShowMenu] = useState(false);
  const [showCommentSection, setShowCommentSection] = useState(showComments);

  const formatTimeAgo = (date: Date): string => {
    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    const postDate = new Date(
      date.getFullYear(),
      date.getMonth(),
      date.getDate()
    );

    // Format time as "h:mm am/pm"
    const timeStr = date.toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true,
    });

    // Check if today
    if (postDate.getTime() === today.getTime()) {
      return `Today at ${timeStr}`;
    }

    // Check if yesterday
    if (postDate.getTime() === yesterday.getTime()) {
      return `Yesterday at ${timeStr}`;
    }

    // Otherwise show full date: "Month Day, Year at h:mm am/pm"
    const dateStr = date.toLocaleDateString('en-US', {
      month: 'long',
      day: 'numeric',
      year: 'numeric',
    });

    return `${dateStr} at ${timeStr}`;
  };

  const getUserInitials = (user: User): string => {
    return user.name
      .split(' ')
      .map(word => word.charAt(0))
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  const getUserColor = (userId: string): string => {
    // Generate consistent color based on user ID
    const colors = [
      'bg-gradient-to-br from-orange-400 to-orange-600',
      'bg-gradient-to-br from-blue-400 to-blue-600',
      'bg-gradient-to-br from-green-400 to-green-600',
      'bg-gradient-to-br from-purple-400 to-purple-600',
      'bg-gradient-to-br from-pink-400 to-pink-600',
      'bg-gradient-to-br from-indigo-400 to-indigo-600',
      'bg-gradient-to-br from-teal-400 to-teal-600',
      'bg-gradient-to-br from-cyan-400 to-cyan-600',
    ];
    const hash = (userId || '')
      .split('')
      .reduce((a, b) => a + b.charCodeAt(0), 0);
    return (
      colors[hash % colors.length] ||
      colors[0] ||
      'bg-gradient-to-br from-orange-400 to-orange-600'
    );
  };

  return (
    <article
      className={`bg-white rounded-xl border border-gray-200 shadow-sm hover:shadow-md transition-all duration-200 overflow-hidden ${className}`}
    >
      {/* Post Header */}
      <div className="p-4">
        <div className="flex items-start justify-between">
          <div className="flex items-center space-x-3 flex-1 min-w-0">
            {/* User Avatar */}
            {post.user.profilePicture ? (
              <img
                src={post.user.profilePicture}
                alt={post.user.name}
                className="w-12 h-12 rounded-full object-cover border-2 border-gray-100"
              />
            ) : (
              <div
                className={`w-12 h-12 ${getUserColor(post.user.id)} rounded-full flex items-center justify-center shadow-sm`}
              >
                <span className="text-base font-semibold text-white">
                  {getUserInitials(post.user)}
                </span>
              </div>
            )}

            {/* User Info */}
            <div className="flex-1 min-w-0">
              <div className="flex items-center gap-2">
                <h3 className="font-bold text-gray-900 hover:text-orange-600 cursor-pointer transition-colors truncate">
                  {post.user.name}
                </h3>
              </div>
              <div className="flex items-center gap-2 text-sm text-gray-600">
                <span className="hover:text-gray-900 cursor-pointer transition-colors">
                  @{post.user.username}
                </span>
                <span>‚Ä¢</span>
                <span>{formatTimeAgo(post.createdAt)}</span>
                {post.user.location && (
                  <>
                    <span>‚Ä¢</span>
                    <span className="truncate">{post.user.location}</span>
                  </>
                )}
              </div>
            </div>
          </div>

          {/* Options Menu */}
          <div className="relative">
            <button
              onClick={() => setShowMenu(!showMenu)}
              className="text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-full p-1 transition-colors"
            >
              <svg
                className="w-5 h-5"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M12 5v.01M12 12v.01M12 19v.01M12 6a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2zm0 7a1 1 0 110-2 1 1 0 010 2z"
                />
              </svg>
            </button>

            {showMenu && (
              <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 py-1 z-10">
                <button className="w-full px-4 py-2 text-left text-sm text-gray-700 hover:bg-gray-50">
                  Edit post
                </button>
                <button className="w-full px-4 py-2 text-left text-sm text-gray-700 hover:bg-gray-50">
                  Delete post
                </button>
                <button className="w-full px-4 py-2 text-left text-sm text-red-600 hover:bg-gray-50">
                  Report post
                </button>
              </div>
            )}
          </div>
        </div>

        {/* Post Description */}
        {post.description && (
          <div className="mt-4">
            <p className="text-gray-900 leading-relaxed whitespace-pre-wrap">
              {post.description}
            </p>
          </div>
        )}
      </div>

      {/* Session Stats */}
      <div className="px-4 pb-4">
        <PostStats session={post} project={post.activity || post.project} />
      </div>

      {/* Support Summary */}
      {post.supportCount > 0 && (
        <div className="px-4 pb-3">
          <div className="flex items-center gap-2 text-sm text-gray-600">
            <div className="flex -space-x-2">
              {/* Show first 3 supporter avatars */}
              {[...Array(Math.min(3, post.supportCount))].map((_, i) => (
                <div
                  key={i}
                  className="w-6 h-6 rounded-full bg-gradient-to-br from-orange-400 to-orange-600 border-2 border-white"
                />
              ))}
            </div>
            <span className="font-medium">
              {post.supportCount}{' '}
              {post.supportCount === 1 ? 'person' : 'people'} gave support
            </span>
          </div>
        </div>
      )}

      {/* Post Interactions */}
      <PostInteractions
        postId={post.id}
        supportCount={post.supportCount}
        commentCount={post.commentCount}
        isSupported={post.isSupported || false}
        onSupport={onSupport}
        onRemoveSupport={onRemoveSupport}
        onShare={onShare}
        onCommentClick={() => setShowCommentSection(!showCommentSection)}
      />

      {/* Comments Section */}
      {showCommentSection && (
        <div className="border-t border-gray-200">
          <CommentList
            sessionId={post.id}
            initialCommentCount={post.commentCount}
          />
        </div>
      )}
    </article>
  );
};

export default PostCard;

'use client';

import React, { useState } from 'react';

interface PostInteractionsProps {
  postId: string;
  supportCount: number;
  commentCount: number;
  isSupported: boolean;
  onSupport: (postId: string) => Promise<void>;
  onRemoveSupport: (postId: string) => Promise<void>;
  onShare: (postId: string) => Promise<void>;
  onCommentClick?: () => void;
  className?: string;
}

export const PostInteractions: React.FC<PostInteractionsProps> = ({
  postId,
  supportCount,
  commentCount,
  isSupported,
  onSupport,
  onRemoveSupport,
  onShare,
  onCommentClick,
  className = ''
}) => {
  const [isSupporting, setIsSupporting] = useState(false);
  const [isSharing, setIsSharing] = useState(false);

  const handleSupport = async () => {
    if (isSupporting) return;

    setIsSupporting(true);
    try {
      if (isSupported) {
        await onRemoveSupport(postId);
      } else {
        await onSupport(postId);
      }
    } catch (error) {
      console.error('Failed to update support:', error);
    } finally {
      setIsSupporting(false);
    }
  };

  const handleShare = async () => {
    if (isSharing) return;

    setIsSharing(true);
    try {
      await onShare(postId);
    } catch (error) {
      console.error('Failed to share post:', error);
    } finally {
      setIsSharing(false);
    }
  };

  return (
    <div className={`flex items-center justify-between py-3 px-4 border-t border-gray-200 bg-gray-50/50 ${className}`}>
      {/* Left side - Support and Comments */}
      <div className="flex items-center gap-1">
        {/* Support Button */}
        <button
          onClick={handleSupport}
          disabled={isSupporting}
          className={`flex items-center gap-1.5 px-3 py-2 rounded-lg font-medium transition-all ${
            isSupported
              ? 'text-orange-600 bg-orange-50 hover:bg-orange-100'
              : 'text-gray-600 hover:bg-gray-100 hover:text-orange-600'
          } ${isSupporting ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer'}`}
        >
          <svg
            className="w-5 h-5"
            fill={isSupported ? 'currentColor' : 'none'}
            stroke="currentColor"
            viewBox="0 0 24 24"
            strokeWidth={isSupported ? 0 : 2}
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M4.318 6.318a4.5 4.5 0 000 6.364L12 20.364l7.682-7.682a4.5 4.5 0 00-6.364-6.364L12 7.636l-1.318-1.318a4.5 4.5 0 00-6.364 0z"
            />
          </svg>
          <span className="text-sm">
            {isSupported ? 'Supported' : 'Give Support'}
          </span>
        </button>

        {/* Comments */}
        <button
          onClick={onCommentClick}
          className="flex items-center gap-1.5 px-3 py-2 rounded-lg font-medium text-gray-600 hover:bg-gray-100 hover:text-blue-600 transition-all"
        >
          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" strokeWidth={2}>
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z"
            />
          </svg>
          <span className="text-sm">
            {commentCount === 1 ? 'Comment' : 'Comments'} {commentCount > 0 && `(${commentCount})`}
          </span>
        </button>
      </div>

      {/* Right side - Share */}
      <button
        onClick={handleShare}
        disabled={isSharing}
        className={`flex items-center gap-1.5 px-3 py-2 rounded-lg font-medium text-gray-600 hover:bg-gray-100 hover:text-green-600 transition-all ${
          isSharing ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer'
        }`}
      >
        <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" strokeWidth={2}>
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            d="M8.684 13.342C8.886 12.938 9 12.482 9 12c0-.482-.114-.938-.316-1.342m0 2.684a3 3 0 110-2.684m0 2.684l6.632 3.316m-6.632-6l6.632-3.316m0 0a3 3 0 105.367-2.684 3 3 0 00-5.367 2.684zm0 9.316a3 3 0 105.367 2.684 3 3 0 00-5.367-2.684z"
          />
        </svg>
        <span className="text-sm">Share</span>
      </button>
    </div>
  );
};

export default PostInteractions;

'use client';

import React, { useState } from 'react';
import { Session, Project } from '@/types';

interface PostStatsProps {
  session: Session;
  project: Project;
  className?: string;
}

export const PostStats: React.FC<PostStatsProps> = ({ 
  session, 
  project, 
  className = '' 
}) => {
  const [showAllTasks, setShowAllTasks] = useState(false);

  const formatDuration = (seconds: number): string => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);

    if (hours > 0) {
      return `${hours}h ${minutes}m`;
    }
    return `${minutes}m`;
  };

  // Task tracking not implemented at session level
  const completedTasks: any[] = [];
  const totalTasks = 0;

  return (
    <div className={`bg-gradient-to-br from-gray-50 to-gray-100 rounded-xl border border-gray-200 overflow-hidden ${className}`}>
      {/* Session Title with Project Badge */}
      <div className="px-4 py-3 bg-white border-b border-gray-200">
        <div className="flex items-center gap-3">
          <div 
            className="w-10 h-10 rounded-lg flex items-center justify-center text-white text-lg font-semibold shadow-sm flex-shrink-0"
            style={{ backgroundColor: project.color }}
          >
            {project.icon}
          </div>
          <div className="flex-1 min-w-0">
            <h3 className="font-bold text-gray-900 text-base leading-tight truncate">
              {session.title}
            </h3>
            <p className="text-sm text-gray-600 truncate">
              {project.name}
            </p>
          </div>
        </div>
      </div>

      {/* Stats Grid - Strava Style */}
      <div className="px-4 py-4">
        <div className="grid grid-cols-2 gap-4">
          {/* Duration */}
          <div>
            <div className="text-xs text-gray-500 uppercase tracking-wide font-medium mb-1">
              Duration
            </div>
            <div className="text-2xl font-bold text-gray-900">
              {formatDuration(session.duration)}
            </div>
          </div>

          {/* Tasks Completed */}
          <div>
            <div className="text-xs text-gray-500 uppercase tracking-wide font-medium mb-1">
              Tasks
            </div>
            <div className="text-2xl font-bold text-gray-900">
              {completedTasks.length}
              {totalTasks > 0 && (
                <span className="text-base font-normal text-gray-500 ml-1">
                  / {totalTasks}
                </span>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Completed Tasks Expandable List */}
      {completedTasks.length > 0 && (
        <div className="border-t border-gray-200">
          <button
            onClick={() => setShowAllTasks(!showAllTasks)}
            className="w-full px-4 py-3 text-left hover:bg-gray-50 transition-colors flex items-center justify-between group"
          >
            <span className="text-sm font-medium text-gray-700">
              {completedTasks.length} {completedTasks.length === 1 ? 'task' : 'tasks'} completed
            </span>
            <svg
              className={`w-5 h-5 text-gray-400 transition-transform ${showAllTasks ? 'rotate-180' : ''}`}
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
            </svg>
          </button>

          {showAllTasks && (
            <div className="px-4 pb-3 space-y-2">
              {completedTasks.map((task, index) => (
                <div key={index} className="flex items-start gap-2">
                  <div className="flex-shrink-0 mt-0.5">
                    <svg className="w-4 h-4 text-green-500" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                    </svg>
                  </div>
                  <span className="text-sm text-gray-700 flex-1">{task.name}</span>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Tags */}
      {session.tags && session.tags.length > 0 && (
        <div className="px-4 pb-4">
          <div className="flex flex-wrap gap-2">
            {session.tags.map((tag, index) => (
              <span
                key={index}
                className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-orange-100 text-orange-700 border border-orange-200"
              >
                #{tag}
              </span>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default PostStats;

'use client';

import React, { useState, useEffect } from 'react';
import { PrivacySettings as PrivacySettingsType, UserProfile } from '@/types';
import { firebaseUserApi } from '@/lib/api';
import { useAuth } from '@/hooks/useAuth';
import { Button } from '@/components/ui/button';
import {
  SettingsSection,
  SettingsHeader,
  SettingsCard,
  SettingsCardHeader,
  SettingsCardContent,
  SettingsField
} from '@/components/ui/settings-section';
import {
  Shield,
  Globe,
  Users,
  Lock,
  Eye,
  UserX,
  Check,
  Activity,
  FolderKanban
} from 'lucide-react';
import { toast } from 'sonner';

interface PrivacySettingsProps {
  onClose?: () => void;
  isModal?: boolean;
}

export const PrivacySettings: React.FC<PrivacySettingsProps> = ({ 
  onClose, 
  isModal = false 
}) => {
  const [settings, setSettings] = useState<PrivacySettingsType>({
    profileVisibility: 'everyone',
    activityVisibility: 'everyone',
    projectVisibility: 'everyone',
    blockedUsers: [],
  });
  const [blockedUsers, setBlockedUsers] = useState<UserProfile[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isSaving, setIsSaving] = useState(false);

  useEffect(() => {
    loadSettings();
  }, []);

  const loadSettings = async () => {
    try {
      setIsLoading(true);
      // TODO: Implement privacy settings fetching when Firebase getPrivacySettings() is available
      // Currently uses firebaseUserApi.getPrivacySettings() - verify implementation status
        const settings = await firebaseUserApi.getPrivacySettings();
        setSettings(settings);
    } catch (error) {
      console.error('Failed to load privacy settings:', error);
      toast.error('Failed to load privacy settings');
    } finally {
      setIsLoading(false);
    }
  };

  const loadBlockedUsers = async (): Promise<UserProfile[]> => {
    try {
      // This would be a new API endpoint to get blocked users
      // For now, return empty array
      return [];
    } catch (error) {
      console.error('Failed to load blocked users:', error);
      return [];
    }
  };

  const handleSettingChange = (key: keyof PrivacySettingsType, value: string) => {
    setSettings(prev => ({ ...prev, [key]: value }));
  };

  const handleSave = async () => {
    try {
      setIsSaving(true);
      await firebaseUserApi.updatePrivacySettings(settings);
      toast.success('Privacy settings updated successfully');
    } catch (error) {
      console.error('Failed to save privacy settings:', error);
      toast.error('Failed to save privacy settings');
    } finally {
      setIsSaving(false);
    }
  };

  const handleUnblockUser = async (userId: string) => {
    try {
      // TODO: Implement unblock user API endpoint in firebaseUserApi
      // Need to add unblockUser() method to handle Firebase user blocking/unblocking
      setBlockedUsers(prev => prev.filter(user => user.id !== userId));
      setSettings(prev => ({
        ...prev,
        blockedUsers: prev.blockedUsers.filter(id => id !== userId)
      }));
      toast.success('User unblocked successfully');
    } catch (error) {
      console.error('Failed to unblock user:', error);
      toast.error('Failed to unblock user');
    }
  };

  const getVisibilityIcon = (visibility: string) => {
    switch (visibility) {
      case 'everyone':
        return <Globe className="w-4 h-4" />;
      case 'followers':
        return <Users className="w-4 h-4" />;
      case 'private':
        return <Lock className="w-4 h-4" />;
      default:
        return <Eye className="w-4 h-4" />;
    }
  };

  const getVisibilityDescription = (visibility: string, type: string) => {
    const descriptions = {
      profileVisibility: {
        everyone: 'Anyone can view your profile and basic information',
        followers: 'Only people you follow back can view your profile',
        private: 'Only you can view your profile'
      },
      activityVisibility: {
        everyone: 'Your activity is visible to everyone',
        followers: 'Your activity is only visible to your followers',
        private: 'Your activity is private'
      },
      projectVisibility: {
        everyone: 'Your projects are visible to everyone',
        followers: 'Your projects are only visible to your followers',
        private: 'Your projects are private'
      }
    };

    return descriptions[type as keyof typeof descriptions]?.[visibility as keyof typeof descriptions.profileVisibility] || '';
  };

  if (isLoading) {
    return (
      <SettingsSection>
        {[1, 2, 3].map((i) => (
          <SettingsCard key={i} className="animate-pulse">
            <SettingsCardHeader title="" description="" />
            <SettingsCardContent>
              <div className="space-y-3">
                <div className="h-10 bg-gray-200 rounded" />
                <div className="h-4 bg-gray-200 rounded w-1/2" />
              </div>
            </SettingsCardContent>
          </SettingsCard>
        ))}
      </SettingsSection>
    );
  }

  return (
    <SettingsSection>
      {/* Header */}
      <div className="flex items-center justify-between">
        <SettingsHeader
          icon={Shield}
          title="Privacy Controls"
          description="Control who can see your profile, activity, and projects"
        />
        {isModal && onClose && (
          <Button variant="ghost" onClick={onClose}>
            Close
          </Button>
        )}
      </div>

      {/* Profile Visibility */}
      <SettingsCard>
        <SettingsCardHeader
          icon={Eye}
          title="Profile Visibility"
          description="Control who can view your profile information"
        />
        <SettingsCardContent>
          <SettingsField label="Profile Access">
            <select
              value={settings.profileVisibility}
              onChange={(e) => handleSettingChange('profileVisibility', e.target.value)}
              className="flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50"
            >
              <option value="everyone">Everyone - Anyone can view your profile</option>
              <option value="followers">Followers Only - Only people you follow back</option>
              <option value="private">Private - Only you can view your profile</option>
            </select>
          </SettingsField>
        </SettingsCardContent>
      </SettingsCard>

      {/* Activity Visibility */}
      <SettingsCard>
        <SettingsCardHeader
          icon={Activity}
          title="Activity Visibility"
          description="Control who can see your productivity activity and sessions"
        />
        <SettingsCardContent>
          <SettingsField label="Activity Access">
            <select
              value={settings.activityVisibility}
              onChange={(e) => handleSettingChange('activityVisibility', e.target.value)}
              className="flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50"
            >
              <option value="everyone">Everyone - Your activity is public</option>
              <option value="followers">Followers Only - Only your followers can see</option>
              <option value="private">Private - Your activity is completely private</option>
            </select>
          </SettingsField>
        </SettingsCardContent>
      </SettingsCard>

      {/* Project Visibility */}
      <SettingsCard>
        <SettingsCardHeader
          icon={FolderKanban}
          title="Project Visibility"
          description="Control who can see your projects and their details"
        />
        <SettingsCardContent>
          <SettingsField label="Project Access">
            <select
              value={settings.projectVisibility}
              onChange={(e) => handleSettingChange('projectVisibility', e.target.value)}
              className="flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50"
            >
              <option value="everyone">Everyone - Your projects are public</option>
