      {/* Delete Confirmation Modal */}
      {deleteConfirm && (
        <div
          className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 p-4 animate-in fade-in duration-200"
          onClick={e => {
            if (e.target === e.currentTarget) {
              setDeleteConfirm(null);
            }
          }}
