  const [showMenu, setShowMenu] = useState(false);
  const [isFollowing, setIsFollowing] = useState(false);
  const [isFollowLoading, setIsFollowLoading] = useState(false);
  const [isExpanded, setIsExpanded] = useState(false);
  const [localCommentCount, setLocalCommentCount] = useState(
    session.commentCount || 0
  );
  const [showLikesModal, setShowLikesModal] = useState(false);
  const [showCommentsModal, setShowCommentsModal] = useState(false);
  const menuRef = useRef<HTMLDivElement>(null);
  const commentSectionRef = useRef<HTMLDivElement>(null);

  // Check if user is following the session author
  useEffect(() => {
    const checkFollowStatus = async () => {
      if (user && session.userId !== user.id && showGroupInfo) {
        try {
          const isUserFollowing = await firebaseApi.user.isFollowing(
            user.id,
            session.userId
          );
          setIsFollowing(isUserFollowing);
        } catch (error) {
          // Error checking follow status - silently fail
        }
      }
    };
    checkFollowStatus();
  }, [user, session.userId, showGroupInfo]);

  // Handle follow/unfollow
  const handleFollowToggle = async (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    if (!user || isFollowLoading) return;

    setIsFollowLoading(true);
    try {
      if (isFollowing) {
        await firebaseApi.user.unfollowUser(session.userId);
        setIsFollowing(false);
      } else {
        await firebaseApi.user.followUser(session.userId);
        setIsFollowing(true);
      }
    } catch (error) {
      // Error toggling follow - silently fail
    } finally {
      setIsFollowLoading(false);
    }
  };

  // Close menu when clicking outside or pressing escape
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (menuRef.current && !menuRef.current.contains(event.target as Node)) {
        setShowMenu(false);
      }
    };

    const handleEscapeKey = (event: KeyboardEvent) => {
      if (event.key === 'Escape') {
        setShowMenu(false);
      }
    };

    if (showMenu) {
      document.addEventListener('mousedown', handleClickOutside);
      document.addEventListener('keydown', handleEscapeKey);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleEscapeKey);
    };
  }, [showMenu]);

  // Guard: Return null if user data is missing (during architecture migration)
  if (!session.user) {
    return null;
  }

  return (
    <article
      className={cn(
        'bg-white md:rounded-lg md:border md:border-gray-200 md:shadow-sm mb-0 md:mb-4 border-b-[6px] border-gray-200 md:border-b-0 hover:shadow-md transition-shadow',
        className
      )}
    >
      {/* Session Header */}
      <div className="flex items-center justify-between px-4 pt-4 pb-3">
        <PrefetchLink
          href={`/profile/${session.user.username}`}
          prefetchProfile={session.user.username}
          prefetchUserId={session.user.id}
          className="flex items-center gap-2 md:gap-3 min-w-0 flex-1"
        >
          {/* User Avatar */}
          {session.user.profilePicture ? (
            <div className="w-10 h-10 min-w-[2.5rem] aspect-square rounded-full overflow-hidden flex-shrink-0 ring-2 ring-white">
              <Image
                src={session.user.profilePicture}
                alt={session.user.name}
                width={40}
                height={40}
                quality={90}
                className="w-full h-full object-cover"
                priority={isAboveFold || priority}
                loading={isAboveFold || priority ? 'eager' : 'lazy'}
              />
            </div>
          ) : (
            <div className="w-10 h-10 min-w-[2.5rem] aspect-square bg-gray-100 rounded-full flex items-center justify-center flex-shrink-0 ring-2 ring-white">
              <span className="text-gray-600 font-semibold text-sm">
                {getUserInitials(session.user.name)}
              </span>
            </div>
          )}

          {/* User Info */}
          <div className="min-w-0 flex-1">
            <div className="flex items-center gap-2">
              <span className="font-semibold text-gray-900 text-sm md:text-base hover:underline truncate">
                {session.user.name}
              </span>
              {/* Follow button - Mobile only when showGroupInfo is true */}
              {showGroupInfo && user && session.userId !== user.id && (
                <button
                  onClick={handleFollowToggle}
                  disabled={isFollowLoading}
                  className={cn(
                    'md:hidden text-xs font-semibold transition-colors duration-200 whitespace-nowrap flex-shrink-0',
                    isFollowing
                      ? 'text-gray-600 hover:text-gray-900'
                      : 'text-[#007AFF] hover:text-[#0051D5]'
                  )}
                >
                  {isFollowing ? 'Following' : 'Follow'}
                </button>
              )}
            </div>
            <div className="text-xs text-gray-500">
              {formatSessionDate(session.createdAt)}
            </div>
          </div>
        </PrefetchLink>

        {/* Options Menu */}
        <div className="relative" ref={menuRef}>
          <button
            onClick={() => setShowMenu(!showMenu)}
            className="text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-lg p-2 transition-colors duration-200 min-h-[44px] min-w-[44px]"
            aria-label="Session options"
            aria-expanded={showMenu}
            aria-haspopup="true"
          >
            <MoreVertical className="w-5 h-5" aria-hidden="true" />
          </button>

          {showMenu && (
            <div
              className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 py-1 z-10"
              role="menu"
              aria-label="Session options menu"
            >
              {onEdit && (
                <button
                  onClick={() => {
                    onEdit(session.id);
                    setShowMenu(false);
                  }}
                  className="w-full px-4 py-2 text-left text-sm text-gray-700 hover:bg-gray-50 transition-colors duration-200"
                  role="menuitem"
                >
                  Edit session
                </button>
              )}
              {onDelete && (
                <button
                  onClick={() => {
                    onDelete(session.id);
                    setShowMenu(false);
                  }}
                  className="w-full px-4 py-2 text-left text-sm text-red-600 hover:bg-gray-50 transition-colors duration-200"
                  role="menuitem"
                >
                  Delete session
                </button>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Title and Description */}
      <Link
        href={`/sessions/${session.id}`}
        className="px-4 pb-3 block cursor-pointer"
      >
        <h3 className="text-lg sm:text-xl md:text-2xl font-bold text-gray-900 mb-1 leading-tight hover:text-[#007AFF] transition-colors duration-200">
          {session.title || 'Focus Session'}
        </h3>
        {session.description && (
          <div>
            <p
              className={cn(
                'text-gray-600 text-sm md:text-base whitespace-pre-wrap break-words',
                !isExpanded &&
                  session.description.length > 280 &&
                  'line-clamp-3 sm:line-clamp-4'
              )}
            >
              {session.description.length > 1000
                ? session.description.slice(0, 1000)
                : session.description}
            </p>
            {session.description.length > 280 && (
              <button
                onClick={e => {
                  e.preventDefault();
                  setIsExpanded(!isExpanded);
                }}
                className="text-[#007AFF] text-sm font-semibold mt-1 hover:underline transition-colors duration-200 min-h-[44px] flex items-center"
                aria-expanded={isExpanded}
                aria-label={
                  isExpanded ? 'Show less description' : 'Show more description'
                }
              >
                {isExpanded ? 'Show less' : 'Show more'}
              </button>
            )}
          </div>
        )}
      </Link>

      {/* Image Gallery */}
      {!isEmpty(session.images) && (
        <div className="px-4 pb-4">
          <ImageGallery
            images={session.images || []}
            priority={isAboveFold || priority}
          />
        </div>
      )}

      {/* Stats - Strava style */}
      <Link
        href={`/sessions/${session.id}`}
        className="px-4 pb-2 block cursor-pointer hover:bg-gray-50/50 transition-colors duration-200"
      >
        <div className="grid grid-cols-2 gap-4">
          <div>
            <div className="text-xs text-gray-500 mb-1">Time</div>
            <div className="text-base font-semibold text-gray-900">
              {formatDuration(session.duration)}
            </div>
          </div>
          <div className="min-w-0">
            <div className="text-xs text-gray-500 mb-1">Activity</div>
            <div
              className="text-base font-semibold text-gray-900 truncate"
              title={session.activity?.name || session.project?.name || 'N/A'}
            >
              {session.activity?.name || session.project?.name || 'N/A'}
            </div>
          </div>
        </div>
      </Link>

      {/* Interactions */}
      <SessionInteractions
        sessionId={session.id}
        supportCount={session.supportCount}
        commentCount={localCommentCount}
        isSupported={session.isSupported || false}
        supportedBy={session.supportedBy}
        onSupport={onSupport}
        onRemoveSupport={onRemoveSupport}
        onShare={onShare}
        onShareImage={() => router.push(`/sessions/${session.id}/share`)}
        isOwnPost={session.userId === user?.id}
        onCommentClick={() => setShowCommentsModal(true)}
        onLikesClick={() => setShowLikesModal(true)}
        onViewAllCommentsClick={() => setShowCommentsModal(true)}
      />

      {/* Top Comments Section */}
      <div ref={commentSectionRef}>
        <TopComments
          sessionId={session.id}
          totalCommentCount={localCommentCount}
          onCommentCountChange={setLocalCommentCount}
        />
      </div>

      {/* Likes Modal */}
      <LikesModal
        isOpen={showLikesModal}
        onClose={() => setShowLikesModal(false)}
        userIds={session.supportedBy || []}
        totalLikes={session.supportCount}
      />

      {/* Comments Modal */}
      <CommentsModal
        isOpen={showCommentsModal}
        onClose={() => setShowCommentsModal(false)}
        sessionId={session.id}
        session={session}
        totalCommentCount={localCommentCount}
        onCommentCountChange={setLocalCommentCount}
      />
    </article>
  );
};

export default SessionCard;

'use client';

import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { Session, Project, SessionFilters, SessionSort } from '@/types';
import { firebaseProjectApi, firebaseSessionApi } from '@/lib/api';
import { MoreVertical } from 'lucide-react';
import ConfirmDialog from './ConfirmDialog';

export const SessionHistory: React.FC = () => {
  const router = useRouter();
  const [sessions, setSessions] = useState<Session[]>([]);
  const [projects, setProjects] = useState<Project[]>([]);
  const [filters, setFilters] = useState<SessionFilters>({});
  const [sort, setSort] = useState<SessionSort>({ field: 'startTime', direction: 'desc' });
  const [isLoading, setIsLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalCount, setTotalCount] = useState(0);
  const [hasMore, setHasMore] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [showMenuForSession, setShowMenuForSession] = useState<string | null>(null);
  const [deleteConfirmSession, setDeleteConfirmSession] = useState<string | null>(null);
  const [isDeleting, setIsDeleting] = useState(false);

  // Load initial data
  useEffect(() => {
    const loadInitialData = async () => {
      try {
        setIsLoading(true);
        // Load projects and user's sessions from Firebase
        const [projectsData, sessionsResp] = await Promise.all([
          firebaseProjectApi.getProjects(),
          firebaseSessionApi.getSessions(1, 20, {})
        ]);

        setProjects(projectsData);
        setSessions(sessionsResp.sessions);
        setTotalCount(sessionsResp.totalCount);
        setHasMore(sessionsResp.hasMore);
      } catch (error) {
        console.error('Failed to load session history:', error);
      } finally {
        setIsLoading(false);
      }
    };

    loadInitialData();
  }, []);

  // Load sessions when filters or sort change
  useEffect(() => {
    const loadSessions = async () => {
      try {
        setIsLoading(true);
        const sessionsResp = await firebaseSessionApi.getSessions(
          currentPage,
          20,
          { ...filters, search: searchQuery }
        );

        setSessions(sessionsResp.sessions);
        setTotalCount(sessionsResp.totalCount);
        setHasMore(sessionsResp.hasMore);
      } catch (error) {
        console.error('Failed to load sessions:', error);
      } finally {
        setIsLoading(false);
      }
    };

    loadSessions();
  }, [filters, sort, currentPage, searchQuery]);

  const handleFiltersChange = (newFilters: SessionFilters) => {
    setFilters(newFilters);
    setCurrentPage(1);
  };

  const handleSortChange = (newSort: SessionSort) => {
    setSort(newSort);
    setCurrentPage(1);
  };

  const handleSessionEdit = (sessionId: string) => {
    router.push(`/sessions/${sessionId}/edit`);
  };

  const handleSessionDelete = (sessionId: string) => {
    setDeleteConfirmSession(sessionId);
  };

  const confirmDelete = async () => {
    if (!deleteConfirmSession) return;

    try {
      setIsDeleting(true);
      await firebaseSessionApi.deleteSession(deleteConfirmSession);

      // Reload sessions from Firebase
      const sessionsResp = await firebaseSessionApi.getSessions(
        currentPage,
        20,
        { ...filters, search: searchQuery }
      );
      setSessions(sessionsResp.sessions);
      setTotalCount(sessionsResp.totalCount);
      setHasMore(sessionsResp.hasMore);
      setDeleteConfirmSession(null);
    } catch (error) {
      console.error('Failed to delete session:', error);
    } finally {
      setIsDeleting(false);
    }
  };

  const formatDuration = (seconds: number): string => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;

    if (hours > 0) {
      return `${hours}h ${minutes}m`;
    } else if (minutes > 0) {
      return `${minutes}m ${secs}s`;
    } else {
      return `${secs}s`;
    }
  };

  const formatDate = (date: Date): string => {
    return new Date(date).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getProjectName = (projectId: string | undefined): string => {
    if (!projectId) return 'Unknown Project';
    const project = projects.find(p => p.id === projectId);
    return project ? project.name : 'Unknown Project';
  };

  const getVisibilityIcon = (visibility: string) => {
    switch (visibility) {
      case 'everyone':
        return 'üåç';
      case 'followers':
        return 'üë•';
      case 'private':
        return 'üîí';
      default:
        return 'üîí';
    }
  };

  const getFeelingEmoji = (rating?: number) => {
    if (!rating) return 'üòê';
    switch (rating) {
      case 1: return 'üòû';
      case 2: return 'üòï';
      case 3: return 'üòê';
      case 4: return 'üôÇ';
      case 5: return 'üòä';
      default: return 'üòê';
    }
  };

  if (isLoading && sessions.length === 0) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Filters and Search */}
      <div className="bg-white rounded-lg shadow p-6">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          {/* Search */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Search
            </label>
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Search sessions..."
            />
          </div>

          {/* Project Filter */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Project
            </label>
            <select
              value={filters.projectId || ''}
              onChange={(e) => handleFiltersChange({ ...filters, projectId: e.target.value || undefined })}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <option value="">All Projects</option>
              {projects.map((project) => (
                <option key={project.id} value={project.id}>
                  {project.name}
                </option>
              ))}
            </select>
          </div>

          {/* Date Range */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              From Date
            </label>
            <input
              type="date"
              value={filters.dateFrom ? filters.dateFrom.toISOString().split('T')[0] : ''}
              onChange={(e) => handleFiltersChange({ 
                ...filters, 
                dateFrom: e.target.value ? new Date(e.target.value) : undefined 
              })}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              To Date
            </label>
            <input
              type="date"
              value={filters.dateTo ? filters.dateTo.toISOString().split('T')[0] : ''}
              onChange={(e) => handleFiltersChange({ 
                ...filters, 
                dateTo: e.target.value ? new Date(e.target.value) : undefined 
              })}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
        </div>

        {/* Sort Options */}
        <div className="mt-4 flex items-center space-x-4">
          <label className="text-sm font-medium text-gray-700">Sort by:</label>
          <select
            value={`${sort.field}-${sort.direction}`}
            onChange={(e) => {
              const [field, direction] = e.target.value.split('-');
              handleSortChange({ field: field as any, direction: direction as any });
            }}
            className="px-3 py-1 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="startTime-desc">Date (Newest First)</option>
            <option value="startTime-asc">Date (Oldest First)</option>
            <option value="duration-desc">Duration (Longest First)</option>
            <option value="duration-asc">Duration (Shortest First)</option>
            <option value="title-asc">Title (A-Z)</option>
            <option value="title-desc">Title (Z-A)</option>
          </select>
        </div>
      </div>

      {/* Session List */}
      <div className="bg-white rounded-lg shadow">
        {sessions.length === 0 ? (
          <div className="p-8 text-center text-gray-500">
            <p className="text-lg">No sessions found</p>
            <p className="text-sm mt-2">Try adjusting your filters or start a new session</p>
          </div>
        ) : (
          <div className="divide-y divide-gray-200">
            {sessions.map((session) => (
              <div key={session.id} className="p-6 hover:bg-gray-50 transition-colors">
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-center space-x-3 mb-2">
                      <h3 className="text-lg font-medium text-gray-900">{session.title}</h3>
                      <span className="text-sm text-gray-500">
                        {getVisibilityIcon(session.visibility)}
                      </span>
                      <span className="text-sm text-gray-500">
                        {getFeelingEmoji(session.howFelt)}
                      </span>
                    </div>

                    <div className="flex items-center space-x-4 text-sm text-gray-600 mb-3">
                      <span>{getProjectName(session.projectId)}</span>
                      <span>‚Ä¢</span>
                      <span>{formatDate(session.startTime)}</span>
                      <span>‚Ä¢</span>
                      <span className="font-medium">{formatDuration(session.duration)}</span>
                    </div>

                    {session.description && (
                      <p className="text-gray-700 text-sm mb-3">{session.description}</p>
                    )}

                    {session.tags && session.tags.length > 0 && (
                      <div className="flex flex-wrap gap-1 mb-3">
                        {session.tags.map((tag) => (
                          <span
                            key={tag}
                            className="px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full"
                          >
                            {tag}
                          </span>
                        ))}
                      </div>
                    )}
                  </div>

                  {/* Options Menu */}
                  <div className="relative">
                    <button
                      onClick={() => setShowMenuForSession(showMenuForSession === session.id ? null : session.id)}
                      className="text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-full p-2 transition-colors"
                    >
                      <MoreVertical className="w-5 h-5" />
                    </button>

                    {showMenuForSession === session.id && (
                      <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg border border-gray-200 py-1 z-10">
                        <button
                          onClick={() => {
                            handleSessionEdit(session.id);
                            setShowMenuForSession(null);
                          }}
                          className="w-full px-4 py-2 text-left text-sm text-gray-700 hover:bg-gray-50"
                        >
                          Edit session
                        </button>
                        <button
                          onClick={() => {
                            handleSessionDelete(session.id);
                            setShowMenuForSession(null);
                          }}
                          className="w-full px-4 py-2 text-left text-sm text-red-600 hover:bg-gray-50"
                        >
                          Delete session
                        </button>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Pagination */}
      {totalCount > 20 && (
        <div className="flex items-center justify-between">
          <div className="text-sm text-gray-700">
            Showing {sessions.length} of {totalCount} sessions
          </div>
          <div className="flex space-x-2">
            <button
              onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
              disabled={currentPage === 1}
              className="px-3 py-1 border border-gray-300 rounded-md text-sm hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              Previous
            </button>
            <span className="px-3 py-1 text-sm text-gray-700">
              Page {currentPage}
            </span>
            <button
              onClick={() => setCurrentPage(currentPage + 1)}
              disabled={!hasMore}
              className="px-3 py-1 border border-gray-300 rounded-md text-sm hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              Next
            </button>
          </div>
        </div>
      )}

      {/* Delete Confirmation Dialog */}
      <ConfirmDialog
        isOpen={deleteConfirmSession !== null}
        onClose={() => setDeleteConfirmSession(null)}
        onConfirm={confirmDelete}
        title="Delete Session"
        message="Are you sure you want to delete this session? This action cannot be undone and all associated data will be permanently removed."
        confirmText="Delete Session"
        cancelText="Cancel"
        variant="danger"
        isLoading={isDeleting}
      />
    </div>
  );
};

'use client';

import React, { useState, useRef, useEffect } from 'react';
import { ThumbsUp, MessageSquare, Share2, Image as ImageIcon, Link as LinkIcon, Copy } from 'lucide-react';

interface SessionInteractionsProps {
  sessionId: string;
  supportCount: number;
  commentCount: number;
  isSupported: boolean;
  supportedBy?: string[];
  onSupport: (sessionId: string) => Promise<void>;
  onRemoveSupport: (sessionId: string) => Promise<void>;
  onShare: (sessionId: string) => Promise<void>;
  onShareImage?: () => void;
  isOwnPost?: boolean;
  onCommentClick?: () => void;
  onLikesClick?: () => void;
  onViewAllCommentsClick?: () => void;
  className?: string;
}

export const SessionInteractions: React.FC<SessionInteractionsProps> = ({
  sessionId,
  supportCount,
  commentCount,
  isSupported,
  supportedBy = [],
  onSupport,
  onRemoveSupport,
  onShare,
  onShareImage,
  isOwnPost = false,
  onCommentClick,
  onLikesClick,
  onViewAllCommentsClick,
  className = ''
}) => {
  const [isSupporting, setIsSupporting] = useState(false);
  const [isSharing, setIsSharing] = useState(false);
  const [showShareMenu, setShowShareMenu] = useState(false);
  const shareMenuRef = useRef<HTMLDivElement>(null);

  const handleSupport = async () => {
    if (isSupporting) return;

    setIsSupporting(true);
    try {
      if (isSupported) {
        await onRemoveSupport(sessionId);
      } else {
        await onSupport(sessionId);
      }
    } catch (error) {
      console.error('Failed to update support:', error);
    } finally {
      setIsSupporting(false);
    }
  };

  const handleShare = async () => {
    if (isSharing) return;

    setIsSharing(true);
    try {
      await onShare(sessionId);
    } catch (error) {
      console.error('Failed to share session:', error);
    } finally {
      setIsSharing(false);
      setShowShareMenu(false);
    }
  };

  const handleShareImage = () => {
    if (onShareImage) {
      onShareImage();
      setShowShareMenu(false);
    }
  };

  const handleCopyLink = async () => {
    try {
      const sessionUrl = `${window.location.origin}/sessions/${sessionId}`;
      await navigator.clipboard.writeText(sessionUrl);
      setShowShareMenu(false);
      // Optional: Show a toast notification here
    } catch (error) {
      console.error('Failed to copy link:', error);
    }
  };

  // Close share menu when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (shareMenuRef.current && !shareMenuRef.current.contains(event.target as Node)) {
        setShowShareMenu(false);
      }
    };

    const handleEscapeKey = (event: KeyboardEvent) => {
      if (event.key === 'Escape') {
        setShowShareMenu(false);
      }
    };

    if (showShareMenu) {
      document.addEventListener('mousedown', handleClickOutside);
      document.addEventListener('keydown', handleEscapeKey);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
      document.removeEventListener('keydown', handleEscapeKey);
    };
  }, [showShareMenu]);

  return (
    <div className={className}>
      {/* Desktop layout - original Strava style */}
      <div className="hidden md:block">
        {/* Counts section - top left */}
        <div className="px-4 pb-3 flex items-center justify-between text-sm text-gray-600">
          <div className="flex items-center gap-4">
            {supportCount > 0 && (
              <button
                onClick={onLikesClick}
                className="hover:text-gray-900 hover:underline transition-colors font-medium"
                aria-label={`View ${supportCount} ${supportCount === 1 ? 'person who liked this' : 'people who liked this'}`}
              >
                {supportCount} {supportCount === 1 ? 'like' : 'likes'}
              </button>
            )}
          </div>
        </div>

        {/* Action buttons - Strava style (bottom right on desktop) */}
        <div className="px-4 py-3">
          <div className="flex items-center justify-end gap-2">
            {/* Like button */}
            <button
              onClick={handleSupport}
              disabled={isSupporting}
              className={`flex items-center gap-1.5 px-3 py-1.5 rounded hover:bg-gray-100 transition-colors ${
                isSupporting ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer'
              }`}
              aria-label={isSupported ? `Unlike session (${supportCount} ${supportCount === 1 ? 'like' : 'likes'})` : `Like session (${supportCount} ${supportCount === 1 ? 'like' : 'likes'})`}
            >
              <ThumbsUp
                className={`w-5 h-5 transition-colors ${
                  isSupported
                    ? 'fill-gray-700 text-gray-700'
                    : 'text-gray-600'
                }`}
                strokeWidth={1.5}
                aria-hidden="true"
              />
              <span className={`text-sm font-medium ${
                isSupported ? 'text-gray-700' : 'text-gray-600'
              }`} aria-hidden="true">
                {supportCount > 0 ? supportCount : ''}
              </span>
            </button>

            {/* Comment button */}
            <button
              onClick={onCommentClick}
              className="flex items-center gap-1.5 px-3 py-1.5 rounded hover:bg-gray-100 transition-colors"
              aria-label={`View and add comments (${commentCount} ${commentCount === 1 ? 'comment' : 'comments'})`}
            >
              <MessageSquare
                className="w-5 h-5 text-gray-600"
                strokeWidth={1.5}
                aria-hidden="true"
              />
              <span className="text-sm font-medium text-gray-600" aria-hidden="true">
                {commentCount > 0 ? commentCount : ''}
              </span>
            </button>

            {/* Share button with dropdown */}
            <div className="relative" ref={shareMenuRef}>
              <button
                onClick={() => setShowShareMenu(!showShareMenu)}
                disabled={isSharing}
                className={`flex items-center gap-1.5 px-3 py-1.5 rounded hover:bg-gray-100 transition-colors ${
                  isSharing ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer'
                }`}
                aria-label="Share session"
                aria-expanded={showShareMenu}
                aria-haspopup="true"
              >
                <Share2
                  className="w-5 h-5 text-gray-600"
                  strokeWidth={1.5}
                  aria-hidden="true"
                />
              </button>

              {/* Share dropdown menu */}
              {showShareMenu && (
                <div
                  className="absolute right-0 mt-2 w-52 bg-white rounded-lg shadow-2xl border-2 border-gray-300 py-2 z-50"
                  role="menu"
                  aria-label="Share options"
                >
                  {isOwnPost && onShareImage && (
                    <>
                      <button
                        onClick={handleShareImage}
                        className="w-full px-4 py-2.5 text-left text-sm font-medium text-gray-900 hover:bg-blue-50 flex items-center gap-3"
                        role="menuitem"
                        aria-label="Share session as image"
                      >
                        <ImageIcon className="w-5 h-5 text-[#007AFF]" aria-hidden="true" />
                        Share as image
                      </button>
                      <div className="border-t border-gray-200 my-1" aria-hidden="true"></div>
                    </>
                  )}
                  <button
                    onClick={handleShare}
                    className="w-full px-4 py-2.5 text-left text-sm font-medium text-gray-900 hover:bg-blue-50 flex items-center gap-3"
                    role="menuitem"
                    aria-label="Share session link"
                  >
                    <LinkIcon className="w-5 h-5 text-[#007AFF]" aria-hidden="true" />
                    Share link
                  </button>
                  <div className="border-t border-gray-200 my-1" aria-hidden="true"></div>
                  <button
                    onClick={handleCopyLink}
                    className="w-full px-4 py-2.5 text-left text-sm font-medium text-gray-900 hover:bg-blue-50 flex items-center gap-3"
                    role="menuitem"
                    aria-label="Copy session link to clipboard"
                  >
                    <Copy className="w-5 h-5 text-[#007AFF]" aria-hidden="true" />
                    Copy to clipboard
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Mobile layout - LinkedIn style */}
      <div className="md:hidden">
        {/* Counts section */}
        <div className="px-4 py-2 flex items-center justify-between text-sm text-gray-600">
          <button
            onClick={onLikesClick}
            className="hover:text-gray-900 transition-colors min-h-[44px] flex items-center"
            disabled={supportCount === 0}
            aria-label={`View ${supportCount} ${supportCount === 1 ? 'person who liked this' : 'people who liked this'}`}
          >
            {supportCount} {supportCount === 1 ? 'like' : 'likes'}
          </button>
          <button
            onClick={onCommentClick}
            className="hover:text-gray-900 transition-colors min-h-[44px] flex items-center"
            aria-label={`View ${commentCount} ${commentCount === 1 ? 'comment' : 'comments'}`}
          >
            {commentCount} {commentCount === 1 ? 'comment' : 'comments'}
          </button>
        </div>

        {/* Action buttons - 3 equal width columns */}
        <div className="px-4 pb-3">
          <div className="grid grid-cols-3 gap-1">
            {/* Like button */}
            <button
              onClick={handleSupport}
              disabled={isSupporting}
              className={`flex flex-col items-center justify-center py-2 rounded hover:bg-gray-50 transition-colors min-h-[44px] ${
                isSupporting ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer'
              }`}
              aria-label={isSupported ? `Unlike session (${supportCount} ${supportCount === 1 ? 'like' : 'likes'})` : `Like session (${supportCount} ${supportCount === 1 ? 'like' : 'likes'})`}
            >
              <ThumbsUp
                className={`w-6 h-6 transition-colors ${
                  isSupported
                    ? 'fill-gray-700 text-gray-700'
                    : 'text-gray-600'
                }`}
                strokeWidth={1.5}
                aria-hidden="true"
              />
            </button>

            {/* Comment button */}
            <button
              onClick={onCommentClick}
              className="flex flex-col items-center justify-center py-2 rounded hover:bg-gray-50 transition-colors min-h-[44px]"
              aria-label={`View and add comments (${commentCount} ${commentCount === 1 ? 'comment' : 'comments'})`}
            >
              <MessageSquare
                className="w-6 h-6 text-gray-600"
                strokeWidth={1.5}
                aria-hidden="true"
              />
            </button>

            {/* Share button with dropdown */}
            <div className="relative">
              <button
                onClick={() => setShowShareMenu(!showShareMenu)}
                disabled={isSharing}
                className={`flex flex-col items-center justify-center py-2 rounded hover:bg-gray-50 transition-colors w-full min-h-[44px] ${
                  isSharing ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer'
                }`}
                aria-label="Share session"
                aria-expanded={showShareMenu}
                aria-haspopup="true"
              >
                <Share2
                  className="w-6 h-6 text-gray-600"
                  strokeWidth={1.5}
                  aria-hidden="true"
                />
              </button>

              {/* Share dropdown menu (mobile) */}
              {showShareMenu && (
                <div
                  className="absolute right-0 bottom-full mb-2 w-56 bg-white rounded-lg shadow-2xl border-2 border-gray-300 py-2 z-50"
                  role="menu"
                  aria-label="Share options"
                >
                  {isOwnPost && onShareImage && (
                    <>
                      <button
                        onClick={handleShareImage}
                        className="w-full px-4 py-3 text-left text-base font-medium text-gray-900 hover:bg-blue-50 flex items-center gap-3"
                        role="menuitem"
                        aria-label="Share session as image"
                      >
                        <ImageIcon className="w-5 h-5 text-[#007AFF]" aria-hidden="true" />
                        Share as image
                      </button>
                      <div className="border-t border-gray-200 my-1" aria-hidden="true"></div>
