                  type="datetime-local"
                  value={
                    customStartTime
                      ? new Date(
                          customStartTime.getTime() -
                            customStartTime.getTimezoneOffset() * 60000
                        )
                          .toISOString()
                          .slice(0, 16)
                      : new Date(
                          new Date().getTime() -
                            new Date().getTimezoneOffset() * 60000
                        )
                          .toISOString()
                          .slice(0, 16)
                  }
                  onChange={e => {
                    if (e.target.value) {
                      setCustomStartTime(new Date(e.target.value));
                    } else {
                      setCustomStartTime(null);
                    }
                  }}
                  max={new Date(
                    new Date().getTime() -
                      new Date().getTimezoneOffset() * 60000
                  )
                    .toISOString()
                    .slice(0, 16)}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-[#007AFF] focus:border-[#007AFF] text-base"
                />
              </div>

              {customStartTime && (
                <div className="bg-blue-50 border border-blue-200 rounded-lg p-3">
                  <p className="text-sm text-blue-900">
                    <strong>Duration:</strong>{' '}
                    {Math.floor(
                      (new Date().getTime() - customStartTime.getTime()) /
                        1000 /
                        60 /
                        60
                    )}
                    h{' '}
                    {Math.floor(
                      ((new Date().getTime() - customStartTime.getTime()) /
                        1000 /
                        60) %
                        60
                    )}
                    m
                  </p>
                </div>
              )}
            </div>

            <div className="flex gap-3 mt-6">
              <button
                onClick={() => {
                  setCustomStartTime(null);
                  setShowTimePickerModal(false);
                }}
                className="flex-1 px-4 py-3 bg-gray-200 text-gray-700 rounded-xl hover:bg-gray-300 transition-colors font-medium"
              >
                Cancel
              </button>
              <button
                onClick={() => setShowTimePickerModal(false)}
                className="flex-1 px-4 py-3 bg-[#007AFF] text-white rounded-xl hover:bg-[#0051D5] transition-colors font-medium"
              >
                Done
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

'use client';

import { useState, useEffect } from 'react';
import { ChevronDown } from 'lucide-react';
import {
  LineChart,
  Line,
  ResponsiveContainer,
  XAxis,
  YAxis,
  Tooltip,
  Area,
  ComposedChart,
} from 'recharts';
import { useAuth } from '@/hooks/useAuth';
import { firebaseSessionApi } from '@/lib/api';
import { Session } from '@/types';

type TimePeriod = 'week' | 'month';

interface ChartDataPoint {
  name: string;
  hours: number;
  isToday?: boolean;
}

function SidebarActivityGraph() {
  const { user } = useAuth();
  const [timePeriod, setTimePeriod] = useState<TimePeriod>('week');
  const [showDropdown, setShowDropdown] = useState(false);
  const [sessions, setSessions] = useState<Session[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [chartData, setChartData] = useState<ChartDataPoint[]>([]);

  useEffect(() => {
    loadSessions();
  }, [user]);

  useEffect(() => {
    processChartData();
  }, [sessions, timePeriod]);

  const loadSessions = async () => {
    if (!user) {
      setIsLoading(false);
      return;
    }

    try {
      setIsLoading(true);
      const response = await firebaseSessionApi.getSessions(1, 500, {});
      setSessions(response.sessions);
    } catch (error) {
      console.error('Failed to load sessions:', error);
      setSessions([]);
    } finally {
      setIsLoading(false);
    }
  };

  const processChartData = () => {
    const now = new Date();
    const data: ChartDataPoint[] = [];

    if (timePeriod === 'week') {
      // Last 7 days
      const dayNames = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
      for (let i = 6; i >= 0; i--) {
        const day = new Date(now);
        day.setDate(day.getDate() - i);
        day.setHours(0, 0, 0, 0);

        const hoursWorked =
          sessions.length > 0
            ? sessions
                .filter(s => {
                  const sessionDate = new Date(s.createdAt);
                  sessionDate.setHours(0, 0, 0, 0);
                  return sessionDate.getTime() === day.getTime();
                })
                .reduce((sum, s) => sum + s.duration / 3600, 0)
            : 0;

        data.push({
          name: dayNames[day.getDay()] || '',
          hours: Number(hoursWorked.toFixed(1)),
          isToday: i === 0,
        });
      }
    } else if (timePeriod === 'month') {
      // Last 4 weeks
      for (let i = 3; i >= 0; i--) {
        const weekStart = new Date(now);
        weekStart.setDate(weekStart.getDate() - (i * 7 + 6));
        weekStart.setHours(0, 0, 0, 0);
        const weekEnd = new Date(now);
        weekEnd.setDate(weekEnd.getDate() - i * 7);
        weekEnd.setHours(23, 59, 59, 999);

        const hoursWorked =
          sessions.length > 0
            ? sessions
                .filter(s => {
                  const sessionDate = new Date(s.createdAt);
                  return sessionDate >= weekStart && sessionDate <= weekEnd;
                })
                .reduce((sum, s) => sum + s.duration / 3600, 0)
            : 0;

        data.push({
          name: `W${4 - i}`,
          hours: Number(hoursWorked.toFixed(1)),
          isToday: i === 0,
        });
      }
    }

    setChartData(data);
  };

  return (
    <div className="p-6">
      {/* Header with dropdown */}
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-xl font-bold text-gray-900">
          {timePeriod === 'week' ? 'This Week' : 'This Month'}
        </h3>
        <div className="relative">
          <button
            onClick={() => setShowDropdown(!showDropdown)}
            className="flex items-center gap-1.5 px-3 py-1.5 text-sm font-medium text-gray-700 bg-white rounded-lg hover:bg-gray-100 transition-colors border border-gray-200"
          >
            {timePeriod === 'week' ? 'Week' : 'Month'}
            <ChevronDown
              className={`w-4 h-4 transition-transform ${showDropdown ? 'rotate-180' : ''}`}
            />
          </button>
          {showDropdown && (
            <div className="absolute right-0 mt-2 w-28 bg-white rounded-xl shadow-lg border border-gray-200 py-1 z-50">
              <button
                onClick={() => {
                  setTimePeriod('week');
                  setShowDropdown(false);
                }}
                className={`w-full text-left px-4 py-2 text-sm hover:bg-gray-50 transition-colors ${
                  timePeriod === 'week'
                    ? 'text-[#007AFF] font-semibold bg-blue-50'
                    : 'text-gray-700'
                }`}
              >
                Week
              </button>
              <button
                onClick={() => {
                  setTimePeriod('month');
                  setShowDropdown(false);
                }}
                className={`w-full text-left px-4 py-2 text-sm hover:bg-gray-50 transition-colors ${
                  timePeriod === 'month'
                    ? 'text-[#007AFF] font-semibold bg-blue-50'
                    : 'text-gray-700'
                }`}
              >
                Month
              </button>
            </div>
          )}
        </div>
      </div>

      {/* Chart */}
      {isLoading ? (
        <div className="h-52 bg-gradient-to-br from-gray-50 to-gray-100 rounded-xl animate-pulse"></div>
      ) : (
        <div className="h-52">
          <ResponsiveContainer width="100%" height="100%">
            <ComposedChart
              data={chartData}
              margin={{ top: 10, right: 10, left: -10, bottom: 5 }}
            >
              <defs>
                <linearGradient id="colorHours" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#007AFF" stopOpacity={0.1} />
                  <stop offset="95%" stopColor="#007AFF" stopOpacity={0} />
                </linearGradient>
              </defs>
              <XAxis
                dataKey="name"
                tick={{ fontSize: 12, fill: '#6b7280', fontWeight: 500 }}
                axisLine={false}
                tickLine={false}
                dy={8}
              />
              <YAxis
                tick={{ fontSize: 12, fill: '#9ca3af' }}
                axisLine={false}
                tickLine={false}
                width={35}
                domain={[0, 'dataMax + 0.5']}
                tickFormatter={value => value.toFixed(1)}
              />
              <Tooltip
                contentStyle={{
                  backgroundColor: 'rgba(0, 0, 0, 0.8)',
                  border: 'none',
                  borderRadius: '8px',
                  padding: '8px 12px',
                  fontSize: '12px',
                  color: 'white',
                }}
                labelStyle={{ color: 'white', marginBottom: '4px' }}
                formatter={(value: any) => [`${value.toFixed(1)} hrs`, 'Time']}
                cursor={{
                  stroke: '#e5e7eb',
                  strokeWidth: 1,
                  strokeDasharray: '3 3',
                }}
              />
              <Area
                type="monotone"
                dataKey="hours"
                stroke="none"
                fill="url(#colorHours)"
              />
              <Line
                type="monotone"
                dataKey="hours"
                stroke="#007AFF"
                strokeWidth={3}
                dot={false}
                activeDot={{
                  r: 8,
                  fill: '#007AFF',
                  stroke: 'white',
                  strokeWidth: 3,
                }}
              />
            </ComposedChart>
          </ResponsiveContainer>
        </div>
      )}
    </div>
  );
}

export default SidebarActivityGraph;

'use client';

import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { useAuth } from '@/hooks/useAuth';
import { SignupCredentials } from '@/types';
import { firebaseAuthApi } from '@/lib/api';

export const SignupForm: React.FC = () => {
  const [formData, setFormData] = useState<SignupCredentials>({
    email: '',
    password: '',
    name: '',
    username: '',
  });
  const [confirmPassword, setConfirmPassword] = useState('');
  const [errors, setErrors] = useState<Partial<SignupCredentials & { confirmPassword: string }>>({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitError, setSubmitError] = useState<string>('');
  const [isCheckingUsername, setIsCheckingUsername] = useState(false);
  const [usernameAvailable, setUsernameAvailable] = useState<boolean | null>(null);

  const { signup } = useAuth();
  const router = useRouter();

  // Real-time username availability check with debounce
  useEffect(() => {
    const checkUsername = async () => {
      const username = formData.username.trim();

      // Only check if username meets minimum requirements
      if (username.length < 3) {
        setUsernameAvailable(null);
        return;
      }

      // Validate username format
      if (!/^[a-zA-Z0-9_]+$/.test(username)) {
        setUsernameAvailable(null);
        return;
      }

      setIsCheckingUsername(true);
      try {
        const isAvailable = await firebaseAuthApi.checkUsernameAvailability(username);
        setUsernameAvailable(isAvailable);
      } catch (error) {
        console.error('Error checking username:', error);
        setUsernameAvailable(null);
      } finally {
        setIsCheckingUsername(false);
      }
    };

    // Debounce: wait 1000ms after user stops typing
    const timeoutId = setTimeout(checkUsername, 1000);
    return () => clearTimeout(timeoutId);
  }, [formData.username]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    if (name === 'confirmPassword') {
      setConfirmPassword(value);
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }

    // Reset username availability when username changes
    if (name === 'username') {
      setUsernameAvailable(null);
    }

    // Clear field-specific error when user starts typing
    if (errors[name as keyof (SignupCredentials & { confirmPassword: string })]) {
      setErrors(prev => ({ ...prev, [name]: undefined }));
    }

    // Clear submit error
    if (submitError) {
      setSubmitError('');
    }
  };

  const validateForm = (): boolean => {
    const newErrors: Partial<SignupCredentials & { confirmPassword: string }> = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    } else if (formData.name.trim().length < 2) {
      newErrors.name = 'Name must be at least 2 characters';
    }

    if (!formData.username.trim()) {
      newErrors.username = 'Username is required';
    } else if (formData.username.trim().length < 3) {
      newErrors.username = 'Username must be at least 3 characters';
    } else if (!/^[a-zA-Z0-9_]+$/.test(formData.username.trim())) {
      newErrors.username = 'Username can only contain letters, numbers, and underscores';
    } else if (usernameAvailable === false) {
      newErrors.username = 'This username is already taken';
    }

    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    if (!confirmPassword) {
      newErrors.confirmPassword = 'Please confirm your password';
    } else if (formData.password !== confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);
    setSubmitError('');

    try {
      await signup(formData);

      // Check for invite context in sessionStorage
      const inviteContextStr = typeof window !== 'undefined'
        ? sessionStorage.getItem('inviteContext')
        : null;

      if (inviteContextStr) {
        try {
          const inviteContext = JSON.parse(inviteContextStr);

          // Clear the invite context
          sessionStorage.removeItem('inviteContext');

          // Redirect based on invite type
          if (inviteContext.type === 'group' && inviteContext.groupId) {
            router.push(`/invite/group/${inviteContext.groupId}`);
            return;
          }
        } catch (err) {
          console.error('Error parsing invite context:', err);
        }
      }

      // Default redirect to home
      router.push('/');
    } catch (error: any) {
      console.error('Signup error:', error);

      // Handle specific Firebase errors with user-friendly messages
      if (error.message?.includes('auth/email-already-in-use')) {
        setSubmitError('This email address is already registered. Please try logging in instead or use a different email.');
      } else if (error.message?.includes('auth/weak-password')) {
        setSubmitError('Password is too weak. Please choose a stronger password with at least 6 characters.');
      } else if (error.message?.includes('auth/invalid-email')) {
        setSubmitError('Please enter a valid email address.');
      } else {
        setSubmitError(error instanceof Error ? error.message : 'Signup failed. Please try again.');
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-8">
      {submitError && (
        <div className="bg-destructive/10 border border-destructive/20 text-destructive px-4 py-3 rounded-md">
          {submitError}
        </div>
      )}

      <div className="space-y-5">
        <div>
          <label htmlFor="name" className="block text-sm font-medium text-foreground mb-2">
            Full Name
          </label>
          <input
            id="name"
            name="name"
            type="text"
            autoComplete="name"
            value={formData.name}
            onChange={handleChange}
