  userId,
  variant = 'default',
  showProgress = true
}) => {
  const [streakStats, setStreakStats] = useState<StreakStats | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const loadStreak = async () => {
      try {

        const stats = await firebaseApi.streak.getStreakStats(userId);


        // Calculate and log time-based info
        if (stats.lastActivityDate) {
          const now = new Date();
          const lastActivity = new Date(stats.lastActivityDate);
          const daysSince = Math.floor((now.getTime() - lastActivity.getTime()) / (1000 * 60 * 60 * 24));
        }

        // Fetch last 7 days of sessions for detailed logging
        const sessionsResponse = await firebaseApi.session.getSessions(1, 100, {} as any);

        // Get date 7 days ago at start of day
        const today = new Date();
        today.setHours(0, 0, 0, 0);
        const sevenDaysAgo = new Date(today);
        sevenDaysAgo.setDate(today.getDate() - 6); // Include today = 7 days total

        // Create a map of dates to sessions
        const dateMap = new Map<string, { totalMinutes: number; sessionCount: number; sessions: any[] }>();

        // Initialize all 7 days
        for (let i = 0; i < 7; i++) {
          const date = new Date(sevenDaysAgo);
          date.setDate(sevenDaysAgo.getDate() + i);
          const dateKey = date.toLocaleDateString('en-US', { year: 'numeric', month: '2-digit', day: '2-digit' });
          dateMap.set(dateKey, { totalMinutes: 0, sessionCount: 0, sessions: [] });
        }

        // Filter sessions from last 7 days and group by date
        sessionsResponse.sessions.forEach(session => {
          const sessionDate = new Date(session.startTime);
          if (sessionDate >= sevenDaysAgo && sessionDate <= new Date()) {
            const dateKey = sessionDate.toLocaleDateString('en-US', { year: 'numeric', month: '2-digit', day: '2-digit' });
            const dayData = dateMap.get(dateKey);
            if (dayData) {
              dayData.totalMinutes += session.duration;
              dayData.sessionCount += 1;
              dayData.sessions.push(session);
            }
          }
        });

        // Log each day with formatted output
        const sortedDates = Array.from(dateMap.entries()).sort((a, b) => {
          const dateA = new Date(a[0]);
          const dateB = new Date(b[0]);
          return dateA.getTime() - dateB.getTime();
        });

        sortedDates.forEach(([dateKey, data], index) => {
          const dayNum = index + 1;
          const hours = (data.totalMinutes / 60).toFixed(2);
          const isComplete = data.totalMinutes > 0;
          const date = new Date(dateKey);
          const dayName = date.toLocaleDateString('en-US', { weekday: 'short' });

          if (data.sessions.length > 0) {
            data.sessions.forEach((s, i) => {
            });
          }
        });


        setStreakStats(stats);
      } catch (error) {
        console.error('Failed to load streak:', error);
      } finally {
        setIsLoading(false);
      }
    };

    loadStreak();
  }, [userId]);

  if (isLoading) {
    return (
      <div className="bg-white border border-gray-200 rounded-xl p-4 md:p-6 animate-pulse">
        <div className="flex items-center gap-3 mb-4">
          <div className="w-10 h-10 bg-gray-200 rounded-full"></div>
          <div className="flex-1">
            <div className="h-5 bg-gray-200 rounded w-24 mb-2"></div>
            <div className="h-3 bg-gray-200 rounded w-16"></div>
          </div>
        </div>
        <div className="h-16 bg-gray-200 rounded"></div>
      </div>
    );
  }

  if (!streakStats) return null;

  const getFlameColor = () => {
    // Grey flame when user has a streak but hasn't completed today's session
    if (streakStats.streakAtRisk && streakStats.currentStreak > 0) return 'text-gray-400';
    // Milestone-based colors for active streaks
    if (streakStats.currentStreak >= 100) return 'text-purple-500';
    if (streakStats.currentStreak >= 30) return 'text-blue-500';
    if (streakStats.currentStreak >= 7) return 'text-orange-500';
    // Default orange for new streaks
    return 'text-orange-400';
  };

  const getGradientColor = () => {
    if (streakStats.streakAtRisk && streakStats.currentStreak > 0) return 'from-gray-50 to-gray-100';
    if (streakStats.currentStreak >= 100) return 'from-purple-50 to-purple-100';
    if (streakStats.currentStreak >= 30) return 'from-blue-50 to-blue-100';
    if (streakStats.currentStreak >= 7) return 'from-orange-50 to-orange-100';
    return 'from-orange-50 to-orange-100';
  };

  const progressToNextMilestone = streakStats.nextMilestone
    ? (streakStats.currentStreak / streakStats.nextMilestone) * 100
    : 0;

  if (variant === 'compact') {
    return (
      <Link href="/analytics" className="block">
        <div className="p-3 hover:bg-gray-50 rounded-xl transition-all cursor-pointer">
          {/* Header */}
          <h3 className="text-sm font-semibold text-gray-900 mb-3">Your streak</h3>

          {/* Flame icon and week calendar side by side */}
          <div className="flex items-center gap-3">
            {/* Left side - Flame with day count */}
            <div className="flex flex-col items-center flex-shrink-0">
              <div className={`${getFlameColor()} relative`}>
                <Flame className="w-10 h-10" fill="currentColor" />
              </div>
              <div className="text-sm font-medium text-gray-900 mt-0.5">
                {streakStats.currentStreak}
              </div>
              <div className="text-xs text-gray-500">
                Day{streakStats.currentStreak !== 1 ? 's' : ''}
              </div>
            </div>

            {/* Right side - Week calendar */}
            <div className="flex-1">
              <WeekStreakCalendar userId={userId} />
            </div>
          </div>
        </div>
      </Link>
    );
  }

  return (
    <div className={`bg-gradient-to-br ${getGradientColor()} border border-gray-200 rounded-xl p-4 md:p-6`}>
      {/* Header */}
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-3">
          <div className={`${getFlameColor()} relative`}>
            <Flame className="w-10 h-10" fill="currentColor" />
          </div>
          <div>
            <h3 className="text-lg font-bold text-gray-900">Streak</h3>
            <p className="text-sm text-gray-600">Stay consistent</p>
          </div>
        </div>
        <Link
          href="/analytics"
          className="text-sm text-blue-600 hover:text-blue-700 font-medium"
        >
          View Analytics ‚Üí
        </Link>
      </div>

      {/* Main Stats */}
      <div className="grid grid-cols-3 gap-4 mb-4">
        <div className="text-center">
          <div className="text-2xl md:text-3xl font-bold text-gray-900">
            {streakStats.currentStreak}
          </div>
          <div className="text-xs text-gray-600 mt-1">Current</div>
        </div>
        <div className="text-center">
          <div className="text-2xl md:text-3xl font-bold text-gray-900">
            {streakStats.longestStreak}
          </div>
          <div className="text-xs text-gray-600 mt-1">Best</div>
        </div>
        <div className="text-center">
          <div className="text-2xl md:text-3xl font-bold text-gray-900">
            {streakStats.totalStreakDays}
          </div>
          <div className="text-xs text-gray-600 mt-1">Total Days</div>
        </div>
      </div>

      {/* Progress to Next Milestone */}
      {showProgress && streakStats.nextMilestone > 0 && (
        <div className="space-y-2">
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-700 font-medium">
              Next goal: {streakStats.nextMilestone} days
            </span>
            <span className="text-gray-600">
              {streakStats.nextMilestone - streakStats.currentStreak} to go
            </span>
          </div>
          <div className="w-full bg-white/60 rounded-full h-2.5 overflow-hidden">
            <div
              className={`h-full rounded-full transition-all duration-500 ${
                streakStats.streakAtRisk && streakStats.currentStreak > 0
                  ? 'bg-gray-400'
                  : streakStats.currentStreak >= 100
                  ? 'bg-purple-500'
                  : streakStats.currentStreak >= 30
                  ? 'bg-blue-500'
                  : 'bg-orange-500'
              }`}
              style={{ width: `${Math.min(progressToNextMilestone, 100)}%` }}
            />
          </div>
        </div>
      )}

    </div>
  );
};

'use client';

import { useState, useEffect } from 'react';
import { X, Users, ChevronLeft, ChevronRight, MapPin } from 'lucide-react';
import Link from 'next/link';
import { firebaseApi } from '@/lib/api';
import { useAuth } from '@/hooks/useAuth';
import GroupAvatar from '@/components/GroupAvatar';

interface SuggestedGroupsModalProps {
  isOpen: boolean;
  onClose: () => void;
}

const GROUPS_PER_PAGE = 10;
const TOTAL_GROUPS_TO_FETCH = 100;

export default function SuggestedGroupsModal({
  isOpen,
  onClose,
}: SuggestedGroupsModalProps) {
  const { user } = useAuth();
  const [allSuggestedGroups, setAllSuggestedGroups] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [joiningGroups, setJoiningGroups] = useState<Set<string>>(new Set());
  const [currentPage, setCurrentPage] = useState(0);

  useEffect(() => {
    if (isOpen && user) {
      loadGroups();
    }
  }, [isOpen, user]);

  // Handle ESC key to close modal
  useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape' && isOpen) {
        onClose();
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleEscape);
    }

    return () => {
      document.removeEventListener('keydown', handleEscape);
    };
  }, [isOpen, onClose]);

  const loadGroups = async () => {
    if (!user) return;

    try {
      setIsLoading(true);
      setCurrentPage(0);

      // Get user's current groups to exclude them from suggestions
      const userGroups = await firebaseApi.group.getUserGroups(user.id);
      const userGroupIds = new Set(userGroups.map(g => g.id));

      // Get all groups and filter out ones user is already in
      const allGroups = await firebaseApi.group.searchGroups('');
      const filteredGroups = allGroups.filter(
        group => !userGroupIds.has(group.id)
      );
      setAllSuggestedGroups(filteredGroups);
    } catch (error) {
      console.error('Error loading groups:', error);
      setAllSuggestedGroups([]);
    } finally {
      setIsLoading(false);
    }
  };

  // Calculate paginated groups
  const totalPages = Math.ceil(allSuggestedGroups.length / GROUPS_PER_PAGE);
  const startIndex = currentPage * GROUPS_PER_PAGE;
  const endIndex = startIndex + GROUPS_PER_PAGE;
  const paginatedGroups = allSuggestedGroups.slice(startIndex, endIndex);

  const goToNextPage = () => {
    if (currentPage < totalPages - 1) {
      setCurrentPage(prev => prev + 1);
    }
  };

  const goToPreviousPage = () => {
    if (currentPage > 0) {
      setCurrentPage(prev => prev - 1);
    }
  };

  const handleJoinGroup = async (groupId: string, e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    if (!user || joiningGroups.has(groupId)) return;

    setJoiningGroups(prev => new Set(prev).add(groupId));
    try {
      await firebaseApi.group.joinGroup(groupId, user.id);
      // Remove from suggestions after joining
      setAllSuggestedGroups(prev => prev.filter(g => g.id !== groupId));
    } catch (error) {
      console.error('Failed to join group:', error);
    } finally {
      setJoiningGroups(prev => {
        const next = new Set(prev);
        next.delete(groupId);
        return next;
      });
    }
  };

  if (!isOpen) return null;

  return (
    <div
      className="fixed inset-0 bg-black/50 z-50 flex items-center justify-center p-4"
      onClick={onClose}
    >
      <div
        className="bg-white rounded-xl shadow-2xl max-w-2xl w-full max-h-[80vh] flex flex-col"
        onClick={e => e.stopPropagation()}
      >
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-gray-200">
          <h2 className="text-xl font-bold text-gray-900">Suggested Groups</h2>
          <button
            onClick={onClose}
            className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <X className="w-5 h-5 text-gray-600" />
          </button>
        </div>

        {/* Content */}
        <div className="flex-1 overflow-y-auto">
          {isLoading ? (
            <div className="p-12 text-center">
              <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-[#007AFF]"></div>
              <p className="text-gray-600 mt-4">Loading groups...</p>
            </div>
          ) : allSuggestedGroups.length === 0 ? (
            <div className="p-12 text-center">
              <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <Users className="w-8 h-8 text-green-600" />
              </div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">
                No groups available
              </h3>
              <p className="text-gray-600">
                Check back later for groups to join
              </p>
            </div>
          ) : (
            <div className="divide-y divide-gray-100">
              {paginatedGroups.map(group => {
                const isJoining = joiningGroups.has(group.id);
                return (
                  <div
                    key={group.id}
                    className="p-4 hover:bg-gray-50 transition-colors"
                  >
                    <div className="flex items-start gap-3">
                      {/* Group Icon */}
                      <Link href={`/groups/${group.id}`}>
                        <GroupAvatar
                          imageUrl={group.imageUrl}
                          name={group.name}
                          size="md"
                        />
                      </Link>

                      {/* Group Info */}
                      <div className="flex-1 min-w-0">
                        <Link href={`/groups/${group.id}`}>
                          <h3 className="font-semibold text-base text-gray-900 truncate hover:text-[#007AFF] transition-colors">
                            {group.name}
                          </h3>
                        </Link>
                        <p className="text-sm text-gray-600 mt-0.5 line-clamp-2">
                          {group.description}
                        </p>

                        {/* Meta Info */}
                        <div className="flex items-center gap-3 mt-2 text-xs text-gray-500">
                          <span className="flex items-center gap-1">
                            <Users className="w-3.5 h-3.5" />
                            {group.memberCount || 0}{' '}
                            {group.memberCount === 1 ? 'member' : 'members'}
                          </span>
                          {group.location && (
                            <span className="flex items-center gap-1">
                              <MapPin className="w-3.5 h-3.5" />
                              {group.location}
                            </span>
                          )}
                        </div>
                      </div>

                      {/* Join Button */}
                      <button
                        onClick={e => handleJoinGroup(group.id, e)}
                        disabled={isJoining}
                        className={`text-sm font-semibold transition-colors flex-shrink-0 ${
                          isJoining
                            ? 'text-gray-400 cursor-not-allowed'
                            : 'text-[#007AFF] hover:text-[#0051D5]'
                        }`}
                      >
                        {isJoining ? 'Joining...' : 'Join'}
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>

        {/* Pagination Controls */}
        {!isLoading && allSuggestedGroups.length > GROUPS_PER_PAGE && (
          <div className="border-t border-gray-100 px-4 py-3 flex items-center justify-between">
            <button
              onClick={goToPreviousPage}
              disabled={currentPage === 0}
              className="p-2 rounded-lg hover:bg-gray-100 disabled:opacity-30 disabled:cursor-not-allowed transition-colors"
              aria-label="Previous page"
            >
              <ChevronLeft className="w-5 h-5 text-gray-700" />
            </button>

            <span className="text-sm text-gray-600 font-medium">
              Page {currentPage + 1} of {totalPages}
            </span>

            <button
              onClick={goToNextPage}
              disabled={currentPage >= totalPages - 1}
              className="p-2 rounded-lg hover:bg-gray-100 disabled:opacity-30 disabled:cursor-not-allowed transition-colors"
              aria-label="Next page"
            >
              <ChevronRight className="w-5 h-5 text-gray-700" />
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect, useCallback } from 'react';
import { X, ChevronLeft, ChevronRight } from 'lucide-react';
import { firebaseUserApi } from '@/lib/api';
import { useAuth } from '@/hooks/useAuth';
import { UserCardCompact } from '@/components/UserCard';

interface SuggestedPeopleModalProps {
  isOpen: boolean;
  onClose: () => void;
}

const USERS_PER_PAGE = 10;
const TOTAL_USERS_TO_FETCH = 100;

export default function SuggestedPeopleModal({ isOpen, onClose }: SuggestedPeopleModalProps) {
  const { user } = useAuth();
  const [allSuggestedUsers, setAllSuggestedUsers] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(0);

  const loadUsers = useCallback(async () => {
    if (!user) return;

    try {
      setIsLoading(true);
      setCurrentPage(0);

      // Use the same getSuggestedUsers API as the sidebar
      const suggestions = await firebaseUserApi.getSuggestedUsers(TOTAL_USERS_TO_FETCH);
      setAllSuggestedUsers(suggestions);
    } catch (error) {
      console.error('Error loading users:', error);
      setAllSuggestedUsers([]);
    } finally {
      setIsLoading(false);
    }
  }, [user]);

  useEffect(() => {
    if (isOpen && user) {
      loadUsers();
    }
  }, [isOpen, user, loadUsers]);

  // Handle ESC key to close modal
  useEffect(() => {
    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape' && isOpen) {
        onClose();
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleEscape);
    }

    return () => {
      document.removeEventListener('keydown', handleEscape);
    };
  }, [isOpen, onClose]);

  // Calculate paginated users
  const totalPages = Math.ceil(allSuggestedUsers.length / USERS_PER_PAGE);
  const startIndex = currentPage * USERS_PER_PAGE;
  const endIndex = startIndex + USERS_PER_PAGE;
  const paginatedUsers = allSuggestedUsers.slice(startIndex, endIndex);

  const goToNextPage = () => {
    if (currentPage < totalPages - 1) {
      setCurrentPage(prev => prev + 1);
    }
  };

  const goToPreviousPage = () => {
    if (currentPage > 0) {
      setCurrentPage(prev => prev - 1);
    }
  };

  const handleFollowChange = (userId: string, isFollowing: boolean) => {
    setAllSuggestedUsers(prev =>
      prev.map(u =>
        u.id === userId
          ? {
              ...u,
              isFollowing,
              followersCount: isFollowing
                ? (u.followersCount || 0) + 1
                : Math.max(0, (u.followersCount || 0) - 1)
            }
          : u
      )
    );
  };

  if (!isOpen) return null;

  return (
    <div
      className="fixed inset-0 bg-black/50 z-50 flex items-center justify-center p-4"
      onClick={onClose}
    >
      <div
        className="bg-white rounded-xl shadow-2xl max-w-2xl w-full max-h-[80vh] flex flex-col"
        onClick={(e) => e.stopPropagation()}
      >
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-gray-200">
          <h2 className="text-xl font-bold text-gray-900">Suggested People</h2>
          <button
            onClick={onClose}
            className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
            aria-label="Close suggested people modal"
          >
            <X className="w-5 h-5 text-gray-600" aria-hidden="true" />
          </button>
        </div>

        {/* Content */}
        <div className="flex-1 overflow-y-auto">
          {isLoading ? (
            <div className="p-12 text-center">
              <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-[#007AFF]"></div>
              <p className="text-gray-600 mt-4">Loading suggestions...</p>
            </div>
          ) : allSuggestedUsers.length === 0 ? (
            <div className="p-12 text-center">
              <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-3xl">üë•</span>
              </div>
              <h3 className="text-lg font-semibold text-gray-900 mb-2">No suggestions yet</h3>
              <p className="text-gray-600">Check back later for people to connect with</p>
            </div>
          ) : (
            <div className="divide-y divide-gray-100">
              {paginatedUsers.map((suggestedUser) => (
                <div key={suggestedUser.id}>
                  <UserCardCompact
                    user={suggestedUser}
                    variant="search"
                    onFollowChange={handleFollowChange}
                  />
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Pagination Controls */}
        {!isLoading && allSuggestedUsers.length > USERS_PER_PAGE && (
          <div className="border-t border-gray-100 px-4 py-3 flex items-center justify-between">
            <button
              onClick={goToPreviousPage}
              disabled={currentPage === 0}
              className="flex items-center gap-2 px-4 py-2 rounded-lg hover:bg-gray-100 disabled:opacity-30 disabled:cursor-not-allowed transition-colors text-sm font-medium text-gray-700"
              aria-label="Previous page"
            >
              <ChevronLeft className="w-4 h-4" aria-hidden="true" />
              Previous
            </button>

            <button
              onClick={goToNextPage}
              disabled={currentPage >= totalPages - 1}
              className="flex items-center gap-2 px-4 py-2 rounded-lg hover:bg-gray-100 disabled:opacity-30 disabled:cursor-not-allowed transition-colors text-sm font-medium text-gray-700"
              aria-label="Next page"
            >
              Next
              <ChevronRight className="w-4 h-4" aria-hidden="true" />
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import { SuggestedUser } from '@/types';
import { firebaseUserApi } from '@/lib/api';
import { UserCard } from './UserCard';
import { Button } from '@/components/ui/button';
import { RefreshCw, Users, Sparkles, TrendingUp } from 'lucide-react';
import { toast } from 'sonner';

interface SuggestedUsersProps {
  limit?: number;
  showHeader?: boolean;
  onUserSelect?: (user: SuggestedUser) => void;
  variant?: 'default' | 'compact';
}

const SuggestedUsers: React.FC<SuggestedUsersProps> = ({
  limit = 10,
  showHeader = true,
  onUserSelect,
  variant = 'default',
}) => {
  const [suggestions, setSuggestions] = useState<SuggestedUser[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isRefreshing, setIsRefreshing] = useState(false);

  const loadSuggestions = async (isRefresh = false) => {
      try {
        if (isRefresh) {
          setIsRefreshing(true);
        } else {
          setIsLoading(true);
        }

        const suggestions = await firebaseUserApi.getSuggestedUsers(limit);
        setSuggestions(suggestions);
    } catch (error) {
      console.error('Failed to load suggestions:', error);
      // Don't show error toast for empty database - just set empty array
      if (error instanceof Error && error.message.includes('permissions')) {
        setSuggestions([]);
      } else {
        toast.error('Failed to load user suggestions');
      }
    } finally {
      setIsLoading(false);
      setIsRefreshing(false);
    }
  };

  useEffect(() => {
    loadSuggestions();
  }, [limit]);

  const handleRefresh = () => {
    loadSuggestions(true);
  };

  const handleFollowChange = (userId: string, isFollowing: boolean) => {
    setSuggestions(prev => 
      prev.map(user => 
        user.id === userId 
          ? { ...user, isFollowing, followersCount: isFollowing ? user.followersCount + 1 : Math.max(0, user.followersCount - 1) }
          : user
      )
    );
  };

  const getReasonIcon = (reason: string) => {
    switch (reason) {
      case 'mutual_followers':
        return <Users className="w-3 h-3" />;
      case 'similar_interests':
        return <Sparkles className="w-3 h-3" />;
      case 'popular_user':
        return <TrendingUp className="w-3 h-3" />;
      case 'location_based':
        return <Users className="w-3 h-3" />;
      case 'activity_based':
        return <TrendingUp className="w-3 h-3" />;
      default:
        return <Users className="w-3 h-3" />;
    }
  };

  const getReasonColor = (reason: string): string => {
    switch (reason) {
      case 'mutual_followers':
        return 'text-blue-600';
      case 'similar_interests':
        return 'text-purple-600';
      case 'popular_user':
        return 'text-orange-600';
      case 'location_based':
        return 'text-green-600';
      case 'activity_based':
        return 'text-pink-600';
      default:
        return 'text-gray-600';
    }
  };

  if (isLoading) {
    return (
      <div className="bg-card-background rounded-lg border border-border p-6">
        {showHeader && (
          <div className="flex items-center justify-between mb-4">
            <h3 className="font-semibold text-foreground flex items-center gap-2">
              <Sparkles className="w-4 h-4" />
              Suggested Users
            </h3>
          </div>
        )}
        <div className="space-y-3">
          {Array.from({ length: 3 }).map((_, index) => (
            <div key={index} className="animate-pulse">
              <div className="flex items-center gap-3">
                <div className="w-12 h-12 bg-muted rounded-full" />
                <div className="flex-1 space-y-2">
                  <div className="h-4 bg-muted rounded w-3/4" />
                  <div className="h-3 bg-muted rounded w-1/2" />
                </div>
                <div className="w-20 h-8 bg-muted rounded" />
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  if (suggestions.length === 0) {
    return (
      <div className="bg-card-background rounded-lg border border-border p-6">
        {showHeader && (
          <div className="flex items-center justify-between mb-4">
            <h3 className="font-semibold text-foreground flex items-center gap-2">
              <Sparkles className="w-4 h-4" />
              Suggested Users
            </h3>
            <Button
              variant="ghost"
              size="sm"
              onClick={handleRefresh}
              disabled={isRefreshing}
              className="h-8 w-8 p-0"
            >
              <RefreshCw className={`w-4 h-4 ${isRefreshing ? 'animate-spin' : ''}`} />
            </Button>
          </div>
        )}
        <div className="text-center py-8">
          <Users className="w-12 h-12 text-muted-foreground mx-auto mb-3" />
          <h4 className="font-medium text-foreground mb-1">No suggestions available</h4>
          <p className="text-sm text-muted-foreground">
            We couldn't find any users to suggest right now. Try again later.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-card-background rounded-lg border border-border p-6">
      {showHeader && (
        <div className="flex items-center justify-between mb-4">
          <h3 className="font-semibold text-foreground flex items-center gap-2">
            <Sparkles className="w-4 h-4" />
            Suggested Users
          </h3>
          <Button
            variant="ghost"
            size="sm"
            onClick={handleRefresh}
            disabled={isRefreshing}
            className="h-8 w-8 p-0"
          >
            <RefreshCw className={`w-4 h-4 ${isRefreshing ? 'animate-spin' : ''}`} />
          </Button>
        </div>
      )}

      <div className="space-y-3">
        {suggestions.map((user) => (
          <div key={user.id} onClick={() => onUserSelect?.(user)}>
            {variant === 'compact' ? (
              <div className="flex items-center justify-between p-3 hover:bg-muted/50 rounded-lg transition-colors">
                <div className="flex items-center gap-3 flex-1 min-w-0">
                  {/* Avatar */}
                  {user.profilePicture ? (
                    <img
                      src={user.profilePicture}
                      alt={`${user.name}'s profile picture`}
                      className="w-10 h-10 rounded-full object-cover border border-border"
                    />
                  ) : (
                    <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center text-white text-sm font-semibold">
                      {user.name.charAt(0).toUpperCase()}
                    </div>
                  )}

                  {/* User Info */}
                  <div className="min-w-0 flex-1">
                    <h4 className="font-medium text-foreground truncate">{user.name}</h4>
                    <p className="text-sm text-muted-foreground truncate">
                      {user.followersCount || 0} followers
                    </p>
                    <div className="flex items-center gap-1 mt-1">
                      {getReasonIcon(user.reason)}
                      <span className={`text-xs ${getReasonColor(user.reason)}`}>
                        {user.reason.replace('_', ' ')}
                      </span>
                    </div>
                  </div>
                </div>

                {/* Follow Button */}
                {!user.isFollowing && (
                  <Button
                    size="sm"
                    className="flex items-center gap-1 min-w-[70px]"
                    onClick={(e) => {
                      e.stopPropagation();
                      // Handle follow logic here
                    }}
                  >
                    Follow
                  </Button>
                )}
              </div>
            ) : (
              <UserCard
                user={user}
                variant="suggestion"
                onFollowChange={handleFollowChange}
              />
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

// Compact sidebar version
export const SuggestedUsersSidebar: React.FC<SuggestedUsersProps> = (props) => {
  return (
    <SuggestedUsers
      {...props}
      limit={5}
      variant="compact"
      showHeader={true}
    />
  );
};

// Widget version for dashboard
export const SuggestedUsersWidget: React.FC<SuggestedUsersProps> = (props) => {
  return (
    <SuggestedUsers
      {...props}
      limit={6}
      variant="compact"
      showHeader={true}
    />
  );
};

export default SuggestedUsers;

'use client';

import React, { useState, useEffect } from 'react';
import { CommentWithDetails } from '@/types';
import { useAuth } from '@/hooks/useAuth';
import {
  useSessionComments,
  useCreateComment,
  useDeleteComment,
  useCommentLike,
} from '@/features/comments/hooks';
import CommentItem from './CommentItem';
import CommentInput from './CommentInput';
import { ChevronLeft, ChevronRight } from 'lucide-react';

interface TopCommentsProps {
  sessionId: string;
  totalCommentCount: number;
  onCommentCountChange?: (count: number) => void;
  autoFocus?: boolean;
  initialExpanded?: boolean;
}

const COMMENTS_PER_PAGE = 5;

export const TopComments: React.FC<TopCommentsProps> = ({
  sessionId,
  totalCommentCount,
  onCommentCountChange,
  autoFocus = false,
  initialExpanded = false,
}) => {
  const { user } = useAuth();
  const [isExpanded, setIsExpanded] = useState(initialExpanded);
  const [currentPage, setCurrentPage] = useState(1);

  // Fetch comments - use limit of 2 for top comments, or 100 when expanded
  const limit = isExpanded ? 100 : 2;
  const {
    data: commentsResponse,
    isLoading,
    refetch,
  } = useSessionComments(sessionId, limit);

  const createCommentMutation = useCreateComment({
    onSuccess: () => {
      if (onCommentCountChange) {
        onCommentCountChange(totalCommentCount + 1);
      }
    },
  });

  const deleteCommentMutation = useDeleteComment({
    onSuccess: () => {
      if (onCommentCountChange) {
        onCommentCountChange(Math.max(0, totalCommentCount - 1));
      }
    },
  });

  const likeMutation = useCommentLike(sessionId);

  const allComments = commentsResponse?.comments || [];
  const totalPages = Math.ceil(allComments.length / COMMENTS_PER_PAGE);

  // Calculate paginated comments when expanded
  const comments = isExpanded
    ? allComments.slice(
        (currentPage - 1) * COMMENTS_PER_PAGE,
        currentPage * COMMENTS_PER_PAGE
      )
    : allComments;

  // Refetch when expanded state changes
  useEffect(() => {
    refetch();
  }, [isExpanded]);

  const handleCreateComment = async (content: string) => {
    try {
      await createCommentMutation.mutateAsync({
        sessionId,
        content,
      });
    } catch (err: any) {
      console.error('Failed to create comment:', err);
      throw err;
    }
  };

  const handleDelete = async (commentId: string) => {
    try {
      await deleteCommentMutation.mutateAsync({
        commentId,
        sessionId,
      });
    } catch (err: any) {
      console.error('Failed to delete comment:', err);
      throw err;
    }
  };

  const handleLike = (commentId: string, action: 'like' | 'unlike') => {
    likeMutation.mutate({ commentId, action });
  };

  if (isLoading && !isExpanded) {
    return (
      <div className="border-t border-gray-100 px-4 py-3 space-y-3">
        {[1, 2].map(i => (
          <div key={i} className="flex gap-3 animate-pulse">
            <div className="w-8 h-8 rounded-full bg-gray-200" />
            <div className="flex-1 space-y-2">
              <div className="h-3 bg-gray-200 rounded w-1/4" />
              <div className="h-3 bg-gray-200 rounded w-full" />
            </div>
          </div>
        ))}
      </div>
    );
  }

  if (comments.length === 0 && totalCommentCount === 0 && !autoFocus) {
    return null;
  }

  return (
    <div
      className={
        isExpanded || (comments.length > 0 && !isExpanded)
          ? 'hidden md:block md:border-t md:border-gray-100'
          : ''
      }
    >
      <div className="px-4 py-2 space-y-2">
        {/* Comments List - Only show in collapsed view if there are comments (hidden on mobile) */}
        {!isExpanded && comments.length > 0 && (
          <>
            {comments.map(comment => (
              <CommentItem
                key={comment.id}
                comment={comment}
                sessionId={sessionId}
                onDelete={handleDelete}
                onLike={handleLike}
                currentUserId={user?.id}
              />
            ))}

            {/* See all comments link */}
            {totalCommentCount > 2 && (
              <button
                onClick={() => setIsExpanded(true)}
                className="text-sm text-gray-500 hover:text-gray-900 font-medium py-2 transition-colors"
              >
                See all {totalCommentCount}{' '}
                {totalCommentCount === 1 ? 'comment' : 'comments'}
              </button>
            )}
          </>
        )}

        {/* Expanded Comments */}
        {isExpanded && (
          <>
            {comments.map(comment => (
              <CommentItem
                key={comment.id}
                comment={comment}
                sessionId={sessionId}
                onDelete={handleDelete}
                onLike={handleLike}
                currentUserId={user?.id}
              />
            ))}
          </>
        )}

        {/* Comment Input - Only show when expanded or autoFocus */}
        {(isExpanded || autoFocus) && (
          <div
            className={`${isExpanded ? 'pt-2 border-t border-gray-100' : ''}`}
          >
            <CommentInput
              sessionId={sessionId}
              placeholder="Add a comment..."
              onSubmit={handleCreateComment}
              autoFocus={autoFocus}
            />
          </div>
        )}

        {/* Pagination Controls - Only in expanded view */}
        {isExpanded && totalPages > 1 && (
          <div className="flex items-center justify-between pt-3 border-t border-gray-100">
            <button
              onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
              disabled={currentPage === 1}
              className="p-2 rounded-lg hover:bg-gray-100 disabled:opacity-30 disabled:cursor-not-allowed transition-colors"
              aria-label="Previous page"
            >
              <ChevronLeft className="w-5 h-5 text-gray-700" />
            </button>

            <span className="text-sm text-gray-600 font-medium">
              Page {currentPage} of {totalPages}
            </span>

            <button
              onClick={() =>
                setCurrentPage(prev => Math.min(totalPages, prev + 1))
              }
              disabled={currentPage === totalPages}
              className="p-2 rounded-lg hover:bg-gray-100 disabled:opacity-30 disabled:cursor-not-allowed transition-colors"
              aria-label="Next page"
            >
              <ChevronRight className="w-5 h-5 text-gray-700" />
            </button>
          </div>
        )}

        {/* Collapse Button */}
        {isExpanded && (
          <button
            onClick={() => {
              setIsExpanded(false);
              setCurrentPage(1);
            }}
            className="text-sm text-gray-600 hover:text-gray-900 font-medium py-1"
          >
            Show less
          </button>
        )}
      </div>
    </div>
  );
};

export default TopComments;

'use client';

import React, { useEffect, useMemo, useState } from 'react';
import { Check } from 'lucide-react';
import { firebaseApi } from '@/lib/api';

interface WeekStreakCalendarProps {
  userId: string;
}

// Normalize a Date to a local YYYY-MM-DD string (avoids UTC off-by-one)
const toLocalYMD = (d: Date) => {
  const year = d.getFullYear();
  const month = String(d.getMonth() + 1).padStart(2, '0');
  const day = String(d.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
};

export const WeekStreakCalendar: React.FC<WeekStreakCalendarProps> = ({
  userId,
}) => {
  const [isLoading, setIsLoading] = useState(true);
  const [activeDates, setActiveDates] = useState<Set<string>>(new Set());

  // Compute this week's start and end - ending with today as the 7th node
  const { weekStart, weekEnd } = useMemo(() => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    // Start is 6 days before today
    const start = new Date(today);
    start.setDate(today.getDate() - 6);

    // End is today
    const end = new Date(today);
    end.setHours(23, 59, 59, 999);

    return { weekStart: start, weekEnd: end };
  }, []);

  useEffect(() => {
    const loadWeeklySessions = async () => {
      try {
        // Fetch a generous amount and filter client-side by week range
        const res = await firebaseApi.session.getSessions(1, 100, {} as any);

        const withinWeek = res.sessions.filter(s => {
          const dt = new Date(s.startTime);
          return dt >= weekStart && dt <= weekEnd;
        });

        const dateSet = new Set<string>();
        withinWeek.forEach(s => {
          const localKey = toLocalYMD(new Date(s.startTime));
          dateSet.add(localKey);
        });

        setActiveDates(dateSet);
      } catch (error) {
        console.error('‚ùå Failed to load sessions for week:', error);
      } finally {
        setIsLoading(false);
      }
    };

    loadWeeklySessions();
  }, [userId, weekStart, weekEnd]);

  if (isLoading) {
    return (
      <div className="flex justify-between gap-0.5 animate-pulse">
        {[...Array(7)].map((_, i) => (
          <div key={i} className="flex flex-col items-center flex-1">
            <div className="h-3 bg-gray-200 rounded mb-1.5 w-4"></div>
            <div className="h-8 w-8 bg-gray-200 rounded-full"></div>
          </div>
        ))}
      </div>
    );
  }

  // Build the visual model for the current week using the activeDates Set
  const getWeekDays = () => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    const days = [] as Array<{
      dayOfWeek: string;
      dayNumber: number;
      hasActivity: boolean;
      isToday: boolean;
      localKey: string;
      isPast: boolean;
    }>;

    const dayLabels = ['S', 'M', 'T', 'W', 'T', 'F', 'S'];

    for (let i = 0; i < 7; i++) {
      const date = new Date(weekStart);
      date.setDate(weekStart.getDate() + i);
      date.setHours(12, 0, 0, 0); // noon to avoid DST edge-cases in formatting

      const localKey = toLocalYMD(date);
      const isToday = toLocalYMD(today) === localKey;
      const hasActivity = activeDates.has(localKey);

      const dayOfWeek = dayLabels[date.getDay()] || 'S';
      const dayInfo = {
        dayOfWeek,
        dayNumber: date.getDate(),
        hasActivity,
        isToday,
        localKey,
        isPast: date < today && !isToday,
      };

      days.push(dayInfo);
    }

    return days;
  };

  const weekDays = getWeekDays();

  return (
    <div className="flex justify-between gap-0.5">
      {weekDays.map((day, index) => (
        <div key={index} className="flex flex-col items-center flex-1">
          <div
            className={`text-xs font-medium mb-1.5 ${day.isToday ? 'text-[#007AFF] font-bold' : 'text-gray-400'}`}
          >
            {day.dayOfWeek}
          </div>
          <div className="h-6 w-6 flex items-center justify-center">
            {day.hasActivity ? (
              // Completed day - orange circle with white checkmark
              <div
                className={`w-6 h-6 rounded-full flex items-center justify-center bg-orange-400 ${
                  day.isToday ? 'ring-2 ring-[#007AFF] ring-offset-1' : ''
                }`}
              >
                <Check className="w-3.5 h-3.5 text-white stroke-[3]" />
              </div>
            ) : day.isToday ? (
              // Today (not completed) - Electric Blue ring with grey circle and white checkmark
              <div className="w-6 h-6 rounded-full flex items-center justify-center bg-gray-300 ring-2 ring-[#007AFF] ring-offset-1">
                <Check className="w-3.5 h-3.5 text-white stroke-[3]" />
              </div>
            ) : (
              // Past incomplete day - light grey circle with grey checkmark
              <div className="w-6 h-6 rounded-full flex items-center justify-center bg-gray-100">
                <Check className="w-3.5 h-3.5 text-gray-300 stroke-[3]" />
              </div>
            )}
          </div>
          <div
            className={`text-[10px] font-medium mt-0.5 ${day.isToday ? 'text-[#007AFF] font-bold' : 'text-gray-500'}`}
          >
            {day.dayNumber}
          </div>
        </div>
      ))}
    </div>
  );
};

'use client';

import { useState } from 'react';
import { usePathname } from 'next/navigation';
import { useAuth } from '@/hooks/useAuth';
import NotificationIcon from '../NotificationIcon';
import Logo from './Logo';
import SearchBar from './SearchBar';
import Navigation from './Navigation';
import TimerStatus from './TimerStatus';
import ProfileMenu from './ProfileMenu';
import MobileMenu from './MobileMenu';
import AuthButtons from './AuthButtons';
import { DIMENSIONS } from './header.constants';

/**
 * Header Component
 *
 * Main application header with responsive design.
 * Orchestrates all header sub-components without managing their internal state.
 *
 * Features:
 * - Logo with home link
 * - Collapsible search bar
 * - Desktop navigation
 * - Timer status and session actions
 * - Profile menu with dropdown
 * - Mobile menu for responsive design
 * - Authentication buttons for non-authenticated users
 * - Notifications
 *
 * This refactored version follows:
 * - Single Responsibility Principle: Orchestrates child components only
 * - Open/Closed Principle: New features added via new child components
 * - Composition over inheritance
 * - Airbnb React style guidelines
 *
 * @example
 * ```tsx
 * import Header from '@/components/header/Header';
 *
 * export default function Layout({ children }) {
 *   return (
 *     <>
 *       <Header />
 *       <main>{children}</main>
 *     </>
 *   );
 * }
 * ```
 */
export default function Header() {
  const pathname = usePathname();
  const { user } = useAuth();

  // Local UI state (simple toggles only)
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  return (
    <header className="sticky top-0 left-0 right-0 z-50 bg-white border-b border-gray-200 shadow-sm">
      <div className="max-w-[1400px] mx-auto px-4">
        <div className="flex items-center justify-between h-14">
          {/* Left side: Logo + Search + Navigation */}
          <div className="flex items-center space-x-4 ml-8">
            <Logo />

            {/* Search - Only show when authenticated */}
            {user && (
              <SearchBar
                isOpen={isSearchOpen}
                onToggle={() => setIsSearchOpen(!isSearchOpen)}
              />
            )}

            {/* Desktop Navigation - Only show when search is closed AND user is authenticated */}
            {!isSearchOpen && user && <Navigation pathname={pathname} />}
          </div>

          {/* Right side: Actions */}
          <div className="flex items-center space-x-3">
            {/* Auth Buttons - Only show when NOT authenticated */}
            {!user && <AuthButtons />}

            {/* Session Actions / Timer Status - Only show when authenticated */}
            {user && <TimerStatus pathname={pathname} />}

            {/* Notifications - Only show when authenticated */}
            {user && (
              <NotificationIcon className="hidden md:flex p-2 text-gray-600 hover:text-[#007AFF] transition-colors" />
            )}

            {/* Profile Menu - Only show when authenticated */}
            {user && <ProfileMenu user={user} />}

            {/* Mobile Menu Toggle - Only show when authenticated */}
            {user && (
              <MobileMenu
                isOpen={isMobileMenuOpen}
                onToggle={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                pathname={pathname}
              />
            )}
          </div>
        </div>
      </div>
    </header>
  );
}

