/**
 * Comment Mutation Hooks - React Query Boundary
 *
 * All write operations for comments (create, update, delete, like).
 */

import {
  useMutation,
  useQueryClient,
  UseMutationOptions,
} from '@tanstack/react-query';
import { CommentService, CommentLikeData } from '../services/CommentService';
import { COMMENT_KEYS } from './useComments';
import {
  CommentWithDetails,
  CreateCommentData,
  UpdateCommentData,
} from '@/types';
import { SESSION_KEYS } from '@/features/sessions/hooks';

const commentService = new CommentService();

/**
 * Create a new comment
 *
 * @example
 * const createMutation = useCreateComment();
 * createMutation.mutate({
 *   sessionId: 'abc123',
 *   content: 'Great work!',
 *   parentId: 'comment-456' // Optional for replies
 * });
 */
export function useCreateComment(
  options?: Partial<
    UseMutationOptions<CommentWithDetails, Error, CreateCommentData>
  >
) {
  const queryClient = useQueryClient();

  return useMutation<CommentWithDetails, Error, CreateCommentData>({
    mutationFn: data => commentService.createComment(data),

    onSuccess: (newComment, variables) => {
      // Invalidate comments for this session
      queryClient.invalidateQueries({
        queryKey: COMMENT_KEYS.session(variables.sessionId),
      });

      // Update comment count in session cache and feed
      const updateSessionCommentCount = (session: any) => {
        if (session?.id !== variables.sessionId) return session;
        return {
          ...session,
          commentCount: (session.commentCount || 0) + 1,
        };
      };

      // Update session detail cache
      queryClient.setQueryData(
        SESSION_KEYS.detail(variables.sessionId),
        updateSessionCommentCount
      );

      // Update feed caches
      queryClient.setQueriesData<any>({ queryKey: ['feed'] }, (old: any) => {
        if (!old) return old;

        if (Array.isArray(old)) {
          return old.map(updateSessionCommentCount);
        } else if (old.sessions) {
          return {
            ...old,
            sessions: old.sessions.map(updateSessionCommentCount),
          };
        } else if (old.pages) {
          // Handle infinite query
          return {
            ...old,
            pages: old.pages.map((page: any) => ({
              ...page,
              sessions: page.sessions.map(updateSessionCommentCount),
            })),
          };
        }

        return old;
      });
    },

    ...options,
  });
}

/**
 * Update a comment
 *
 * @example
 * const updateMutation = useUpdateComment();
 * updateMutation.mutate({
 *   commentId: 'comment-123',
 *   sessionId: 'session-456',
 *   data: { content: 'Updated comment text' }
 * });
 */
export function useUpdateComment(
  options?: Partial<
    UseMutationOptions<
      void,
      Error,
      { commentId: string; sessionId: string; data: UpdateCommentData }
    >
  >
) {
  const queryClient = useQueryClient();

  return useMutation<
    void,
    Error,
    { commentId: string; sessionId: string; data: UpdateCommentData }
  >({
    mutationFn: ({ commentId, data }) =>
      commentService.updateComment(commentId, data),

    onMutate: async ({ commentId, sessionId, data }) => {
      await queryClient.cancelQueries({
        queryKey: COMMENT_KEYS.session(sessionId),
      });

      const previousComments = queryClient.getQueryData(
        COMMENT_KEYS.session(sessionId)
      );

      // Optimistically update
      queryClient.setQueryData(COMMENT_KEYS.list(sessionId), (old: any) => {
        if (!old?.comments) return old;

        return {
          ...old,
          comments: old.comments.map((comment: any) =>
            comment.id === commentId
              ? { ...comment, ...data, isEdited: true }
              : comment
          ),
        };
      });

      return { previousComments };
    },

    onError: (error, { sessionId }, context: unknown) => {
      if (
        context &&
        typeof context === 'object' &&
        'previousComments' in context &&
        context.previousComments
      ) {
        queryClient.setQueryData(
          COMMENT_KEYS.session(sessionId),
          context.previousComments
        );
      }
    },

    onSuccess: (_, { sessionId }) => {
      queryClient.invalidateQueries({
        queryKey: COMMENT_KEYS.session(sessionId),
      });
    },

    ...options,
  });
}

/**
 * Delete a comment
 *
 * @example
 * const deleteMutation = useDeleteComment();
 * deleteMutation.mutate({
 *   commentId: 'comment-123',
 *   sessionId: 'session-456'
 * });
 */
export function useDeleteComment(
  options?: Partial<
    UseMutationOptions<void, Error, { commentId: string; sessionId: string }>
  >
) {
  const queryClient = useQueryClient();

  return useMutation<void, Error, { commentId: string; sessionId: string }>({
    mutationFn: ({ commentId }) => commentService.deleteComment(commentId),

    onMutate: async ({ commentId, sessionId }) => {
      await queryClient.cancelQueries({
        queryKey: COMMENT_KEYS.session(sessionId),
      });

      const previousComments = queryClient.getQueryData(
        COMMENT_KEYS.session(sessionId)
      );

      // Optimistically remove from comments list
      queryClient.setQueryData(COMMENT_KEYS.list(sessionId), (old: any) => {
        if (!old?.comments) return old;

        return {
          ...old,
          comments: old.comments.filter(
            (comment: any) => comment.id !== commentId
          ),
        };
      });

      return { previousComments };
    },

    onError: (error, { sessionId }, context: unknown) => {
      if (
        context &&
        typeof context === 'object' &&
        'previousComments' in context &&
        context.previousComments
      ) {
        queryClient.setQueryData(
          COMMENT_KEYS.session(sessionId),
          context.previousComments
        );
      }
    },

    onSuccess: (_, { sessionId }) => {
      // Invalidate comments
      queryClient.invalidateQueries({
        queryKey: COMMENT_KEYS.session(sessionId),
      });

      // Update comment count in session cache and feed
      const updateSessionCommentCount = (session: any) => {
        if (session?.id !== sessionId) return session;
        return {
          ...session,
          commentCount: Math.max(0, (session.commentCount || 0) - 1),
        };
      };

      // Update session detail cache
      queryClient.setQueryData(
        SESSION_KEYS.detail(sessionId),
        updateSessionCommentCount
      );

      // Update feed caches
      queryClient.setQueriesData<any>({ queryKey: ['feed'] }, (old: any) => {
        if (!old) return old;

        if (Array.isArray(old)) {
          return old.map(updateSessionCommentCount);
        } else if (old.sessions) {
          return {
            ...old,
            sessions: old.sessions.map(updateSessionCommentCount),
          };
        } else if (old.pages) {
          return {
            ...old,
            pages: old.pages.map((page: any) => ({
              ...page,
              sessions: page.sessions.map(updateSessionCommentCount),
            })),
          };
        }

        return old;
      });
    },

    ...options,
  });
}

/**
 * Like or unlike a comment
 *
 * @example
 * const likeMutation = useCommentLike('session-123');
 * likeMutation.mutate({ commentId: 'comment-456', action: 'like' });
 * likeMutation.mutate({ commentId: 'comment-456', action: 'unlike' });
 */
export function useCommentLike(
  sessionId: string,
  options?: Partial<UseMutationOptions<void, Error, CommentLikeData>>
) {
  const queryClient = useQueryClient();

  return useMutation<void, Error, CommentLikeData>({
    mutationFn: async ({ commentId, action }) => {
      try {
        if (action === 'like') {
          await commentService.likeComment(commentId);
        } else {
          await commentService.unlikeComment(commentId);
        }
      } catch (error: any) {
        // If already liked/unliked, treat as success (idempotent)
        const errorMsg = error.message || String(error);
        if (
          errorMsg.includes('Already liked') ||
          errorMsg.includes('not liked')
        ) {
          return;
        }
        throw error;
      }
    },

    onMutate: async ({ commentId, action }) => {
      await queryClient.cancelQueries({
        queryKey: COMMENT_KEYS.session(sessionId),
      });

      const previousComments = queryClient.getQueryData(
        COMMENT_KEYS.session(sessionId)
      );

      const increment = action === 'like' ? 1 : -1;

      // Optimistically update comments
      queryClient.setQueryData(COMMENT_KEYS.list(sessionId), (old: any) => {
        if (!old?.comments) return old;

        return {
          ...old,
          comments: old.comments.map((comment: any) =>
            comment.id === commentId
              ? {
                  ...comment,
                  likeCount: Math.max(0, (comment.likeCount || 0) + increment),
                  isLiked: action === 'like',
                }
              : comment
          ),
        };
      });

      return { previousComments };
    },

    onError: (error, variables, context: unknown) => {
      if (
        context &&
        typeof context === 'object' &&
        'previousComments' in context &&
        context.previousComments
      ) {
        queryClient.setQueryData(
          COMMENT_KEYS.session(sessionId),
          context.previousComments
        );
      }
    },

    onSuccess: () => {
      // Refetch to ensure consistency
      queryClient.invalidateQueries({
        queryKey: COMMENT_KEYS.session(sessionId),
      });
    },

    ...options,
  });
}

/**
 * Helper hook to invalidate comments for a session
 *
 * @example
 * const invalidateComments = useInvalidateComments();
 * invalidateComments(sessionId);
 */
export function useInvalidateComments() {
  const queryClient = useQueryClient();

  return (sessionId: string) => {
    queryClient.invalidateQueries({
      queryKey: COMMENT_KEYS.session(sessionId),
    });
    queryClient.invalidateQueries({ queryKey: COMMENT_KEYS.list(sessionId) });
  };
}

/**
 * Helper hook to invalidate all comments
 *
 * @example
 * const invalidateAllComments = useInvalidateAllComments();
 * invalidateAllComments();
 */
export function useInvalidateAllComments() {
  const queryClient = useQueryClient();

  return () => {
    queryClient.invalidateQueries({ queryKey: COMMENT_KEYS.all() });
  };
}

/**
 * Comment Service - Business Logic Layer
 *
 * Orchestrates business workflows for comments.
 * No React dependencies - pure TypeScript for testability.
 */

import { firebaseApi } from '@/lib/api';
import {
  Comment,
  CommentWithDetails,
  CreateCommentData,
  UpdateCommentData,
  CommentsResponse,
} from '@/types';

export class CommentService {
  /**
   * Get all comments for a session
   */
  async getSessionComments(
    sessionId: string,
    limit: number = 20,
    cursor?: string
  ): Promise<CommentsResponse> {
    try {
      return await firebaseApi.comment.getSessionComments(sessionId, limit);
    } catch (error) {
      console.error('Error getting session comments:', error);
      return {
        comments: [],
        hasMore: false,
      };
    }
  }

  /**
   * Create a new comment
   */
  async createComment(data: CreateCommentData): Promise<CommentWithDetails> {
    return firebaseApi.comment.createComment(data);
  }

  /**
   * Update a comment
   */
  async updateComment(
    commentId: string,
    data: UpdateCommentData
  ): Promise<void> {
    await firebaseApi.comment.updateComment(commentId, data);
  }

  /**
   * Delete a comment
   */
  async deleteComment(commentId: string): Promise<void> {
    return firebaseApi.comment.deleteComment(commentId);
  }

  /**
   * Like a comment
   */
  async likeComment(commentId: string): Promise<void> {
    return firebaseApi.comment.likeComment(commentId);
  }

  /**
   * Unlike a comment
   */
  async unlikeComment(commentId: string): Promise<void> {
    return firebaseApi.comment.unlikeComment(commentId);
  }
}

export interface CommentLikeData {
  commentId: string;
  action: 'like' | 'unlike';
}

/**
 * Feed Query Hooks - React Query Boundary
 *
 * This is the ONLY place where React Query should be used for feed.
 * All components should use these hooks instead of direct React Query or firebaseApi calls.
 *
 * Architecture:
 * Components ‚Üí useFeed hooks (React Query) ‚Üí FeedService ‚Üí Repositories ‚Üí Firebase
 */

import {
  useInfiniteQuery,
  useQuery,
  UseQueryOptions,
  UseInfiniteQueryOptions,
  InfiniteData,
} from '@tanstack/react-query';
import { FeedService, FeedFilters, FeedOptions } from '../services/FeedService';
import { Session } from '@/domain/entities/Session';
import { STANDARD_CACHE_TIMES } from '@/lib/react-query';

// Singleton service instance
const feedService = new FeedService();

// ==================== TYPES ====================

// Type for infinite query options without 'select' property
// Infinite queries must return InfiniteData, so select cannot transform to FeedResult
type InfiniteQueryOptions<TData, TError> = Omit<
  Partial<UseInfiniteQueryOptions<TData, TError, InfiniteData<TData, unknown>>>,
  'select'
>;

export interface FeedResult {
  sessions: Session[];
  hasMore: boolean;
  nextCursor?: string;
}

export interface FeedPage {
  sessions: Session[];
  hasMore: boolean;
  nextCursor?: string;
}

// ==================== CACHE KEYS ====================
// Hierarchical cache keys for efficient invalidation

export const FEED_KEYS = {
  all: () => ['feed'] as const,
  lists: () => [...FEED_KEYS.all(), 'list'] as const,
  list: (userId: string, filters: FeedFilters) =>
    [...FEED_KEYS.lists(), userId, filters] as const,
  user: (userId: string) => [...FEED_KEYS.all(), 'user', userId] as const,
  group: (groupId: string) => [...FEED_KEYS.all(), 'group', groupId] as const,
};

// ==================== QUERY HOOKS ====================

/**
 * Get paginated feed with infinite scroll support
 *
 * This is the primary hook for feed functionality with pagination.
 *
 * @example
 * const {
 *   data,
 *   fetchNextPage,
 *   hasNextPage,
 *   isFetchingNextPage
 * } = useFeedInfinite(userId, { type: 'following' });
 *
 * // Access all pages:
 * const allSessions = data?.pages.flatMap(page => page.sessions) || [];
 */
export function useFeedInfinite(
  currentUserId: string,
  filters: FeedFilters = {},
  options?: InfiniteQueryOptions<FeedResult, Error>
) {
  return useInfiniteQuery<FeedResult, Error>({
    queryKey: FEED_KEYS.list(currentUserId, filters),
    queryFn: ({ pageParam }) =>
      feedService.getFeed(currentUserId, filters, {
        limit: 20,
        cursor: pageParam as string | undefined,
      }),
    getNextPageParam: lastPage => {
      return lastPage.hasMore ? lastPage.nextCursor : undefined;
    },
    initialPageParam: undefined as string | undefined,
    staleTime: STANDARD_CACHE_TIMES.SHORT, // 1 minute - feed data changes frequently
    enabled: !!currentUserId,
    ...options,
  });
}

/**
 * Get initial feed page (non-paginated)
 *
 * Use this for simple feed displays without infinite scroll.
 *
 * @example
 * const { data: feedData, isLoading, error } = useFeed(userId, { type: 'all' });
 */
export function useFeed(
  currentUserId: string,
  filters: FeedFilters = {},
  limit: number = 20,
  options?: Partial<UseQueryOptions<FeedResult, Error>>
) {
  return useQuery<FeedResult, Error>({
    queryKey: [...FEED_KEYS.list(currentUserId, filters), limit],
    queryFn: () => feedService.getFeed(currentUserId, filters, { limit }),
    staleTime: STANDARD_CACHE_TIMES.SHORT, // 1 minute
    enabled: !!currentUserId,
    ...options,
  });
}

/**
 * Get user-specific feed
 *
 * Shows sessions from a specific user (for profile pages).
 *
 * @example
 * const { data: userFeed } = useUserFeed(currentUserId, targetUserId);
 */
export function useUserFeed(
  currentUserId: string,
  userId: string,
  limit: number = 20,
  options?: Partial<UseQueryOptions<FeedResult, Error>>
) {
  return useQuery<FeedResult, Error>({
    queryKey: [...FEED_KEYS.user(userId), limit],
    queryFn: () =>
      feedService.getFeed(currentUserId, { type: 'user', userId }, { limit }),
    staleTime: STANDARD_CACHE_TIMES.MEDIUM, // 5 minutes
    enabled: !!currentUserId && !!userId,
    ...options,
  });
}

/**
 * Get group-specific feed
 *
 * Shows sessions from a specific group.
 *
 * @example
 * const { data: groupFeed } = useGroupFeed(currentUserId, groupId);
 */
export function useGroupFeed(
  currentUserId: string,
  groupId: string,
  limit: number = 20,
  options?: Partial<UseQueryOptions<FeedResult, Error>>
) {
  return useQuery<FeedResult, Error>({
    queryKey: [...FEED_KEYS.group(groupId), limit],
    queryFn: () =>
      feedService.getFeed(currentUserId, { type: 'group', groupId }, { limit }),
    staleTime: STANDARD_CACHE_TIMES.MEDIUM, // 5 minutes
    enabled: !!currentUserId && !!groupId,
    ...options,
  });
}

/**
 * Get following feed (infinite scroll version)
 *
 * Shows sessions from users the current user follows.
 *
 * @example
 * const {
 *   data,
 *   fetchNextPage,
 *   hasNextPage
 * } = useFollowingFeedInfinite(userId);
 */
export function useFollowingFeedInfinite(
  currentUserId: string,
  options?: InfiniteQueryOptions<FeedResult, Error>
) {
  return useFeedInfinite(currentUserId, { type: 'following' }, options);
}

/**
 * Get public/all feed (infinite scroll version)
 *
 * Shows all public sessions.
 *
 * @example
 * const {
 *   data,
 *   fetchNextPage,
 *   hasNextPage
 * } = usePublicFeedInfinite(userId);
 */
export function usePublicFeedInfinite(
  currentUserId: string,
  options?: InfiniteQueryOptions<FeedResult, Error>
) {
  return useFeedInfinite(currentUserId, { type: 'all' }, options);
}

/**
 * Feed Mutation Hooks - React Query Boundary
 *
 * Handles mutations that affect the feed (refresh, invalidation).
 * Session creation/deletion is handled in the Sessions feature.
 */

import { useMutation, useQueryClient, UseMutationOptions } from '@tanstack/react-query';
import { FeedService, FeedFilters } from '../services/FeedService';
import { FEED_KEYS, FeedResult } from './useFeed';

const feedService = new FeedService();

/**
 * Refresh the feed
 *
 * Forces a fresh fetch of feed data.
 *
 * @example
 * const refreshMutation = useRefreshFeed();
 * refreshMutation.mutate({ userId, filters: { type: 'following' } });
 */
export function useRefreshFeed(
  options?: Partial<UseMutationOptions<FeedResult, Error, { userId: string; filters?: FeedFilters }>>
) {
  const queryClient = useQueryClient();

  return useMutation<FeedResult, Error, { userId: string; filters?: FeedFilters }>({
    mutationFn: ({ userId, filters }) => feedService.refreshFeed(userId, filters),

    onSuccess: (_, { userId, filters = {} }) => {
      // Invalidate the specific feed query
      queryClient.invalidateQueries({ queryKey: FEED_KEYS.list(userId, filters) });
    },

    ...options,
  });
}

/**
 * Helper hook to invalidate all feed queries
 *
 * Use this when an action affects the feed but is handled elsewhere
 * (e.g., creating a session, supporting a session, etc.)
 *
 * @example
 * const invalidateFeeds = useInvalidateFeeds();
 *
 * // After creating a session:
 * invalidateFeeds();
 */
export function useInvalidateFeeds() {
  const queryClient = useQueryClient();

  return () => {
    queryClient.invalidateQueries({ queryKey: FEED_KEYS.all() });
  };
}

/**
 * Helper hook to invalidate a specific user's feed
 *
 * @example
 * const invalidateUserFeed = useInvalidateUserFeed();
 * invalidateUserFeed(userId);
 */
export function useInvalidateUserFeed() {
  const queryClient = useQueryClient();

  return (userId: string) => {
    queryClient.invalidateQueries({ queryKey: FEED_KEYS.user(userId) });
  };
}

/**
 * Helper hook to invalidate a specific group's feed
 *
 * @example
 * const invalidateGroupFeed = useInvalidateGroupFeed();
 * invalidateGroupFeed(groupId);
 */
export function useInvalidateGroupFeed() {
  const queryClient = useQueryClient();

  return (groupId: string) => {
    queryClient.invalidateQueries({ queryKey: FEED_KEYS.group(groupId) });
  };
}

/**
 * Helper to manually add a session to the feed cache (optimistic update)
 *
 * Use this when creating a new session to immediately show it in the feed.
 *
 * @example
 * const addToFeed = useAddToFeedCache();
 *
 * // After creating a session:
 * addToFeed(userId, { type: 'following' }, newSession);
 */
export function useAddToFeedCache() {
  const queryClient = useQueryClient();

  return (userId: string, filters: FeedFilters, newSession: any) => {
    const queryKey = FEED_KEYS.list(userId, filters);

    // Update infinite query cache
    queryClient.setQueryData<any>(queryKey, (old: any) => {
      if (!old?.pages) return old;

      // Add to the first page
      return {
        ...old,
        pages: old.pages.map((page: any, index: number) => {
          if (index === 0) {
            return {
              ...page,
              sessions: [newSession, ...page.sessions],
            };
          }
          return page;
        }),
      };
    });
  };
}

/**
 * Helper to manually remove a session from the feed cache (optimistic update)
 *
 * Use this when deleting a session to immediately remove it from the feed.
 *
 * @example
 * const removeFromFeed = useRemoveFromFeedCache();
 *
 * // After deleting a session:
 * removeFromFeed(userId, { type: 'following' }, sessionId);
 */
export function useRemoveFromFeedCache() {
  const queryClient = useQueryClient();

  return (userId: string, filters: FeedFilters, sessionId: string) => {
    const queryKey = FEED_KEYS.list(userId, filters);

    // Update infinite query cache
    queryClient.setQueryData<any>(queryKey, (old: any) => {
      if (!old?.pages) return old;

      return {
        ...old,
        pages: old.pages.map((page: any) => ({
          ...page,
          sessions: page.sessions.filter((s: any) => s.id !== sessionId),
        })),
      };
    });
  };
}

/**
 * Feed Service - Application Layer
 *
 * Orchestrates feed workflows and business logic.
 * Coordinates between repositories to build different feed types.
 */

import { Session } from '@/domain/entities/Session';
import {
  FeedRepository,
  FeedResult,
} from '@/infrastructure/firebase/repositories/FeedRepository';
import { SocialGraphRepository } from '@/infrastructure/firebase/repositories/SocialGraphRepository';
import { SessionRepository } from '@/infrastructure/firebase/repositories/SessionRepository';

export type FeedType =
  | 'following'
  | 'trending'
  | 'all'
  | 'user'
  | 'group'
  | 'recent'
  | 'group-members-unfollowed';

export interface FeedFilters {
  type?: FeedType;
  userId?: string;
  groupId?: string;
}

export interface FeedOptions {
  limit?: number;
  cursor?: string;
}

export class FeedService {
  private readonly feedRepo: FeedRepository;
  private readonly socialGraphRepo: SocialGraphRepository;
  private readonly sessionRepo: SessionRepository;

  constructor() {
    this.feedRepo = new FeedRepository();
    this.socialGraphRepo = new SocialGraphRepository();
    this.sessionRepo = new SessionRepository();
  }

  /**
   * Get feed based on filters
   */
  async getFeed(
    currentUserId: string,
    filters: FeedFilters = {},
    options: FeedOptions = {}
  ): Promise<FeedResult> {
    const { type = 'following' } = filters;
    const { limit = 20, cursor } = options;

    switch (type) {
      case 'following':
        return this.getFollowingFeed(currentUserId, limit, cursor);

      case 'trending':
      case 'all':
      case 'recent':
        return this.getPublicFeed(limit, cursor);

      case 'user':
        if (!filters.userId) {
          throw new Error('userId required for user feed');
        }
        return this.getUserFeed(filters.userId, limit, cursor);

      case 'group':
        if (!filters.groupId) {
          throw new Error('groupId required for group feed');
        }
        return this.getGroupFeed(filters.groupId, limit, cursor);

      case 'group-members-unfollowed':
        return this.getGroupMembersUnfollowedFeed(currentUserId, limit, cursor);

      default:
        throw new Error(`Unknown feed type: ${type}`);
    }
  }

  /**
   * Get feed from users the current user is following
   */
  private async getFollowingFeed(
    currentUserId: string,
    limit: number,
    cursor?: string
  ): Promise<FeedResult> {
    // Get list of following
    const followingIds =
      await this.socialGraphRepo.getFollowingIds(currentUserId);

    if (followingIds.length === 0) {
      return { sessions: [], hasMore: false };
    }

    // Fetch sessions from followed users
    return this.feedRepo.getFeedForFollowing(followingIds, limit, cursor);
  }

  /**
   * Get public feed (all sessions with everyone visibility)
   */
  private async getPublicFeed(
    limit: number,
    cursor?: string
  ): Promise<FeedResult> {
    return this.feedRepo.getPublicFeed(limit, cursor);
  }

  /**
   * Get feed for a specific user
   */
  private async getUserFeed(
    userId: string,
    limit: number,
    cursor?: string
  ): Promise<FeedResult> {
    // Use SessionRepository's findByUserId method
    const sessions = await this.sessionRepo.findByUserId(userId, limit);

    // For now, we don't have pagination cursor support in SessionRepository
    // This would need to be enhanced for proper pagination
    return {
      sessions,
      hasMore: sessions.length >= limit,
      nextCursor:
        sessions.length >= limit
          ? sessions[sessions.length - 1]?.id
          : undefined,
    };
  }

  /**
   * Get feed for a specific group
   */
  private async getGroupFeed(
    groupId: string,
    limit: number,
    cursor?: string
  ): Promise<FeedResult> {
    // Use SessionRepository's findByGroupId method
    const sessions = await this.sessionRepo.findByGroupId(groupId, limit);

    return {
      sessions,
      hasMore: sessions.length >= limit,
      nextCursor:
        sessions.length >= limit
          ? sessions[sessions.length - 1]?.id
          : undefined,
    };
  }

  /**
   * Get feed from group members who are not followed
   */
  private async getGroupMembersUnfollowedFeed(
    currentUserId: string,
    limit: number,
    cursor?: string
  ): Promise<FeedResult> {
    // Get all group member IDs
    const groupMemberIds =
      await this.socialGraphRepo.getGroupMemberIds(currentUserId);

    if (groupMemberIds.length === 0) {
      return { sessions: [], hasMore: false };
    }

    // Get following IDs
    const followingIds =
      await this.socialGraphRepo.getFollowingIds(currentUserId);

    // Fetch sessions from unfollowed group members
    return this.feedRepo.getFeedForGroupMembersUnfollowed(
      groupMemberIds,
      followingIds,
      limit,
      cursor
    );
  }

  /**
   * Refresh feed (invalidate cache - handled by React Query in hooks layer)
   */
  async refreshFeed(
    currentUserId: string,
    filters: FeedFilters = {}
  ): Promise<FeedResult> {
    // Simply re-fetch with no cursor
    return this.getFeed(currentUserId, filters, { limit: 20 });
  }
}

