/**
 * useGroupLeaderboard Hook
 *
 * Fetches and manages group leaderboard data based on member activity.
 */

import { useQuery } from '@tanstack/react-query';
import {
  collection,
  query,
  where,
  getDocs,
  getDoc,
  doc,
} from 'firebase/firestore';
import { db } from '@/lib/firebase';
import { STANDARD_CACHE_TIMES } from '@/lib/react-query';

export interface LeaderboardEntry {
  userId: string;
  name: string;
  username: string;
  profilePicture?: string;
  totalHours: number;
  sessionCount: number;
  rank: number;
}

export function useGroupLeaderboard(
  groupId: string,
  timeframe: 'week' | 'month' | 'allTime' = 'allTime'
) {
  return useQuery({
    queryKey: ['group-leaderboard', groupId, timeframe],
    queryFn: async () => {
      // Fetch group memberships
      const membershipsRef = collection(db, 'groupMemberships');
      const membershipsQuery = query(
        membershipsRef,
        where('groupId', '==', groupId),
        where('status', '==', 'active')
      );

      const membershipsSnapshot = await getDocs(membershipsQuery);
      const memberIds = membershipsSnapshot.docs.map(doc => doc.data().userId);

      if (memberIds.length === 0) {
        return [];
      }

      // Calculate date range based on timeframe
      let startDate: Date | undefined;
      const now = new Date();

      if (timeframe === 'week') {
        startDate = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
      } else if (timeframe === 'month') {
        startDate = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);
      }

      // Fetch sessions for each member
      const leaderboardPromises = memberIds.map(async userId => {
        try {
          // Fetch user data
          const userDoc = await getDoc(doc(db, 'users', userId));
          if (!userDoc.exists()) {
            return null;
          }

          const userData = userDoc.data();

          // Fetch sessions
          const sessionsRef = collection(db, 'sessions');
          let sessionsQuery = query(sessionsRef, where('userId', '==', userId));

          // Add date filter if needed
          if (startDate) {
            sessionsQuery = query(
              sessionsRef,
              where('userId', '==', userId),
              where('createdAt', '>=', startDate)
            );
          }

          const sessionsSnapshot = await getDocs(sessionsQuery);

          // Calculate total hours and session count
          let totalSeconds = 0;
          let sessionCount = 0;

          sessionsSnapshot.docs.forEach(sessionDoc => {
            const sessionData = sessionDoc.data();
            totalSeconds += sessionData.duration || 0;
            sessionCount++;
          });

          const totalHours = totalSeconds / 3600;

          return {
            userId,
            name: userData.name || 'Unknown User',
            username: userData.username || 'unknown',
            profilePicture: userData.profilePicture,
            totalHours,
            sessionCount,
            rank: 0, // Will be set after sorting
          } as LeaderboardEntry;
        } catch (error) {
          // Failed to fetch leaderboard data for user
          return null;
        }
      });

      const leaderboardData = await Promise.all(leaderboardPromises);

      // Filter out null values and sort by total hours
      const validEntries = leaderboardData
        .filter((entry): entry is LeaderboardEntry => entry !== null)
        .sort((a, b) => b.totalHours - a.totalHours);

      // Assign ranks
      validEntries.forEach((entry, index) => {
        entry.rank = index + 1;
      });

      return validEntries;
    },
    staleTime: STANDARD_CACHE_TIMES.SHORT, // 1 minute - leaderboard data changes frequently
    enabled: !!groupId,
  });
}

/**
 * useGroupMembers Hook
 *
 * Fetches and manages group member data with their profiles.
 */

import { useQuery } from '@tanstack/react-query';
import {
  collection,
  query,
  where,
  getDocs,
  getDoc,
  doc,
} from 'firebase/firestore';
import { db } from '@/lib/firebase';
import { STANDARD_CACHE_TIMES } from '@/lib/react-query';

export interface GroupMember {
  id: string;
  name: string;
  username: string;
  email: string;
  profilePicture?: string;
  bio?: string;
  joinedAt: Date;
}

export function useGroupMembers(groupId: string) {
  return useQuery({
    queryKey: ['group-members', groupId],
    queryFn: async () => {
      // Fetch group memberships
      const membershipsRef = collection(db, 'groupMemberships');
      const q = query(
        membershipsRef,
        where('groupId', '==', groupId),
        where('status', '==', 'active')
      );

      const membershipsSnapshot = await getDocs(q);

      // Fetch user details for each member
      const memberPromises = membershipsSnapshot.docs.map(
        async membershipDoc => {
          const membershipData = membershipDoc.data();
          const userId = membershipData.userId;

          try {
            const userDoc = await getDoc(doc(db, 'users', userId));

            if (!userDoc.exists()) {
              return null;
            }

            const userData = userDoc.data();

            return {
              id: userId,
              name: userData.name || 'Unknown User',
              username: userData.username || 'unknown',
              email: userData.email || '',
              profilePicture: userData.profilePicture,
              bio: userData.bio,
              joinedAt: membershipData.joinedAt?.toDate() || new Date(),
            } as GroupMember;
          } catch (error) {
            // Failed to fetch user
            return null;
          }
        }
      );

      const members = await Promise.all(memberPromises);

      // Filter out null values (deleted/inaccessible users)
      return members.filter((member): member is GroupMember => member !== null);
    },
    staleTime: STANDARD_CACHE_TIMES.MEDIUM, // 5 minutes
    enabled: !!groupId,
  });
}

/**
 * Group Mutations - React Query Boundary
 *
 * This file contains all group mutation hooks (create, update, delete, join, leave).
 * Includes optimistic updates and cache invalidation.
 */

import {
  useMutation,
  useQueryClient,
  UseMutationOptions,
} from '@tanstack/react-query';
import { GroupService } from '../services/GroupService';
import { GROUPS_KEYS } from './useGroups';

// Singleton service instance
const groupService = new GroupService();

// ==================== MUTATION HOOKS ====================

/**
 * Join a group
 *
 * @example
 * const joinMutation = useJoinGroup();
 * joinMutation.mutate({ groupId: '123', userId: 'user-456' });
 */
export function useJoinGroup(
  options?: Partial<
    UseMutationOptions<void, Error, { groupId: string; userId: string }>
  >
) {
  const queryClient = useQueryClient();

  return useMutation<void, Error, { groupId: string; userId: string }>({
    mutationFn: ({ groupId, userId }) =>
      groupService.joinGroup(groupId, userId),

    onMutate: async ({ groupId, userId }) => {
      // Cancel outgoing queries
      await queryClient.cancelQueries({
        queryKey: GROUPS_KEYS.detail(groupId),
      });
      await queryClient.cancelQueries({
        queryKey: GROUPS_KEYS.userGroups(userId),
      });

      // Snapshot previous values for rollback
      const previousGroup = queryClient.getQueryData(
        GROUPS_KEYS.detail(groupId)
      );
      const previousUserGroups = queryClient.getQueryData(
        GROUPS_KEYS.userGroups(userId)
      );

      // Optimistically update group member count
      queryClient.setQueryData(GROUPS_KEYS.detail(groupId), (old: any) => {
        if (!old) return old;
        return {
          ...old,
          memberIds: [...old.memberIds, userId],
        };
      });

      return { previousGroup, previousUserGroups };
    },

    onError: (error, variables, context: unknown) => {
      // Rollback on error
      if (
        context &&
        typeof context === 'object' &&
        'previousGroup' in context &&
        context.previousGroup
      ) {
        queryClient.setQueryData(
          GROUPS_KEYS.detail(variables.groupId),
          context.previousGroup
        );
      }
      if (
        context &&
        typeof context === 'object' &&
        'previousUserGroups' in context &&
        context.previousUserGroups
      ) {
        queryClient.setQueryData(
          GROUPS_KEYS.userGroups(variables.userId),
          context.previousUserGroups
        );
      }
    },

    onSuccess: (_, { groupId, userId }) => {
      // Invalidate relevant caches
      queryClient.invalidateQueries({ queryKey: GROUPS_KEYS.detail(groupId) });
      queryClient.invalidateQueries({
        queryKey: GROUPS_KEYS.userGroups(userId),
      });
      queryClient.invalidateQueries({ queryKey: GROUPS_KEYS.stats(groupId) });
    },

    ...options,
  });
}

/**
 * Leave a group
 *
 * @example
 * const leaveMutation = useLeaveGroup();
 * leaveMutation.mutate({ groupId: '123', userId: 'user-456' });
 */
export function useLeaveGroup(
  options?: Partial<
    UseMutationOptions<void, Error, { groupId: string; userId: string }>
  >
) {
  const queryClient = useQueryClient();

  return useMutation<void, Error, { groupId: string; userId: string }>({
    mutationFn: ({ groupId, userId }) =>
      groupService.leaveGroup(groupId, userId),

    onMutate: async ({ groupId, userId }) => {
      // Cancel outgoing queries
      await queryClient.cancelQueries({
        queryKey: GROUPS_KEYS.detail(groupId),
      });
      await queryClient.cancelQueries({
        queryKey: GROUPS_KEYS.userGroups(userId),
      });

      // Snapshot previous values
      const previousGroup = queryClient.getQueryData(
        GROUPS_KEYS.detail(groupId)
      );
      const previousUserGroups = queryClient.getQueryData(
        GROUPS_KEYS.userGroups(userId)
      );

      // Optimistically update group
      queryClient.setQueryData(GROUPS_KEYS.detail(groupId), (old: any) => {
        if (!old) return old;
        return {
          ...old,
          memberIds: old.memberIds.filter((id: string) => id !== userId),
        };
      });

      // Optimistically remove from user groups
      queryClient.setQueryData(GROUPS_KEYS.userGroups(userId), (old: any) => {
        if (!Array.isArray(old)) return old;
        return old.filter((group: any) => group.id !== groupId);
      });

      return { previousGroup, previousUserGroups };
    },

    onError: (error, variables, context: unknown) => {
      // Rollback on error
      if (
        context &&
        typeof context === 'object' &&
        'previousGroup' in context &&
        context.previousGroup
      ) {
        queryClient.setQueryData(
          GROUPS_KEYS.detail(variables.groupId),
          context.previousGroup
        );
      }
      if (
        context &&
        typeof context === 'object' &&
        'previousUserGroups' in context &&
        context.previousUserGroups
      ) {
        queryClient.setQueryData(
          GROUPS_KEYS.userGroups(variables.userId),
          context.previousUserGroups
        );
      }
    },

    onSuccess: (_, { groupId, userId }) => {
      // Invalidate relevant caches
      queryClient.invalidateQueries({ queryKey: GROUPS_KEYS.detail(groupId) });
      queryClient.invalidateQueries({
        queryKey: GROUPS_KEYS.userGroups(userId),
      });
      queryClient.invalidateQueries({ queryKey: GROUPS_KEYS.stats(groupId) });
    },

    ...options,
  });
}

/**
 * Helper hook to invalidate all group-related queries
 * Useful for scenarios where you need to refresh all group data
 *
 * @example
 * const invalidateGroups = useInvalidateGroups();
 * invalidateGroups();
 */
export function useInvalidateGroups() {
  const queryClient = useQueryClient();

  return () => {
    queryClient.invalidateQueries({ queryKey: GROUPS_KEYS.all() });
  };
}

/**
 * Helper hook to invalidate specific group data
 *
 * @example
 * const invalidateGroup = useInvalidateGroup();
 * invalidateGroup('group-123');
 */
export function useInvalidateGroup() {
  const queryClient = useQueryClient();

  return (groupId: string) => {
    queryClient.invalidateQueries({ queryKey: GROUPS_KEYS.detail(groupId) });
  };
}

/**
 * Groups Feature Hooks - React Query Boundary
 *
 * This is the ONLY place where React Query should be used for groups.
 * All components should use these hooks instead of direct React Query or firebaseApi calls.
 *
 * Architecture:
 * Components ‚Üí useGroups hooks (React Query) ‚Üí GroupService ‚Üí GroupRepository ‚Üí Firebase
 */

import { useQuery, UseQueryOptions } from '@tanstack/react-query';
import { GroupService } from '../services/GroupService';
import { Group as DomainGroup } from '@/domain/entities/Group';
import { Group as UIGroup } from '@/types';
import {
  LeaderboardEntry,
  TimePeriod,
  GroupStats,
} from '../types/groups.types';

// Singleton service instance
const groupService = new GroupService();

// ==================== ADAPTERS ====================
/**
 * Convert domain Group entity to UI Group interface
 * Maps domain model to the shape expected by UI components
 */
function adaptDomainGroupToUI(domainGroup: DomainGroup): UIGroup {
  return {
    id: domainGroup.id,
    name: domainGroup.name,
    description: domainGroup.description,
    category: domainGroup.category,
    type: 'professional', // Default type - can be made configurable
    privacySetting:
      domainGroup.privacy === 'public' ? 'public' : 'approval-required',
    memberCount: domainGroup.getMemberCount(),
    adminUserIds: Array.from(domainGroup.adminUserIds),
    memberIds: Array.from(domainGroup.memberIds),
    createdByUserId: domainGroup.createdByUserId,
    createdAt: domainGroup.createdAt,
    updatedAt: domainGroup.createdAt, // Use createdAt as updatedAt since domain doesn't track it
    imageUrl: domainGroup.imageUrl,
    location: domainGroup.location,
    // Optional fields - add defaults if needed by UI layer
    icon: undefined,
    color: undefined,
    bannerUrl: undefined,
  };
}

/**
 * Convert array of domain Groups to UI Groups
 */
function adaptDomainGroupsToUI(domainGroups: DomainGroup[]): UIGroup[] {
  return domainGroups.map(adaptDomainGroupToUI);
}

// ==================== CACHE KEYS ====================
// Hierarchical cache keys for efficient invalidation
export const GROUPS_KEYS = {
  all: () => ['groups'] as const,
  lists: () => [...GROUPS_KEYS.all(), 'list'] as const,
  list: (filters?: string) => [...GROUPS_KEYS.lists(), { filters }] as const,
  details: () => [...GROUPS_KEYS.all(), 'detail'] as const,
  detail: (id: string) => [...GROUPS_KEYS.details(), id] as const,
  userGroups: (userId: string) =>
    [...GROUPS_KEYS.all(), 'user', userId] as const,
  publicGroups: () => [...GROUPS_KEYS.lists(), 'public'] as const,
  leaderboard: (groupId: string, period: TimePeriod) =>
    [...GROUPS_KEYS.detail(groupId), 'leaderboard', period] as const,
  stats: (groupId: string) =>
    [...GROUPS_KEYS.detail(groupId), 'stats'] as const,
};

// ==================== CACHE TIMES ====================
const CACHE_TIMES = {
  SHORT: 1 * 60 * 1000, // 1 minute
  MEDIUM: 5 * 60 * 1000, // 5 minutes
  LONG: 15 * 60 * 1000, // 15 minutes
  VERY_LONG: 60 * 60 * 1000, // 1 hour
};

// ==================== QUERY HOOKS ====================

/**
 * Get details for a specific group
 *
 * @example
 * const { data: group, isLoading, error } = useGroupDetails(groupId);
 */
export function useGroupDetails(
  groupId: string,
  options?: Partial<UseQueryOptions<UIGroup | null, Error>>
) {
  return useQuery<UIGroup | null, Error>({
    queryKey: GROUPS_KEYS.detail(groupId),
    queryFn: async () => {
      const domainGroup = await groupService.getGroupDetails(groupId);
      return domainGroup ? adaptDomainGroupToUI(domainGroup) : null;
    },
    staleTime: CACHE_TIMES.LONG, // 15 minutes cache
    enabled: !!groupId,
    ...options,
  });
}

/**
 * Get all groups the user is a member of
 *
 * @example
 * const { data: userGroups, isLoading } = useUserGroups(userId);
 */
export function useUserGroups(
  userId: string,
  options?: Partial<UseQueryOptions<UIGroup[], Error>>
) {
  return useQuery<UIGroup[], Error>({
    queryKey: GROUPS_KEYS.userGroups(userId),
    queryFn: async () => {
      const domainGroups = await groupService.getUserGroups(userId);
      return adaptDomainGroupsToUI(domainGroups);
    },
    staleTime: CACHE_TIMES.LONG, // 15 minutes cache
    enabled: !!userId,
    ...options,
  });
}

/**
 * Get all public groups for discovery
 *
 * @example
 * const { data: publicGroups, isLoading } = usePublicGroups();
 */
export function usePublicGroups(
  limit?: number,
  options?: Partial<UseQueryOptions<UIGroup[], Error>>
) {
  return useQuery<UIGroup[], Error>({
    queryKey: GROUPS_KEYS.publicGroups(),
    queryFn: async () => {
      const domainGroups = await groupService.getPublicGroups(limit);
      return adaptDomainGroupsToUI(domainGroups);
    },
    staleTime: CACHE_TIMES.LONG, // 15 minutes cache
    ...options,
  });
}

/**
 * Get group leaderboard for a specific time period
 *
 * @example
 * const { data: leaderboard } = useGroupLeaderboard(groupId, 'week');
 */
export function useGroupLeaderboard(
  groupId: string,
  period: TimePeriod,
  options?: Partial<UseQueryOptions<LeaderboardEntry[], Error>>
) {
  return useQuery<LeaderboardEntry[], Error>({
    queryKey: GROUPS_KEYS.leaderboard(groupId, period),
    queryFn: () => groupService.getGroupLeaderboard(groupId, period),
    staleTime: CACHE_TIMES.MEDIUM, // 5 minutes cache for leaderboards
    enabled: !!groupId,
    ...options,
  });
}

/**
 * Get group statistics
 *
 * @example
 * const { data: stats } = useGroupStats(groupId);
 */
export function useGroupStats(
  groupId: string,
  options?: Partial<UseQueryOptions<GroupStats, Error>>
) {
  return useQuery<GroupStats, Error>({
    queryKey: GROUPS_KEYS.stats(groupId),
    queryFn: () => groupService.getGroupStats(groupId),
    staleTime: CACHE_TIMES.MEDIUM, // 5 minutes cache for stats
    enabled: !!groupId,
    ...options,
  });
}

/**
 * Check if a user can join a specific group
 *
 * @example
 * const { data: canJoin } = useCanJoinGroup(groupId, userId);
 */
export function useCanJoinGroup(
  groupId: string,
  userId: string,
  options?: Partial<UseQueryOptions<boolean, Error>>
) {
  return useQuery<boolean, Error>({
    queryKey: [...GROUPS_KEYS.detail(groupId), 'canJoin', userId],
    queryFn: () => groupService.canUserJoin(groupId, userId),
    staleTime: CACHE_TIMES.MEDIUM,
    enabled: !!groupId && !!userId,
    ...options,
  });
}

/**
 * Check if a user can invite others to a specific group
 *
 * @example
 * const { data: canInvite } = useCanInviteToGroup(groupId, userId);
 */
export function useCanInviteToGroup(
  groupId: string,
  userId: string,
  options?: Partial<UseQueryOptions<boolean, Error>>
) {
  return useQuery<boolean, Error>({
    queryKey: [...GROUPS_KEYS.detail(groupId), 'canInvite', userId],
    queryFn: () => groupService.canUserInvite(groupId, userId),
    staleTime: CACHE_TIMES.MEDIUM,
    enabled: !!groupId && !!userId,
    ...options,
  });
}

/**
 * Get all groups with optional filters
 *
 * @example
 * const { data: groups } = useGroups({ category: 'fitness' });
 */
export function useGroups(
  filters?: Record<string, unknown>,
  options?: Partial<UseQueryOptions<UIGroup[], Error>>
) {
  return useQuery<UIGroup[], Error>({
    queryKey: GROUPS_KEYS.list(JSON.stringify(filters || {})),
    queryFn: async () => {
      const domainGroups = await groupService.getPublicGroups();
      return adaptDomainGroupsToUI(domainGroups);
    },
    staleTime: CACHE_TIMES.LONG,
    ...options,
  });
}

/**
 * Search for groups with specific criteria
 *
 * @example
 * const { data: groups } = useGroupSearch({ name: 'fitness' }, 50);
 */
export function useGroupSearch(
  filters: { name?: string; location?: string; category?: string },
  limit: number = 50,
  options?: Partial<UseQueryOptions<UIGroup[], Error>>
) {
  return useQuery<UIGroup[], Error>({
    queryKey: [...GROUPS_KEYS.lists(), 'search', filters, limit],
    queryFn: async () => {
      // Get all public groups and filter client-side
      const domainGroups = await groupService.getPublicGroups(limit);
      const filtered = domainGroups.filter(group => {
        const matchesName =
          !filters.name ||
          group.name.toLowerCase().includes(filters.name.toLowerCase());
        const matchesLocation =
          !filters.location ||
          group.location
            ?.toLowerCase()
            .includes(filters.location.toLowerCase());
        const matchesCategory =
          !filters.category || group.category === filters.category;
        return matchesName && matchesLocation && matchesCategory;
      });
      return adaptDomainGroupsToUI(filtered);
    },
    staleTime: CACHE_TIMES.SHORT,
    ...options,
  });
}

/**
 * Group Service - Application Layer
 *
 * Orchestrates business workflows for groups.
 * Coordinates between repositories and domain services.
 */

import { Group } from '@/domain/entities/Group';
import { GroupRepository } from '@/infrastructure/firebase/repositories/GroupRepository';
import { UserRepository } from '@/infrastructure/firebase/repositories/UserRepository';
import { SessionRepository } from '@/infrastructure/firebase/repositories/SessionRepository';
import { LeaderboardCalculator } from '../domain/LeaderboardCalculator';
import {
  LeaderboardEntry,
  TimePeriod,
  GroupStats,
} from '../types/groups.types';

/**
 * Note: For now, we're using direct instantiation.
 * In the future, we can add DI (tsyringe) for better testability.
 */
export class GroupService {
  private readonly groupRepo: GroupRepository;
  private readonly userRepo: UserRepository;
  private readonly sessionRepo: SessionRepository;
  private readonly leaderboardCalc: LeaderboardCalculator;

  constructor() {
    this.groupRepo = new GroupRepository();
    this.userRepo = new UserRepository();
    this.sessionRepo = new SessionRepository();
    this.leaderboardCalc = new LeaderboardCalculator();
  }

  /**
   * Get group details by ID
   */
  async getGroupDetails(groupId: string): Promise<Group | null> {
    return this.groupRepo.findById(groupId);
  }

  /**
   * Get groups for a user
   */
  async getUserGroups(userId: string, limit?: number): Promise<Group[]> {
    return this.groupRepo.findByMemberId(userId, limit);
  }

  /**
   * Get public groups
   */
  async getPublicGroups(limit?: number): Promise<Group[]> {
    return this.groupRepo.findPublic(limit);
  }

  /**
   * Join a group
   */
  async joinGroup(groupId: string, userId: string): Promise<void> {
    const group = await this.groupRepo.findById(groupId);

    if (!group) {
      throw new Error('Group not found');
    }

    // Business rule: Check if already a member
    if (group.isMember(userId)) {
      throw new Error('Already a member of this group');
    }

    // Create updated group with new member
    const updatedGroup = group.withAddedMember(userId);

    // Save to repository
    await this.groupRepo.save(updatedGroup);
  }

  /**
   * Leave a group
   */
  async leaveGroup(groupId: string, userId: string): Promise<void> {
    const group = await this.groupRepo.findById(groupId);

    if (!group) {
      throw new Error('Group not found');
    }

    // Business rule: Check if member
    if (!group.isMember(userId)) {
      throw new Error('Not a member of this group');
    }

    // Business rule: Owner cannot leave
    if (group.isOwner(userId)) {
      throw new Error(
        'Group owner cannot leave. Please delete the group or transfer ownership first.'
      );
    }

    // Create updated group with member removed
    const updatedGroup = group.withRemovedMember(userId);

    // Save to repository
    await this.groupRepo.save(updatedGroup);
  }

  /**
   * Get group leaderboard
   */
  async getGroupLeaderboard(
    groupId: string,
    period: TimePeriod
  ): Promise<LeaderboardEntry[]> {
    const group = await this.groupRepo.findById(groupId);

    if (!group) {
      throw new Error('Group not found');
    }

    // Fetch all members (convert readonly array to mutable)
    const memberIds = Array.from(group.memberIds);
    const users = await this.userRepo.findByIds(memberIds);

    // Calculate date range based on period
    const dateRange = this.getDateRangeForPeriod(period);

    // Fetch sessions for all group members in the date range
    const sessions = await this.sessionRepo.findByUserIds(memberIds, {
      groupId: groupId,
      startDate: dateRange.start,
      endDate: dateRange.end,
    });

    // Calculate leaderboard using domain service
    return this.leaderboardCalc.calculate(users, sessions, period);
  }

  /**
   * Helper method to get date range for a time period
   */
  private getDateRangeForPeriod(period: TimePeriod): {
    start: Date;
    end: Date;
  } {
    const now = new Date();
    const end = now;
    let start: Date;

    switch (period) {
      case 'today':
        start = new Date(now);
        start.setHours(0, 0, 0, 0);
        break;

      case 'week':
        start = new Date(now);
        start.setDate(start.getDate() - 7);
        break;

      case 'month':
        start = new Date(now);
        start.setMonth(start.getMonth() - 1);
        break;

      case 'all-time':
        start = new Date(0); // Beginning of time
        break;

      default:
        start = new Date(now);
        start.setDate(start.getDate() - 7);
    }

    return { start, end };
  }

  /**
   * Get group stats
   */
  async getGroupStats(groupId: string): Promise<GroupStats> {
    const group = await this.groupRepo.findById(groupId);

    if (!group) {
      throw new Error('Group not found');
    }

    // Fetch all sessions for this group
    const allSessions = await this.sessionRepo.findByGroupId(groupId, 1000);

    // Calculate total hours
    const totalSeconds = allSessions.reduce(
      (sum, session) => sum + session.duration,
      0
    );
    const totalHours = totalSeconds / 3600;

    // Get sessions from the last week
    const weekAgo = new Date();
    weekAgo.setDate(weekAgo.getDate() - 7);
    const recentSessions = allSessions.filter(s => s.createdAt >= weekAgo);

    // Count unique active members this week
    const activeMemberIds = new Set(recentSessions.map(s => s.userId));

    return {
      memberCount: group.getMemberCount(),
      totalSessions: allSessions.length,
      totalHours: Math.round(totalHours * 10) / 10, // Round to 1 decimal
      activeMembersThisWeek: activeMemberIds.size,
    };
  }

  /**
   * Check if user can join group
   */
  async canUserJoin(groupId: string, userId: string): Promise<boolean> {
    const group = await this.groupRepo.findById(groupId);

    if (!group) {
      return false;
    }

    // Cannot join if already a member
    if (group.isMember(userId)) {
      return false;
    }

    // Can join if group is public
    if (group.privacy === 'public') {
      return true;
    }

    // For approval-required groups, would need invitation logic
    return false;
  }

  /**
   * Check if user can invite to group
   */
  async canUserInvite(groupId: string, userId: string): Promise<boolean> {
    const group = await this.groupRepo.findById(groupId);

    if (!group) {
      return false;
    }

    return group.canUserInvite(userId);
  }

  /**
   * Create a new group
   */
  async createGroup(
    data: {
      name: string;
      description: string;
      category: string;
      privacySetting: 'public' | 'approval-required';
      location?: string;
      imageUrl?: string;
      [key: string]: unknown; // Allow additional properties from CreateGroupData
    },
    userId: string
  ): Promise<Group> {
    // Generate ID for the group
    const groupId = this.generateGroupId();

    // Create domain group with creator as both member and admin
    const group = new Group(
      groupId,
      data.name,
      data.description,
      (data.category as any) || 'other', // Map to domain category type
      (data.privacySetting as any) === 'approval-required'
        ? 'approval-required'
        : 'public',
      [userId], // Creator is first member
      [userId], // Creator is first admin
      userId,
      new Date(),
      data.location,
      data.imageUrl
    );

    // Save to repository
    await this.groupRepo.save(group);

    return group;
  }

  /**
   * Generate a unique group ID
   */
  private generateGroupId(): string {
    // Simple ID generation using timestamp + random suffix
    return `group_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }
}

