 */
export function useCanViewProfile(
  profileUser: User | null,
  viewerId: string | null,
  options?: Partial<UseQueryOptions<boolean, Error>>
) {
  return useQuery<boolean, Error>({
    queryKey: [...PROFILE_KEYS.detail(profileUser?.id || ''), 'canView', viewerId],
    queryFn: () => {
      if (!profileUser) return false;
      return profileService.canViewProfile(profileUser, viewerId);
    },
    staleTime: STANDARD_CACHE_TIMES.MEDIUM, // 5 minutes
    enabled: !!profileUser,
    ...options,
  });
}

/**
 * Profile Mutation Hooks - React Query Boundary
 *
 * Note: Follow/unfollow mutations are currently placeholders as the ProfileService
 * shows these operations aren't fully implemented in the clean architecture yet.
 * When they are implemented, these hooks will handle the React Query integration.
 */

import {
  useMutation,
  useQueryClient,
  UseMutationOptions,
} from '@tanstack/react-query';
import { ProfileService } from '../services/ProfileService';
import { PROFILE_KEYS } from './useProfile';

const profileService = new ProfileService();

// Context types for optimistic updates
interface FollowUserContext {
  previousProfile: unknown;
  previousFollowing: unknown;
}

interface UnfollowUserContext {
  previousProfile: unknown;
}

/**
 * Follow a user
 *
 * Note: Currently a placeholder. The actual implementation is pending
 * in the ProfileService. Use the existing firebaseApi.user.followUser for now.
 *
 * @example
 * const followMutation = useFollowUser();
 * followMutation.mutate({ currentUserId, targetUserId });
 */
export function useFollowUser(
  options?: Partial<
    UseMutationOptions<
      void,
      Error,
      { currentUserId: string; targetUserId: string },
      FollowUserContext
    >
  >
) {
  const queryClient = useQueryClient();

  return useMutation<
    void,
    Error,
    { currentUserId: string; targetUserId: string },
    FollowUserContext
  >({
    mutationFn: ({ currentUserId, targetUserId }) =>
      profileService.followUser(currentUserId, targetUserId),

    onMutate: async ({ currentUserId, targetUserId }) => {
      // Cancel outgoing queries
      await queryClient.cancelQueries({
        queryKey: PROFILE_KEYS.detail(targetUserId),
      });
      await queryClient.cancelQueries({
        queryKey: PROFILE_KEYS.following(currentUserId),
      });
      await queryClient.cancelQueries({
        queryKey: PROFILE_KEYS.followers(targetUserId),
      });

      // Snapshot previous values
      const previousProfile = queryClient.getQueryData(
        PROFILE_KEYS.detail(targetUserId)
      );
      const previousFollowing = queryClient.getQueryData(
        PROFILE_KEYS.following(currentUserId)
      );

      // Optimistically update isFollowing
      queryClient.setQueryData(
        PROFILE_KEYS.isFollowing(currentUserId, targetUserId),
        true
      );

      // Optimistically update follower count on profile
      queryClient.setQueryData(
        PROFILE_KEYS.detail(targetUserId),
        (old: any) => {
          if (!old) return old;
          return {
            ...old,
            followerCount: (old.followerCount || 0) + 1,
          };
        }
      );

      return { previousProfile, previousFollowing };
    },

    onError: (error, variables, context: FollowUserContext | undefined) => {
      // Rollback on error
      if (context?.previousProfile) {
        queryClient.setQueryData(
          PROFILE_KEYS.detail(variables.targetUserId),
          context.previousProfile
        );
      }
      if (context?.previousFollowing) {
        queryClient.setQueryData(
          PROFILE_KEYS.following(variables.currentUserId),
          context.previousFollowing
        );
      }
      queryClient.setQueryData(
        PROFILE_KEYS.isFollowing(
          variables.currentUserId,
          variables.targetUserId
        ),
        false
      );
    },

    onSuccess: (_, { currentUserId, targetUserId }) => {
      // Invalidate relevant caches
      queryClient.invalidateQueries({
        queryKey: PROFILE_KEYS.detail(targetUserId),
      });
      queryClient.invalidateQueries({
        queryKey: PROFILE_KEYS.following(currentUserId),
      });
      queryClient.invalidateQueries({
        queryKey: PROFILE_KEYS.followers(targetUserId),
      });
      queryClient.invalidateQueries({
        queryKey: PROFILE_KEYS.isFollowing(currentUserId, targetUserId),
      });
    },

    ...options,
  });
}

/**
 * Unfollow a user
 *
 * Note: Currently a placeholder. The actual implementation is pending
 * in the ProfileService. Use the existing firebaseApi.user.unfollowUser for now.
 *
 * @example
 * const unfollowMutation = useUnfollowUser();
 * unfollowMutation.mutate({ currentUserId, targetUserId });
 */
export function useUnfollowUser(
  options?: Partial<
    UseMutationOptions<
      void,
      Error,
      { currentUserId: string; targetUserId: string },
      UnfollowUserContext
    >
  >
) {
  const queryClient = useQueryClient();

  return useMutation<
    void,
    Error,
    { currentUserId: string; targetUserId: string },
    UnfollowUserContext
  >({
    mutationFn: ({ currentUserId, targetUserId }) =>
      profileService.unfollowUser(currentUserId, targetUserId),

    onMutate: async ({ currentUserId, targetUserId }) => {
      // Cancel outgoing queries
      await queryClient.cancelQueries({
        queryKey: PROFILE_KEYS.detail(targetUserId),
      });
      await queryClient.cancelQueries({
        queryKey: PROFILE_KEYS.following(currentUserId),
      });
      await queryClient.cancelQueries({
        queryKey: PROFILE_KEYS.followers(targetUserId),
      });

      // Snapshot previous values
      const previousProfile = queryClient.getQueryData(
        PROFILE_KEYS.detail(targetUserId)
      );

      // Optimistically update isFollowing
      queryClient.setQueryData(
        PROFILE_KEYS.isFollowing(currentUserId, targetUserId),
        false
      );

      // Optimistically update follower count on profile
      queryClient.setQueryData(
        PROFILE_KEYS.detail(targetUserId),
        (old: any) => {
          if (!old) return old;
          return {
            ...old,
            followerCount: Math.max(0, (old.followerCount || 0) - 1),
          };
        }
      );

      return { previousProfile };
    },

    onError: (error, variables, context: UnfollowUserContext | undefined) => {
      // Rollback on error
      if (context?.previousProfile) {
        queryClient.setQueryData(
          PROFILE_KEYS.detail(variables.targetUserId),
          context.previousProfile
        );
      }
      queryClient.setQueryData(
        PROFILE_KEYS.isFollowing(
          variables.currentUserId,
          variables.targetUserId
        ),
        true
      );
    },

    onSuccess: (_, { currentUserId, targetUserId }) => {
      // Invalidate relevant caches
      queryClient.invalidateQueries({
        queryKey: PROFILE_KEYS.detail(targetUserId),
      });
      queryClient.invalidateQueries({
        queryKey: PROFILE_KEYS.following(currentUserId),
      });
      queryClient.invalidateQueries({
        queryKey: PROFILE_KEYS.followers(targetUserId),
      });
      queryClient.invalidateQueries({
        queryKey: PROFILE_KEYS.isFollowing(currentUserId, targetUserId),
      });
    },

    ...options,
  });
}

