  async restoreProject(projectId: string): Promise<Project> {
    return this.updateProject(projectId, { status: 'active' });
  }
}

/**
 * Following List Hook
 *
 * ULTRA-OPTIMIZED React Query hook for fetching ONLY following IDs.
 *
 * Key optimizations:
 * - Fetches only IDs, not full user objects (10x faster)
 * - Returns Set for O(1) lookup (instant .has() checks)
 * - Aggressive caching (30 min stale time)
 * - No refetching on mount/focus
 */

import { useQuery } from '@tanstack/react-query';
import { getFollowingIds } from '@/lib/api/users/getFollowingIds';
import { CACHE_KEYS, CACHE_TIMES } from '@/lib/queryClient';

interface UseFollowingListOptions {
  userId?: string;
  enabled?: boolean;
}

interface UseFollowingListReturn {
  followingIds: Set<string>;
  isLoading: boolean;
  isError: boolean;
  error: Error | null;
}

export function useFollowingList({
  userId,
  enabled = true,
}: UseFollowingListOptions): UseFollowingListReturn {
  const { data, isLoading, isError, error } = useQuery({
    queryKey: userId ? ['following-ids', userId] : ['following-ids', 'null'],
    queryFn: async () => {
      if (!userId) {
        return new Set<string>();
      }
      return await getFollowingIds(userId);
    },
    enabled: enabled && !!userId,
    staleTime: CACHE_TIMES.VERY_LONG, // 30 minutes - following changes infrequently
    gcTime: 60 * 60 * 1000, // 1 hour - keep in memory longer
    refetchOnWindowFocus: false,
    refetchOnMount: false, // Critical: don't refetch if we have data
    refetchOnReconnect: false,
  });

  return {
    followingIds: data ?? new Set<string>(),
    isLoading,
    isError,
    error: error as Error | null,
  };
}

/**
 * Search Users Hook
 *
 * Optimized React Query hook for searching users with:
 * - Automatic caching and deduplication
 * - Stale-while-revalidate pattern
 * - Proper loading and error states
 */

import { useQuery } from '@tanstack/react-query';
import { firebaseUserApi } from '@/lib/api';
import { CACHE_KEYS, CACHE_TIMES } from '@/lib/queryClient';
import type { UserSearchResult } from '@/types';

interface UseSearchUsersOptions {
  searchTerm: string;
  enabled?: boolean;
  page?: number;
  limit?: number;
}

interface UseSearchUsersReturn {
  users: UserSearchResult[];
  totalCount: number;
  hasMore: boolean;
  isLoading: boolean;
  isError: boolean;
  error: Error | null;
}

export function useSearchUsers({
  searchTerm,
  enabled = true,
  page = 1,
  limit = 20,
}: UseSearchUsersOptions): UseSearchUsersReturn {
  const trimmedTerm = searchTerm.trim();

  const { data, isLoading, isError, error } = useQuery({
    queryKey: ['search', 'users', trimmedTerm, page, limit],
    queryFn: async () => {
      if (!trimmedTerm) {
        return { users: [], totalCount: 0, hasMore: false };
      }
      return firebaseUserApi.searchUsers(trimmedTerm, page, limit);
    },
    enabled: enabled && trimmedTerm.length > 0,
    staleTime: CACHE_TIMES.MEDIUM, // 5 minutes
    gcTime: CACHE_TIMES.LONG, // 15 minutes
    refetchOnWindowFocus: false, // Don't refetch on window focus for search results
  });

  return {
    users: data?.users ?? [],
    totalCount: data?.totalCount ?? 0,
    hasMore: data?.hasMore ?? false,
    isLoading,
    isError,
    error: error as Error | null,
  };
}

/**
 * Suggested Groups Hook
 *
 * Optimized React Query hook for fetching suggested groups with:
 * - Automatic caching to prevent repeated loads
 * - Filters out already-joined groups
 * - Proper loading and error states
 */

import { useQuery } from '@tanstack/react-query';
import {
  collection,
  query as firestoreQuery,
  orderBy,
  limit as limitFn,
  getDocs,
} from 'firebase/firestore';
import { db } from '@/lib/firebase';
import { CACHE_KEYS, CACHE_TIMES } from '@/lib/queryClient';
import { useUserGroups } from './useUserGroups';

interface SuggestedGroup {
  id: string;
  name: string;
  description: string;
  imageUrl?: string;
  location?: string;
  category?: string;
  memberCount: number;
  members: number;
  image?: string;
}

interface UseSuggestedGroupsOptions {
  userId?: string;
  enabled?: boolean;
  limit?: number;
}

interface UseSuggestedGroupsReturn {
  suggestedGroups: SuggestedGroup[];
  isLoading: boolean;
  isError: boolean;
  error: Error | null;
}

export function useSuggestedGroups({
  userId,
  enabled = true,
  limit = 20,
}: UseSuggestedGroupsOptions): UseSuggestedGroupsReturn {
  // Fetch user's joined groups first
  const { groups: userGroups, isLoading: isLoadingUserGroups } = useUserGroups({
    userId,
    enabled: enabled && !!userId,
  });

  const joinedGroupIds = new Set(userGroups.map(g => g.id));

  const {
    data,
    isLoading: isLoadingSuggested,
    isError,
    error,
  } = useQuery({
    queryKey: [...CACHE_KEYS.SUGGESTED_GROUPS(), userId],
    queryFn: async () => {
      // Fetch all groups ordered by popularity
      const allGroupsSnapshot = await getDocs(
        firestoreQuery(
          collection(db, 'groups'),
          orderBy('memberCount', 'desc'),
          limitFn(limit)
        )
      );

      const allGroups: SuggestedGroup[] = allGroupsSnapshot.docs.map(doc => {
        const data = doc.data();
        return {
          id: doc.id,
          name: data.name,
          description: data.description,
          imageUrl: data.imageUrl,
          location: data.location,
          category: data.category,
          memberCount: data.memberCount,
          members: data.memberCount,
          image: data.imageUrl || 'üìÅ',
        };
      });

      // Filter out groups user is already in
      return allGroups.filter(group => !joinedGroupIds.has(group.id));
    },
    enabled: enabled && !isLoadingUserGroups,
    staleTime: CACHE_TIMES.LONG, // 15 minutes
    gcTime: CACHE_TIMES.VERY_LONG, // 1 hour
    refetchOnWindowFocus: false,
  });

  return {
    suggestedGroups: data ?? [],
    isLoading: isLoadingUserGroups || isLoadingSuggested,
    isError,
    error: error as Error | null,
  };
}

