/**
 * Timer Mutation Hooks - React Query Boundary
 *
 * All write operations for timer (start, pause, resume, complete, cancel).
 */

import {
  useMutation,
  useQueryClient,
  UseMutationOptions,
} from '@tanstack/react-query';
import {
  TimerService,
  StartTimerData,
  CompleteTimerData,
} from '../services/TimerService';
import { ActiveSession } from '@/domain/entities/ActiveSession';
import { Session } from '@/domain/entities/Session';

const timerService = new TimerService();

// Query keys for timer-related queries
export const TIMER_KEYS = {
  active: (userId: string) => ['timer', 'active', userId] as const,
};

/**
 * Start a new timer
 *
 * @example
 * const startMutation = useStartTimer();
 * startMutation.mutate({
 *   userId,
 *   projectId,
 *   activityId,
 *   title: 'Working on feature X'
 * });
 */
export function useStartTimer(
  options?: Partial<UseMutationOptions<ActiveSession, Error, StartTimerData>>
) {
  const queryClient = useQueryClient();

  return useMutation<ActiveSession, Error, StartTimerData>({
    mutationFn: data => timerService.startTimer(data),

    onSuccess: (activeSession, variables) => {
      // Update cache with new active session
      queryClient.setQueryData(
        TIMER_KEYS.active(variables.userId),
        activeSession
      );
    },

    ...options,
  });
}

/**
 * Pause the active timer
 *
 * @example
 * const pauseMutation = usePauseTimer();
 * pauseMutation.mutate(userId);
 */
export function usePauseTimer(
  options?: Partial<
    UseMutationOptions<
      ActiveSession,
      Error,
      string,
      { previousSession: ActiveSession | undefined }
    >
  >
) {
  const queryClient = useQueryClient();

  return useMutation<
    ActiveSession,
    Error,
    string,
    { previousSession: ActiveSession | undefined }
  >({
    mutationFn: userId => timerService.pauseTimer(userId),

    onMutate: async (
      userId
    ): Promise<{ previousSession: ActiveSession | undefined }> => {
      // Cancel outgoing queries
      await queryClient.cancelQueries({ queryKey: TIMER_KEYS.active(userId) });

      // Snapshot
      const previousSession = queryClient.getQueryData<ActiveSession>(
        TIMER_KEYS.active(userId)
      );

      // Optimistically update
      queryClient.setQueryData(TIMER_KEYS.active(userId), (old: any) => {
        if (!old) return old;
        return {
          ...old,
          status: 'paused',
        };
      });

      return { previousSession };
    },

    onError: (error, userId, context) => {
      // Rollback
      if (context?.previousSession) {
        queryClient.setQueryData(
          TIMER_KEYS.active(userId),
          context.previousSession
        );
      }
    },

    onSuccess: (pausedSession, userId) => {
      // Update with actual paused session
      queryClient.setQueryData(TIMER_KEYS.active(userId), pausedSession);
    },

    ...options,
  });
}

/**
 * Resume a paused timer
 *
 * @example
 * const resumeMutation = useResumeTimer();
 * resumeMutation.mutate(userId);
 */
export function useResumeTimer(
  options?: Partial<
    UseMutationOptions<
      ActiveSession,
      Error,
      string,
      { previousSession: ActiveSession | undefined }
    >
  >
) {
  const queryClient = useQueryClient();

  return useMutation<
    ActiveSession,
    Error,
    string,
    { previousSession: ActiveSession | undefined }
  >({
    mutationFn: userId => timerService.resumeTimer(userId),

    onMutate: async (
      userId
    ): Promise<{ previousSession: ActiveSession | undefined }> => {
      await queryClient.cancelQueries({ queryKey: TIMER_KEYS.active(userId) });

      const previousSession = queryClient.getQueryData<ActiveSession>(
        TIMER_KEYS.active(userId)
      );

      queryClient.setQueryData(TIMER_KEYS.active(userId), (old: any) => {
        if (!old) return old;
        return {
          ...old,
          status: 'running',
        };
      });

      return { previousSession };
    },

    onError: (error, userId, context) => {
      if (context?.previousSession) {
        queryClient.setQueryData(
          TIMER_KEYS.active(userId),
          context.previousSession
        );
      }
    },

    onSuccess: (resumedSession, userId) => {
      queryClient.setQueryData(TIMER_KEYS.active(userId), resumedSession);
    },

    ...options,
  });
}

/**
 * Complete and save the timer session
 *
 * @example
 * const completeMutation = useCompleteTimer();
 * completeMutation.mutate({
 *   userId,
 *   data: {
 *     title: 'Final title',
 *     description: 'Work completed',
 *     visibility: 'everyone'
 *   }
 * });
 */
export function useCompleteTimer(
  options?: Partial<
    UseMutationOptions<
      Session,
      Error,
      { userId: string; data: CompleteTimerData }
    >
  >
) {
  const queryClient = useQueryClient();

  return useMutation<
    Session,
    Error,
    { userId: string; data: CompleteTimerData }
  >({
    mutationFn: ({ userId, data }) => timerService.completeTimer(userId, data),

    onSuccess: async (session, { userId }) => {
      // Clear active session
      queryClient.setQueryData(TIMER_KEYS.active(userId), null);

      // Force immediate refetch of feed queries to show new session at top
      // This creates an Instagram-like experience where posts appear immediately
      // Using refetchType: 'active' ensures only currently-mounted queries refetch
      await queryClient.invalidateQueries({
        queryKey: ['feed'],
        refetchType: 'active', // Only refetch queries that are currently being observed
      });

      // Invalidate other related data (these will refetch when needed)
      queryClient.invalidateQueries({ queryKey: ['profile'] }); // Invalidate all profile-related queries
      queryClient.invalidateQueries({ queryKey: ['sessions'] }); // Invalidate session queries
    },

    ...options,
  });
}

/**
 * Cancel the active timer without saving
 *
 * @example
 * const cancelMutation = useCancelTimer();
 * cancelMutation.mutate(userId);
 */
export function useCancelTimer(
  options?: Partial<UseMutationOptions<void, Error, string>>
) {
  const queryClient = useQueryClient();

  return useMutation<void, Error, string>({
    mutationFn: userId => timerService.cancelTimer(userId),

    onSuccess: (_, userId) => {
      // Clear active session
      queryClient.setQueryData(TIMER_KEYS.active(userId), null);
    },

    ...options,
  });
}

/**
 * Adjust the start time of the active timer
 *
 * @example
 * const adjustMutation = useAdjustStartTime();
 * adjustMutation.mutate({ userId, newStartTime: new Date('2025-10-27T10:00:00') });
 */
export function useAdjustStartTime(
  options?: Partial<
    UseMutationOptions<
      ActiveSession,
      Error,
      { userId: string; newStartTime: Date }
    >
  >
) {
  const queryClient = useQueryClient();

  return useMutation<
    ActiveSession,
    Error,
    { userId: string; newStartTime: Date }
  >({
    mutationFn: ({ userId, newStartTime }) =>
      timerService.adjustStartTime(userId, newStartTime),

    onSuccess: (adjustedSession, { userId }) => {
      // Update cache with adjusted session
      queryClient.setQueryData(TIMER_KEYS.active(userId), adjustedSession);
    },

    ...options,
  });
}

/**
 * Helper hook to invalidate timer data
 *
 * @example
 * const invalidateTimer = useInvalidateTimer();
 * invalidateTimer(userId);
 */
export function useInvalidateTimer() {
  const queryClient = useQueryClient();

  return (userId: string) => {
    queryClient.invalidateQueries({ queryKey: TIMER_KEYS.active(userId) });
  };
}

import { renderHook, waitFor } from '@testing-library/react';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { useCommentLike, COMMENT_KEYS } from '@/features/comments/hooks';
import { firebaseApi } from '@/lib/api';
import React from 'react';

// Mock the firebaseApi
jest.mock('@/lib/api', () => ({
  firebaseApi: {
    comment: {
      likeComment: jest.fn(),
      unlikeComment: jest.fn(),
    },
  },
}));

describe('useCommentLike', () => {
  let queryClient: QueryClient;
  const sessionId = 'session-123';
  const commentId = 'comment-456';

  beforeEach(() => {
    queryClient = new QueryClient({
      defaultOptions: {
        queries: { retry: false },
        mutations: { retry: false },
      },
    });
    jest.clearAllMocks();
  });

  const wrapper = ({ children }: { children: React.ReactNode }) => (
    <QueryClientProvider client={queryClient}>
      {children}
    </QueryClientProvider>
  );

  describe('Like Comment', () => {
    it('should call likeComment API when action is "like"', async () => {
      const { result } = renderHook(
        () => useCommentLike(sessionId),
        { wrapper }
      );

      result.current.mutate({ commentId, action: 'like' });

      await waitFor(() => {
        expect(firebaseApi.comment.likeComment).toHaveBeenCalledWith(commentId);
      });
    });

    it('should optimistically update comment like state', async () => {
      // Set up initial query data
      const initialComments = {
        comments: [
          {
            id: commentId,
            content: 'Great session!',
            likeCount: 5,
            isLiked: false,
          },
          {
            id: 'comment-789',
            content: 'Nice work!',
            likeCount: 3,
            isLiked: false,
          },
        ],
        hasMore: false,
      };

      queryClient.setQueryData(COMMENT_KEYS.list(sessionId), initialComments);

      const { result } = renderHook(
        () => useCommentLike(sessionId),
        { wrapper }
      );

      // Mock successful API call
      (firebaseApi.comment.likeComment as jest.Mock).mockResolvedValueOnce(undefined);

      result.current.mutate({ commentId, action: 'like' });

      // Check optimistic update
      await waitFor(() => {
        const updatedData = queryClient.getQueryData(COMMENT_KEYS.list(sessionId)) as any;
        expect(updatedData.comments[0].isLiked).toBe(true);
        expect(updatedData.comments[0].likeCount).toBe(6);
        // Other comment should remain unchanged
        expect(updatedData.comments[1].isLiked).toBe(false);
        expect(updatedData.comments[1].likeCount).toBe(3);
      });
    });

    it('should invalidate comments query after successful like', async () => {
      const invalidateQueriesSpy = jest.spyOn(queryClient, 'invalidateQueries');

      const { result } = renderHook(
        () => useCommentLike(sessionId),
        { wrapper }
      );

      (firebaseApi.comment.likeComment as jest.Mock).mockResolvedValueOnce(undefined);

      result.current.mutate({ commentId, action: 'like' });

      await waitFor(() => {
        expect(invalidateQueriesSpy).toHaveBeenCalledWith({
          queryKey: COMMENT_KEYS.list(sessionId),
        });
      });
    });
  });

  describe('Unlike Comment', () => {
    it('should call unlikeComment API when action is "unlike"', async () => {
      const { result } = renderHook(
        () => useCommentLike(sessionId),
        { wrapper }
      );

      result.current.mutate({ commentId, action: 'unlike' });

      await waitFor(() => {
        expect(firebaseApi.comment.unlikeComment).toHaveBeenCalledWith(commentId);
      });
    });

    it('should optimistically update comment unlike state', async () => {
      // Set up initial query data with liked comment
      const initialComments = {
        comments: [
          {
            id: commentId,
            content: 'Great session!',
            likeCount: 6,
            isLiked: true,
          },
        ],
        hasMore: false,
      };

      queryClient.setQueryData(COMMENT_KEYS.list(sessionId), initialComments);

      const { result } = renderHook(
        () => useCommentLike(sessionId),
        { wrapper }
      );

      (firebaseApi.comment.unlikeComment as jest.Mock).mockResolvedValueOnce(undefined);

      result.current.mutate({ commentId, action: 'unlike' });

      // Check optimistic update
      await waitFor(() => {
        const updatedData = queryClient.getQueryData(COMMENT_KEYS.list(sessionId)) as any;
        expect(updatedData.comments[0].isLiked).toBe(false);
        expect(updatedData.comments[0].likeCount).toBe(5);
      });
    });

    it('should not allow likeCount to go below 0', async () => {
      // Edge case: comment with 0 likes being unliked
      const initialComments = {
        comments: [
          {
            id: commentId,
            content: 'Great session!',
            likeCount: 0,
            isLiked: true, // Inconsistent state but handled defensively
          },
        ],
        hasMore: false,
      };

      queryClient.setQueryData(COMMENT_KEYS.list(sessionId), initialComments);

      const { result } = renderHook(
        () => useCommentLike(sessionId),
        { wrapper }
      );

      (firebaseApi.comment.unlikeComment as jest.Mock).mockResolvedValueOnce(undefined);

      result.current.mutate({ commentId, action: 'unlike' });

      await waitFor(() => {
        const updatedData = queryClient.getQueryData(COMMENT_KEYS.list(sessionId)) as any;
        expect(updatedData.comments[0].likeCount).toBe(0);
        expect(updatedData.comments[0].likeCount).toBeGreaterThanOrEqual(0);
      });
    });
  });

  describe('Error Handling', () => {
    it('should rollback optimistic update on error', async () => {
      const initialComments = {
        comments: [
          {
            id: commentId,
            content: 'Great session!',
            likeCount: 5,
            isLiked: false,
          },
        ],
        hasMore: false,
      };

      queryClient.setQueryData(COMMENT_KEYS.list(sessionId), initialComments);

      const { result } = renderHook(
        () => useCommentLike(sessionId),
        { wrapper }
      );

      // Mock failed API call
      (firebaseApi.comment.likeComment as jest.Mock).mockRejectedValueOnce(
        new Error('Network error')
      );

      result.current.mutate({ commentId, action: 'like' });

      // Wait for error and rollback
      await waitFor(() => {
        expect(result.current.isError).toBe(true);
      });

      // Data should be rolled back to original state
      const finalData = queryClient.getQueryData(COMMENT_KEYS.list(sessionId)) as any;
      expect(finalData.comments[0].isLiked).toBe(false);
      expect(finalData.comments[0].likeCount).toBe(5);
    });

    it('should handle missing comments data gracefully', async () => {
      const { result } = renderHook(
        () => useCommentLike(sessionId),
        { wrapper }
      );

      (firebaseApi.comment.likeComment as jest.Mock).mockResolvedValueOnce(undefined);

      // Should not throw error when no initial data
      expect(() => {
        result.current.mutate({ commentId, action: 'like' });
      }).not.toThrow();

      await waitFor(() => {
        expect(firebaseApi.comment.likeComment).toHaveBeenCalled();
      });
    });
  });

  describe('Multiple Mutations', () => {
    it('should handle sequential like/unlike operations', async () => {
      const initialComments = {
        comments: [
          {
            id: commentId,
            content: 'Great session!',
            likeCount: 5,
            isLiked: false,
          },
        ],
        hasMore: false,
      };

      queryClient.setQueryData(COMMENT_KEYS.list(sessionId), initialComments);

      const { result } = renderHook(
        () => useCommentLike(sessionId),
        { wrapper }
      );

      (firebaseApi.comment.likeComment as jest.Mock).mockResolvedValue(undefined);
      (firebaseApi.comment.unlikeComment as jest.Mock).mockResolvedValue(undefined);

      // Like
      result.current.mutate({ commentId, action: 'like' });
      await waitFor(() => {
        const data = queryClient.getQueryData(COMMENT_KEYS.list(sessionId)) as any;
        expect(data.comments[0].isLiked).toBe(true);
        expect(data.comments[0].likeCount).toBe(6);
      });

      // Unlike
      result.current.mutate({ commentId, action: 'unlike' });
      await waitFor(() => {
        const data = queryClient.getQueryData(COMMENT_KEYS.list(sessionId)) as any;
        expect(data.comments[0].isLiked).toBe(false);
        expect(data.comments[0].likeCount).toBe(5);
      });
    });

    it('should handle likes on different comments independently', async () => {
      const initialComments = {
        comments: [
          {
            id: 'comment-1',
            content: 'First comment',
            likeCount: 5,
            isLiked: false,
          },
          {
            id: 'comment-2',
            content: 'Second comment',
            likeCount: 3,
            isLiked: false,
          },
        ],
        hasMore: false,
      };

      queryClient.setQueryData(COMMENT_KEYS.list(sessionId), initialComments);

      const { result } = renderHook(
        () => useCommentLike(sessionId),
        { wrapper }
      );

      (firebaseApi.comment.likeComment as jest.Mock).mockResolvedValue(undefined);

      // Like first comment
      result.current.mutate({ commentId: 'comment-1', action: 'like' });

      await waitFor(() => {
        const data = queryClient.getQueryData(COMMENT_KEYS.list(sessionId)) as any;
        expect(data.comments[0].isLiked).toBe(true);
        expect(data.comments[0].likeCount).toBe(6);
        // Second comment should be unchanged
        expect(data.comments[1].isLiked).toBe(false);
        expect(data.comments[1].likeCount).toBe(3);
      });
    });
  });

  describe('Cache Invalidation', () => {
    it('should cancel ongoing queries before mutation', async () => {
      const cancelQueriesSpy = jest.spyOn(queryClient, 'cancelQueries');

      const { result } = renderHook(
        () => useCommentLike(sessionId),
        { wrapper }
      );

      (firebaseApi.comment.likeComment as jest.Mock).mockResolvedValueOnce(undefined);

      result.current.mutate({ commentId, action: 'like' });

      await waitFor(() => {
        expect(cancelQueriesSpy).toHaveBeenCalledWith({
          queryKey: COMMENT_KEYS.list(sessionId),
        });
      });
    });
  });
});

import { useQuery, useMutation, useQueryClient, UseQueryOptions } from '@tanstack/react-query';
import { useAuth } from '@/hooks/useAuth';
import {
  Activity,
  ActivityStats,
  CreateActivityData,
  UpdateActivityData,
} from '@/types';
import { firebaseActivityApi } from '@/lib/api';
import { CACHE_KEYS, CACHE_TIMES } from '@/lib/queryClient';
import { db } from '@/lib/firebase';
import { collection, getDocs, query, where, or, and } from 'firebase/firestore';
import { safeNumber } from '@/lib/utils';

/**
 * Hook to fetch user activities with caching
 * Replaces ActivitiesContext for better performance and code splitting
 *
 * @param userId - User ID to fetch activities for (defaults to current user)
 * @param options - Additional React Query options
 */
export function useActivities(
  userId?: string,
  options?: Partial<UseQueryOptions<Activity[]>>
) {
  const { user } = useAuth();
  const effectiveUserId = userId || user?.id;

  return useQuery({
    queryKey: CACHE_KEYS.PROJECTS(effectiveUserId || 'none'),
    queryFn: async () => {
      const activities = await firebaseActivityApi.getProjects();
      // Icon migration happens automatically in the API
      return activities;
    },
    enabled: !!effectiveUserId,
    staleTime: CACHE_TIMES.LONG, // 15 minutes cache
    gcTime: CACHE_TIMES.LONG,
    ...options,
  });
}

/**
 * Hook to get a single activity by ID
 * Derived from the activities list for efficiency
 */
export function useActivity(
  activityId: string,
  options?: Partial<UseQueryOptions<Activity | null>>
) {
  const { user } = useAuth();
  const { data: activities } = useActivities(user?.id);

  return useQuery({
    queryKey: ['activity', activityId],
    queryFn: () => {
      const activity = activities?.find((a) => a.id === activityId);
      return activity || null;
    },
    enabled: !!activities && !!activityId,
    staleTime: CACHE_TIMES.LONG,
    ...options,
  });
}

/**
 * Hook to get activity statistics
 * Fetches on-demand for heavy computation
 */
export function useActivityStats(
  activityId: string,
  options?: Partial<UseQueryOptions<ActivityStats>>
) {
  const { user } = useAuth();

  return useQuery({
    queryKey: ['activity', 'stats', activityId],
    queryFn: async () => {
      if (!user?.id) {
        return {
          totalHours: 0,
          weeklyHours: 0,
          sessionCount: 0,
          currentStreak: 0,
          weeklyProgressPercentage: 0,
          totalProgressPercentage: 0,
          averageSessionDuration: 0,
        };
      }

      // Query for both activityId and projectId (backwards compatibility)
      const q = query(
        collection(db, 'sessions'),
        and(
          where('userId', '==', user.id),
          or(where('activityId', '==', activityId), where('projectId', '==', activityId))
        )
      );
      const snapshot = await getDocs(q);

      let totalSeconds = 0;
      let weeklySeconds = 0;
      let sessionCount = 0;
      const now = new Date();
      const weekStart = new Date(now);
      weekStart.setDate(now.getDate() - now.getDay());
      weekStart.setHours(0, 0, 0, 0);

      snapshot.forEach((doc) => {
        const data: any = doc.data();
        const duration = safeNumber(data.duration, 0);
        const start = data.startTime?.toDate ? data.startTime.toDate() : new Date(data.startTime);
        totalSeconds += duration;
        sessionCount += 1;
        if (start >= weekStart) weeklySeconds += duration;
      });

      const totalHours = totalSeconds / 3600;
      const weeklyHours = weeklySeconds / 3600;

      // Streak placeholder for now
      const currentStreak = sessionCount > 0 ? 1 : 0;

      return {
        totalHours,
        weeklyHours,
        sessionCount,
        currentStreak,
        weeklyProgressPercentage: 0,
        totalProgressPercentage: 0,
        averageSessionDuration: sessionCount > 0 ? totalSeconds / sessionCount : 0,
      };
    },
    enabled: !!activityId && !!user,
    staleTime: CACHE_TIMES.VERY_LONG, // 1 hour cache for stats
    ...options,
  });
}

/**
 * Hook to create a new activity with optimistic updates
 */
export function useCreateActivity() {
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const queryKey = CACHE_KEYS.PROJECTS(user?.id || 'none');

  return useMutation({
    mutationFn: (data: CreateActivityData) => firebaseActivityApi.createProject(data),

    // Optimistic update
    onMutate: async (newActivity) => {
      // Cancel any outgoing refetches
      await queryClient.cancelQueries({ queryKey });

      // Snapshot the previous value
      const previousActivities = queryClient.getQueryData<Activity[]>(queryKey);

      // Optimistically create a temporary activity
      const tempActivity: Activity = {
        id: `temp-${Date.now()}`,
        userId: user?.id || '',
        name: newActivity.name,
        description: newActivity.description,
        icon: newActivity.icon || 'flat-color-icons:briefcase',
        color: newActivity.color || '#007AFF',
        status: 'active',
        weeklyTarget: newActivity.weeklyTarget,
        totalTarget: newActivity.totalTarget,
        createdAt: new Date(),
        updatedAt: new Date(),
      };

      // Add to cache optimistically
      if (previousActivities) {
        queryClient.setQueryData<Activity[]>(queryKey, [...previousActivities, tempActivity]);
      }

      return { previousActivities, tempActivity };
    },

    // On success, replace temp with real activity
    onSuccess: (newActivity, variables, context) => {
      const previousActivities = context?.previousActivities || [];
      const tempActivity = context?.tempActivity;

      // Replace temp activity with real one
      queryClient.setQueryData<Activity[]>(
        queryKey,
        previousActivities
          .filter((a) => a.id !== tempActivity?.id)
          .concat(newActivity)
      );
    },

    // On error, roll back
    onError: (err, variables, context) => {
      if (context?.previousActivities) {
        queryClient.setQueryData(queryKey, context.previousActivities);
      }
      console.error('Error creating activity:', err);
    },

    // Always refetch after error or success
    onSettled: () => {
      queryClient.invalidateQueries({ queryKey });
    },
  });
}

/**
 * Hook to update an existing activity with optimistic updates
 */
export function useUpdateActivity() {
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const queryKey = CACHE_KEYS.PROJECTS(user?.id || 'none');

  return useMutation({
    mutationFn: ({ id, data }: { id: string; data: UpdateActivityData }) =>
      firebaseActivityApi.updateProject(id, data),

    // Optimistic update
    onMutate: async ({ id, data }) => {
      await queryClient.cancelQueries({ queryKey });

      const previousActivities = queryClient.getQueryData<Activity[]>(queryKey);

      // Optimistically update the activity
      if (previousActivities) {
        queryClient.setQueryData<Activity[]>(
          queryKey,
          previousActivities.map((a) =>
            a.id === id ? { ...a, ...data, updatedAt: new Date() } : a
          )
        );
      }

      return { previousActivities };
    },

    // On success, use the real updated activity
    onSuccess: (updatedActivity, variables, context) => {
      const previousActivities = context?.previousActivities || [];
      queryClient.setQueryData<Activity[]>(
        queryKey,
        previousActivities.map((a) => (a.id === updatedActivity.id ? updatedActivity : a))
      );
    },

    // On error, roll back
    onError: (err, variables, context) => {
      if (context?.previousActivities) {
        queryClient.setQueryData(queryKey, context.previousActivities);
      }
      console.error('Error updating activity:', err);
    },

    onSettled: () => {
      queryClient.invalidateQueries({ queryKey });
    },
  });
}

/**
 * Hook to delete an activity with optimistic updates
 */
export function useDeleteActivity() {
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const queryKey = CACHE_KEYS.PROJECTS(user?.id || 'none');

  return useMutation({
    mutationFn: (activityId: string) => firebaseActivityApi.deleteProject(activityId),

    // Optimistic update
    onMutate: async (activityId) => {
      await queryClient.cancelQueries({ queryKey });

      const previousActivities = queryClient.getQueryData<Activity[]>(queryKey);

      // Optimistically remove the activity
      if (previousActivities) {
        queryClient.setQueryData<Activity[]>(
          queryKey,
          previousActivities.filter((a) => a.id !== activityId)
        );
      }

      return { previousActivities };
    },

    // On error, roll back
    onError: (err, activityId, context) => {
      if (context?.previousActivities) {
        queryClient.setQueryData(queryKey, context.previousActivities);
      }
      console.error('Error deleting activity:', err);
    },

    onSettled: () => {
