/**
 * Session Mapper
 *
 * Converts between Firestore documents and Session domain entities.
 */

import {
  DocumentSnapshot,
  Timestamp,
  getDoc,
  doc as firestoreDoc,
} from 'firebase/firestore';
import {
  Session,
  SessionVisibility,
  SessionUser,
  SessionActivity,
} from '@/domain/entities/Session';
import { db, auth } from '@/lib/firebase';
import { DEFAULT_ACTIVITIES } from '@/types';

export class SessionMapper {
  /**
   * Convert Firestore document to Session domain entity (without enrichment)
   */
  toDomain(doc: DocumentSnapshot): Session {
    const data = doc.data();
    if (!data) {
      throw new Error(`Session document ${doc.id} does not exist`);
    }

    return new Session(
      doc.id,
      data.userId,
      data.projectId,
      data.activityId || null,
      data.duration,
      this.timestampToDate(data.createdAt),
      data.title,
      data.description,
      (data.visibility as SessionVisibility) || 'everyone',
      data.supportCount || 0,
      data.commentCount || 0,
      data.groupIds || []
    );
  }

  /**
   * Convert Firestore document to enriched Session domain entity (with user/activity data)
   * Returns null if user data cannot be fetched (deleted/inaccessible users)
   */
  async toDomainEnriched(doc: DocumentSnapshot): Promise<Session | null> {
    const data = doc.data();
    if (!data) {
      throw new Error(`Session document ${doc.id} does not exist`);
    }

    // Fetch user data - REQUIRED, skip session if user is inaccessible
    let user: SessionUser | undefined;
    try {
      const userDoc = await getDoc(firestoreDoc(db, 'users', data.userId));
      if (!userDoc.exists()) {
        // User does not exist - skip session
        return null;
      }

      const userData = userDoc.data();
      user = {
        id: data.userId,
        email: userData.email || '',
        name: userData.name || 'Unknown User',
        username: userData.username || 'unknown',
        bio: userData.bio,
        profilePicture: userData.profilePicture,
        createdAt: this.timestampToDate(userData.createdAt),
        updatedAt: this.timestampToDate(userData.updatedAt),
      };
    } catch (error) {
      // If we can't fetch the user (permissions, deleted, etc), skip this session
      return null;
    }

    // Fetch activity data
    let activity: SessionActivity | undefined;
    const activityId = data.activityId || data.projectId;

    if (activityId) {
      // Check if it's a default activity first
      const defaultActivity = DEFAULT_ACTIVITIES.find(a => a.id === activityId);

      if (defaultActivity) {
        activity = {
          id: defaultActivity.id,
          userId: data.userId,
          name: defaultActivity.name,
          description: '',
          icon: defaultActivity.icon,
          color: defaultActivity.color,
          status: 'active' as const,
          isDefault: true,
          createdAt: new Date(),
          updatedAt: new Date(),
        };
      } else {
        // Fetch custom activity
        try {
          const activityDoc = await getDoc(
            firestoreDoc(
              db,
              'projects',
              data.userId,
              'userProjects',
              activityId
            )
          );
          if (activityDoc.exists()) {
            const activityData = activityDoc.data();
            activity = {
              id: activityId,
              userId: data.userId,
              name: activityData.name || 'Unknown Activity',
              description: activityData.description || '',
              icon: activityData.icon || 'flat-color-icons:briefcase',
              color: activityData.color || '#007AFF',
              status: activityData.status || 'active',
              isDefault: false,
              createdAt: this.timestampToDate(activityData.createdAt),
              updatedAt: this.timestampToDate(activityData.updatedAt),
            };
          }
        } catch (error) {
          // Failed to fetch activity - use default
        }
      }
    }

    // Check if current user has supported this session
    const supportedBy = data.supportedBy || [];
    const isSupported = auth.currentUser
      ? supportedBy.includes(auth.currentUser.uid)
      : false;

    return new Session(
      doc.id,
      data.userId,
      data.projectId,
      data.activityId || null,
      data.duration,
      this.timestampToDate(data.createdAt),
      data.title,
      data.description,
      (data.visibility as SessionVisibility) || 'everyone',
      data.supportCount || 0,
      data.commentCount || 0,
      data.groupIds || [],
      // Enriched data
      user,
      activity,
      activity, // project (backwards compatibility)
      data.images || [],
      isSupported,
      supportedBy,
      data.allowComments !== false,
      this.timestampToDate(data.updatedAt),
      this.timestampToDate(data.startTime),
      data.tags || [],
      data.showStartTime,
      data.howFelt,
      data.privateNotes,
      data.isArchived || false
    );
  }

  /**
   * Convert Session domain entity to Firestore document data
   */
  toFirestore(session: Session): Record<string, unknown> {
    const data: Record<string, unknown> = {
      userId: session.userId,
      projectId: session.projectId,
      duration: session.duration,
      createdAt: Timestamp.fromDate(session.createdAt),
      visibility: session.visibility,
      supportCount: session.supportCount,
      commentCount: session.commentCount,
    };

    // Add optional fields only if defined
    if (session.activityId !== null && session.activityId !== undefined) {
      data.activityId = session.activityId;
    }

    if (session.title !== undefined) {
      data.title = session.title;
    }

    if (session.description !== undefined) {
      data.description = session.description;
    }

    if (session.groupIds && session.groupIds.length > 0) {
      data.groupIds = Array.from(session.groupIds);
    }

    return data;
  }

  /**
   * Convert Firestore Timestamp to Date
   */
  private timestampToDate(timestamp: Timestamp | Date | undefined): Date {
    if (!timestamp) {
      return new Date();
    }

    if (timestamp instanceof Date) {
      return timestamp;
    }

    return timestamp.toDate();
  }

  /**
   * Convert multiple Firestore documents to Session entities (without enrichment)
   */
  toDomainList(docs: DocumentSnapshot[]): Session[] {
    return docs.map(doc => this.toDomain(doc));
  }

  /**
   * Convert multiple Firestore documents to enriched Session entities (with user/activity data)
   * Processes in batches for performance
   */
  async toDomainListEnriched(docs: DocumentSnapshot[]): Promise<Session[]> {
    const sessions: Session[] = [];
    const batchSize = 10;

    for (let i = 0; i < docs.length; i += batchSize) {
      const batch = docs.slice(i, i + batchSize);
      const batchResults = await Promise.all(
        batch.map(doc =>
          this.toDomainEnriched(doc).catch(error => {
            // Failed to enrich session - return null to filter out
            return null;
          })
        )
      );

      // Filter out null values (failed enrichments)
      const validSessions = batchResults.filter(
        (session): session is Session => session !== null
      );
      sessions.push(...validSessions);
    }

    return sessions;
  }
}

/**
 * ActiveSession Repository
 *
 * Handles data access for active timer sessions.
 * Stores in users/{userId}/activeSession subcollection for better security.
 */

import {
  collection,
  doc,
  getDoc,
  getDocs,
  setDoc,
  deleteDoc,
  query,
  where,
  limit as limitFn,
} from 'firebase/firestore';
import { db } from '@/lib/firebase';
import { ActiveSession } from '@/domain/entities/ActiveSession';
import { ActiveSessionMapper } from '../mappers/ActiveSessionMapper';

export class ActiveSessionRepository {
  private readonly mapper: ActiveSessionMapper;

  constructor() {
    this.mapper = new ActiveSessionMapper();
  }

  /**
   * Get active session for a user
   */
  async getActiveSession(userId: string): Promise<ActiveSession | null> {
    try {
      // Check subcollection: users/{userId}/activeSession
      const activeSessionRef = collection(db, `users/${userId}/activeSession`);
      const q = query(activeSessionRef, limitFn(1));
      const snapshot = await getDocs(q);

      if (snapshot.empty) {
        return null;
      }

      const doc = snapshot.docs[0];
      if (!doc) {
        return null;
      }
      return this.mapper.toDomain(doc);
    } catch (error) {
      console.error(`Error getting active session for user ${userId}:`, error);
      throw new Error(
        `Failed to get active session: ${error instanceof Error ? error.message : 'Unknown error'}`
      );
    }
  }

  /**
   * Save active session
   */
  async saveActiveSession(session: ActiveSession): Promise<void> {
    try {
      const docRef = doc(
        db,
        `users/${session.userId}/activeSession`,
        session.id
      );
      const data = this.mapper.toFirestore(session);

      await setDoc(docRef, data);
    } catch (error) {
      console.error(`Error saving active session ${session.id}:`, error);
      throw new Error(
        `Failed to save active session: ${error instanceof Error ? error.message : 'Unknown error'}`
      );
    }
  }

  /**
   * Delete active session
   */
  async deleteActiveSession(userId: string, sessionId: string): Promise<void> {
    try {
      const docRef = doc(db, `users/${userId}/activeSession`, sessionId);
      await deleteDoc(docRef);
    } catch (error) {
      console.error(`Error deleting active session ${sessionId}:`, error);
      throw new Error(
        `Failed to delete active session: ${error instanceof Error ? error.message : 'Unknown error'}`
      );
    }
  }

  /**
   * Clear all active sessions for a user (cleanup)
   */
  async clearActiveSession(userId: string): Promise<void> {
    try {
      const activeSessionRef = collection(db, `users/${userId}/activeSession`);
      const snapshot = await getDocs(activeSessionRef);

      const deletePromises = snapshot.docs.map(doc => deleteDoc(doc.ref));

      await Promise.all(deletePromises);
    } catch (error) {
      console.error(
        `Error clearing active sessions for user ${userId}:`,
        error
      );
      throw new Error(
        `Failed to clear active sessions: ${error instanceof Error ? error.message : 'Unknown error'}`
      );
    }
  }

  /**
   * Check if user has an active session
   */
  async hasActiveSession(userId: string): Promise<boolean> {
    try {
      const session = await this.getActiveSession(userId);
      return session !== null;
    } catch (error) {
      console.error(`Error checking active session for user ${userId}:`, error);
      return false;
    }
  }
}

/**
 * Group Repository
 *
 * Handles all data access for Groups.
 * Implements the Repository pattern for clean separation between domain and infrastructure.
 */

import {
  collection,
  doc,
  getDoc,
  getDocs,
  setDoc,
  updateDoc,
  deleteDoc,
  query,
  where,
  orderBy,
  limit as limitFn,
  writeBatch,
  increment,
  Timestamp
} from 'firebase/firestore';
import { db } from '@/lib/firebase';
import { Group } from '@/domain/entities/Group';
import { GroupMapper } from '../mappers/GroupMapper';

export class GroupRepository {
  private readonly mapper: GroupMapper;
  private readonly collectionName = 'groups';

  constructor() {
    this.mapper = new GroupMapper();
  }

  /**
   * Find group by ID
   */
  async findById(groupId: string): Promise<Group | null> {
    try {
      const docRef = doc(db, this.collectionName, groupId);
      const docSnap = await getDoc(docRef);

      if (!docSnap.exists()) {
        return null;
      }

      return this.mapper.toDomain(docSnap);
    } catch (error) {
      console.error(`Error finding group ${groupId}:`, error);
      throw new Error(`Failed to find group: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Find groups by member ID
   */
  async findByMemberId(userId: string, limit: number = 50): Promise<Group[]> {
    try {
      const q = query(
        collection(db, this.collectionName),
        where('memberIds', 'array-contains', userId),
        orderBy('createdAt', 'desc'),
        limitFn(limit)
      );

      const snapshot = await getDocs(q);
      return this.mapper.toDomainList(snapshot.docs);
    } catch (error) {
      console.error(`Error finding groups for user ${userId}:`, error);
      throw new Error(`Failed to find groups: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Find public groups
   */
  async findPublic(limit: number = 50): Promise<Group[]> {
    try {
      const q = query(
        collection(db, this.collectionName),
        where('privacy', '==', 'public'),
        orderBy('memberCount', 'desc'),
        limitFn(limit)
      );

      const snapshot = await getDocs(q);
      return this.mapper.toDomainList(snapshot.docs);
    } catch (error) {
      console.error('Error finding public groups:', error);
      throw new Error(`Failed to find public groups: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Save group (create or update)
   */
  async save(group: Group): Promise<void> {
    try {
      const docRef = doc(db, this.collectionName, group.id);
      const data = this.mapper.toFirestore(group);

      await setDoc(docRef, data, { merge: true });
    } catch (error) {
      console.error(`Error saving group ${group.id}:`, error);
      throw new Error(`Failed to save group: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Add member to group (updates group entity)
   */
  async addMember(groupId: string, userId: string): Promise<void> {
    try {
      // Firestore doesn't have array append in batches
      // We need to read first, then write
      const group = await this.findById(groupId);
      if (!group) {
        throw new Error('Group not found');
      }

      const updatedGroup = group.withAddedMember(userId);
      await this.save(updatedGroup);
    } catch (error) {
      console.error(`Error adding member ${userId} to group ${groupId}:`, error);
      throw new Error(`Failed to add member: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Remove member from group (transactional with count update)
   */
  async removeMember(groupId: string, userId: string): Promise<void> {
    try {
      const batch = writeBatch(db);
      const groupRef = doc(db, this.collectionName, groupId);

      // Note: Firestore doesn't have array remove operator in batches
      // We need to read first, then write
      const group = await this.findById(groupId);
      if (!group) {
        throw new Error('Group not found');
      }

      const updatedGroup = group.withRemovedMember(userId);
      const data = this.mapper.toFirestore(updatedGroup);

      batch.set(groupRef, data);
      await batch.commit();
    } catch (error) {
      console.error(`Error removing member ${userId} from group ${groupId}:`, error);
      throw new Error(`Failed to remove member: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Delete group
   */
  async delete(groupId: string): Promise<void> {
    try {
      const docRef = doc(db, this.collectionName, groupId);
      await deleteDoc(docRef);
    } catch (error) {
      console.error(`Error deleting group ${groupId}:`, error);
      throw new Error(`Failed to delete group: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Check if group exists
   */
  async exists(groupId: string): Promise<boolean> {
    try {
      const docRef = doc(db, this.collectionName, groupId);
      const docSnap = await getDoc(docRef);
      return docSnap.exists();
    } catch (error) {
      console.error(`Error checking if group ${groupId} exists:`, error);
      return false;
    }
  }

  /**
   * Get member count for a group
   */
  async getMemberCount(groupId: string): Promise<number> {
    try {
      const docRef = doc(db, this.collectionName, groupId);
      const docSnap = await getDoc(docRef);

      if (!docSnap.exists()) {
        return 0;
      }

      const data = docSnap.data();
      return data.memberCount || data.memberIds?.length || 0;
    } catch (error) {
      console.error(`Error getting member count for group ${groupId}:`, error);
      return 0;
    }
  }
}

/**
 * Social Graph Repository
 *
 * Handles queries for social relationships (follows, group memberships).
 */

import {
  collection,
  getDocs,
  query,
  where
} from 'firebase/firestore';
import { db } from '@/lib/firebase';

export class SocialGraphRepository {
  /**
   * Get list of users that a user is following
   */
  async getFollowingIds(userId: string): Promise<string[]> {
    try {
      let followingIds: string[] = [];

      // Try new social_graph structure first
      try {
        const outboundRef = collection(db, `social_graph/${userId}/outbound`);
        const outboundSnapshot = await getDocs(outboundRef);

        if (!outboundSnapshot.empty) {
          followingIds = outboundSnapshot.docs.map(doc => doc.id);
          return followingIds;
        }
      } catch (socialGraphError) {
        // If social_graph doesn't exist, fall through to legacy follows
      }

      // Fallback to old follows collection
      const followingQuery = query(
        collection(db, 'follows'),
        where('followerId', '==', userId)
      );
      const followingSnapshot = await getDocs(followingQuery);

      followingIds = followingSnapshot.docs.map(doc => {
        const data = doc.data();
        return data.followingId;
      });

      return followingIds;
    } catch (error) {
      console.error(`Error getting following IDs for user ${userId}:`, error);
      throw new Error(`Failed to get following IDs: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Get list of followers for a user
   */
  async getFollowerIds(userId: string): Promise<string[]> {
    try {
      let followerIds: string[] = [];

      // Try new social_graph structure first
      try {
        const inboundRef = collection(db, `social_graph/${userId}/inbound`);
        const inboundSnapshot = await getDocs(inboundRef);

        if (!inboundSnapshot.empty) {
          followerIds = inboundSnapshot.docs.map(doc => doc.id);
          return followerIds;
        }
      } catch (socialGraphError) {
        // If social_graph doesn't exist, fall through to legacy follows
      }

      // Fallback to old follows collection
      const followersQuery = query(
        collection(db, 'follows'),
        where('followingId', '==', userId)
      );
      const followersSnapshot = await getDocs(followersQuery);

      followerIds = followersSnapshot.docs.map(doc => {
        const data = doc.data();
        return data.followerId;
      });

      return followerIds;
    } catch (error) {
      console.error(`Error getting follower IDs for user ${userId}:`, error);
      throw new Error(`Failed to get follower IDs: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Get all group member IDs for groups a user belongs to
   */
  async getGroupMemberIds(userId: string): Promise<string[]> {
    try {
      // Get user's groups
      const membershipsQuery = query(
        collection(db, 'groupMemberships'),
        where('userId', '==', userId),
        where('status', '==', 'active')
      );
      const membershipsSnapshot = await getDocs(membershipsQuery);
      const groupIds = membershipsSnapshot.docs.map(doc => doc.data().groupId);

      if (groupIds.length === 0) {
        return [];
      }

      // Get all members from those groups (batch queries due to 'in' limitation)
      const allMemberIds = new Set<string>();

      // Process in batches of 10 (Firestore 'in' limit)
      for (let i = 0; i < groupIds.length; i += 10) {
        const batchGroupIds = groupIds.slice(i, i + 10);

        const groupMembershipsQuery = query(
          collection(db, 'groupMemberships'),
          where('groupId', 'in', batchGroupIds),
          where('status', '==', 'active')
        );

        const groupMembershipsSnapshot = await getDocs(groupMembershipsQuery);
        groupMembershipsSnapshot.docs.forEach(doc => {
          const memberId = doc.data().userId;
          // Don't include the user themselves
          if (memberId !== userId) {
            allMemberIds.add(memberId);
          }
        });
      }

      return Array.from(allMemberIds);
    } catch (error) {
      console.error(`Error getting group member IDs for user ${userId}:`, error);
      throw new Error(`Failed to get group member IDs: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Check if user A follows user B
   */
  async isFollowing(followerId: string, followingId: string): Promise<boolean> {
    try {
      const followingIds = await this.getFollowingIds(followerId);
      return followingIds.includes(followingId);
    } catch (error) {
      console.error(`Error checking if ${followerId} follows ${followingId}:`, error);
      return false;
    }
  }
}

/**
 * User Repository
 *
 * Handles all data access for Users.
 * Implements the Repository pattern for clean separation between domain and infrastructure.
 */

import {
  collection,
  doc,
  getDoc,
  getDocs,
  setDoc,
  updateDoc,
  query,
  where,
  orderBy,
  limit as limitFn,
  writeBatch,
  increment,
} from 'firebase/firestore';
import { db } from '@/lib/firebase';
import { User } from '@/domain/entities/User';
import { UserMapper } from '../mappers/UserMapper';

export class UserRepository {
  private readonly mapper: UserMapper;
  private readonly collectionName = 'users';

  constructor() {
    this.mapper = new UserMapper();
  }

  /**
   * Find user by ID
   */
  async findById(userId: string): Promise<User | null> {
    try {
      const docRef = doc(db, this.collectionName, userId);
      const docSnap = await getDoc(docRef);

      if (!docSnap.exists()) {
        return null;
      }

      return this.mapper.toDomain(docSnap);
    } catch (error) {
      console.error(`Error finding user ${userId}:`, error);
      throw new Error(
        `Failed to find user: ${error instanceof Error ? error.message : 'Unknown error'}`
      );
    }
  }

  /**
   * Find user by username
   */
  async findByUsername(username: string): Promise<User | null> {
    try {
      const q = query(
        collection(db, this.collectionName),
        where('username', '==', username),
        limitFn(1)
      );

      const snapshot = await getDocs(q);

      if (snapshot.empty) {
        return null;
      }

      const doc = snapshot.docs[0];
      if (!doc) {
        return null;
      }
      return this.mapper.toDomain(doc);
    } catch (error) {
      console.error(`Error finding user by username ${username}:`, error);
      throw new Error(
        `Failed to find user: ${error instanceof Error ? error.message : 'Unknown error'}`
      );
    }
  }

  /**
   * Find multiple users by IDs
   */
  async findByIds(userIds: string[]): Promise<User[]> {
    try {
      if (userIds.length === 0) {
        return [];
      }

      // Firestore 'in' queries are limited to 10 items
      // If we have more, we need to batch the requests
      const batchSize = 10;
      const batches: Promise<User[]>[] = [];

      for (let i = 0; i < userIds.length; i += batchSize) {
        const batchIds = userIds.slice(i, i + batchSize);
        const batchPromise = this.fetchUserBatch(batchIds);
        batches.push(batchPromise);
      }

      const results = await Promise.all(batches);
      return results.flat();
    } catch (error) {
      console.error('Error finding users by IDs:', error);
      throw new Error(
        `Failed to find users: ${error instanceof Error ? error.message : 'Unknown error'}`
      );
    }
  }

  /**
   * Helper method to fetch a batch of users (max 10)
   */
  private async fetchUserBatch(userIds: string[]): Promise<User[]> {
    const q = query(
      collection(db, this.collectionName),
      where('__name__', 'in', userIds)
    );

    const snapshot = await getDocs(q);
    return this.mapper.toDomainList(snapshot.docs);
  }

  /**
   * Search users by username (prefix match)
   */
  async searchByUsername(prefix: string, limit: number = 20): Promise<User[]> {
    try {
      // Firestore doesn't support full text search
      // This is a simple prefix match using range queries
      const q = query(
        collection(db, this.collectionName),
        where('username', '>=', prefix),
        where('username', '<=', prefix + '\uf8ff'),
        orderBy('username'),
        limitFn(limit)
      );

      const snapshot = await getDocs(q);
      return this.mapper.toDomainList(snapshot.docs);
    } catch (error) {
      console.error(
        `Error searching users by username prefix ${prefix}:`,
        error
      );
      throw new Error(
        `Failed to search users: ${error instanceof Error ? error.message : 'Unknown error'}`
      );
    }
  }

  /**
   * Save user (create or update)
   */
  async save(user: User): Promise<void> {
    try {
      const docRef = doc(db, this.collectionName, user.id);
      const data = this.mapper.toFirestore(user);

      await setDoc(docRef, data, { merge: true });
    } catch (error) {
      console.error(`Error saving user ${user.id}:`, error);
      throw new Error(
        `Failed to save user: ${error instanceof Error ? error.message : 'Unknown error'}`
      );
    }
  }

  /**
   * Update follower count
   */
  async updateFollowerCount(userId: string, delta: number): Promise<void> {
    try {
      const docRef = doc(db, this.collectionName, userId);
      await updateDoc(docRef, {
        followerCount: increment(delta),
      });
    } catch (error) {
      console.error(`Error updating follower count for user ${userId}:`, error);
      throw new Error(
        `Failed to update follower count: ${error instanceof Error ? error.message : 'Unknown error'}`
      );
    }
  }

  /**
   * Update following count
   */
  async updateFollowingCount(userId: string, delta: number): Promise<void> {
    try {
      const docRef = doc(db, this.collectionName, userId);
      await updateDoc(docRef, {
        followingCount: increment(delta),
      });
    } catch (error) {
      console.error(
        `Error updating following count for user ${userId}:`,
        error
      );
      throw new Error(
        `Failed to update following count: ${error instanceof Error ? error.message : 'Unknown error'}`
      );
    }
  }

  /**
   * Check if user exists
   */
  async exists(userId: string): Promise<boolean> {
    try {
      const docRef = doc(db, this.collectionName, userId);
      const docSnap = await getDoc(docRef);
      return docSnap.exists();
    } catch (error) {
      console.error(`Error checking if user ${userId} exists:`, error);
      return false;
    }
  }

  /**
   * Check if username is available
   */
  async isUsernameAvailable(username: string): Promise<boolean> {
    try {
      const user = await this.findByUsername(username);
      return user === null;
    } catch (error) {
      console.error(
        `Error checking username availability for ${username}:`,
        error
      );
      return false;
    }
  }
}

/**
 * Tests for error handler utility
 */

import {
  handleError,
  createApiError,
  isPermissionError,
  isNotFoundError,
  isAuthError,
  isNetworkError,
  withNullOnError,
  ErrorSeverity,
} from '../errorHandler';

describe('errorHandler', () => {
  describe('createApiError', () => {
    it('creates error from Firebase auth error', () => {
      const firebaseError = {
        code: 'auth/invalid-email',
        message: 'The email address is badly formatted.',
      };

      const apiError = createApiError(firebaseError, 'Login');

      expect(apiError.code).toBe('auth/invalid-email');
      expect(apiError.message).toContain('Login');
      expect(apiError.userMessage).toBe('Please enter a valid email address.');
    });

    it('creates error from Firestore error', () => {
      const firestoreError = {
        code: 'permission-denied',
        message: 'Missing or insufficient permissions.',
      };

      const apiError = createApiError(firestoreError, 'Fetch user');

      expect(apiError.code).toBe('permission-denied');
      expect(apiError.userMessage).toBe("You don't have permission to perform this action.");
    });

    it('creates error from generic Error', () => {
      const error = new Error('Something went wrong');

      const apiError = createApiError(error, 'Operation');

      expect(apiError.code).toBe('unknown');
      expect(apiError.message).toContain('Operation');
      expect(apiError.userMessage).toBe('An error occurred. Please try again.');
    });

    it('uses default message when error code is unknown', () => {
      const error = { code: 'unknown-error', message: 'Unknown' };
      const defaultMessage = 'Custom default message';

      const apiError = createApiError(error, 'Operation', defaultMessage);

      expect(apiError.userMessage).toBe(defaultMessage);
    });
  });

  describe('handleError', () => {
    it('handles error and returns ApiError', () => {
      const error = {
        code: 'auth/user-not-found',
        message: 'User not found',
      };

      const apiError = handleError(error, 'Login', {
        silent: true, // Don't log during tests
      });

      expect(apiError.code).toBe('auth/user-not-found');
      expect(apiError.userMessage).toContain('No account found');
    });

    it('respects custom default message', () => {
      const error = new Error('Unknown error');

      const apiError = handleError(error, 'Custom operation', {
        defaultMessage: 'Custom error message',
        silent: true,
      });

      expect(apiError.userMessage).toBe('Custom error message');
    });
  });

  describe('isPermissionError', () => {
    it('detects permission-denied error', () => {
      const error = { code: 'permission-denied' };
      expect(isPermissionError(error)).toBe(true);
    });

    it('detects auth/unauthorized error', () => {
      const error = { code: 'auth/unauthorized' };
      expect(isPermissionError(error)).toBe(true);
    });

    it('returns false for other errors', () => {
      const error = { code: 'not-found' };
      expect(isPermissionError(error)).toBe(false);
    });
  });

  describe('isNotFoundError', () => {
    it('detects not-found error', () => {
      const error = { code: 'not-found' };
      expect(isNotFoundError(error)).toBe(true);
    });

    it('detects storage/object-not-found error', () => {
      const error = { code: 'storage/object-not-found' };
      expect(isNotFoundError(error)).toBe(true);
    });

    it('returns false for other errors', () => {
      const error = { code: 'permission-denied' };
      expect(isNotFoundError(error)).toBe(false);
    });
  });

  describe('isAuthError', () => {
    it('detects unauthenticated error', () => {
      const error = { code: 'unauthenticated' };
      expect(isAuthError(error)).toBe(true);
    });

    it('detects auth/ prefixed errors', () => {
      const error = { code: 'auth/invalid-credential' };
      expect(isAuthError(error)).toBe(true);
    });

    it('returns false for other errors', () => {
      const error = { code: 'not-found' };
      expect(isAuthError(error)).toBe(false);
    });
  });

  describe('isNetworkError', () => {
    it('detects unavailable error', () => {
      const error = { code: 'unavailable' };
      expect(isNetworkError(error)).toBe(true);
    });

    it('detects network-related message', () => {
      const error = new Error('Network connection failed');
      expect(isNetworkError(error)).toBe(true);
    });

    it('returns false for other errors', () => {
      const error = { code: 'not-found' };
      expect(isNetworkError(error)).toBe(false);
    });
  });

  describe('withNullOnError', () => {
    it('returns result on success', async () => {
      const operation = async () => 'success';

      const result = await withNullOnError(operation, 'Test', {
        silent: true,
      });

      expect(result).toBe('success');
    });

    it('returns null on permission error when configured', async () => {
      const operation = async () => {
        throw { code: 'permission-denied' };
      };

      const result = await withNullOnError(operation, 'Test', {
        nullOnPermissionDenied: true,
        silent: true,
      });

      expect(result).toBeNull();
    });

    it('returns null on not-found error when configured', async () => {
      const operation = async () => {
        throw { code: 'not-found' };
      };

      const result = await withNullOnError(operation, 'Test', {
        nullOnNotFound: true,
        silent: true,
      });

      expect(result).toBeNull();
    });

    it('throws on other errors', async () => {
      const operation = async () => {
        throw new Error('Test error');
      };

      await expect(
        withNullOnError(operation, 'Test', {
          silent: true,
        })
      ).rejects.toThrow();
    });

    it('does not return null when not configured', async () => {
      const operation = async () => {
        throw { code: 'permission-denied' };
      };

      await expect(
        withNullOnError(operation, 'Test', {
          silent: true,
        })
      ).rejects.toThrow();
    });
  });
});

import { uploadImage, uploadImages, deleteImage, deleteImages, compressImage } from '../imageUpload';
import { storage, auth } from '../firebase';
import { ref, uploadBytes, getDownloadURL, deleteObject } from 'firebase/storage';

// Mock Firebase modules
jest.mock('../firebase', () => ({
  storage: {},
  auth: {
    currentUser: {
      uid: 'test-user-123'
    }
  }
}));

jest.mock('firebase/storage', () => ({
  ref: jest.fn(),
  uploadBytes: jest.fn(),
  getDownloadURL: jest.fn(),
  deleteObject: jest.fn()
}));

describe('imageUpload utilities', () => {
  const mockFile = new File(['test content'], 'test-image.jpg', { type: 'image/jpeg' });
  const mockStorageRef = { fullPath: 'session-images/test-user-123/test-image.jpg' };
  const mockDownloadURL = 'https://firebasestorage.googleapis.com/test-image.jpg';

  beforeEach(() => {
    jest.clearAllMocks();
    (ref as jest.Mock).mockReturnValue(mockStorageRef);
    (uploadBytes as jest.Mock).mockResolvedValue({});
    (getDownloadURL as jest.Mock).mockResolvedValue(mockDownloadURL);
    (deleteObject as jest.Mock).mockResolvedValue(undefined);
  });

  describe('uploadImage', () => {
    it('should upload a valid image file', async () => {
      const result = await uploadImage(mockFile);

      expect(result).toEqual({
        url: mockDownloadURL,
        path: mockStorageRef.fullPath
      });
      expect(ref).toHaveBeenCalled();
      expect(uploadBytes).toHaveBeenCalledWith(mockStorageRef, mockFile);
      expect(getDownloadURL).toHaveBeenCalledWith(mockStorageRef);
    });

    it('should throw error if user is not authenticated', async () => {
      const originalUser = auth.currentUser;
      (auth as any).currentUser = null;

      await expect(uploadImage(mockFile)).rejects.toThrow('User must be authenticated to upload images');

      (auth as any).currentUser = originalUser;
    });

    it('should throw error for non-image files', async () => {
      const textFile = new File(['text content'], 'test.txt', { type: 'text/plain' });

      await expect(uploadImage(textFile)).rejects.toThrow('File must be an image');
    });

    it('should throw error for files larger than 5MB', async () => {
      const largeFile = new File(['x'.repeat(6 * 1024 * 1024)], 'large.jpg', { type: 'image/jpeg' });

      await expect(uploadImage(largeFile)).rejects.toThrow('Image must be less than 5MB');
    });

    it('should use custom folder path', async () => {
      await uploadImage(mockFile, 'custom-folder');

      expect(ref).toHaveBeenCalledWith(
        storage,
        expect.stringContaining('custom-folder/test-user-123/')
      );
    });

    it('should generate unique filenames', async () => {
      await uploadImage(mockFile);
      const firstCall = (ref as jest.Mock).mock.calls[0][1];

      jest.clearAllMocks();
      (ref as jest.Mock).mockReturnValue(mockStorageRef);

      await uploadImage(mockFile);
      const secondCall = (ref as jest.Mock).mock.calls[0][1];

      expect(firstCall).not.toBe(secondCall);
    });

    it('should handle upload errors gracefully', async () => {
      (uploadBytes as jest.Mock).mockRejectedValue(new Error('Network error'));

      await expect(uploadImage(mockFile)).rejects.toThrow('Failed to upload image');
    });
  });

  describe('uploadImages', () => {
    it('should upload multiple images', async () => {
      const files = [
        new File(['content1'], 'image1.jpg', { type: 'image/jpeg' }),
        new File(['content2'], 'image2.jpg', { type: 'image/jpeg' }),
        new File(['content3'], 'image3.jpg', { type: 'image/jpeg' })
      ];

      const results = await uploadImages(files);

      expect(results).toHaveLength(3);
      expect(uploadBytes).toHaveBeenCalledTimes(3);
      expect(results.every(r => r.url === mockDownloadURL)).toBe(true);
    });

    it('should throw error for more than 3 images', async () => {
      const files = [
        new File(['1'], '1.jpg', { type: 'image/jpeg' }),
        new File(['2'], '2.jpg', { type: 'image/jpeg' }),
        new File(['3'], '3.jpg', { type: 'image/jpeg' }),
        new File(['4'], '4.jpg', { type: 'image/jpeg' })
      ];

      await expect(uploadImages(files)).rejects.toThrow('Maximum 3 images allowed');
    });

    it('should handle empty array', async () => {
      const results = await uploadImages([]);

      expect(results).toHaveLength(0);
      expect(uploadBytes).not.toHaveBeenCalled();
    });

    it('should upload all images in parallel', async () => {
      const files = [
        new File(['1'], '1.jpg', { type: 'image/jpeg' }),
        new File(['2'], '2.jpg', { type: 'image/jpeg' })
      ];

      const uploadPromise = uploadImages(files);

      // All uploads should be initiated before any completes
      await Promise.resolve(); // Let promises start
      expect(uploadBytes).toHaveBeenCalledTimes(2);

      await uploadPromise;
    });
  });

  describe('deleteImage', () => {
    it('should delete an image by path', async () => {
      const path = 'session-images/test-user-123/test-image.jpg';

      await deleteImage(path);

      expect(ref).toHaveBeenCalledWith(storage, path);
      expect(deleteObject).toHaveBeenCalledWith(mockStorageRef);
    });

    it('should throw error if user is not authenticated', async () => {
      const originalUser = auth.currentUser;
      (auth as any).currentUser = null;

      await expect(deleteImage('test-path')).rejects.toThrow('User must be authenticated to delete images');

      (auth as any).currentUser = originalUser;
    });

    it('should handle delete errors gracefully', async () => {
      (deleteObject as jest.Mock).mockRejectedValue(new Error('File not found'));

      await expect(deleteImage('test-path')).rejects.toThrow('Failed to delete image');
    });
  });

  describe('deleteImages', () => {
    it('should delete multiple images', async () => {
      const paths = [
        'session-images/user/image1.jpg',
        'session-images/user/image2.jpg',
        'session-images/user/image3.jpg'
      ];

      await deleteImages(paths);

      expect(deleteObject).toHaveBeenCalledTimes(3);
    });

    it('should handle empty array', async () => {
      await deleteImages([]);

      expect(deleteObject).not.toHaveBeenCalled();
    });

    it('should delete all images in parallel', async () => {
      const paths = ['path1.jpg', 'path2.jpg'];

      const deletePromise = deleteImages(paths);

      await Promise.resolve();
      expect(deleteObject).toHaveBeenCalledTimes(2);

      await deletePromise;
    });
  });

  describe('compressImage', () => {
    it('should return the original file without compression', async () => {
      const file = new File(['content'], 'test.jpg', { type: 'image/jpeg' });

      const result = await compressImage(file);

      expect(result).toBe(file);
      expect(result.name).toBe('test.jpg');
      expect(result.type).toBe('image/jpeg');
    });

    it('should accept optional parameters without using them', async () => {
      const file = new File(['content'], 'test.jpg', { type: 'image/jpeg' });

      const result = await compressImage(file, 800, 800, 0.5);

      expect(result).toBe(file);
    });
  });
});

/**
 * Rate Limiting Tests
 */

import {
  checkRateLimit,
  withRateLimit,
  getRateLimitInfo,
  resetRateLimit,
  RateLimitError,
  formatRetryAfter,
  rateLimiter,
  RATE_LIMITS,
} from '../rateLimit';

describe('Rate Limiting', () => {
  const testUserId = 'test-user-123';

  beforeEach(() => {
    // Reset all rate limits before each test
    jest.clearAllMocks();
  });

  afterAll(() => {
    // Clean up the rate limiter
    rateLimiter.destroy();
  });

  describe('checkRateLimit', () => {
    it('should allow requests within rate limit', () => {
      expect(() => {
        checkRateLimit(testUserId, 'AUTH_LOGIN');
      }).not.toThrow();
    });

    it('should throw RateLimitError when limit exceeded', () => {
      const userId = testUserId + '-exceed';
      const limit = RATE_LIMITS.AUTH_LOGIN.maxRequests;

      // Make requests up to the limit
      for (let i = 0; i < limit; i++) {
        checkRateLimit(userId, 'AUTH_LOGIN');
      }

      // Next request should throw
      expect(() => {
        checkRateLimit(userId, 'AUTH_LOGIN');
      }).toThrow(RateLimitError);
    });

    it('should track different operations separately', () => {
      const userId = testUserId + '-separate';
      // Use up login limit
      const loginLimit = RATE_LIMITS.AUTH_LOGIN.maxRequests;
      for (let i = 0; i < loginLimit; i++) {
        checkRateLimit(userId, 'AUTH_LOGIN');
      }

      // Signup should still work
      expect(() => {
        checkRateLimit(userId, 'AUTH_SIGNUP');
      }).not.toThrow();
    });

    it('should track different users separately', () => {
      const user1 = 'user-1';
      const user2 = 'user-2';
      const limit = RATE_LIMITS.FOLLOW.maxRequests;

      // Use up limit for user1
      for (let i = 0; i < limit; i++) {
        checkRateLimit(user1, 'FOLLOW');
      }

      // User1 should be rate limited
      expect(() => {
        checkRateLimit(user1, 'FOLLOW');
      }).toThrow(RateLimitError);

      // User2 should still work
      expect(() => {
        checkRateLimit(user2, 'FOLLOW');
      }).not.toThrow();
    });
  });

  describe('RateLimitError', () => {
    it('should include retry information', () => {
      const limit = RATE_LIMITS.SUPPORT.maxRequests;

      for (let i = 0; i < limit; i++) {
        checkRateLimit(testUserId, 'SUPPORT');
      }

      try {
        checkRateLimit(testUserId, 'SUPPORT');
        fail('Should have thrown RateLimitError');
      } catch (error) {
        expect(error).toBeInstanceOf(RateLimitError);
        if (error instanceof RateLimitError) {
          expect(error.retryAfter).toBeGreaterThan(0);
          expect(error.limit).toBe(limit);
          expect(error.message).toContain('slow down');
        }
      }
    });

    it('should apply exponential backoff for repeated violations', () => {
      const limit = RATE_LIMITS.COMMENT.maxRequests;

      // First violation
      for (let i = 0; i <= limit; i++) {
        try {
          checkRateLimit(testUserId + '-backoff', 'COMMENT');
        } catch (error) {
          if (error instanceof RateLimitError) {
            const firstRetryAfter = error.retryAfter;

            // Try again (second violation)
            try {
              checkRateLimit(testUserId + '-backoff', 'COMMENT');
            } catch (error2) {
              if (error2 instanceof RateLimitError) {
                // Second violation should have longer retry time
                expect(error2.retryAfter).toBeGreaterThanOrEqual(
                  firstRetryAfter
                );
              }
            }
          }
        }
      }
    });
  });

  describe('withRateLimit', () => {
    it('should execute function if within rate limit', async () => {
      const mockFn = jest.fn().mockResolvedValue('success');

      const result = await withRateLimit(testUserId, 'PROJECT_CREATE', mockFn);

      expect(result).toBe('success');
      expect(mockFn).toHaveBeenCalledTimes(1);
    });

    it('should throw RateLimitError if limit exceeded', async () => {
      const mockFn = jest.fn().mockResolvedValue('success');
      const limit = RATE_LIMITS.PROJECT_CREATE.maxRequests;

      // Use up the limit
      for (let i = 0; i < limit; i++) {
        await withRateLimit(testUserId + '-fn', 'PROJECT_CREATE', mockFn);
      }

      // Next call should throw
      await expect(
        withRateLimit(testUserId + '-fn', 'PROJECT_CREATE', mockFn)
      ).rejects.toThrow(RateLimitError);
    });

    it('should not execute function if rate limited', async () => {
      const mockFn = jest.fn().mockResolvedValue('success');
      const limit = RATE_LIMITS.TASK_CREATE.maxRequests;

      // Use up the limit
      for (let i = 0; i < limit; i++) {
        checkRateLimit(testUserId + '-noexec', 'TASK_CREATE');
      }

      // Function should not be called
      try {
        await withRateLimit(testUserId + '-noexec', 'TASK_CREATE', mockFn);
      } catch (error) {
        // Expected to throw
      }

      expect(mockFn).not.toHaveBeenCalled();
    });
  });

  describe('getRateLimitInfo', () => {
    it('should return correct info for unused limit', () => {
      const info = getRateLimitInfo(testUserId + '-info', 'SESSION_CREATE');

      expect(info.remaining).toBe(RATE_LIMITS.SESSION_CREATE.maxRequests);
      expect(info.limit).toBe(RATE_LIMITS.SESSION_CREATE.maxRequests);
      expect(info.resetTime).toBeNull();
      expect(info.violations).toBe(0);
    });

    it('should return correct remaining count', () => {
      const userId = testUserId + '-remaining';
      const limit = RATE_LIMITS.FOLLOW.maxRequests;

      // Use 3 requests
      checkRateLimit(userId, 'FOLLOW');
      checkRateLimit(userId, 'FOLLOW');
      checkRateLimit(userId, 'FOLLOW');

      const info = getRateLimitInfo(userId, 'FOLLOW');

      expect(info.remaining).toBe(limit - 3);
      expect(info.limit).toBe(limit);
    });

    it('should return reset time when limit is active', () => {
      const userId = testUserId + '-reset';

      checkRateLimit(userId, 'SUPPORT');

      const info = getRateLimitInfo(userId, 'SUPPORT');

      expect(info.resetTime).toBeGreaterThan(0);
    });
  });

  describe('resetRateLimit', () => {
    it('should reset rate limit for user and operation', () => {
      const userId = testUserId + '-reset-test';
      const limit = RATE_LIMITS.FILE_UPLOAD.maxRequests;

      // Use up the limit
      for (let i = 0; i < limit; i++) {
        checkRateLimit(userId, 'FILE_UPLOAD');
      }

      // Should be rate limited
      expect(() => {
        checkRateLimit(userId, 'FILE_UPLOAD');
      }).toThrow(RateLimitError);

      // Reset
      resetRateLimit(userId, 'FILE_UPLOAD');

      // Should work again
      expect(() => {
        checkRateLimit(userId, 'FILE_UPLOAD');
      }).not.toThrow();
    });
  });

  describe('formatRetryAfter', () => {
    it('should format seconds correctly', () => {
      expect(formatRetryAfter(1)).toBe('1 second');
      expect(formatRetryAfter(30)).toBe('30 seconds');
      expect(formatRetryAfter(59)).toBe('59 seconds');
    });

    it('should format minutes correctly', () => {
      expect(formatRetryAfter(60)).toBe('1 minute');
      expect(formatRetryAfter(90)).toBe('2 minutes');
      expect(formatRetryAfter(300)).toBe('5 minutes');
    });
  });

  describe('RATE_LIMITS configuration', () => {
    it('should have reasonable limits for authentication', () => {
      expect(RATE_LIMITS.AUTH_LOGIN.maxRequests).toBeLessThanOrEqual(10);
      expect(RATE_LIMITS.AUTH_LOGIN.windowMs).toBeGreaterThanOrEqual(60000);

      expect(RATE_LIMITS.AUTH_SIGNUP.maxRequests).toBeLessThanOrEqual(5);
      expect(RATE_LIMITS.AUTH_SIGNUP.windowMs).toBeGreaterThanOrEqual(300000);
    });

    it('should have reasonable limits for social operations', () => {
      expect(RATE_LIMITS.FOLLOW.maxRequests).toBeGreaterThan(10);
      expect(RATE_LIMITS.SUPPORT.maxRequests).toBeGreaterThan(10);
      expect(RATE_LIMITS.COMMENT.maxRequests).toBeGreaterThan(5);
    });

    it('should have reasonable limits for content creation', () => {
      expect(RATE_LIMITS.SESSION_CREATE.maxRequests).toBeGreaterThan(10);
      expect(RATE_LIMITS.PROJECT_CREATE.maxRequests).toBeGreaterThan(5);
      expect(RATE_LIMITS.TASK_CREATE.maxRequests).toBeGreaterThan(10);
    });

    it('should have user-friendly error messages', () => {
      Object.values(RATE_LIMITS).forEach(config => {
        expect(config.message).toBeTruthy();
        expect(config.message.length).toBeGreaterThan(10);
      });
    });
  });

  describe('Time window expiration', () => {
    it('should reset counter after time window expires', async () => {
      const userId = testUserId + '-expire';

      // Make a request
      checkRateLimit(userId, 'FOLLOW');

      const info1 = getRateLimitInfo(userId, 'FOLLOW');
      expect(info1.remaining).toBe(RATE_LIMITS.FOLLOW.maxRequests - 1);

      // Wait for window to expire (simulate by resetting)
      resetRateLimit(userId, 'FOLLOW');

      const info2 = getRateLimitInfo(userId, 'FOLLOW');
      expect(info2.remaining).toBe(RATE_LIMITS.FOLLOW.maxRequests);
    }, 10000);
  });
});

import axios, { AxiosInstance, AxiosResponse } from 'axios';
import {
  AuthResponse,
  LoginCredentials,
  SignupCredentials,
  AuthUser,
  Project,
  ProjectStats,
  CreateProjectData,
  UpdateProjectData,
  ActiveTimer,
  Session,
  CreateSessionData,
  SessionFormData,
  SessionFilters,
  SessionSort,
  SessionListResponse,
  UserProfile,
  UserStats,
  ActivityData,
  WeeklyActivity,
  ProjectBreakdown,
  PrivacySettings,
  UserSearchResult,
  SuggestedUser
} from '@/types';

// Create axios instance
const api: AxiosInstance = axios.create({
  baseURL: process.env.NEXT_PUBLIC_API_URL || 'http://localhost:3001/api',
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Token management (in memory)
let authToken: string | null = null;

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    if (authToken) {
      config.headers.Authorization = `Bearer ${authToken}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor to handle 401 errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Clear token and redirect to login
      authToken = null;
      if (typeof window !== 'undefined') {
        window.location.href = '/login';
      }
    }
    return Promise.reject(error);
  }
);

// Auth API methods
export const authApi = {
  // Set token for authenticated requests
  setToken: (token: string) => {
    authToken = token;
    if (typeof window !== 'undefined') {
      localStorage.setItem('auth_token', token);
    }
  },

  // Clear token
  clearToken: () => {
    authToken = null;
    if (typeof window !== 'undefined') {
      localStorage.removeItem('auth_token');
    }
  },

  // Get current token
  getToken: () => {
    if (authToken) return authToken;
    if (typeof window !== 'undefined') {
      return localStorage.getItem('auth_token');
    }
    return null;
  },

  // Login
  login: async (credentials: LoginCredentials): Promise<AuthResponse> => {
    const response: AxiosResponse<AuthResponse> = await api.post('/auth/login', credentials);
    return response.data;
  },

  // Signup
  signup: async (credentials: SignupCredentials): Promise<AuthResponse> => {
    const response: AxiosResponse<AuthResponse> = await api.post('/auth/signup', credentials);
    return response.data;
  },

  // Logout
  logout: async (): Promise<void> => {
    await api.post('/auth/logout');
    authToken = null;
  },

  // Get current user
  getCurrentUser: async (): Promise<AuthUser> => {
    const response: AxiosResponse<AuthUser> = await api.get('/auth/me');
    return response.data;
  },

  // Verify token
  verifyToken: async (): Promise<boolean> => {
    try {
      await api.get('/auth/verify');
      return true;
    } catch {
      return false;
    }
  },
};

// Project API methods
export const projectApi = {
  // Get all user's projects
  getProjects: async (): Promise<Project[]> => {
    const response: AxiosResponse<Project[]> = await api.get('/projects');
    return response.data;
  },

  // Get single project by ID
  getProject: async (id: string): Promise<Project> => {
    const response: AxiosResponse<Project> = await api.get(`/projects/${id}`);
    return response.data;
  },

  // Create new project
  createProject: async (data: CreateProjectData): Promise<Project> => {
    const response: AxiosResponse<Project> = await api.post('/projects', data);
    return response.data;
  },

  // Update project
  updateProject: async (id: string, data: UpdateProjectData): Promise<Project> => {
    const response: AxiosResponse<Project> = await api.put(`/projects/${id}`, data);
    return response.data;
  },

  // Delete project
  deleteProject: async (id: string): Promise<void> => {
    await api.delete(`/projects/${id}`);
  },

  // Get project statistics
  getProjectStats: async (id: string): Promise<ProjectStats> => {
    const response: AxiosResponse<ProjectStats> = await api.get(`/projects/${id}/stats`);
    return response.data;
  },

  // Archive project
  archiveProject: async (id: string): Promise<Project> => {
    const response: AxiosResponse<Project> = await api.patch(`/projects/${id}/archive`);
    return response.data;
  },

  // Restore project
