    } catch (error) {
      // Re-throw rate limit errors as-is
      if (error instanceof RateLimitError) {
        throw error;
      }
      const apiError = handleError(error, 'Login', {
        defaultMessage: 'Login failed',
      });
      throw new Error(apiError.userMessage);
