export const firebaseGroupApi = {
  /**
   * Get a group by ID
   */
  getGroup: async (groupId: string): Promise<Group | null> => {
    try {
      const docRef = doc(db, 'groups', groupId);
      const docSnap = await getDoc(docRef);

      if (!docSnap.exists()) {
        return null;
      }

      const data = docSnap.data();
      const memberIds = data.memberIds || [];
      return {
        id: docSnap.id,
        name: data.name,
        description: data.description,
        imageUrl: data.imageUrl,
        category: data.category,
        type: data.type,
        privacySetting: data.privacySetting,
        location: data.location,
        adminUserIds: data.adminUserIds || [],
        memberIds: memberIds,
        memberCount: data.memberCount ?? memberIds.length,
        createdByUserId: data.createdByUserId,
        createdAt: convertTimestamp(data.createdAt),
        updatedAt: convertTimestamp(data.updatedAt),
      } as Group;
    } catch (error) {
      console.error('Error getting group:', error);
      throw new Error(
        typeof error === 'string' ? error : 'Failed to get group'
      );
    }
  },

  /**
   * Create a new group
   */
  createGroup: async (
    groupData: CreateGroupData,
    userId: string
  ): Promise<Group> => {
    try {
      const groupId = doc(db, 'groups').id;
      const now = serverTimestamp();

      const newGroup = {
        ...removeUndefinedFields(groupData),
        adminUserIds: [userId],
        memberIds: [userId],
        memberCount: 1,
        createdByUserId: userId,
        createdAt: now,
        updatedAt: now,
      };

      const docRef = doc(db, 'groups', groupId);
      await setDoc(docRef, newGroup);

      return {
        id: groupId,
        ...groupData,
        adminUserIds: [userId],
        memberIds: [userId],
        memberCount: 1,
        createdByUserId: userId,
        createdAt: new Date(),
        updatedAt: new Date(),
      } as Group;
    } catch (error) {
      console.error('Error creating group:', error);
      throw new Error('Failed to create group');
    }
  },

  /**
   * Update a group
   */
  updateGroup: async (
    groupId: string,
    updateData: UpdateGroupData
  ): Promise<void> => {
    try {
      const docRef = doc(db, 'groups', groupId);
      const updatePayload = {
        ...removeUndefinedFields(updateData),
        updatedAt: serverTimestamp(),
      };

      await updateDoc(docRef, updatePayload);
    } catch (error) {
      console.error('Error updating group:', error);
      throw new Error('Failed to update group');
    }
  },

  /**
   * Delete a group
   */
  deleteGroup: async (groupId: string): Promise<void> => {
    try {
      const docRef = doc(db, 'groups', groupId);
      await deleteDoc(docRef);
    } catch (error) {
      console.error('Error deleting group:', error);
      throw new Error('Failed to delete group');
    }
  },

  /**
   * Add a member to a group
   */
  addMember: async (groupId: string, userId: string): Promise<void> => {
    try {
      const groupRef = doc(db, 'groups', groupId);
      const groupSnap = await getDoc(groupRef);

      if (!groupSnap.exists()) {
        throw new Error('Group not found');
      }

      const groupData = groupSnap.data();
      const memberIds = groupData.memberIds || [];

      if (memberIds.includes(userId)) {
        throw new Error('User is already a member of this group');
      }

      await updateDoc(groupRef, {
        memberIds: [...memberIds, userId],
        updatedAt: serverTimestamp(),
      });
    } catch (error) {
      console.error('Error adding member to group:', error);
      throw new Error('Failed to add member');
    }
  },

  /**
   * Remove a member from a group
   */
  removeMember: async (groupId: string, userId: string): Promise<void> => {
    try {
      const groupRef = doc(db, 'groups', groupId);
      const groupSnap = await getDoc(groupRef);

      if (!groupSnap.exists()) {
        throw new Error('Group not found');
      }

      const groupData = groupSnap.data();
      const memberIds = groupData.memberIds || [];

      if (!memberIds.includes(userId)) {
        throw new Error('User is not a member of this group');
      }

      await updateDoc(groupRef, {
        memberIds: memberIds.filter((id: string) => id !== userId),
        updatedAt: serverTimestamp(),
      });
    } catch (error) {
      console.error('Error removing member from group:', error);
      throw new Error('Failed to remove member');
    }
  },

  /**
   * Join a group
   */
  joinGroup: async (groupId: string, userId: string): Promise<void> => {
    try {
      // Use GroupService for business logic
      const { GroupService } = await import(
        '@/features/groups/services/GroupService'
      );
      const groupService = new GroupService();
      await groupService.joinGroup(groupId, userId);
    } catch (error) {
      console.error('Error joining group:', error);
      throw new Error(
        typeof error === 'string'
          ? error
          : error instanceof Error
            ? error.message
            : 'Failed to join group'
      );
    }
  },

  /**
   * Leave a group
   */
  leaveGroup: async (groupId: string, userId: string): Promise<void> => {
    try {
      // Use GroupService for business logic
      const { GroupService } = await import(
        '@/features/groups/services/GroupService'
      );
      const groupService = new GroupService();
      await groupService.leaveGroup(groupId, userId);
    } catch (error) {
      console.error('Error leaving group:', error);
      throw new Error(
        typeof error === 'string'
          ? error
          : error instanceof Error
            ? error.message
            : 'Failed to leave group'
      );
    }
  },

  /**
   * Get all groups for a user
   */
  getUserGroups: async (userId: string, limit?: number): Promise<Group[]> => {
    try {
      // Use GroupService for business logic
      const { GroupService } = await import(
