          return {
            id: docSnapshot.id,
            sessionId: data.sessionId,
            userId: data.userId,
            parentId: data.parentId,
            content: data.content,
            likeCount: data.likeCount || 0,
            replyCount: data.replyCount || 0,
            isLiked: likedCommentIds.has(docSnapshot.id),
