        severity: ErrorSeverity.ERROR,
      });
      return false;
    }
  },

  /**
   * Get all followers for a user (people who follow this user)
   * Supports both new social_graph structure and legacy follows collection
   *
   * @param userId - The user ID whose followers to retrieve
   * @returns Promise resolving to array of follower users (empty array on error)
   * @throws Error if fetch fails (except permission errors which return empty array)
   */
  getFollowers: async (userId: string): Promise<User[]> => {
    try {
      let followerIds: string[] = [];

      // Try new social_graph structure first
      try {
        const inboundRef = collection(db, `social_graph/${userId}/inbound`);
        const inboundSnapshot = await getDocs(inboundRef);

        if (!inboundSnapshot.empty) {
          followerIds = inboundSnapshot.docs.map(doc => doc.id);
        }
      } catch (socialGraphError) {
        // If social_graph doesn't exist or has permission issues, continue to fallback
      }

      // Fallback to old follows collection if no followers found via social_graph
      if (followerIds.length === 0) {
        const followersQuery = query(
          collection(db, 'follows'),
          where('followingId', '==', userId)
        );
        const followersSnapshot = await getDocs(followersQuery);

        followerIds = followersSnapshot.docs.map(doc => {
          const data = doc.data();
          return data.followerId;
        });
      }

      if (followerIds.length === 0) {
        return [];
      }

      // Get user details for all followers
      const followers: User[] = [];

      for (const followerId of followerIds) {
        const userDoc = await getDoc(doc(db, 'users', followerId));
        if (userDoc.exists()) {
          const userData = userDoc.data();
          followers.push({
            id: userDoc.id,
            username: userData.username,
            email: userData.email,
            name: userData.name,
            bio: userData.bio || '',
            profilePicture: userData.profilePicture,
            followersCount: userData.followersCount || 0,
            followingCount: userData.followingCount || 0,
            createdAt: userData.createdAt?.toDate() || new Date(),
            updatedAt: userData.updatedAt?.toDate() || new Date(),
          });
        } else {
        }
      }

      return followers;
    } catch (error) {
      // Handle permission errors silently for privacy-protected data
      if (isPermissionError(error)) {
        return [];
      }
      console.error('[getFollowers] Error:', error);
      const apiError = handleError(error, 'Fetch followers', {
        defaultMessage: ERROR_MESSAGES.PROFILE_LOAD_FAILED,
      });
      throw new Error(apiError.userMessage);
    }
  },

  /**
   * Get all users that a user is following
   * Supports both new social_graph structure and legacy follows collection
   *
   * @param userId - The user ID whose following list to retrieve
   * @returns Promise resolving to array of following users (empty array on error)
   * @throws Error if fetch fails (except permission errors which return empty array)
   */
  getFollowing: async (userId: string): Promise<User[]> => {
    try {
      let followingIds: string[] = [];

      // Try new social_graph structure first
      try {
        const outboundRef = collection(db, `social_graph/${userId}/outbound`);
        const outboundSnapshot = await getDocs(outboundRef);

        if (!outboundSnapshot.empty) {
          followingIds = outboundSnapshot.docs.map(doc => doc.id);
        }
      } catch (socialGraphError) {
        // If social_graph doesn't exist or has permission issues, continue to fallback
      }

      // Fallback to old follows collection if no following found via social_graph
      if (followingIds.length === 0) {
        const followingQuery = query(
          collection(db, 'follows'),
          where('followerId', '==', userId)
        );
        const followingSnapshot = await getDocs(followingQuery);

        followingIds = followingSnapshot.docs.map(doc => {
          const data = doc.data();
          return data.followingId;
        });
      }

      if (followingIds.length === 0) {
        return [];
      }

      // Get user details for all following
      const following: User[] = [];

      for (const followingId of followingIds) {
        const userDoc = await getDoc(doc(db, 'users', followingId));
        if (userDoc.exists()) {
          const userData = userDoc.data();
          following.push({
            id: userDoc.id,
            username: userData.username,
            email: userData.email,
            name: userData.name,
            bio: userData.bio || '',
            profilePicture: userData.profilePicture,
            followersCount: userData.followersCount || 0,
            followingCount: userData.followingCount || 0,
            createdAt: userData.createdAt?.toDate() || new Date(),
            updatedAt: userData.updatedAt?.toDate() || new Date(),
          });
        } else {
        }
      }

      return following;
    } catch (error) {
      // Handle permission errors silently for privacy-protected data
      if (isPermissionError(error)) {
        return [];
      }
      console.error('[getFollowing] Error:', error);
      const apiError = handleError(error, 'Fetch following', {
        defaultMessage: ERROR_MESSAGES.PROFILE_LOAD_FAILED,
      });
      throw new Error(apiError.userMessage);
    }
  },

  /**
   * Recalculate and sync follower/following counts from the follows collection
   * Useful for fixing stale or incorrect counts
   *
   * @param userId - The user ID whose counts to sync
   * @returns Promise resolving to the updated follower and following counts
   * @throws Error if sync fails
   */
  syncFollowerCounts: async (
    userId: string
  ): Promise<{ followersCount: number; followingCount: number }> => {
    try {
      // Count followers (people who follow this user)
      const followersQuery = query(
        collection(db, 'follows'),
        where('followingId', '==', userId)
      );
      const followersSnapshot = await getDocs(followersQuery);
      const followersCount = followersSnapshot.size;

      // Count following (people this user follows)
      const followingQuery = query(
        collection(db, 'follows'),
        where('followerId', '==', userId)
      );
      const followingSnapshot = await getDocs(followingQuery);
      const followingCount = followingSnapshot.size;

      // Update the user document with correct counts
      await updateDoc(doc(db, 'users', userId), {
        followersCount,
        followingCount,
        updatedAt: serverTimestamp(),
      });

      return { followersCount, followingCount };
    } catch (error) {
      const apiError = handleError(error, 'Sync follower counts', {
        defaultMessage: 'Failed to sync follower counts',
      });
      throw new Error(apiError.userMessage);
    }
  },

  /**
   * Search for users by username or name (case-insensitive prefix matching)
   *
   * @param searchTerm - The search term to match against username/name
   * @param page - Page number for pagination (default: 1)
   * @param limitCount - Maximum number of results to return (default: 20)
   * @returns Promise resolving to search results with pagination metadata
   * @throws Error if rate limit exceeded or search fails
   */
  searchUsers: async (
    searchTerm: string,
    page: number = 1,
    limitCount: number = 20
  ): Promise<{
    users: UserSearchResult[];
    totalCount: number;
    hasMore: boolean;
  }> => {
    try {
      // Rate limitFn search operations
      if (auth.currentUser) {
        checkRateLimit(auth.currentUser.uid, 'SEARCH');
      }

      const term = (searchTerm || '').trim();
      if (!term) {
        return { users: [], totalCount: 0, hasMore: false };
      }

      // Convert search term to lowercase for case-insensitive search
      const termLower = term.toLowerCase();

      // 1) Search by username prefix (case-insensitive)
      const usernameQ = query(
        collection(db, 'users'),
        orderBy('usernameLower'),
        where('usernameLower', '>=', termLower),
        where('usernameLower', '<=', termLower + '\uf8ff'),
        limitFn(limitCount)
      );

      // 2) Search by name prefix (case-insensitive)
      const nameQ = query(
        collection(db, 'users'),
        orderBy('nameLower'),
        where('nameLower', '>=', termLower),
        where('nameLower', '<=', termLower + '\uf8ff'),
        limitFn(limitCount)
      );

      const [usernameSnap, nameSnap] = await Promise.all([
        getDocs(usernameQ),
        getDocs(nameQ),
      ]);

      // Merge and de-duplicate results, prefer username matches first
      const byId: Record<string, UserSearchResult> = {};
      const pushDoc = (docSnap: any) => {
        const userData = docSnap.data();
        byId[docSnap.id] = {
          id: docSnap.id,
          username: userData.username,
          name: userData.name,
          bio: userData.bio,
          profilePicture: userData.profilePicture,
          followersCount:
            userData.inboundFriendshipCount || userData.followersCount || 0,
          isFollowing: false,
        } as UserSearchResult;
      };

      usernameSnap.forEach(pushDoc);
      nameSnap.forEach(d => {
        if (!byId[d.id]) pushDoc(d);
      });

      // Convert to array and apply a basic relevance sort: exact prefix on username > name > others
      let users = Object.values(byId)
        .sort((a, b) => {
          const t = term.toLowerCase();
          const aUser = a.username?.toLowerCase() || '';
          const bUser = b.username?.toLowerCase() || '';
          const aName = a.name?.toLowerCase() || '';
          const bName = b.name?.toLowerCase() || '';

          const aScore =
            (aUser.startsWith(t) ? 2 : 0) + (aName.startsWith(t) ? 1 : 0);
          const bScore =
            (bUser.startsWith(t) ? 2 : 0) + (bName.startsWith(t) ? 1 : 0);
          return bScore - aScore;
        })
        .slice(0, limitCount);

      // Check if current user is following each user
      if (auth.currentUser) {
        const followingChecks = await Promise.all(
          users.map(async user => {
            if (user.id === auth.currentUser!.uid) {
              return { ...user, isFollowing: false }; // Don't check for own profile
            }
            const socialGraphDoc = await getDoc(
              doc(db, `social_graph/${auth.currentUser!.uid}/outbound`, user.id)
            );
            return { ...user, isFollowing: socialGraphDoc.exists() };
          })
        );
        users = followingChecks;
      }

      return {
        users,
        totalCount: users.length,
        hasMore: users.length === limitCount,
      };
    } catch (error) {
      const apiError = handleError(error, 'Search users', {
        defaultMessage: 'Failed to search users',
      });
      throw new Error(apiError.userMessage);
    }
  },

  /**
   * Get suggested users to follow based on popularity
   * Filters out current user and users already being followed
   *
   * @param limitCount - Maximum number of suggestions to return (default: 10)
   * @returns Promise resolving to array of suggested users with follow reasons
   * @throws Error if fetch fails (returns empty array on error)
   */
  getSuggestedUsers: async (
    limitCount: number = 10
  ): Promise<SuggestedUser[]> => {
    try {
      if (!auth.currentUser) {
        return [];
      }

      // Get list of users we're already following directly from social_graph
      const outboundRef = collection(
        db,
        `social_graph/${auth.currentUser.uid}/outbound`
      );
      const outboundSnapshot = await getDocs(outboundRef);
      const followingIds = new Set(outboundSnapshot.docs.map(doc => doc.id));
      followingIds.add(auth.currentUser.uid); // Also exclude current user

      // Also check old follows collection for backward compatibility if social_graph is empty
      if (followingIds.size === 1) {
        // Only current user in set
        const followingQuery = query(
          collection(db, 'follows'),
          where('followerId', '==', auth.currentUser.uid)
        );
        const followingSnapshot = await getDocs(followingQuery);
        followingSnapshot.docs.forEach(doc => {
          const data = doc.data();
          if (data.followingId) {
            followingIds.add(data.followingId);
          }
        });
      }

      // Query users ordered by popularity (follower count descending)
      // Fetch a reasonable buffer to account for already-followed users
      // If we need 5 suggestions and user follows ~20 people, fetching 30 should be sufficient
      const fetchLimit = Math.min(limitCount * 5, 50); // 5x multiplier, max 50
      const usersQuery = query(
        collection(db, 'users'),
        where('profileVisibility', '==', 'everyone'),
        orderBy('followersCount', 'desc'),
        limitFn(fetchLimit)
      );

      const querySnapshot = await getDocs(usersQuery);
      const suggestions: SuggestedUser[] = [];

      // Filter and collect until we have enough suggestions
      for (const doc of querySnapshot.docs) {
        if (suggestions.length >= limitCount) {
          break;
        }

        // Skip users we're already following or current user
        if (followingIds.has(doc.id)) {
          continue;
        }

        const userData = doc.data();
        suggestions.push({
          id: doc.id,
          username: userData.username,
          name: userData.name,
          bio: userData.bio,
          profilePicture: userData.profilePicture,
          followersCount: userData.followersCount || 0,
          reason:
            (userData.followersCount || 0) > 10
              ? 'popular_user'
              : 'similar_interests',
          isFollowing: false,
        });
      }

      return suggestions;
    } catch (error) {
      handleError(error, 'getting suggested users', {
        severity: ErrorSeverity.ERROR,
      });
      const apiError = handleError(error, 'Get suggested users', {
        defaultMessage: 'Failed to get suggested users',
      });
      throw new Error(apiError.userMessage);
    }
  },

  /**
   * Get privacy settings for the authenticated user
   *
   * @returns Promise resolving to the user's privacy settings
   * @throws Error if user is not authenticated or fetch fails
   */
  getPrivacySettings: async (): Promise<PrivacySettings> => {
    try {
      if (!auth.currentUser) {
        throw new Error('User not authenticated');
      }

      const userDoc = await getDoc(doc(db, 'users', auth.currentUser.uid));
      const userData = userDoc.data();

      return {
        profileVisibility: userData?.profileVisibility || 'everyone',
        activityVisibility: userData?.activityVisibility || 'everyone',
        projectVisibility: userData?.projectVisibility || 'everyone',
        blockedUsers: userData?.blockedUsers || [],
      };
    } catch (error) {
      const apiError = handleError(error, 'Get privacy settings', {
        defaultMessage: 'Failed to get privacy settings',
      });
      throw new Error(apiError.userMessage);
    }
  },

  /**
   * Update privacy settings for the authenticated user
   *
   * @param settings - Partial privacy settings with fields to update
   * @returns Promise resolving to the updated privacy settings
   * @throws Error if user is not authenticated or update fails
   */
  updatePrivacySettings: async (
    settings: Partial<PrivacySettings>
  ): Promise<PrivacySettings> => {
    try {
      if (!auth.currentUser) {
        throw new Error('User not authenticated');
      }

      const updateData = {
        ...settings,
        updatedAt: serverTimestamp(),
      };

      await updateDoc(doc(db, 'users', auth.currentUser.uid), updateData);

      return {
        profileVisibility: settings.profileVisibility || 'everyone',
        activityVisibility: settings.activityVisibility || 'everyone',
        projectVisibility: settings.projectVisibility || 'everyone',
        blockedUsers: settings.blockedUsers || [],
      };
    } catch (error) {
      const apiError = handleError(error, 'Update privacy settings', {
        defaultMessage: 'Failed to update privacy settings',
      });
      throw new Error(apiError.userMessage);
    }
  },

  /**
   * Check if a username is available (not already taken)
   *
   * @param username - The username to check
   * @returns Promise resolving to true if available, false if taken
   * @throws Error if check fails
   */
  checkUsernameAvailability: async (username: string): Promise<boolean> => {
    try {
      const usersQuery = query(
        collection(db, 'users'),
        where('username', '==', username),
        limitFn(1)
      );
      const querySnapshot = await getDocs(usersQuery);
      return querySnapshot.empty;
    } catch (error) {
      // Handle Firebase permission errors gracefully
      if (isPermissionError(error)) {
        handleError(error, 'Check username availability', {
          severity: ErrorSeverity.WARNING,
        });
        // In case of permission error, assume username is available to allow registration to proceed
        // The actual uniqueness will be enforced by Firebase Auth and server-side validation
        return true;
      }
      const apiError = handleError(error, 'Check username availability');
      throw new Error(
        apiError.userMessage ||
          'Unable to verify username availability. Please try again.'
      );
    }
  },

  /**
   * Migration utility: Add lowercase username and name fields for search functionality
   * Should only be run once as a data migration
   *
   * @returns Promise resolving to migration statistics (success, failed, total counts)
   * @throws Error if user is not authenticated or migration fails
   */
  migrateUsersToLowercase: async (): Promise<{
    success: number;
    failed: number;
    total: number;
  }> => {
    try {
      if (!auth.currentUser) {
        throw new Error('User not authenticated');
      }

      const usersQuery = query(collection(db, 'users'), limitFn(500));
      const querySnapshot = await getDocs(usersQuery);

      let success = 0;
      let failed = 0;
      const total = querySnapshot.size;

      for (const userDoc of querySnapshot.docs) {
        try {
          const userData = userDoc.data();
          const updates: any = { updatedAt: serverTimestamp() };

          if (userData.username && !userData.usernameLower) {
            updates.usernameLower = userData.username.toLowerCase();
          }

          if (userData.name && !userData.nameLower) {
            updates.nameLower = userData.name.toLowerCase();
          }

          // Only update if there are new fields to add
          if (Object.keys(updates).length > 1) {
            await updateDoc(doc(db, 'users', userDoc.id), updates);
            success++;
          }
        } catch (error) {
          failed++;
          console.error(`Failed to migrate user ${userDoc.id}:`, error);
        }
      }

      const result = { success, failed, total };
      return result;
    } catch (error) {
      const apiError = handleError(error, 'Migrate users to lowercase', {
        defaultMessage: 'Failed to migrate users',
      });
      throw new Error(apiError.userMessage);
    }
  },

  /**
   * Delete user account and all associated data permanently
   * Includes sessions, comments, follows, projects, tasks, streaks, and profile picture
   *
   * @returns Promise that resolves when account is completely deleted
   * @throws Error if user is not authenticated or deletion fails
   */
  deleteAccount: async (): Promise<void> => {
    try {
      if (!auth.currentUser) {
        throw new Error('No authenticated user');
      }

      const userId = auth.currentUser.uid;

      // 1. Delete all user's sessions
      const sessionsQuery = query(
        collection(db, 'sessions'),
        where('userId', '==', userId)
      );
      const sessionsSnapshot = await getDocs(sessionsQuery);
      const sessionDeletes = sessionsSnapshot.docs.map(doc =>
        deleteDoc(doc.ref)
      );
      await Promise.all(sessionDeletes);

      // 2. Delete all user's comments
      const commentsQuery = query(
        collection(db, 'comments'),
        where('userId', '==', userId)
      );
      const commentsSnapshot = await getDocs(commentsQuery);
      const commentDeletes = commentsSnapshot.docs.map(doc =>
        deleteDoc(doc.ref)
      );
      await Promise.all(commentDeletes);

      // 3. Delete all follow relationships where user is follower or following
      const followsAsFollowerQuery = query(
        collection(db, 'follows'),
        where('followerId', '==', userId)
      );
      const followsAsFollowingQuery = query(
        collection(db, 'follows'),
        where('followingId', '==', userId)
      );
      const [followsAsFollowerSnapshot, followsAsFollowingSnapshot] =
        await Promise.all([
          getDocs(followsAsFollowerQuery),
          getDocs(followsAsFollowingQuery),
        ]);
      const followDeletes = [
        ...followsAsFollowerSnapshot.docs.map(doc => deleteDoc(doc.ref)),
        ...followsAsFollowingSnapshot.docs.map(doc => deleteDoc(doc.ref)),
      ];
      await Promise.all(followDeletes);

      // 4. Delete user's projects and their tasks
      const projectsRef = collection(db, 'projects', userId, 'userProjects');
      const projectsSnapshot = await getDocs(projectsRef);

      for (const projectDoc of projectsSnapshot.docs) {
        // Delete tasks in each project
        const tasksRef = collection(
          db,
          'projects',
          userId,
          'userProjects',
          projectDoc.id,
          'tasks'
        );
        const tasksSnapshot = await getDocs(tasksRef);
        const taskDeletes = tasksSnapshot.docs.map(doc => deleteDoc(doc.ref));
        await Promise.all(taskDeletes);

        // Delete the project
        await deleteDoc(projectDoc.ref);
      }

      // 5. Delete user's streak data
      try {
        const streakRef = doc(db, 'streaks', userId);
        await deleteDoc(streakRef);
      } catch (error) {}

      // 6. Delete user's active session data
      try {
        const activeSessionRef = doc(
          db,
          'users',
          userId,
          'activeSession',
          'current'
        );
        await deleteDoc(activeSessionRef);
      } catch (error) {}

      // 7. Delete profile picture from storage if it exists
      try {
        const userDoc = await getDoc(doc(db, 'users', userId));
        const userData = userDoc.data();
        if (userData?.profilePicture) {
          const storageRef = ref(storage, `profile-pictures/${userId}`);
          await deleteObject(storageRef);
        }
      } catch (error) {}

      // 8. Delete the user document from Firestore
      await deleteDoc(doc(db, 'users', userId));

      // 9. Finally, delete the Firebase Auth user
      await auth.currentUser.delete();
    } catch (error) {
      const apiError = handleError(error, 'Delete account', {
        defaultMessage:
          'Failed to delete account. Please try logging out and back in, then try again.',
      });
      throw new Error(apiError.userMessage);
    }
  },
};

/**
 * Client-side caching utilities to reduce Firestore reads
 *
 * This module provides:
 * 1. localStorage caching for user preferences and static data
 * 2. sessionStorage caching for temporary data within a session
 * 3. Memory caching for frequently accessed data
 * 4. Query deduplication to prevent duplicate concurrent requests
 */

import { debug } from './debug';
import { CACHE_TIMES } from '@/config/constants';

// ==================== STORAGE UTILITIES ====================

const STORAGE_PREFIX = 'ambira_';
const CACHE_VERSION = 'v1';

interface CacheItem<T> {
  data: T;
  timestamp: number;
  version: string;
}

/**
 * Safely access localStorage with error handling
 */
function getStorage(type: 'local' | 'session'): Storage | null {
  try {
    const storage = type === 'local' ? localStorage : sessionStorage;
    // Test if storage is accessible
    const testKey = `${STORAGE_PREFIX}test`;
    storage.setItem(testKey, 'test');
    storage.removeItem(testKey);
    return storage;
  } catch (e) {
    debug.warn(`${type}Storage is not available:`, e);
    return null;
  }
}

/**
 * LocalStorage cache with TTL support
 */
export class LocalCache {
  private static readonly PREFIX = `${STORAGE_PREFIX}${CACHE_VERSION}_`;

  static set<T>(key: string, data: T, ttlMs: number = CACHE_TIMES.DAILY): void {
    const storage = getStorage('local');
    if (!storage) return;

    try {
      const item: CacheItem<T> = {
        data,
        timestamp: Date.now(),
        version: CACHE_VERSION,
      };
      storage.setItem(`${this.PREFIX}${key}`, JSON.stringify(item));
    } catch (e) {
      // Storage might be full, try to clear old items
      this.clearExpired();
      try {
        const item: CacheItem<T> = {
          data,
          timestamp: Date.now(),
          version: CACHE_VERSION,
        };
        storage.setItem(`${this.PREFIX}${key}`, JSON.stringify(item));
      } catch (e2) {
        debug.warn('Failed to cache data:', e2);
      }
    }
  }

  static get<T>(key: string, ttlMs: number = CACHE_TIMES.DAILY): T | null {
    const storage = getStorage('local');
    if (!storage) return null;

    try {
      const itemStr = storage.getItem(`${this.PREFIX}${key}`);
      if (!itemStr) return null;

      const item: CacheItem<T> = JSON.parse(itemStr);

      // Check version
      if (item.version !== CACHE_VERSION) {
        storage.removeItem(`${this.PREFIX}${key}`);
        return null;
      }

      // Check TTL
      const age = Date.now() - item.timestamp;
      if (age > ttlMs) {
        storage.removeItem(`${this.PREFIX}${key}`);
        return null;
      }

      return item.data;
    } catch (e) {
      debug.warn('Failed to retrieve cached data:', e);
      return null;
    }
  }

  static remove(key: string): void {
    const storage = getStorage('local');
    if (!storage) return;
    storage.removeItem(`${this.PREFIX}${key}`);
  }

  static clear(): void {
    const storage = getStorage('local');
    if (!storage) return;

    try {
      const keys = Object.keys(storage);
      keys.forEach(key => {
        if (key.startsWith(this.PREFIX)) {
          storage.removeItem(key);
        }
      });
    } catch (e) {
      debug.warn('Failed to clear cache:', e);
    }
  }

  static clearExpired(): void {
    const storage = getStorage('local');
    if (!storage) return;

    try {
      const keys = Object.keys(storage);
      const now = Date.now();

      keys.forEach(key => {
        if (!key.startsWith(this.PREFIX)) return;

        try {
          const itemStr = storage.getItem(key);
          if (!itemStr) return;

          const item: CacheItem<unknown> = JSON.parse(itemStr);
          // Remove items older than 7 days
          if (now - item.timestamp > CACHE_TIMES.WEEKLY) {
            storage.removeItem(key);
          }
        } catch (e) {
          // Invalid item, remove it
          storage.removeItem(key);
        }
      });
    } catch (e) {
      debug.warn('Failed to clear expired cache:', e);
    }
  }
}

/**
 * SessionStorage cache (cleared when browser tab closes)
 */
export class SessionCache {
  private static readonly PREFIX = `${STORAGE_PREFIX}${CACHE_VERSION}_`;

  static set<T>(key: string, data: T): void {
    const storage = getStorage('session');
    if (!storage) return;

    try {
      const item: CacheItem<T> = {
        data,
        timestamp: Date.now(),
        version: CACHE_VERSION,
      };
      storage.setItem(`${this.PREFIX}${key}`, JSON.stringify(item));
    } catch (e) {
      debug.warn('Failed to cache data in session:', e);
    }
  }

  static get<T>(key: string): T | null {
    const storage = getStorage('session');
    if (!storage) return null;

    try {
      const itemStr = storage.getItem(`${this.PREFIX}${key}`);
      if (!itemStr) return null;

      const item: CacheItem<T> = JSON.parse(itemStr);

      // Check version
      if (item.version !== CACHE_VERSION) {
        storage.removeItem(`${this.PREFIX}${key}`);
        return null;
      }

      return item.data;
    } catch (e) {
      debug.warn('Failed to retrieve cached data from session:', e);
      return null;
    }
  }

  static remove(key: string): void {
    const storage = getStorage('session');
    if (!storage) return;
    storage.removeItem(`${this.PREFIX}${key}`);
  }

  static clear(): void {
    const storage = getStorage('session');
    if (!storage) return;

    try {
      const keys = Object.keys(storage);
      keys.forEach(key => {
        if (key.startsWith(this.PREFIX)) {
          storage.removeItem(key);
        }
      });
    } catch (e) {
      debug.warn('Failed to clear session cache:', e);
    }
  }
}

// ==================== MEMORY CACHE ====================

interface MemoryCacheItem<T> {
  data: T;
  timestamp: number;
}

/**
 * In-memory cache for ultra-fast access (cleared on page refresh)
 */
export class MemoryCache {
  private static cache = new Map<string, MemoryCacheItem<any>>();
  private static readonly MAX_SIZE = 100; // Prevent memory leaks

  static set<T>(key: string, data: T, ttlMs: number = CACHE_TIMES.MEDIUM): void {
    // If cache is too large, remove oldest items
    if (this.cache.size >= this.MAX_SIZE) {
      const oldestKey = this.cache.keys().next().value as string | undefined;
      if (oldestKey) {
        this.cache.delete(oldestKey);
      }
    }

    this.cache.set(key, {
      data,
      timestamp: Date.now(),
    });
  }

  static get<T>(key: string, ttlMs: number = CACHE_TIMES.MEDIUM): T | null {
    const item = this.cache.get(key);
    if (!item) return null;

    const age = Date.now() - item.timestamp;
    if (age > ttlMs) {
      this.cache.delete(key);
      return null;
    }

    return item.data;
  }

  static remove(key: string): void {
    this.cache.delete(key);
  }

  static clear(): void {
    this.cache.clear();
  }

  static clearExpired(ttlMs: number = CACHE_TIMES.MEDIUM): void {
    const now = Date.now();
    for (const [key, item] of this.cache.entries()) {
      if (now - item.timestamp > ttlMs) {
        this.cache.delete(key);
      }
    }
  }
}

// ==================== QUERY DEDUPLICATION ====================

interface PendingQuery<T> {
  promise: Promise<T>;
  timestamp: number;
}

/**
 * Prevents duplicate concurrent requests for the same data
 */
export class QueryDeduplicator {
  private static pending = new Map<string, PendingQuery<any>>();

  static async dedupe<T>(
    key: string,
    queryFn: () => Promise<T>,
    ttlMs: number = 1000
  ): Promise<T> {
    // Check if there's a pending request for this key
    const existing = this.pending.get(key);
    if (existing) {
      const age = Date.now() - existing.timestamp;
      if (age < ttlMs) {
        // Return the existing promise
        return existing.promise;
      }
    }

    // Create new request
    const promise = queryFn()
      .finally(() => {
        // Clean up after request completes
        this.pending.delete(key);
      });

    this.pending.set(key, {
      promise,
      timestamp: Date.now(),
    });

    return promise;
  }

  static clear(): void {
    this.pending.clear();
  }
}

// ==================== CACHE HELPERS ====================

/**
 * Multi-layer cache wrapper for Firestore queries
 *
 * Checks caches in order:
 * 1. Memory cache (fastest)
 * 2. SessionStorage (fast, tab-scoped)
 * 3. LocalStorage (persistent)
 * 4. Firestore query (slowest)
 */
export async function cachedQuery<T>(
  key: string,
  queryFn: () => Promise<T>,
  options: {
    memoryTtl?: number;
    sessionCache?: boolean;
    localTtl?: number;
    dedupe?: boolean;
  } = {}
): Promise<T> {
  const {
    memoryTtl = CACHE_TIMES.SHORT, // 1 minute in memory
    sessionCache = false,
    localTtl = 0, // 0 = no localStorage caching
    dedupe = true,
  } = options;

  // Check memory cache first
  const memoryData = MemoryCache.get<T>(key, memoryTtl);
  if (memoryData !== null) {
    return memoryData;
  }

  // Check session cache
  if (sessionCache) {
    const sessionData = SessionCache.get<T>(key);
    if (sessionData !== null) {
      // Store in memory for even faster next access
      MemoryCache.set(key, sessionData, memoryTtl);
      return sessionData;
    }
  }

  // Check local cache
  if (localTtl > 0) {
    const localData = LocalCache.get<T>(key, localTtl);
    if (localData !== null) {
      // Store in higher-level caches
      MemoryCache.set(key, localData, memoryTtl);
      if (sessionCache) {
        SessionCache.set(key, localData);
      }
      return localData;
    }
  }

  // Execute query (with optional deduplication)
  const executeQuery = async (): Promise<T> => {
    const data = await queryFn();

    // Store in all caches
    MemoryCache.set(key, data, memoryTtl);
    if (sessionCache) {
      SessionCache.set(key, data);
    }
    if (localTtl > 0) {
      LocalCache.set(key, data, localTtl);
    }

    return data;
  };

  if (dedupe) {
    return QueryDeduplicator.dedupe(key, executeQuery);
  } else {
    return executeQuery();
  }
}

/**
 * Invalidate cached data across all layers
 */
export function invalidateCache(key: string): void {
  MemoryCache.remove(key);
  SessionCache.remove(key);
  LocalCache.remove(key);
}

/**
 * Invalidate all caches with a specific prefix
 */
export function invalidateCachePrefix(prefix: string): void {
  // Memory cache
  MemoryCache.clear(); // Simple clear for now

  // Session and local storage would need iteration
  // This is a simplified version
}

/**
 * Clear all caches (useful on logout)
 */
export function clearAllCaches(): void {
  MemoryCache.clear();
  SessionCache.clear();
  LocalCache.clear();
  QueryDeduplicator.clear();
}

// Clear expired items periodically
if (typeof window !== 'undefined') {
  setInterval(() => {
    MemoryCache.clearExpired();
    LocalCache.clearExpired();
  }, CACHE_TIMES.MEDIUM); // Every 5 minutes
}

