/**
 * Centralized error handling utility for Firebase API operations
 * Provides standardized error responses and user-friendly messages
 */

import { FirebaseError } from 'firebase/app';
import { debug, isDevelopment } from './debug';

/**
 * Standard error response interface
 */
export interface ApiError {
  code: string;
  message: string;
  userMessage: string;
  originalError?: unknown;
}

/**
 * Error severity levels for logging
 */
export enum ErrorSeverity {
  INFO = 'info',
  WARNING = 'warning',
  ERROR = 'error',
  CRITICAL = 'critical',
}

/**
 * Firebase error code to user-friendly message mapping
 */
const FIREBASE_ERROR_MESSAGES: Record<string, string> = {
  // Auth errors
  'auth/email-already-in-use': 'This email is already registered. Please sign in or use a different email.',
  'auth/invalid-email': 'Please enter a valid email address.',
  'auth/operation-not-allowed': 'This operation is not allowed. Please contact support.',
  'auth/weak-password': 'Password is too weak. Please use at least 6 characters.',
  'auth/user-disabled': 'This account has been disabled. Please contact support.',
  'auth/user-not-found': 'No account found with this email. Please check your email or sign up.',
  'auth/wrong-password': 'Incorrect password. Please try again.',
  'auth/invalid-credential': 'Invalid email or password. Please try again.',
  'auth/too-many-requests': 'Too many failed attempts. Please try again later.',
  'auth/network-request-failed': 'Network error. Please check your connection and try again.',

  // Firestore errors
  'permission-denied': 'You don\'t have permission to perform this action.',
  'not-found': 'The requested resource was not found.',
  'already-exists': 'This resource already exists.',
  'resource-exhausted': 'Too many requests. Please try again later.',
  'failed-precondition': 'Operation cannot be completed. Please refresh and try again.',
  'aborted': 'Operation was cancelled. Please try again.',
  'out-of-range': 'Invalid value provided.',
  'unimplemented': 'This feature is not yet available.',
  'internal': 'An internal error occurred. Please try again later.',
  'unavailable': 'Service is temporarily unavailable. Please try again later.',
  'data-loss': 'Data error occurred. Please contact support.',
  'unauthenticated': 'Please sign in to continue.',
  'deadline-exceeded': 'Request timed out. Please try again.',
  'cancelled': 'Operation was cancelled.',

  // Storage errors
  'storage/unauthorized': 'You don\'t have permission to access this file.',
  'storage/canceled': 'Upload was cancelled.',
  'storage/unknown': 'An error occurred while uploading. Please try again.',
  'storage/object-not-found': 'File not found.',
  'storage/bucket-not-found': 'Storage bucket not configured. Please contact support.',
  'storage/quota-exceeded': 'Storage quota exceeded. Please contact support.',
  'storage/unauthenticated': 'Please sign in to upload files.',
  'storage/retry-limit-exceeded': 'Upload failed after multiple attempts. Please try again later.',
  'storage/invalid-checksum': 'File validation failed. Please try uploading again.',
};

/**
 * Extract error code from various error types
 */
function getErrorCode(error: unknown): string {
  if (!error) return 'unknown';

  // Firebase error
  if (typeof error === 'object' && 'code' in error) {
    return (error as FirebaseError).code || 'unknown';
  }

  // Generic Error
  if (error instanceof Error && 'code' in error) {
    return (error as any).code || 'unknown';
  }

  return 'unknown';
}

/**
 * Extract error message from various error types
 */
function getErrorMessage(error: unknown): string {
  if (!error) return 'An unknown error occurred';

  if (error instanceof Error) {
    return error.message;
  }

  if (typeof error === 'string') {
    return error;
  }

  if (typeof error === 'object' && 'message' in error) {
    return String((error as any).message);
  }

  return 'An unknown error occurred';
}

/**
 * Get user-friendly message for error code
 */
function getUserFriendlyMessage(code: string, defaultMessage?: string): string {
  const message = FIREBASE_ERROR_MESSAGES[code];
  if (message) return message;

  // Try to extract the error type from code (e.g., 'auth/user-not-found' -> 'user-not-found')
  const parts = code.split('/');
  if (parts.length > 1) {
    const subCode = parts.slice(1).join('/');
    const subMessage = FIREBASE_ERROR_MESSAGES[subCode];
    if (subMessage) return subMessage;
  }

  return defaultMessage || 'An error occurred. Please try again.';
}

/**
 * Create standardized API error object
 */
export function createApiError(
  error: unknown,
  context: string,
  defaultMessage?: string
): ApiError {
  const code = getErrorCode(error);
  const message = getErrorMessage(error);
  const userMessage = getUserFriendlyMessage(code, defaultMessage);

  return {
    code,
    message: `${context}: ${message}`,
    userMessage,
    originalError: error,
  };
}

/**
 * Log error with appropriate severity
 */
export function logError(
  apiError: ApiError,
  severity: ErrorSeverity = ErrorSeverity.ERROR,
  additionalContext?: Record<string, unknown>
): void {
  // Only log errors in development or for critical errors
  if (isDevelopment || severity === ErrorSeverity.CRITICAL) {
    const logMethod = severity === ErrorSeverity.ERROR || severity === ErrorSeverity.CRITICAL
      ? debug.error
      : severity === ErrorSeverity.WARNING
      ? debug.warn
      : debug.log;

    // Log as a single string to avoid object logging which triggers intercept-console-error
    const errorMessage = `[API Error] ${JSON.stringify({
      code: apiError.code,
      message: apiError.message,
      userMessage: apiError.userMessage,
      severity,
      ...additionalContext,
    })}`;

    logMethod(errorMessage);

    // Log original error for debugging
    if (apiError.originalError && severity === ErrorSeverity.CRITICAL) {
      const originalErrorMsg = apiError.originalError instanceof Error
        ? apiError.originalError.message
        : String(apiError.originalError);
      debug.error(`[Original Error] ${originalErrorMsg}`);
    }
  }
}

/**
 * Handle error with logging and return standardized error
 */
export function handleError(
  error: unknown,
  context: string,
  options: {
    defaultMessage?: string;
    severity?: ErrorSeverity;
    logContext?: Record<string, unknown>;
    silent?: boolean; // Don't log to console
  } = {}
): ApiError {
  const apiError = createApiError(error, context, options.defaultMessage);

  if (!options.silent) {
    logError(apiError, options.severity || ErrorSeverity.ERROR, options.logContext);
  }

  return apiError;
}

/**
 * Check if error is a permission denied error
 */
export function isPermissionError(error: unknown): boolean {
  const code = getErrorCode(error);
  return code === 'permission-denied' || code === 'auth/unauthorized';
}

/**
 * Check if error is a not found error
 */
export function isNotFoundError(error: unknown): boolean {
  const code = getErrorCode(error);
  return code === 'not-found' || code === 'storage/object-not-found';
}

/**
 * Check if error is an authentication error
 */
export function isAuthError(error: unknown): boolean {
  const code = getErrorCode(error);
  return code === 'unauthenticated' || code.startsWith('auth/');
}

/**
 * Check if error is a network error
 */
export function isNetworkError(error: unknown): boolean {
  const code = getErrorCode(error);
  const message = getErrorMessage(error).toLowerCase();
  return (
    code === 'unavailable' ||
    code === 'deadline-exceeded' ||
    code === 'auth/network-request-failed' ||
    message.includes('network') ||
    message.includes('connection')
  );
}

/**
 * Wrapper for async operations with standardized error handling
 */
export async function withErrorHandling<T>(
  operation: () => Promise<T>,
  context: string,
  options: {
    defaultMessage?: string;
    severity?: ErrorSeverity;
    logContext?: Record<string, unknown>;
    silent?: boolean;
    onError?: (error: ApiError) => void;
  } = {}
): Promise<T> {
  try {
    return await operation();
  } catch (error) {
    const apiError = handleError(error, context, options);

    if (options.onError) {
      options.onError(apiError);
    }

    throw new Error(apiError.userMessage);
  }
}

/**
 * Wrapper for operations that should return null on specific errors
 */
export async function withNullOnError<T>(
  operation: () => Promise<T>,
  context: string,
  options: {
    nullOnPermissionDenied?: boolean;
    nullOnNotFound?: boolean;
    silent?: boolean;
  } = {}
): Promise<T | null> {
  try {
    return await operation();
  } catch (error) {
    const shouldReturnNull =
      (options.nullOnPermissionDenied && isPermissionError(error)) ||
      (options.nullOnNotFound && isNotFoundError(error));

    if (shouldReturnNull) {
      return null;
    }

    const apiError = handleError(error, context);
    throw new Error(apiError.userMessage);
  }
}

import { storage } from './firebase';
import {
  ref,
  uploadBytes,
  getDownloadURL,
  deleteObject,
} from 'firebase/storage';
import { auth } from './firebase';
import { checkRateLimit } from './rateLimit';
import { debug } from './debug';
import { TIMEOUTS } from '@/config/constants';
import { TIMEOUT_ERRORS } from '@/config/errorMessages';

export interface ImageUploadResult {
  url: string;
  path: string;
}

/**
 * Check if a file is actually HEIC/HEIF by reading its magic bytes
 */
async function isActuallyHeic(file: File): Promise<boolean> {
  try {
    // Read the first 12 bytes of the file
    const arrayBuffer = await file.slice(0, 12).arrayBuffer();
    const bytes = new Uint8Array(arrayBuffer);

    // Check for HEIC/HEIF file signatures
    // HEIC files start with: ftyp followed by heic, heix, hevc, hevx, heim, heis, hevm, or hevs
    // Bytes 4-11 should contain "ftypheic", "ftypheix", "ftyphevc", etc.
    const signature = String.fromCharCode(...bytes.slice(4, 12));

    return (
      signature.startsWith('ftyphei') ||
      signature.startsWith('ftyphev') ||
      signature.startsWith('ftypmif1')
    ); // mif1 is also used for HEIF
  } catch (error) {
    debug.error('Error checking file signature:', error);
    return false;
  }
}

/**
 * Convert HEIC/HEIF images to JPEG
 */
async function convertHeicToJpeg(file: File): Promise<File> {
  // Check if it's a HEIC/HEIF file by extension or type
  const hasHeicExtension =
    file.name.toLowerCase().endsWith('.heic') ||
    file.name.toLowerCase().endsWith('.heif');
  const hasHeicType = file.type === 'image/heic' || file.type === 'image/heif';

  // Also check the actual file content (magic bytes)
  const isActualHeic = await isActuallyHeic(file);

  const isHeic = hasHeicExtension || hasHeicType || isActualHeic;

  if (!isHeic) {
    return file;
  }

  try {
    const heic2any = (await import('heic2any')).default;

    const convertedBlob = await heic2any({
      blob: file,
      toType: 'image/jpeg',
      quality: 0.9,
    });

    // heic2any can return Blob or Blob[]
    const blob: Blob | undefined = Array.isArray(convertedBlob)
      ? convertedBlob[0]
      : convertedBlob;

    if (!blob) {
      throw new Error('Failed to convert HEIC image: No blob data');
    }

    // Create a new File from the converted blob
    const convertedFile = new File(
      [blob as Blob],
      file.name.replace(/\.heic$/i, '.jpg').replace(/\.heif$/i, '.jpg'),
      { type: 'image/jpeg' }
    );

    return convertedFile;
  } catch (error) {
    debug.error('Failed to convert HEIC:', error);
    throw new Error(
      'Failed to convert HEIC image. Please use JPG or PNG format.'
    );
  }
}

/**
 * Create a timeout promise that rejects after a specified duration
 * @param ms - Timeout duration in milliseconds
 * @param errorMessage - Error message to throw
 */
function createTimeout(ms: number, errorMessage: string): Promise<never> {
  return new Promise((_, reject) => {
    setTimeout(() => reject(new Error(errorMessage)), ms);
  });
}

/**
 * Upload an image to Firebase Storage with timeout protection
 * @param file - The image file to upload
 * @param folder - The storage folder (e.g., 'session-images')
 * @returns The download URL and storage path
 */
export async function uploadImage(
  file: File,
  folder: string = 'session-images'
): Promise<ImageUploadResult> {
  if (!auth.currentUser) {
    throw new Error('User must be authenticated to upload images');
  }

  // Rate limit file uploads
  checkRateLimit(auth.currentUser.uid, 'FILE_UPLOAD');

  // Convert HEIC to JPEG if needed
  let processedFile = file;
  try {
    processedFile = await convertHeicToJpeg(file);
  } catch (error: any) {
    throw error; // Re-throw conversion errors
  }

  // Validate file type after conversion
  if (!processedFile.type.startsWith('image/')) {
    throw new Error('File must be an image (JPG, PNG, GIF, WebP)');
  }

  // Compress if file is larger than 5MB
  const maxSize = 5 * 1024 * 1024; // 5MB
  if (processedFile.size > maxSize) {
    const sizeMB = (processedFile.size / 1024 / 1024).toFixed(1);
    try {
      processedFile = await compressToSize(processedFile, 5);
    } catch (error: any) {
      debug.error('Compression failed:', error);
      throw new Error('Failed to compress image. Please try a smaller file.');
    }
  }

  // Generate unique filename
  const timestamp = Date.now();
  const randomString = Math.random().toString(36).substring(7);
  const extension = processedFile.name.split('.').pop();
  const filename = `${timestamp}_${randomString}.${extension}`;

  // Create storage reference
  const storageRef = ref(
    storage,
    `${folder}/${auth.currentUser.uid}/${filename}`
  );

  try {
    // Upload with timeout protection (30 seconds)
    const uploadPromise = (async () => {
      // Upload the processed file (not the original)
      const snapshot = await uploadBytes(storageRef, processedFile);
      // Get download URL
      const url = await getDownloadURL(storageRef);
      return { url, path: storageRef.fullPath };
    })();

    // Race between upload and timeout
    const result = await Promise.race([
      uploadPromise,
      createTimeout(TIMEOUTS.IMAGE_UPLOAD, TIMEOUT_ERRORS.IMAGE_UPLOAD),
    ]);

    return result;
  } catch (error: any) {
    debug.error('Error uploading image:', error);

    // Check if this is a timeout error
    if (error.message === TIMEOUT_ERRORS.IMAGE_UPLOAD) {
      throw error; // Re-throw timeout errors with user-friendly message
    }

    if (error.code === 'storage/unauthorized') {
      throw new Error('Permission denied. Please make sure you are logged in.');
    }
    throw new Error(error.message || 'Failed to upload image');
  }
}

/**
 * Upload multiple images (max 3)
 * @param files - Array of image files
 * @param folder - The storage folder
 * @returns Array of download URLs and storage paths
 */
export async function uploadImages(
  files: File[],
  folder: string = 'session-images'
): Promise<ImageUploadResult[]> {
  if (files.length > 3) {
    throw new Error('Maximum 3 images allowed');
  }

  const uploadPromises = files.map(file => uploadImage(file, folder));
  return Promise.all(uploadPromises);
}

/**
 * Delete an image from Firebase Storage
 * @param path - The storage path of the image
 */
export async function deleteImage(path: string): Promise<void> {
  if (!auth.currentUser) {
    throw new Error('User must be authenticated to delete images');
  }

  try {
    const storageRef = ref(storage, path);
    await deleteObject(storageRef);
  } catch (error) {
    debug.error('Error deleting image:', error);
    throw new Error('Failed to delete image');
  }
}

/**
 * Delete multiple images
 * @param paths - Array of storage paths
 */
export async function deleteImages(paths: string[]): Promise<void> {
  const deletePromises = paths.map(path => deleteImage(path));
  await Promise.all(deletePromises);
}

/**
 * Compress and resize image before upload using Canvas API
 * @param file - The image file
 * @param maxWidth - Maximum width in pixels
 * @param maxHeight - Maximum height in pixels
 * @param quality - Image quality (0-1)
 * @returns Compressed image file
 */
export async function compressImage(
  file: File,
  maxWidth: number = 1920,
  maxHeight: number = 1920,
  quality: number = 0.8
): Promise<File> {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();

    reader.onload = e => {
      const img = new Image();

      img.onload = () => {
        try {
          // Calculate new dimensions maintaining aspect ratio
          let width = img.width;
          let height = img.height;

          if (width > maxWidth || height > maxHeight) {
            const aspectRatio = width / height;

            if (width > height) {
              width = maxWidth;
              height = width / aspectRatio;
            } else {
              height = maxHeight;
              width = height * aspectRatio;
            }
          }

          // Create canvas and draw resized image
          const canvas = document.createElement('canvas');
          canvas.width = width;
          canvas.height = height;

          const ctx = canvas.getContext('2d');
          if (!ctx) {
            throw new Error('Failed to get canvas context');
          }

          // Use better image smoothing
          ctx.imageSmoothingEnabled = true;
          ctx.imageSmoothingQuality = 'high';
          ctx.drawImage(img, 0, 0, width, height);

          // Convert to blob
          canvas.toBlob(
            blob => {
              if (!blob) {
                reject(new Error('Failed to compress image'));
                return;
              }

              // Create new file from blob
              const compressedFile = new File(
                [blob],
                file.name.replace(/\.[^.]+$/, '.jpg'),
                { type: 'image/jpeg' }
              );

              resolve(compressedFile);
            },
            'image/jpeg',
            quality
          );
        } catch (error) {
          reject(error);
        }
      };

      img.onerror = () => {
        reject(new Error('Failed to load image'));
      };

      img.src = e.target?.result as string;
    };

    reader.onerror = () => {
      reject(new Error('Failed to read file'));
    };

    reader.readAsDataURL(file);
  });
}

/**
 * Progressively compress an image until it's under the target size
 * @param file - The image file
 * @param maxSizeMB - Maximum size in megabytes
 * @returns Compressed image file
 */
async function compressToSize(
  file: File,
  maxSizeMB: number = 5
): Promise<File> {
  const maxSizeBytes = maxSizeMB * 1024 * 1024;

  // If file is already under the limit, return it
  if (file.size <= maxSizeBytes) {
    return file;
  }

  // Start with aggressive compression settings
  let compressedFile = file;

  // Try progressively more aggressive compression
  const attempts = [
    { quality: 0.8, maxDimension: 1920 },
    { quality: 0.7, maxDimension: 1600 },
    { quality: 0.6, maxDimension: 1400 },
    { quality: 0.5, maxDimension: 1200 },
  ];

  for (const attempt of attempts) {
    compressedFile = await compressImage(
      file,
      attempt.maxDimension,
      attempt.maxDimension,
      attempt.quality
    );

    if (compressedFile.size <= maxSizeBytes) {
      return compressedFile;
    }
  }

  // If still too large after all attempts, return the smallest version
  debug.warn(
    `Could not compress to under ${maxSizeMB}MB, using smallest version (${(compressedFile.size / 1024 / 1024).toFixed(2)}MB)`
  );
  return compressedFile;
}

import { QueryClient } from '@tanstack/react-query';

// Create a query client with optimized cache settings
export const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      // Cache data for 5 minutes by default
      staleTime: 5 * 60 * 1000,
      // Keep unused data in cache for 10 minutes
      gcTime: 10 * 60 * 1000,
      // Retry failed requests 1 time
      retry: 1,
      // Refetch on window focus for fresh data
      refetchOnWindowFocus: true,
      // Don't refetch on mount if data is fresh
      refetchOnMount: false,
      // Don't refetch on reconnect if data is fresh
      refetchOnReconnect: false,
    },
    mutations: {
      // Retry failed mutations once
      retry: 1,
    },
  },
});

