/**
 * React Query Auth Hooks
 *
 * This module provides React Query hooks for authentication state management.
 * This is the ONLY place where React Query is used for authentication functionality.
 *
 * CRITICAL PATTERNS:
 * - Firebase onAuthStateChanged listener updates React Query cache
 * - Auth state cached with infinite staleTime (managed by Firebase listener)
 * - All mutations (login, signup, logout) update cache optimistically
 * - Handles OAuth redirect flows and session persistence
 */

import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useRouter } from 'next/navigation';
import { firebaseAuthApi } from '@/lib/api/auth';
import type {
  AuthUser,
  LoginCredentials,
  SignupCredentials,
} from '@/types';

/**
 * Cache Keys for Auth
 * Hierarchical structure for efficient cache invalidation
 */
export const AUTH_KEYS = {
  all: () => ['auth'] as const,
  session: () => [...AUTH_KEYS.all(), 'session'] as const,
  user: (userId: string) => [...AUTH_KEYS.all(), 'user', userId] as const,
} as const;

/**
 * Hook to get current auth state from React Query cache
 *
 * This hook reads from the cache that's updated by Firebase onAuthStateChanged.
 * It does NOT make a direct Firebase call on every render.
 *
 * The cache is updated by AuthInitializer component's Firebase listener.
 */
export function useAuth() {
  return useQuery({
    queryKey: AUTH_KEYS.session(),
    queryFn: async () => {
      // This is only called on initial mount or when cache is invalidated
      try {
        return await firebaseAuthApi.getCurrentUser();
      } catch (error) {
        // User is not authenticated
        return null;
      }
    },
    staleTime: Infinity, // Never auto-refetch - managed by Firebase listener
    retry: false, // Don't retry auth checks
    refetchOnWindowFocus: false, // Firebase listener handles this
    refetchOnMount: false, // Firebase listener handles this
    refetchOnReconnect: false, // Firebase listener handles this
  });
}

/**
 * Login mutation
 * Logs in with email/password and updates auth cache
 */
export function useLogin() {
  const queryClient = useQueryClient();
  const router = useRouter();

  return useMutation({
    mutationFn: async (credentials: LoginCredentials) => {
      return await firebaseAuthApi.login(credentials);
    },
    onSuccess: (data) => {
      // Update auth cache with logged-in user
      queryClient.setQueryData(AUTH_KEYS.session(), data.user);

      // Navigate to home
      router.push('/');
    },
    onError: (error) => {
      // Error is already formatted by firebaseAuthApi
      console.error('[useLogin] Login failed:', error);
    },
  });
}

/**
 * Signup mutation
 * Creates new account and updates auth cache
 */
export function useSignup() {
  const queryClient = useQueryClient();
  const router = useRouter();

  return useMutation({
    mutationFn: async (credentials: SignupCredentials) => {
      return await firebaseAuthApi.signup(credentials);
    },
    onSuccess: (data) => {
      // Update auth cache with new user
      queryClient.setQueryData(AUTH_KEYS.session(), data.user);

      // Navigate to home
      router.push('/');
    },
    onError: (error) => {
      console.error('[useSignup] Signup failed:', error);
    },
  });
}

/**
 * Google Sign-In mutation
 * Handles Google OAuth popup flow
 */
export function useGoogleSignIn() {
  const queryClient = useQueryClient();
  const router = useRouter();

  return useMutation({
    mutationFn: async () => {
      return await firebaseAuthApi.signInWithGoogle();
    },
    onSuccess: (data) => {
      // Update auth cache with Google user
      queryClient.setQueryData(AUTH_KEYS.session(), data.user);

      // Navigate to home
      router.push('/');
    },
    onError: (error: any) => {
      // Special case: redirect in progress (mobile OAuth)
      if (error?.message === 'REDIRECT_IN_PROGRESS') {
        // Don't show error - page will redirect
        return;
      }
      console.error('[useGoogleSignIn] Google sign-in failed:', error);
    },
  });
}

/**
 * Logout mutation
 * Signs out and clears all user-specific caches
 */
export function useLogout() {
  const queryClient = useQueryClient();
  const router = useRouter();

  return useMutation({
    mutationFn: async () => {
      await firebaseAuthApi.logout();
    },
    onMutate: async () => {
      // Optimistically clear auth state
      queryClient.setQueryData(AUTH_KEYS.session(), null);
    },
    onSuccess: () => {
      // Clear ALL caches (user-specific data should not persist)
      queryClient.clear();

      // Ensure auth cache is null
      queryClient.setQueryData(AUTH_KEYS.session(), null);

      // Navigate to landing page
      router.push('/');
    },
    onError: (error) => {
      console.error('[useLogout] Logout failed:', error);

      // Still navigate and clear cache even on error
      queryClient.clear();
      queryClient.setQueryData(AUTH_KEYS.session(), null);
      router.push('/');
    },
  });
}

/**
 * Update profile mutation
 * Updates user profile and refreshes auth cache
 */
export function useUpdateProfile() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async (updates: Partial<AuthUser>) => {
      // This would call firebaseUserApi.updateProfile
      // For now, just return the updated user
      const currentUser = queryClient.getQueryData<AuthUser>(AUTH_KEYS.session());
      if (!currentUser) {
        throw new Error('Not authenticated');
      }
      return { ...currentUser, ...updates };
    },
    onSuccess: (updatedUser) => {
      // Update auth cache
      queryClient.setQueryData(AUTH_KEYS.session(), updatedUser);
    },
  });
}

/**
 * Delete account mutation
 * Deletes user account and clears all caches
 */
export function useDeleteAccount() {
  const queryClient = useQueryClient();
  const router = useRouter();

  return useMutation({
    mutationFn: async () => {
      // This would call firebaseUserApi.deleteAccount
      // For now, just logout
      await firebaseAuthApi.logout();
    },
    onSuccess: () => {
      // Clear all caches
      queryClient.clear();
      queryClient.setQueryData(AUTH_KEYS.session(), null);

      // Navigate to landing
      router.push('/');
    },
  });
}

/**
 * Check username availability
 * Used during signup to validate username
 */
export function useCheckUsernameAvailability() {
  return useMutation({
    mutationFn: async (username: string) => {
      return await firebaseAuthApi.checkUsernameAvailability(username);
    },
  });
}

/**
 * React Query Helper Utilities
 *
 * Reusable utilities for implementing the standardized caching pattern.
 */

import { QueryClient } from '@tanstack/react-query';
import type { CacheKeyFactory } from './types';

/**
 * Create a standard cache key factory for a feature
 *
 * This helper enforces the hierarchical cache key pattern.
 *
 * @example
 * export const GROUPS_KEYS = createCacheKeyFactory('groups', {
 *   lists: () => [],
 *   list: (filters?: string) => [{ filters }],
 *   details: () => [],
 *   detail: (id: string) => [id],
 * });
 *
 * // Results in:
 * // GROUPS_KEYS.all() => ['groups']
 * // GROUPS_KEYS.lists() => ['groups', 'list']
 * // GROUPS_KEYS.list('active') => ['groups', 'list', { filters: 'active' }]
 * // GROUPS_KEYS.details() => ['groups', 'detail']
 * // GROUPS_KEYS.detail('123') => ['groups', 'detail', '123']
 */
export function createCacheKeyFactory<T extends Record<string, (...args: any[]) => any[]>>(
  featureName: string,
  keys: T
): CacheKeyFactory & T & { all: () => readonly [string] } {
  const all = () => [featureName] as const;

  const factory = Object.entries(keys).reduce(
    (acc, [key, fn]) => {
      acc[key] = (...args: any[]) => {
        const base = all();
        const suffix = fn(...args);
        return [...base, ...suffix] as const;
      };
      return acc;
    },
    { all } as any
  );

  return factory;
}

/**
 * Create an invalidation helper for a feature
 *
 * Returns a function that invalidates all queries for a feature.
 *
 * @example
 * export function useInvalidateGroups() {
 *   const queryClient = useQueryClient();
 *   return createInvalidator(queryClient, GROUPS_KEYS.all());
 * }
 *
 * // Usage:
 * const invalidate = useInvalidateGroups();
 * invalidate(); // Invalidates all group queries
 */
export function createInvalidator(queryClient: QueryClient, queryKey: readonly unknown[]) {
  return () => {
    queryClient.invalidateQueries({ queryKey });
  };
}

/**
 * Create a prefetch helper for a feature
 *
 * Returns a function that prefetches data for better UX.
 *
 * @example
 * export function usePrefetchGroup() {
 *   const queryClient = useQueryClient();
 *
 *   return (groupId: string) => {
 *     prefetchQuery(
 *       queryClient,
 *       GROUPS_KEYS.detail(groupId),
 *       () => groupService.getGroupDetails(groupId),
 *       { staleTime: 15 * 60 * 1000 }
 *     );
 *   };
 * }
 *
 * // Usage in component:
 * const prefetchGroup = usePrefetchGroup();
 *
 * <Link
 *   href={`/groups/${group.id}`}
 *   onMouseEnter={() => prefetchGroup(group.id)}
 * >
 */
export function prefetchQuery<TData>(
  queryClient: QueryClient,
  queryKey: readonly unknown[],
  queryFn: () => Promise<TData>,
  options?: { staleTime?: number }
) {
  return queryClient.prefetchQuery({
    queryKey,
    queryFn,
    staleTime: options?.staleTime,
  });
}

/**
 * Create an optimistic update helper
 *
 * Simplifies the boilerplate for optimistic updates with automatic rollback.
 *
 * @example
 * export function useJoinGroup() {
 *   const queryClient = useQueryClient();
 *
 *   return useMutation({
 *     mutationFn: ({ groupId, userId }) => groupService.joinGroup(groupId, userId),
 *     ...createOptimisticUpdate(
 *       queryClient,
 *       ({ groupId }) => GROUPS_KEYS.detail(groupId),
 *       (old: Group, { userId }) => ({
 *         ...old,
 *         memberIds: [...old.memberIds, userId],
 *       })
 *     ),
 *   });
 * }
 */
export function createOptimisticUpdate<TData, TVariables>(
  queryClient: QueryClient,
  getQueryKey: (variables: TVariables) => readonly unknown[],
  updater: (oldData: TData, variables: TVariables) => TData
) {
  return {
    onMutate: async (variables: TVariables) => {
      const queryKey = getQueryKey(variables);

      // Cancel outgoing queries
      await queryClient.cancelQueries({ queryKey });

      // Snapshot previous value
      const previousData = queryClient.getQueryData<TData>(queryKey);

      // Optimistically update
      if (previousData) {
        queryClient.setQueryData<TData>(queryKey, updater(previousData, variables));
      }

      return { previousData, queryKey };
    },

    onError: (_error: Error, _variables: TVariables, context: any) => {
      // Rollback on error
      if (context?.previousData && context?.queryKey) {
        queryClient.setQueryData(context.queryKey, context.previousData);
      }
    },

    onSettled: (_data: any, _error: any, variables: TVariables) => {
      // Invalidate to refetch
      const queryKey = getQueryKey(variables);
      queryClient.invalidateQueries({ queryKey });
    },
  };
}

/**
 * Batch invalidate multiple cache keys
 *
 * Useful when a mutation affects multiple features.
 *
 * @example
 * onSuccess: (_, variables) => {
 *   batchInvalidate(queryClient, [
 *     GROUPS_KEYS.detail(variables.groupId),
 *     GROUPS_KEYS.userGroups(variables.userId),
 *     FEED_KEYS.all(),
 *   ]);
 * }
 */
export function batchInvalidate(queryClient: QueryClient, queryKeys: readonly unknown[][]) {
  queryKeys.forEach((queryKey) => {
    queryClient.invalidateQueries({ queryKey });
  });
}

/**
 * Check if a query is currently cached and fresh
 *
 * Useful for conditional logic based on cache state.
 *
 * @example
 * const hasCachedGroup = isCached(queryClient, GROUPS_KEYS.detail(groupId));
 * if (!hasCachedGroup) {
 *   // Prefetch or show loading state
 * }
 */
export function isCached(queryClient: QueryClient, queryKey: readonly unknown[]): boolean {
  const state = queryClient.getQueryState(queryKey);
  return !!state && state.status === 'success' && !state.isInvalidated;
}

/**
 * Get cached data without triggering a fetch
 *
 * Useful for reading cached data in event handlers or effects.
 *
 * @example
 * const handleClick = () => {
 *   const cachedGroup = getCachedData(queryClient, GROUPS_KEYS.detail(groupId));
 *   if (cachedGroup) {
 *     // Use cached data
 *   }
 * }
 */
export function getCachedData<TData>(
  queryClient: QueryClient,
  queryKey: readonly unknown[]
): TData | undefined {
  return queryClient.getQueryData<TData>(queryKey);
}

/**
 * Set cached data manually
 *
 * Useful for updating cache from external sources (like WebSocket updates).
 *
 * @example
 * // In a WebSocket listener:
 * socket.on('group-updated', (group) => {
 *   setCachedData(queryClient, GROUPS_KEYS.detail(group.id), group);
 * });
 */
export function setCachedData<TData>(
  queryClient: QueryClient,
  queryKey: readonly unknown[],
  data: TData | ((old: TData | undefined) => TData)
) {
  queryClient.setQueryData<TData>(queryKey, data);
}

/**
 * Standardized React Query Types
 *
 * These types ensure consistency across all feature hooks.
 * Use these instead of importing directly from @tanstack/react-query in feature hooks.
 */

import type {
  UseQueryOptions as TanStackUseQueryOptions,
  UseMutationOptions as TanStackUseMutationOptions,
  QueryKey,
} from '@tanstack/react-query';

/**
 * Standard query options for feature hooks
 *
 * @example
 * export function useGroupDetails(
 *   groupId: string,
 *   options?: QueryOptions<Group | null>
 * ) {
 *   return useQuery({
 *     queryKey: GROUPS_KEYS.detail(groupId),
 *     queryFn: () => groupService.getGroupDetails(groupId),
 *     ...options,
 *   });
 * }
 */
export type QueryOptions<
  TData = unknown,
  TError = Error,
  TQueryKey extends QueryKey = QueryKey
> = Partial<TanStackUseQueryOptions<TData, TError, TData, TQueryKey>>;

/**
 * Standard mutation options for feature hooks
 *
 * @example
 * export function useJoinGroup(
 *   options?: MutationOptions<void, { groupId: string; userId: string }>
 * ) {
 *   return useMutation({
 *     mutationFn: ({ groupId, userId }) => groupService.joinGroup(groupId, userId),
 *     ...options,
 *   });
 * }
 */
export type MutationOptions<TData = unknown, TVariables = void, TError = Error> = Partial<
  TanStackUseMutationOptions<TData, TError, TVariables>
>;

/**
 * Cache key factory pattern
 *
 * Use this to create hierarchical cache keys for efficient invalidation.
 *
 * @example
 * export const GROUPS_KEYS: CacheKeyFactory = {
 *   all: () => ['groups'] as const,
 *   lists: () => [...GROUPS_KEYS.all(), 'list'] as const,
 *   list: (filters?: string) => [...GROUPS_KEYS.lists(), { filters }] as const,
 *   details: () => [...GROUPS_KEYS.all(), 'detail'] as const,
 *   detail: (id: string) => [...GROUPS_KEYS.details(), id] as const,
 * };
 */
export type CacheKeyFactory = Record<string, (...args: any[]) => readonly unknown[]>;

/**
 * Standard cache times for different data types
 *
 * Use these constants for consistent caching across features.
 */
export const STANDARD_CACHE_TIMES = {
  /** 30 seconds - Real-time data that changes frequently */
  REAL_TIME: 30 * 1000,

  /** 1 minute - Frequently changing data (feed, search results) */
  SHORT: 1 * 60 * 1000,

  /** 5 minutes - Moderately changing data (sessions, comments) */
  MEDIUM: 5 * 60 * 1000,

  /** 15 minutes - Relatively static data (user profiles, groups) */
  LONG: 15 * 60 * 1000,

  /** 1 hour - Rarely changing data (stats, analytics) */
  VERY_LONG: 60 * 60 * 1000,

  /** Infinite - Data that never changes */
  INFINITE: Infinity,
} as const;

/**
 * Helper type for service methods that feature hooks wrap
 */
export type ServiceMethod<TReturn, TParams extends any[] = []> = (...args: TParams) => Promise<TReturn>;

/**
 * Extract parameters from a service method
 *
 * @example
 * type GroupServiceParams = ServiceParams<typeof groupService.getGroupDetails>;
 * // string
 */
export type ServiceParams<T> = T extends ServiceMethod<any, infer P> ? P : never;

/**
 * Extract return type from a service method
 *
 * @example
 * type GroupReturn = ServiceReturn<typeof groupService.getGroupDetails>;
 * // Group | null
 */
export type ServiceReturn<T> = T extends ServiceMethod<infer R> ? R : never;

import { type ClassValue, clsx } from 'clsx';
import { twMerge } from 'tailwind-merge';

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

/**
 * Parse a date and time string in the user's local timezone
 * @param dateString - Date in YYYY-MM-DD format
 * @param timeString - Time in HH:MM format (24-hour)
 * @returns Date object in local timezone
 *
 * NOTE: DO NOT use `new Date("YYYY-MM-DDTHH:MM")` as it interprets the time as UTC!
 * This function correctly parses the date components to create a Date in local time.
 */
export function parseLocalDateTime(
  dateString: string,
  timeString: string
): Date {
  const dateParts = dateString.split('-').map(Number);
  const timeParts = timeString.split(':').map(Number);

  const year = dateParts[0] ?? 1970;
  const month = dateParts[1] ?? 1;
  const day = dateParts[2] ?? 1;
  const hours = timeParts[0] ?? 0;
  const minutes = timeParts[1] ?? 0;

  // month is 0-indexed in JavaScript Date constructor
  return new Date(year, month - 1, day, hours, minutes, 0, 0);
}

/**
 * Safely convert value to number with fallback
 * Protects against NaN and Infinity that can cause display/calculation issues
 *
 * @param value - Value to convert to number
 * @param fallback - Fallback value if conversion fails (default: 0)
 * @returns Converted number or fallback
 *
 * @example
 * safeNumber('123') // 123
 * safeNumber('abc') // 0
 * safeNumber(null, 10) // 10
 * safeNumber(Infinity, 0) // 0
 */
export const safeNumber = (value: any, fallback: number = 0): number => {
  const num = Number(value);
  return isNaN(num) || !isFinite(num) ? fallback : num;
};

/**
 * Safely parse integer with fallback
 * Protects against NaN from invalid string parsing
 *
 * @param value - Value to parse as integer
 * @param fallback - Fallback value if parsing fails (default: 0)
 * @returns Parsed integer or fallback
 *
 * @example
 * safeParseInt('123') // 123
 * safeParseInt('12.9') // 12
 * safeParseInt('abc') // 0
 */
export const safeParseInt = (value: any, fallback: number = 0): number => {
  const num = parseInt(value, 10);
  return isNaN(num) ? fallback : num;
};

/**
 * Safely parse float with fallback
 * Protects against NaN from invalid string parsing
 *
 * @param value - Value to parse as float
 * @param fallback - Fallback value if parsing fails (default: 0)
 * @returns Parsed float or fallback
 *
 * @example
 * safeParseFloat('123.45') // 123.45
 * safeParseFloat('abc') // 0
 * safeParseFloat(null, 1.5) // 1.5
 */
export const safeParseFloat = (value: any, fallback: number = 0): number => {
  const num = parseFloat(value);
  return isNaN(num) ? fallback : num;
};

/**
 * Check if an array is null, undefined, or empty
 * Consolidates duplicate empty array checks throughout the codebase
 *
 * @param arr - Array to check (can be null or undefined)
 * @returns True if array is null, undefined, or has length 0
 *
 * @example
 * isEmpty([]) // true
 * isEmpty(null) // true
 * isEmpty(undefined) // true
 * isEmpty([1, 2, 3]) // false
 */
export function isEmpty<T>(arr: T[] | null | undefined): boolean {
  return !arr || arr.length === 0;
}

